INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex12',
  'a2-m2-l10',
  12,
  'speaking-analysis',
  $_$Speaking Analysis: Leisure Activities$_$,
  $_${"title":"Speaking Analysis: Leisure Activities","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"Your friend asks if you like swimming.","question":"What is the most natural response?","options":["I like swim.","I like swimming.","I like to swim.","I am liking swimming."],"correctAnswer":"B","explanation":"En inglés, después de verbos como 'like', 'love', 'hate', se usa el gerundio (-ing) para expresar gustos o preferencias. Por eso, 'I like swimming' es la opción correcta.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You rarely play basketball.","question":"How would you express this using 'hardly ever'?","options":["I hardly play basketball.","I hardly ever play basketball.","I don't hardly ever play basketball.","I play basketball hardly."],"correctAnswer":"B","explanation":"'Hardly ever' se usa para indicar que algo ocurre muy raramente. La opción correcta es 'I hardly ever play basketball'.","points":2},{"id":"q3","type":"multiple-choice","scenario":"Talking to your friend about your weekend plans.","question":"Which is the most informal way to suggest playing football?","options":["Would you like to play football this weekend?","Do you fancy playing football this weekend?","Would you care to play football this weekend?","Shall we play football this weekend?"],"correctAnswer":"B","explanation":"'Do you fancy' es una forma informal y común en inglés británico para sugerir hacer algo.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You're describing your friend's hobby.","question":"Which sentence is correct?","options":["He loves to collect stamps.","He loves collecting stamps.","He love collecting stamps.","He loving collect stamps."],"correctAnswer":"B","explanation":"Después de 'love', se puede usar tanto 'to + infinitivo' como el gerundio (-ing), pero en este contexto, 'He loves collecting stamps' es más común.","points":2},{"id":"q5","type":"multiple-choice","scenario":"Your friend is describing their dislike for jogging.","question":"What is the most appropriate expression?","options":["I hate jogging.","I hate to jog.","I am hating jogging.","I hate jog."],"correctAnswer":"A","explanation":"El verbo 'hate' se combina con el gerundio para expresar aversión de forma general, por lo que 'I hate jogging' es correcto.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You want to politely suggest going to the cinema.","question":"Choose the most diplomatic way.","options":["Why not go to the cinema?","We could go to the cinema, maybe?","Let's go to the cinema!","Cinema is a good idea."],"correctAnswer":"B","explanation":"La opción 'We could go to the cinema, maybe?' es una sugerencia cortés y abierta, adecuada en una conversación diplomática.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
