INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex26',
  'a2-m2-l10',
  26,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"My brother _______________ (like) playing basketball every weekend.","gap":"likes","explanation":"En inglés, usamos 'like' seguido de un verbo en forma -ing cuando hablamos de actividades que disfrutamos.","points":1},{"sentence":"Claire _______________ (hardly ever) goes swimming in the winter.","gap":"hardly ever","explanation":"La expresión 'hardly ever' indica que algo sucede con muy poca frecuencia, casi nunca.","points":1},{"sentence":"They _______________ (love) going to the park to play soccer.","gap":"love","explanation":"Usamos 'love' seguido de un verbo en -ing para expresar actividades que nos encantan.","points":1},{"sentence":"I _______________ (not like) watching TV because I prefer reading.","gap":"don't like","explanation":"Para expresar que no disfrutamos de una actividad, usamos 'don't like' seguido de un verbo en -ing.","points":1},{"sentence":"Sarah _______________ (hate) running in the rain.","gap":"hates","explanation":"Usamos 'hate' seguido de un verbo en -ing para actividades que no nos gustan en absoluto.","points":1},{"sentence":"We _______________ (hardly ever) play board games because we prefer outdoor activities.","gap":"hardly ever","explanation":"La expresión 'hardly ever' se utiliza para decir que algo casi nunca sucede.","points":1},{"sentence":"Tom _______________ (enjoy) cycling in the countryside.","gap":"enjoys","explanation":"El verbo 'enjoy' se sigue de un verbo en -ing para indicar actividades que nos gustan.","points":1},{"sentence":"Do you _______________ (like) hiking in the mountains?","gap":"like","explanation":"Usamos 'like' seguido de un verbo en -ing para preguntar si a alguien le gusta una actividad.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
