  'speaking-analysis',
  $_$Speaking Analysis: Leisure Activities$_$,
  $_${"title":"Speaking Analysis: Leisure Activities","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"Your friend asks you about your hobbies.","question":"What is the most natural response?","options":["A: I like play soccer on weekends.","B: I like playing soccer on weekends.","C: I like to playing soccer on weekends.","D: I like soccer play on weekends."],"correctAnswer":"B","explanation":"La forma correcta es 'I like playing' porque después de 'like' se utiliza el verbo con '-ing'.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are telling someone about how often you go running.","question":"Which sentence is correct?","options":["A: I run always in the morning.","B: I run hardly in the morning.","C: I hardly ever run in the morning.","D: I hardly run ever in the morning."],"correctAnswer":"C","explanation":"'Hardly ever' significa 'casi nunca' y es la opción correcta para describir la frecuencia de una actividad.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You're discussing your dislike for a particular sport.","question":"Which sentence is most appropriate?","options":["A: I hate to play basketball.","B: I hate playing basketball.","C: I hate play basketball.","D: I hate to playing basketball."],"correctAnswer":"B","explanation":"Después de 'hate', se utiliza el verbo con '-ing' para hablar de actividades que no te gustan.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are asked about your weekend plans.","question":"Which is the most informal response?","options":["A: I might partake in some leisure activities.","B: I may engage in some recreational activities.","C: I think I'll do some fun stuff.","D: I will participate in several leisure pursuits."],"correctAnswer":"C","explanation":"'Fun stuff' es una expresión informal y adecuada para una conversación casual.","points":2},{"id":"q5","type":"multiple-choice","scenario":"Someone asks you if you enjoy swimming.","question":"Which reply is most appropriate in a friendly conversation?","options":["A: Swimming is a pastime I find enjoyable.","B: I derive pleasure from swimming.","C: I like swimming a lot.","D: Swimming is quite satisfactory."],"correctAnswer":"C","explanation":"La respuesta 'I like swimming a lot' es directa y común en conversaciones informales.","points":2},{"id":"q6","type":"multiple-choice","scenario":"Discussing a new hobby with a friend.","question":"Which sentence uses the correct idiomatic expression?","options":["A: I have taken up gardening recently.","B: I have taken down gardening recently.","C: I have put up gardening recently.","D: I have put down gardening recently."],"correctAnswer":"A","explanation":"'Take up' es la expresión idiomática correcta para empezar un nuevo hobby.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
