INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex1',
  'a2-m1-l5',
  1,
  'multiple-choice',
  $_$Personality Traits and Adjectives$_$,
  $_${"title":"Personality Traits and Adjectives","instructions":"Elige la opción correcta para completar las oraciones sobre rasgos de personalidad. Usa 'very' o 'really' cuando sea necesario.","questions":[{"id":"q1","question":"Maria is a ______ teacher. Her students love her.","options":["A. very friendly","B. really friend","C. very friend","D. really friendly"],"correctAnswer":"D","explanation":"La opción correcta es 'really friendly'. 'Friendly' es un adjetivo apropiado para describir a una persona, y 'really' intensifica el adjetivo. 'Friend' no es un adjetivo, y 'friend' no puede ser usado para describir una cualidad en este contexto."},{"id":"q2","question":"The book is ______ interesting. I couldn't stop reading it.","options":["A. very","B. really","C. very interest","D. really interest"],"correctAnswer":"B","explanation":"La opción correcta es 'really interesting'. 'Interesting' es un adjetivo que describe el libro, y 'really' se utiliza para intensificar el adjetivo. 'Interest' no es un adjetivo en este contexto, por lo que 'very interest' y 'really interest' son incorrectos."},{"id":"q3","question":"Tom is ______ shy. He doesn't like talking to new people.","options":["A. very","B. really","C. very shyness","D. really shyness"],"correctAnswer":"A","explanation":"La opción correcta es 'very shy'. 'Shy' es un adjetivo que describe una característica de Tom y 'very' intensifica el adjetivo. 'Shyness' es un sustantivo, por lo que 'very shyness' y 'really shyness' no son correctos en este contexto."},{"id":"q4","question":"This place is ______ beautiful. I want to come back.","options":["A. very","B. really","C. very beauty","D. really beauty"],"correctAnswer":"B","explanation":"La opción correcta es 'really beautiful'. 'Beautiful' es un adjetivo que describe el lugar, y 'really' intensifica el adjetivo. 'Beauty' es un sustantivo, por lo que 'very beauty' y 'really beauty' son incorrectos."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex2',
  'a2-m1-l5',
  2,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"She is a ______ girl, but her sister is very shy.","gap":"friendly","explanation":"En este contexto, 'friendly' es un adjetivo que describe una cualidad de la chica. Los adjetivos no tienen plural en inglés.","points":1},{"sentence":"The teacher is ______ enough to explain everything clearly.","gap":"patient","explanation":"En esta oración, 'patient' es un adjetivo que describe una característica del profesor. Se usa con 'enough' para indicar que tiene suficiente paciencia.","points":1},{"sentence":"My brother is really ______. He always makes everyone laugh.","gap":"funny","explanation":"Aquí, 'funny' es un adjetivo que describe cómo es el hermano. 'Really' se utiliza para enfatizar el grado del adjetivo.","points":1},{"sentence":"This book is more ______ than the last one I read.","gap":"interesting","explanation":"Usamos el comparativo 'more interesting' para comparar este libro con otro. 'Interesting' es un adjetivo.","points":1},{"sentence":"He must be ______ because he always helps people.","gap":"kind","explanation":"En esta oración, 'kind' describe una característica de la persona. Se usa después de 'must be' para hacer una suposición sobre una cualidad.","points":1},{"sentence":"The room is too ______ for all these people.","gap":"small","explanation":"Aquí, 'small' es un adjetivo que describe el tamaño de la habitación. 'Too' indica que es más pequeño de lo adecuado.","points":1},{"sentence":"She isn't ______ enough to lift the heavy box.","gap":"strong","explanation":"Se usa 'strong' para describir una capacidad física. 'Enough' indica que no tiene la fuerza suficiente.","points":1},{"sentence":"I think this movie is really ______. I can't stop watching it!","gap":"exciting","explanation":"Usamos 'exciting' para describir una cualidad del filme. 'Really' enfatiza cuán emocionante es.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex3',
  'a2-m1-l5',
  3,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["He","is","going","to","buy","a","new","car","."],"correctOrder":["He","is","going","to","buy","a","new","car","."],"translation":"Él va a comprar un coche nuevo.","hint":"Usa 'going to' para expresar planes.","points":2},{"id":2,"words":["You","should","drink","more","water","."],"correctOrder":["You","should","drink","more","water","."],"translation":"Deberías beber más agua.","hint":"Usa 'should' para dar un consejo.","points":2},{"id":3,"words":["I","would","like","to","visit","Paris","."],"correctOrder":["I","would","like","to","visit","Paris","."],"translation":"Me gustaría visitar París.","hint":"Usa 'would like to' para expresar deseos.","points":2},{"id":4,"words":["There","is","a","lot","of","sugar","in","this","cake","."],"correctOrder":["There","is","a","lot","of","sugar","in","this","cake","."],"translation":"Hay mucho azúcar en este pastel.","hint":"Usa 'a lot of' para un sustantivo incontable.","points":2},{"id":5,"words":["She","will","probably","arrive","at","8","o'clock","."],"correctOrder":["She","will","probably","arrive","at","8","o'clock","."],"translation":"Ella probablemente llegará a las 8 en punto.","hint":"Usa 'will' para hacer una predicción.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex4',
  'a2-m1-l5',
  4,
  'speaking-analysis',
  $_$Speaking Analysis: Personality Traits$_$,
  $_${"title":"Speaking Analysis: Personality Traits","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking to a friend about a new colleague at work.","question":"How would you describe someone who is always happy and brings joy to the office?","options":["A. lazy","B. cheerful","C. rude","D. boring"],"correctAnswer":"B","explanation":"La palabra 'cheerful' se usa para describir a alguien que siempre está feliz y aporta alegría, mientras que las otras opciones tienen significados negativos o no relacionados.","points":2},{"id":"q2","type":"multiple-choice","scenario":"Your friend is asking for advice about a movie.","question":"How would you express that a movie was very interesting?","options":["A. It was a very boring movie.","B. It was a very confusing movie.","C. It was a very interesting movie.","D. It was a very tiring movie."],"correctAnswer":"C","explanation":"'Interesting' es el adjetivo correcto para expresar que una película captó tu atención de manera positiva.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are discussing someone's personality with a friend.","question":"What would you say to describe someone who is always kind and helpful?","options":["A. He's very selfish.","B. He's very generous.","C. He's very stubborn.","D. He's very lazy."],"correctAnswer":"B","explanation":"'Generous' es el adjetivo que describe a una persona amable y dispuesta a ayudar, en contraste con las otras descripciones que tienen significados negativos.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You need to give your opinion about a friend's behavior during a conversation.","question":"How would you describe someone who doesn't listen and interrupts others?","options":["A. He's very polite.","B. He's very rude.","C. He's very quiet.","D. He's very friendly."],"correctAnswer":"B","explanation":"La palabra 'rude' se usa para describir a alguien que no escucha y interrumpe, lo cual es considerado de mala educación.","points":2},{"id":"q5","type":"multiple-choice","scenario":"A friend asks your opinion about another friend's personality.","question":"How would you describe someone who is always thinking about new ideas and plans?","options":["A. He's very creative.","B. He's very lazy.","C. He's very shy.","D. He's very boring."],"correctAnswer":"A","explanation":"'Creative' es el adjetivo que describe a una persona que siempre piensa en nuevas ideas, en contraste con las otras opciones que no son adecuadas.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are talking about a friend's new partner.","question":"What would you say to describe someone who is always polite and thoughtful?","options":["A. She's very rude.","B. She's very kind.","C. She's very selfish.","D. She's very quiet."],"correctAnswer":"B","explanation":"'Kind' es el adjetivo correcto para describir a alguien que es educado y considerado con los demás.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex5',
  'a2-m1-l5',
  5,
  'word-search',
  $_$Word Search: Personality Traits$_$,
  $_${"title":"Word Search: Personality Traits","instructions":"Find the hidden words in the grid","words":["KIND","HAPPY","QUIET","FRIENDLY","LAZY","ANGRY","BRAVE","POLITE","SHY","FUNNY"],"gridSize":10,"clues":[{"word":"KIND","clue":"Amable"},{"word":"HAPPY","clue":"Contento"},{"word":"QUIET","clue":"Tranquilo"},{"word":"FRIENDLY","clue":"Amigable"},{"word":"LAZY","clue":"Perezoso"},{"word":"ANGRY","clue":"Enojado"},{"word":"BRAVE","clue":"Valiente"},{"word":"POLITE","clue":"Cortés"},{"word":"SHY","clue":"Tímido"},{"word":"FUNNY","clue":"Divertido"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex6',
  'a2-m1-l5',
  6,
  'crossword',
  $_$Crossword: Personality Traits$_$,
  $_${"title":"Crossword: Personality Traits","instructions":"Complete the crossword puzzle","items":[{"word":"KIND","clue":"A person who is very friendly and helps others.","direction":"across","row":0,"col":0},{"word":"HAPPY","clue":"Feeling very good and smiling a lot.","direction":"down","row":0,"col":2},{"word":"SHY","clue":"Not really comfortable with new people.","direction":"across","row":2,"col":0},{"word":"POLITE","clue":"Someone who says 'please' and 'thank you'.","direction":"down","row":0,"col":5},{"word":"LAZY","clue":"Not very willing to work or move a lot.","direction":"across","row":4,"col":3},{"word":"BRAVE","clue":"A person who is not afraid of danger.","direction":"down","row":2,"col":7}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex7',
  'a2-m1-l5',
  7,
  'reading-comprehension',
  $_$Understanding Personality Traits$_$,
  $_${"title":"Understanding Personality Traits","textPassage":"Maria is a very friendly and outgoing person. She really likes to meet new people and learn about their lives. Her friends say she is very kind and always ready to help. Maria is never shy, and she is definitely not a boring person. However, her brother, Carlos, is the opposite. He is quiet and prefers to stay at home. Carlos is not very talkative, and he feels uncomfortable in large groups. But, he is really clever and enjoys reading books about science. Maria thinks Carlos is interesting because he knows many things. Even though they are different, Maria and Carlos get along well and help each other.","wordCount":110,"estimatedReadingTime":1,"questions":[{"question":"What is the main idea of the passage?","options":["Maria and Carlos have similar personalities.","Maria and Carlos have different personalities but get along well.","Maria likes reading books about science.","Carlos is a boring person."],"answer":"Maria and Carlos have different personalities but get along well.","explanation":"La idea principal es que Maria y Carlos tienen personalidades distintas pero se llevan bien."},{"question":"Which word describes Maria?","options":["Shy","Outgoing","Quiet","Boring"],"answer":"Outgoing","explanation":"Maria es descrita como amigable y extrovertida, lo que significa 'outgoing'."},{"question":"How does Carlos feel in large groups?","options":["Comfortable","Excited","Uncomfortable","Happy"],"answer":"Uncomfortable","explanation":"Carlos se siente incómodo en grupos grandes, lo cual es lo opuesto a cómodo."},{"question":"True or False: Maria is a boring person.","options":["True","False"],"answer":"False","explanation":"El texto indica que Maria no es una persona aburrida."},{"question":"What does Carlos enjoy?","options":["Playing sports","Reading books about science","Going to parties","Cooking"],"answer":"Reading books about science","explanation":"Carlos disfruta leyendo libros sobre ciencia, según el texto."},{"question":"What is the opposite of 'outgoing' as used in the passage?","options":["Friendly","Shy","Talkative","Kind"],"answer":"Shy","explanation":"El opuesto de 'outgoing' (extrovertido) es 'shy' (tímido)."},{"question":"True or False: Carlos is very talkative.","options":["True","False"],"answer":"False","explanation":"Carlos no es muy hablador, así que la afirmación es falsa."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex8',
  'a2-m1-l5',
  8,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Estrés de Palabras$_$,
  $_${"title":"Práctica de Pronunciación: Estrés de Palabras","instructions":"Escucha atentamente las oraciones y presta atención al estrés de las palabras. Practica repitiendo las oraciones y enfocándote en las palabras clave resaltadas.","targetSentences":[{"englishSentence":"She is a very confident leader.","spanishTranslation":"Ella es una líder muy segura.","phoneticTranscription":"ˈkɒnfɪdənt","audioModelURL":"https://example.com/audio1","pronunciationTips":"Focus on stressing the first syllable in 'confident'.","focusPoints":"Stressing 'confident'","commonMistakes":"Avoid placing stress on the second syllable."},{"englishSentence":"He is really hardworking and honest.","spanishTranslation":"Él es realmente trabajador y honesto.","phoneticTranscription":"ˈhɑːdwɜːkɪŋ","audioModelURL":"https://example.com/audio2","pronunciationTips":"Emphasize the first syllable in 'hardworking'.","focusPoints":"Stressing 'hardworking'","commonMistakes":"Do not stress the second part of the word too much."},{"englishSentence":"She seems very optimistic today.","spanishTranslation":"Ella parece muy optimista hoy.","phoneticTranscription":"ˌɒptɪˈmɪstɪk","audioModelURL":"https://example.com/audio3","pronunciationTips":"The stress should be on the third syllable.","focusPoints":"Stressing 'optimistic' correctly","commonMistakes":"Avoid stressing the first syllable."},{"englishSentence":"He is an ambitious young man.","spanishTranslation":"Él es un joven ambicioso.","phoneticTranscription":"æmˈbɪʃəs","audioModelURL":"https://example.com/audio4","pronunciationTips":"Place stress on the second syllable of 'ambitious'.","focusPoints":"Stressing 'ambitious'","commonMistakes":"Do not stress the first syllable."},{"englishSentence":"My friend is quite adventurous.","spanishTranslation":"Mi amigo es bastante aventurero.","phoneticTranscription":"ədˈvɛntʃərəs","audioModelURL":"https://example.com/audio5","pronunciationTips":"Focus on stressing the second syllable.","focusPoints":"Stressing 'adventurous'","commonMistakes":"Avoid stressing the first syllable."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex9',
  'a2-m1-l5',
  9,
  'multiple-choice',
  $_$Adjectives and Personality Traits$_$,
  $_${"title":"Adjectives and Personality Traits","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"My brother is very _____. He always helps me with my homework.","options":["kind","angry","sad","tired"],"correctAnswer":"kind","explanation":"La palabra 'kind' significa amable, que es el adjetivo correcto porque describe a alguien que ayuda a los demás. Las otras opciones no son apropiadas en este contexto."},{"id":"q2","question":"She is really _____. She never smiles or laughs.","options":["serious","funny","happy","excited"],"correctAnswer":"serious","explanation":"La palabra 'serious' significa serio, que es el adjetivo correcto para describir a alguien que no sonríe ni ríe. Las otras opciones indican lo contrario."},{"id":"q3","question":"The movie was _____ interesting that I watched it twice.","options":["very","really","too","enough"],"correctAnswer":"really","explanation":"La palabra 'really' intensifica el adjetivo 'interesting', indicando que la película era extremadamente interesante. 'Too' y 'enough' no son correctos en este contexto."},{"id":"q4","question":"He thinks he is very clever, but he makes a lot of _____.","options":["mistakes","success","friends","decisions"],"correctAnswer":"mistakes","explanation":"La palabra 'mistakes' significa errores, que es lo opuesto a ser realmente inteligente (clever). Las otras opciones no encajan en el contexto de la oración."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex10',
  'a2-m1-l5',
  10,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"Maria is a ______ person and always helps others.","options":["kind","selfish","lazy"],"correct_answer":"kind","explanation":"En inglés, 'kind' significa 'amable'. Es un adjetivo de personalidad positivo y va en singular antes del sustantivo 'person'."},{"sentence":"Tom is ______ good at playing the guitar.","options":["very","really","quite"],"correct_answer":"really","explanation":"Usamos 'really' para enfatizar el adjetivo 'good'. Significa 'realmente' en español."},{"sentence":"The movie was ______ boring that I fell asleep.","options":["so","very","really"],"correct_answer":"so","explanation":"Utilizamos 'so' seguido de un adjetivo y 'that' para expresar una consecuencia o resultado. 'So boring that...' significa 'tan aburrido que...'."},{"sentence":"Jenny is ______ careful when she drives.","options":["very","really","quite"],"correct_answer":"very","explanation":"El uso de 'very' antes de 'careful' indica que Jenny es muy cuidadosa al conducir."},{"sentence":"John is ______ confident about his presentation.","options":["very","really","quite"],"correct_answer":"quite","explanation":"'Quite' se utiliza para indicar que algo es moderadamente cierto, como en 'bastante confiado'."},{"sentence":"That book is not interesting; it's ______.","options":["boring","exciting","fascinating"],"correct_answer":"boring","explanation":"La palabra 'boring' es el opuesto de 'interesting' y se usa para describir algo que no es emocionante."},{"sentence":"She is ______ happy with her new job.","options":["really","very","quite"],"correct_answer":"really","explanation":"Usamos 'really' para enfatizar cuán feliz está ella con su nuevo trabajo."},{"sentence":"He is ______ shy to speak in front of the class.","options":["too","very","quite"],"correct_answer":"too","explanation":"'Too' se usa aquí para indicar que es 'demasiado tímido' para hablar."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex11',
  'a2-m1-l5',
  11,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["likes","playing","Kevin","really","football","."],"correctOrder":["Kevin","really","likes","playing","football","."],"translation":"A Kevin realmente le gusta jugar al fútbol.","hint":"Recuerda que 'really' se usa antes del verbo para enfatizar.","points":2},{"id":2,"words":["The","excited","very","are","children","."],"correctOrder":["The","children","are","very","excited","."],"translation":"Los niños están muy emocionados.","hint":"La estructura es sujeto + verbo 'to be' + 'very' + adjetivo.","points":2},{"id":3,"words":["is","a","person","nice","She","."],"correctOrder":["She","is","a","nice","person","."],"translation":"Ella es una persona agradable.","hint":"Recuerda que el adjetivo va antes del sustantivo.","points":2},{"id":4,"words":["not","He","should","be","rude","."],"correctOrder":["He","should","not","be","rude","."],"translation":"Él no debería ser grosero.","hint":"Usa 'should' para dar un consejo negativo.","points":2},{"id":5,"words":["going","to","is","She","study","hard","."],"correctOrder":["She","is","going","to","study","hard","."],"translation":"Ella va a estudiar mucho.","hint":"Usa 'going to' para expresar planes futuros.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex12',
  'a2-m1-l5',
  12,
  'speaking-analysis',
  $_$Speaking Analysis: Personality Traits$_$,
  $_${"title":"Speaking Analysis: Personality Traits","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are at a party and someone asks about your friend who is always very cheerful.","question":"How would you describe your friend?","options":["She is really happy.","She is very happiness.","She is really happiness.","She is very cheerful."],"correctAnswer":"She is very cheerful.","explanation":"La opción correcta es 'She is very cheerful.' porque 'cheerful' es un adjetivo que describe a una persona, mientras que 'happiness' es un sustantivo y no se utiliza para describir directamente.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are explaining why your brother is an interesting person.","question":"Which sentence would you use?","options":["My brother is very interested.","My brother is very interesting.","My brother is really interested.","My brother is interesting really."],"correctAnswer":"My brother is very interesting.","explanation":"La opción correcta es 'My brother is very interesting.' porque 'interesting' se usa para describir una característica permanente de una persona, mientras que 'interested' se usa para describir un estado temporal.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are at a restaurant and the waiter asks if everything is okay with your meal.","question":"What is the most polite response?","options":["It is very tasty, thank you.","It is tasty very, thanks.","It's really good, thanks.","It's really tasty, thank you."],"correctAnswer":"It's really tasty, thank you.","explanation":"La opción correcta es 'It's really tasty, thank you.' porque es una respuesta educada que utiliza 'really' para enfatizar lo buena que está la comida, y 'thank you' es una manera cortés de cerrar la respuesta.","points":2},{"id":"q4","type":"multiple-choice","scenario":"Your teacher asks you to describe your classroom.","question":"What would be the best description?","options":["The classroom is very clean.","The classroom is very cleanliness.","The classroom is clean very.","The classroom is clean really."],"correctAnswer":"The classroom is very clean.","explanation":"La opción correcta es 'The classroom is very clean.' ya que 'clean' es el adjetivo correcto para describir el aula y 'very' intensifica el adjetivo.","points":2},{"id":"q5","type":"multiple-choice","scenario":"A friend asks you about your new teacher's personality.","question":"How would you describe your teacher?","options":["She is really strict.","She is strict really.","She is very strict.","She is strict very."],"correctAnswer":"She is very strict.","explanation":"La opción correcta es 'She is very strict.' porque el uso de 'very' antes del adjetivo 'strict' es gramaticalmente correcto para describir a la profesora.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are telling a story about a boring movie you watched last night.","question":"Which sentence best describes your opinion?","options":["The movie was really bored.","The movie was really boring.","The movie was bored really.","The movie was bored very."],"correctAnswer":"The movie was really boring.","explanation":"La opción correcta es 'The movie was really boring.' porque 'boring' es el adjetivo que describe la película y 'really' intensifica el adjetivo.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex13',
