  $_${"title":"Word Search: Personality Traits","instructions":"Find the hidden words in the grid","words":["KIND","HAPPY","LAZY","FRIENDLY","SHY","BRAVE","POLITE","RUDE","FUNNY","SERIOUS"],"gridSize":10,"clues":[{"word":"KIND","clue":"Amable"},{"word":"HAPPY","clue":"Contento"},{"word":"LAZY","clue":"Perezoso"},{"word":"FRIENDLY","clue":"Amigable"},{"word":"SHY","clue":"Tímido"},{"word":"BRAVE","clue":"Valiente"},{"word":"POLITE","clue":"Educado"},{"word":"RUDE","clue":"Grosero"},{"word":"FUNNY","clue":"Divertido"},{"word":"SERIOUS","clue":"Serio"}]}$_$
