  'crossword',
  $_$Crossword: Personality Traits$_$,
  $_${"title":"Crossword: Personality Traits","instructions":"Complete the crossword puzzle","items":[{"word":"KIND","clue":"A person who is very nice and helpful.","direction":"across","row":0,"col":0},{"word":"HAPPY","clue":"Feeling very good and smiling a lot.","direction":"down","row":0,"col":2},{"word":"SHY","clue":"Not very confident, especially with new people.","direction":"across","row":2,"col":0},{"word":"FUNNY","clue":"Someone who makes you laugh a lot.","direction":"across","row":4,"col":0},{"word":"ANGRY","clue":"Feeling very upset about something.","direction":"down","row":0,"col":5},{"word":"LAZY","clue":"Not very active and doesn't like working hard.","direction":"across","row":6,"col":0},{"word":"BRAVE","clue":"Someone who is not afraid of danger.","direction":"down","row":2,"col":3}]}$_$
