INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex31',
  'a2-m1-l5',
  31,
  'reading-comprehension',
  $_$Describing Personality Traits$_$,
  $_${"title":"Describing Personality Traits","textPassage":"Anna is a very friendly and cheerful person. She always has a smile on her face and is really helpful to everyone around her. Her friends say she is very kind and always ready to listen. However, her brother Mike is quite different. He is usually quiet and prefers to stay alone. Mike is very serious and does not like to joke much. Their parents think that both Anna and Mike are good in their own ways. Anna is outgoing and makes friends easily, while Mike is thoughtful and likes to spend time reading books. Despite their differences, they get along very well and support each other. Anna admires Mike's intelligence, and Mike appreciates Anna's ability to make people happy.","wordCount":123,"estimatedReadingTime":2,"questions":[{"question":"What is the main idea of the passage?","options":["Anna and Mike have similar personalities.","Anna is more serious than Mike.","Anna and Mike have different personalities but get along well.","Mike is more cheerful than Anna."],"answer":"Anna and Mike have different personalities but get along well.","explanation":"En el pasaje, se describe cómo Anna y Mike tienen personalidades diferentes, pero aún así se llevan bien y se apoyan mutuamente."},{"question":"How is Anna described in the passage?","options":["Quiet and serious","Friendly and cheerful","Thoughtful and intelligent","Outgoing and quiet"],"answer":"Friendly and cheerful","explanation":"Anna es descrita como una persona amistosa y alegre, siempre con una sonrisa y dispuesta a ayudar a los demás."},{"question":"What does Mike prefer to do?","options":["Make jokes","Stay alone","Go out with friends","Play sports"],"answer":"Stay alone","explanation":"Mike prefiere estar solo, es bastante serio y no le gusta bromear mucho."},{"question":"True or False: Anna and Mike do not get along because they are different.","answer":"False","explanation":"Falso. Aunque Anna y Mike son diferentes, se llevan bien y se apoyan mutuamente."},{"question":"What does Anna admire about Mike?","options":["His friendliness","His intelligence","His sense of humor","His athletic skills"],"answer":"His intelligence","explanation":"Anna admira la inteligencia de Mike, lo cual se menciona en el pasaje."},{"question":"Which adjective describes a personality trait of Mike?","options":["Cheerful","Outgoing","Serious","Friendly"],"answer":"Serious","explanation":"Mike es descrito como una persona seria en el texto."},{"question":"True or False: Anna and Mike's parents think both of them are good in their own ways.","answer":"True","explanation":"Verdadero. Los padres de Anna y Mike creen que ambos son buenos a su manera."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex32',
  'a2-m1-l5',
  32,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Enfatizar Adjetivos de Personalidad$_$,
  $_${"title":"Práctica de Pronunciación: Enfatizar Adjetivos de Personalidad","instructions":"Escucha y repite las oraciones en inglés, prestando atención a la pronunciación y el uso correcto de los adjetivos de personalidad. Fíjate en cómo se enfatiza la palabra en inglés.","targetSentences":[{"englishSentence":"She is a very friendly person.","spanishTranslation":"Ella es una persona muy amistosa.","phoneticTranscription":"/ˈfrɛndli/","audioModelURL":"https://example.com/audio/friendly.mp3","pronunciationTips":"Asegúrate de pronunciar la 'r' suavemente y enfatizar la primera sílaba.","focusPoints":"Enfatizar la primera sílaba de 'friendly'.","commonMistakes":"No omitir la 'd' en 'friendly'."},{"englishSentence":"He is really adventurous.","spanishTranslation":"Él es realmente aventurero.","phoneticTranscription":"/ədˈvɛntʃərəs/","audioModelURL":"https://example.com/audio/adventurous.mp3","pronunciationTips":"Enfatiza la segunda sílaba y asegúrate de que el sonido 'ch' sea claro.","focusPoints":"La sílaba tónica es la segunda, 'ven'.","commonMistakes":"Pronunciar 'ch' como 'sh'."},{"englishSentence":"They are quite shy.","spanishTranslation":"Ellos son bastante tímidos.","phoneticTranscription":"/ʃaɪ/","audioModelURL":"https://example.com/audio/shy.mp3","pronunciationTips":"Mantén el sonido 'sh' suave y claro.","focusPoints":"El sonido inicial 'sh'.","commonMistakes":"No pronunciar 'sh' como 's'."},{"englishSentence":"My brother is very generous.","spanishTranslation":"Mi hermano es muy generoso.","phoneticTranscription":"/ˈdʒɛnərəs/","audioModelURL":"https://example.com/audio/generous.mp3","pronunciationTips":"La primera sílaba debe ser clara y el sonido 'j' debe ser suave.","focusPoints":"Enfatizar la primera sílaba 'gen'.","commonMistakes":"No pronunciar el 'j' suave."},{"englishSentence":"She feels really confident.","spanishTranslation":"Ella se siente realmente segura.","phoneticTranscription":"/ˈkɒnfɪdənt/","audioModelURL":"https://example.com/audio/confident.mp3","pronunciationTips":"Enfatizar la primera sílaba y mantener el sonido 'f' suave.","focusPoints":"La primera sílaba 'con' debe ser clara.","commonMistakes":"No pronunciar 'f' como 'p'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex33',
  'a2-m1-l5',
  33,
  'multiple-choice',
  $_$Personality Traits and Adjectives Practice$_$,
  $_${"title":"Personality Traits and Adjectives Practice","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"She is really ______ and always helps everyone with their problems.","options":["A) kind","B) shy","C) angry","D) lazy"],"correctAnswer":"A","explanation":"La opción 'kind' es correcta porque describe a una persona que es buena y ayuda a los demás, lo cual coincide con la descripción en la pregunta."},{"id":"q2","question":"My brother is very ______; he talks to everyone at the party.","options":["A) quiet","B) friendly","C) sad","D) tired"],"correctAnswer":"B","explanation":"La palabra 'friendly' es correcta aquí porque describe a alguien que disfruta socializar y hablar con otras personas, que es lo que hace el hermano en la fiesta."},{"id":"q3","question":"This book is more ______ than the last one I read. I can't stop reading it!","options":["A) boring","B) interesting","C) difficult","D) cheap"],"correctAnswer":"B","explanation":"La opción 'interesting' es correcta porque indica que el libro es tan cautivador que no se puede dejar de leer, lo cual es una característica de algo interesante."},{"id":"q4","question":"He is very ______; he never shares his toys with other children.","options":["A) generous","B) selfish","C) smart","D) funny"],"correctAnswer":"B","explanation":"La opción 'selfish' es correcta porque describe a alguien que no comparte con los demás, lo cual es lo que hace el niño mencionado en la pregunta."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex34',
  'a2-m1-l5',
  34,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"question":"1. How ______ apples are there in the basket?","gap":"many","explanation":"Usamos 'many' con sustantivos contables en plural. 'Apples' es contable y plural.","points":1},{"question":"2. There isn't ______ milk left in the fridge.","gap":"much","explanation":"Usamos 'much' con sustantivos incontables. 'Milk' es incontable.","points":1},{"question":"3. You mustn't eat ______ sugar. It's not healthy.","gap":"much","explanation":"Usamos 'much' con sustantivos incontables. 'Sugar' es incontable.","points":1},{"question":"4. I think it will ______ tomorrow. The sky is very cloudy.","gap":"rain","explanation":"Usamos 'will' para predicciones. 'Rain' es el verbo que sigue.","points":1},{"question":"5. This book is ______ than the other one. I really like it.","gap":"more interesting","explanation":"Usamos 'more' con adjetivos largos para comparativos. 'Interesting' es un adjetivo largo.","points":1},{"question":"6. You ______ take your umbrella. It might rain later.","gap":"should","explanation":"Usamos 'should' para dar consejos.","points":1},{"question":"7. How ______ water do you need for the recipe?","gap":"much","explanation":"Usamos 'much' con sustantivos incontables. 'Water' es incontable.","points":1},{"question":"8. He is ______ tall man with very kind eyes.","gap":"a","explanation":"Usamos 'a' antes de sustantivos singulares que empiezan con consonante. 'Tall man' es singular y empieza con consonante.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex35',
  'a2-m1-l5',
  35,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["is","very","She","friendly","."],"correctOrder":["She","is","very","friendly","."],"translation":"Ella es muy amigable.","hint":"La estructura es: Sujeto + verbo + adverbio de grado + adjetivo.","points":2},{"id":2,"words":["really","The","movie","is","interesting","."],"correctOrder":["The","movie","is","really","interesting","."],"translation":"La película es realmente interesante.","hint":"Recuerda que el adverbio de grado va antes del adjetivo.","points":2},{"id":3,"words":["funny","is","My","very","brother","."],"correctOrder":["My","brother","is","very","funny","."],"translation":"Mi hermano es muy divertido.","hint":"El adjetivo describe al sustantivo después del verbo 'to be'.","points":2},{"id":4,"words":["very","Her","is","kind","mother","."],"correctOrder":["Her","mother","is","very","kind","."],"translation":"Su madre es muy amable.","hint":"Utiliza 'very' para intensificar el adjetivo.","points":2},{"id":5,"words":["is","The","teacher","really","patient","."],"correctOrder":["The","teacher","is","really","patient","."],"translation":"El maestro es realmente paciente.","hint":"Coloca el adverbio de grado entre el verbo y el adjetivo.","points":2}]}$_$
