INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l5-ex38',
  'a2-m1-l5',
  38,
  'crossword',
  $_$Crossword: Personality Traits$_$,
  $_${"title":"Crossword: Personality Traits","instructions":"Complete the crossword puzzle","items":[{"word":"KIND","clue":"A person who is very nice and helpful.","direction":"across","row":0,"col":0},{"word":"LAZY","clue":"Someone who does not like to work or be active.","direction":"down","row":0,"col":4},{"word":"HAPPY","clue":"Feeling very good and smiling a lot.","direction":"across","row":2,"col":0},{"word":"QUIET","clue":"A person who does not talk much.","direction":"down","row":1,"col":2},{"word":"ANGRY","clue":"The opposite of calm; feeling very mad.","direction":"across","row":4,"col":0},{"word":"FUNNY","clue":"Someone who makes you laugh a lot.","direction":"down","row":0,"col":6},{"word":"BRAVE","clue":"A person who is very courageous.","direction":"across","row":3,"col":3}]}$_$
