INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex23',
  'a2-m2-l7',
  23,
  'reading-comprehension',
  $_$Daily Routines$_$,
  $_${"title":"Daily Routines","textPassage":"Every morning, Maria wakes up at 7 o'clock. She has breakfast with her family in the kitchen. At 8:30, she takes the bus to school. Maria's classes start at 9 o'clock and finish at 2 o'clock in the afternoon. After school, Maria usually does her homework in the living room. In the evening, she has dinner at 7 o'clock with her family. She watches TV or reads a book before going to bed at 10 o'clock.","wordCount":84,"estimatedReadingTime":1,"questions":[{"question":"What time does Maria wake up in the morning?","options":["At 6 o'clock","At 7 o'clock","At 8 o'clock","At 9 o'clock"],"answer":"At 7 o'clock","explanation":"Maria se despierta a las 7 en punto, como se menciona en la primera oración."},{"question":"Where does Maria have breakfast?","options":["In the living room","In the garden","In the kitchen","In her bedroom"],"answer":"In the kitchen","explanation":"Maria desayuna en la cocina con su familia, como se indica en el texto."},{"question":"Maria's classes finish at...","options":["1 o'clock","2 o'clock","3 o'clock","4 o'clock"],"answer":"2 o'clock","explanation":"Las clases de Maria terminan a las 2 en punto de la tarde, según el texto."},{"question":"True or False: Maria goes to bed at 9 o'clock.","answer":"False","explanation":"Falso. Maria se acuesta a las 10 en punto, no a las 9."},{"question":"What does Maria do after school?","options":["She goes shopping","She visits friends","She does her homework","She plays sports"],"answer":"She does her homework","explanation":"Después de la escuela, Maria generalmente hace su tarea en la sala de estar."},{"question":"True or False: Maria takes the bus to school in the evening.","answer":"False","explanation":"Falso. Maria toma el autobús a la escuela por la mañana, a las 8:30."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
