INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex31',
  'a2-m2-l7',
  31,
  'reading-comprehension',
  $_$Daily Routines$_$,
  $_${"title":"Daily Routines","textPassage":"Every day, Maria wakes up at 7:00 in the morning. She gets out of bed and has breakfast in the kitchen at 7:30. Maria likes to eat toast and drink orange juice. At 8:00, she leaves home and catches the bus to work. She arrives at her office at 8:45 and starts working at 9:00. Maria works in an office in the city. She has lunch at 12:30 in the afternoon at a small cafe near her office. After lunch, she continues working until 5:00 in the evening. Maria usually gets home at 6:00. In the evening, she likes to cook dinner and watch TV. She goes to bed at 10:30 at night. On weekends, Maria wakes up later and enjoys spending time with her friends.","wordCount":130,"estimatedReadingTime":2,"questions":[{"question":"What time does Maria wake up?","options":["6:30","7:00","7:30","8:00"],"answer":"7:00","explanation":"El texto menciona que Maria se despierta a las 7:00 de la mañana."},{"question":"Where does Maria have breakfast?","options":["In her bedroom","At her office","In the kitchen","At a cafe"],"answer":"In the kitchen","explanation":"Maria desayuna en la cocina, según el texto."},{"question":"What does Maria do at 8:00?","options":["She starts working","She catches the bus","She has lunch","She goes to bed"],"answer":"She catches the bus","explanation":"A las 8:00, Maria toma el autobús para ir al trabajo."},{"question":"Maria has lunch at a small cafe.","options":["True","False"],"answer":"True","explanation":"El texto dice que Maria almuerza en un pequeño café cerca de su oficina."},{"question":"On weekends, Maria:","options":["Wakes up earlier","Works at the office","Spends time with friends","Cooks dinner"],"answer":"Spends time with friends","explanation":"Durante los fines de semana, Maria disfruta pasar tiempo con sus amigos."},{"question":"Which of the following is NOT mentioned about Maria's routine?","options":["She works in the city","She reads books","She watches TV","She cooks dinner"],"answer":"She reads books","explanation":"El texto no menciona que Maria lea libros, pero sí menciona que trabaja en la ciudad, ve televisión y cocina la cena."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex32',
  'a2-m2-l7',
  32,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Enfatizar Palabras en Oraciones$_$,
  $_${"title":"Práctica de Pronunciación: Enfatizar Palabras en Oraciones","instructions":"Practica la pronunciación de las siguientes oraciones enfocándote en el uso correcto del presente simple y las preposiciones de tiempo. Escucha los modelos de audio y repite las oraciones en voz alta. Presta atención a la pronunciación de las palabras clave y al ritmo de la frase.","targetSentences":[{"englishSentence":"I wake up at six o'clock every morning.","spanishTranslation":"Me despierto a las seis en punto todas las mañanas.","phoneticTranscription":"/aɪ weɪk ʌp æt sɪks əˈklɒk ˈɛvri ˈmɔːrnɪŋ/","audioModelURL":"https://example.com/audio1","pronunciationTips":"Enfatiza 'wake' y 'six o'clock' para indicar la acción principal y el tiempo.","focusPoints":"Enfatizar 'wake' y 'six o'clock'","commonMistakes":"No omitas el sonido final en 'wake' y asegúrate de pronunciar el sonido /s/ en 'six'."},{"englishSentence":"She has breakfast in the kitchen.","spanishTranslation":"Ella desayuna en la cocina.","phoneticTranscription":"/ʃiː hæz ˈbrɛkfəst ɪn ðə ˈkɪtʃɪn/","audioModelURL":"https://example.com/audio2","pronunciationTips":"Resalta 'breakfast' para subrayar la actividad principal.","focusPoints":"Enfatizar 'breakfast'","commonMistakes":"No pronuncies 'breakfast' como dos palabras separadas."},{"englishSentence":"We go to work at eight in the morning.","spanishTranslation":"Vamos al trabajo a las ocho de la mañana.","phoneticTranscription":"/wiː ɡoʊ tuː wɜːrk æt eɪt ɪn ðə ˈmɔːrnɪŋ/","audioModelURL":"https://example.com/audio3","pronunciationTips":"Asegúrate de enfatizar 'work' y 'eight' para claridad.","focusPoints":"Enfatizar 'work' y 'eight'","commonMistakes":"Evita decir 'at eight' como una sola palabra; debe sonar separado."},{"englishSentence":"They have lunch at noon.","spanishTranslation":"Ellos almuerzan al mediodía.","phoneticTranscription":"/ðeɪ hæv lʌntʃ æt nuːn/","audioModelURL":"https://example.com/audio4","pronunciationTips":"Resalta 'lunch' y 'noon' para indicar claramente el evento y el tiempo.","focusPoints":"Enfatizar 'lunch' y 'noon'","commonMistakes":"No olvides el sonido /n/ final en 'noon'."},{"englishSentence":"He goes to bed at ten o'clock.","spanishTranslation":"Él se acuesta a las diez en punto.","phoneticTranscription":"/hiː ɡoʊz tuː bɛd æt tɛn əˈklɒk/","audioModelURL":"https://example.com/audio5","pronunciationTips":"Enfatiza 'bed' y 'ten o'clock' para claridad en la rutina.","focusPoints":"Enfatizar 'bed' y 'ten o'clock'","commonMistakes":"Asegúrate de no unir 'ten' y 'o'clock' como una sola palabra."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
