  $_${"questions":[{"sentence":"I __________ (go) to school at eight o'clock every morning.","acceptableAnswers":["go"],"explanation":"En presente simple afirmativo, usamos la forma base del verbo para sujetos como 'I', 'you', 'we', 'they'.","points":1},{"sentence":"We have lunch __________ (at/on/in) noon.","acceptableAnswers":["at"],"explanation":"Usamos 'at' para referirnos a horas específicas del día.","points":1},{"sentence":"She __________ (watch) TV in the evening.","acceptableAnswers":["watches"],"explanation":"Para tercera persona del singular, añadimos '-es' al verbo en presente simple.","points":1},{"sentence":"My brother usually __________ (play) football on Saturdays.","acceptableAnswers":["plays"],"explanation":"En presente simple para tercera persona, añadimos '-s' al verbo.","points":1},{"sentence":"They go to the park __________ (at/on/in) the weekend.","acceptableAnswers":["on"],"explanation":"Usamos 'on' para referirnos a días específicos y fines de semana.","points":1},{"sentence":"We __________ (have) dinner at 7 p.m.","acceptableAnswers":["have"],"explanation":"En presente simple afirmativo, usamos la forma base del verbo para sujetos como 'I', 'you', 'we', 'they'.","points":1},{"sentence":"She reads a book __________ (at/on/in) the afternoon.","acceptableAnswers":["in"],"explanation":"Usamos 'in' para referirnos a partes del día como la tarde, mañana o noche.","points":1},{"sentence":"Tom __________ (study) English every day.","acceptableAnswers":["studies"],"explanation":"Para tercera persona del singular, cambiamos 'y' por 'ies' en verbos terminados en consonante + 'y'.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
