INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex39',
  'a2-m2-l7',
  39,
  'reading-comprehension',
  $_$A Day in the Life of Maria$_$,
  $_${"title":"A Day in the Life of Maria","text":"Maria is a student. She goes to school from Monday to Friday. Every morning, she wakes up at 7 o'clock. After she wakes up, she brushes her teeth and has breakfast at 7:30. She usually eats toast and drinks orange juice. At 8:15, she leaves the house and walks to school. School starts at 9 o'clock. She finishes school at 3 o'clock in the afternoon. After school, Maria goes home and does her homework. She likes to read books in the evening. Maria has dinner with her family at 7 o'clock. They often eat pasta or chicken. Maria goes to bed at 10 o'clock. She always reads a little before sleeping.","wordCount":120,"estimatedReadingTime":1,"questions":[{"question":"What time does Maria wake up?","options":["6 o'clock","7 o'clock","8 o'clock","9 o'clock"],"answer":"7 o'clock","explanation":"Maria se despierta a las 7 en punto, como se menciona al principio del texto."},{"question":"Maria has breakfast at:","options":["7:00","7:30","8:00","8:30"],"answer":"7:30","explanation":"El texto dice que Maria desayuna a las 7:30."},{"question":"TRUE or FALSE: Maria walks to school at 8:15.","options":["TRUE","FALSE"],"answer":"TRUE","explanation":"Es cierto que Maria sale de casa y camina a la escuela a las 8:15, según el texto."},{"question":"What does Maria do after school?","options":["She reads books.","She does her homework.","She goes to bed.","She plays video games."],"answer":"She does her homework.","explanation":"Después de la escuela, Maria va a casa y hace su tarea, como se indica en el texto."},{"question":"Maria has dinner with her family at:","options":["6:00","6:30","7:00","7:30"],"answer":"7:00","explanation":"Maria cena con su familia a las 7:00, según el texto."},{"question":"What does Maria do before sleeping?","options":["She watches TV.","She reads a little.","She plays games.","She listens to music."],"answer":"She reads a little.","explanation":"Maria siempre lee un poco antes de dormir, como se menciona al final del texto."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
