  42,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"Tom ______ (have to) wake up early every day because he works at a bakery.","acceptableAnswers":["has to"],"explanation":"Usamos 'have to' para indicar obligación. En tercera persona singular del presente simple, se convierte en 'has to'.","points":1},{"sentence":"Sarah ______ (would like to) visit her grandmother this weekend.","acceptableAnswers":["would like to"],"explanation":"'Would like to' se utiliza para expresar deseos o preferencias. No cambia con el sujeto.","points":1},{"sentence":"You ______ (mustn't) eat too much candy before dinner.","acceptableAnswers":["mustn't"],"explanation":"Se utiliza 'mustn't' para indicar prohibición, es decir, algo que no está permitido.","points":1},{"sentence":"It is too hot ______ (in/on/at) summer, so we usually go to the beach.","acceptableAnswers":["in"],"explanation":"Usamos 'in' con estaciones del año como 'in summer'.","points":1},{"sentence":"Lisa ______ (have to) finish her homework before she can watch TV.","acceptableAnswers":["has to"],"explanation":"Usamos 'have to' para obligación. En tercera persona singular del presente simple, se convierte en 'has to'.","points":1},{"sentence":"We ______ (would like to) try the new Italian restaurant in town.","acceptableAnswers":["would like to"],"explanation":"'Would like to' se utiliza para expresar deseos o preferencias. No cambia con el sujeto.","points":1},{"sentence":"You ______ (don't have to) bring any food, we have plenty for everyone.","acceptableAnswers":["don't have to"],"explanation":"Usamos 'don't have to' para indicar que algo no es necesario o obligatorio.","points":1},{"sentence":"We ______ (must) wear a uniform at school every day.","acceptableAnswers":["must"],"explanation":"Se utiliza 'must' para indicar obligación fuerte o reglas.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
