  'crossword',
  $_$Crossword: Weekly Habits$_$,
  $_${"title":"Crossword: Weekly Habits","instructions":"Complete the crossword puzzle","items":[{"word":"MONDAY","clue":"The first day of the work week","direction":"across","row":0,"col":0},{"word":"NEVER","clue":"An adverb of frequency meaning 'not at any time'","direction":"down","row":0,"col":2},{"word":"OFTEN","clue":"An adverb of frequency meaning 'many times'","direction":"across","row":1,"col":1},{"word":"ALWAYS","clue":"An adverb of frequency meaning 'every time'","direction":"down","row":1,"col":3},{"word":"SUNDAY","clue":"The last day of the week","direction":"across","row":3,"col":0},{"word":"RARELY","clue":"An adverb of frequency meaning 'not often'","direction":"across","row":4,"col":1}]}$_$
