  $_${"title":"Crossword: Greetings and Introductions","instructions":"Complete the crossword puzzle","items":[{"word":"HELLO","clue":"A common greeting in English","direction":"across","row":0,"col":0},{"word":"JAPANESE","clue":"Nationality of someone from Japan","direction":"down","row":0,"col":2},{"word":"THEY","clue":"Subject pronoun for a group of people","direction":"across","row":2,"col":0},{"word":"ARE","clue":"Present simple form of 'be' for 'they'","direction":"down","row":2,"col":3},{"word":"FIFTY","clue":"The number 50 in words","direction":"across","row":4,"col":0},{"word":"SPANISH","clue":"Language spoken in Spain","direction":"down","row":0,"col":5}]}$_$
