INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex1',
  'a2-m1-l2',
  1,
  'multiple-choice',
  $_$Personal Information Practice$_$,
  $_${"title":"Personal Information Practice","instructions":"Elige la respuesta correcta para cada pregunta.","questions":[{"id":"q1","question":"What is the correct spelling of this name: M-a-r-i-a?","options":["A. Mary","B. Marie","C. Maria","D. Marry"],"correctAnswer":"C","explanation":"La opción C, 'Maria', es la correcta porque sigue la secuencia de letras M-a-r-i-a."},{"id":"q2","question":"Is Peter a teacher?","options":["A. Yes, he is not.","B. No, he is.","C. No, he isn't.","D. Yes, he isn't."],"correctAnswer":"C","explanation":"La opción C, 'No, he isn't.', es correcta para negar que Peter es un profesor."},{"id":"q3","question":"How do you spell the word 'doctor'?","options":["A. D-o-k-t-o-r","B. D-o-c-t-o-r","C. D-o-c-t-e-r","D. D-o-c-k-t-o-r"],"correctAnswer":"B","explanation":"La opción B, 'D-o-c-t-o-r', es la correcta porque es la ortografía correcta de 'doctor'."},{"id":"q4","question":"What is his job title if he works in a hospital?","options":["A. Chef","B. Pilot","C. Doctor","D. Teacher"],"correctAnswer":"C","explanation":"La opción C, 'Doctor', es correcta porque es la profesión de una persona que trabaja en un hospital."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex2',
  'a2-m1-l2',
  2,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"1. _______ is your name? (Answer with a question word)","acceptableAnswers":["What"],"explanation":"Usamos 'What' para preguntar por información personal como el nombre.","points":1},{"sentence":"2. I _______ not a teacher. (Fill with the correct form of 'be' verb)","acceptableAnswers":["am"],"explanation":"Se usa 'am' con 'I' para formar el negativo: 'I am not'.","points":1},{"sentence":"3. _______ you a student? (Fill with the correct form of 'be' verb)","acceptableAnswers":["Are"],"explanation":"Usamos 'Are' para hacer preguntas sobre el estado o la ocupación de alguien.","points":1},{"sentence":"4. My last name is _______ (Fill with 'Smith' spelled out)","acceptableAnswers":["S-M-I-T-H"],"explanation":"Debes deletrear el apellido 'Smith' letra por letra.","points":1},{"sentence":"5. She _______ a doctor. (Fill with the correct form of 'be' verb)","acceptableAnswers":["is"],"explanation":"Usamos 'is' con 'she' para describir su profesión.","points":1},{"sentence":"6. _______ is your favorite color? (Answer with a question word)","acceptableAnswers":["What"],"explanation":"Usamos 'What' para preguntar sobre preferencias o información específica.","points":1},{"sentence":"7. He _______ a student. (Fill with the correct form of 'be' verb, negative)","acceptableAnswers":["is not","isn't"],"explanation":"Para formar el negativo, usamos 'is not' o la contracción 'isn't'.","points":1},{"sentence":"8. _______ you from Spain? (Fill with the correct form of 'be' verb)","acceptableAnswers":["Are"],"explanation":"Usamos 'Are' para preguntar sobre el origen de una persona.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex3',
  'a2-m1-l2',
  3,
  'sentence-building',
  $_$Exercise$_$,
  $_${"exercises":[{"id":1,"words":["Sarah","teacher","not","is","a","."],"correctOrder":["Sarah","is","not","a","teacher","."],"translation":"Sarah no es una profesora.","hint":"Forma una oración negativa usando 'is not'.","points":2},{"id":2,"words":["is","name","your","What","?"],"correctOrder":["What","is","your","name","?"],"translation":"¿Cuál es tu nombre?","hint":"Forma una pregunta usando 'What' y 'is'.","points":2},{"id":3,"words":["a","is","not","doctor","He","."],"correctOrder":["He","is","not","a","doctor","."],"translation":"Él no es un doctor.","hint":"Usa 'is not' para hacer una oración negativa.","points":2},{"id":4,"words":["spell","How","you","do","your","last","name","?"],"correctOrder":["How","do","you","spell","your","last","name","?"],"translation":"¿Cómo deletreas tu apellido?","hint":"Comienza con 'How' para una pregunta sobre ortografía.","points":2},{"id":5,"words":["is","your","What","job","?"],"correctOrder":["What","is","your","job","?"],"translation":"¿Cuál es tu trabajo?","hint":"Usa 'What' para preguntar sobre el trabajo de alguien.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex4',
  'a2-m1-l2',
  4,
  'speaking-analysis',
  $_$Speaking Analysis: Personal Information$_$,
  $_${"title":"Speaking Analysis: Personal Information","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are meeting someone for the first time. They ask for your name.","question":"What is the most natural way to respond?","options":["A: My name is John. How do you spell your name?","B: My name John.","C: I calling John.","D: I name is John."],"correctAnswer":"A","explanation":"La opción A es la más adecuada porque utiliza la forma correcta del verbo 'be' para presentarse y sigue con una pregunta común en una conversación inicial.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are in a formal job interview. The interviewer asks about your previous job.","question":"Which response is most formal and appropriate?","options":["A: I was working as a teacher.","B: I worked as a teacher.","C: I work teacher.","D: I am teacher."],"correctAnswer":"B","explanation":"La opción B es la más formal y correcta para una entrevista de trabajo, ya que utiliza el pasado simple de manera adecuada.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are at a party and someone asks for your job title.","question":"Which idiomatic expression is used correctly?","options":["A: I'm in between jobs.","B: I'm jobless.","C: My job is nothing.","D: I no work."],"correctAnswer":"A","explanation":"La opción A utiliza una expresión idiomática correcta para indicar que actualmente no tienes un empleo pero estás buscando.","points":2},{"id":"q4","type":"multiple-choice","scenario":"Someone asks you to spell your last name over the phone.","question":"Which sentence correctly uses discourse markers?","options":["A: It's S-M-I-T-H, you know?","B: So, it's S, M, I, T, H.","C: Um, it's S-M-I-T-H.","D: It's S-M-I-T-H, like I said."],"correctAnswer":"B","explanation":"La opción B utiliza 'So' como marcador discursivo para introducir la información de manera clara y adecuada.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are writing an email to a colleague and need to be polite.","question":"Which version is the most diplomatic?","options":["A: Can you do this for me?","B: Could you please handle this task?","C: You must do this.","D: Do this now."],"correctAnswer":"B","explanation":"La opción B es la más diplomática porque utiliza 'could' y 'please', lo que suaviza la solicitud.","points":2},{"id":"q6","type":"multiple-choice","scenario":"Your friend asks you about your job and you want to reply casually.","question":"Which informal expression fits the casual context?","options":["A: I am currently employed as a waiter.","B: I work as a server.","C: I'm a server at the café.","D: My occupation is in food service."],"correctAnswer":"C","explanation":"La opción C es informal y adecuada para una conversación casual entre amigos.","points":2},{"id":"q7","type":"multiple-choice","scenario":"You are in a group discussion and want to add your opinion.","question":"Which phrase appropriately manages conversation flow?","options":["A: That's wrong, listen to me.","B: Can I just add something here?","C: I will speak now.","D: You are incorrect, let me say."],"correctAnswer":"B","explanation":"La opción B es adecuada para tomar el turno de palabra de manera educada y respetuosa durante una discusión grupal.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex5',
  'a2-m1-l2',
  5,
  'word-search',
  $_$Word Search: Personal Information$_$,
  $_${"title":"Word Search: Personal Information","instructions":"Find the hidden words in the grid","words":["WHO","WHAT","WHERE","WHY","ISN'T","AREN'T","DOCTOR","TEACHER","SPELL","NAME"],"gridSize":10,"clues":[{"word":"WHO","clue":"¿Quién?"},{"word":"WHAT","clue":"¿Qué?"},{"word":"WHERE","clue":"¿Dónde?"},{"word":"WHY","clue":"¿Por qué?"},{"word":"ISN'T","clue":"Forma negativa del verbo 'be' para 'he/she/it'"},{"word":"AREN'T","clue":"Forma negativa del verbo 'be' para 'you/we/they'"},{"word":"DOCTOR","clue":"Alguien que trabaja en un hospital"},{"word":"TEACHER","clue":"Alguien que trabaja en una escuela"},{"word":"SPELL","clue":"Decir las letras de una palabra"},{"word":"NAME","clue":"Tu identidad personal escrita"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex6',
  'a2-m1-l2',
  6,
  'crossword',
  $_$Crossword: Personal Information$_$,
  $_${"title":"Crossword: Personal Information","instructions":"Complete the crossword puzzle","items":[{"word":"WHO","clue":"Question word used to ask about a person","direction":"across","row":0,"col":0},{"word":"WHERE","clue":"Question word for asking about a place","direction":"down","row":0,"col":2},{"word":"TEACHER","clue":"Job title for someone who works in a school","direction":"across","row":1,"col":0},{"word":"IS","clue":"Be verb used with he, she, it","direction":"down","row":0,"col":4},{"word":"WHY","clue":"Question word to ask for a reason","direction":"across","row":3,"col":1},{"word":"DOCTOR","clue":"Job title for someone who helps sick people","direction":"across","row":4,"col":0},{"word":"ARE","clue":"Be verb used with you, we, they","direction":"down","row":1,"col":4},{"word":"WHAT","clue":"Question word used to ask for information","direction":"across","row":5,"col":0}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex7',
  'a2-m1-l2',
  7,
  'reading-comprehension',
  $_$Getting to Know Maria$_$,
  $_${"title":"Getting to Know Maria","textPassage":"Maria is a friendly woman who lives in a small town. She works as a nurse at the local hospital. Her job is very important because she helps many people every day. Maria is not a doctor, but she loves her job very much. Her best friend, Anna, is a teacher. Maria and Anna often spend time together on weekends. They like to go to the park and have picnics. Maria's last name is Rodriguez, which is spelled R-O-D-R-I-G-U-E-Z. She has two brothers, and both are engineers. Maria is not married, but she has a pet cat named Coco. She enjoys reading books and watching movies in her free time. Maria's favorite color is blue, and she likes to listen to music while she works.","wordCount":128,"estimatedReadingTime":2,"questions":[{"question":"What is Maria's job?","options":["Teacher","Engineer","Nurse","Doctor"],"answer":"Nurse","explanation":"Maria trabaja como enfermera en el hospital local, lo cual se menciona claramente en el pasaje."},{"question":"Is Maria married?","options":["Yes","No","She is divorced","It's not mentioned"],"answer":"No","explanation":"El texto dice que Maria no está casada, pero tiene un gato como mascota."},{"question":"What do Maria and Anna like to do on weekends?","options":["Go shopping","Have picnics","Visit museums","Go to the movies"],"answer":"Have picnics","explanation":"Maria y Anna disfrutan de pasar tiempo juntas en el parque haciendo picnics."},{"question":"Maria's last name is spelled correctly as:","options":["R-O-D-R-I-G-U-E-Z","R-O-D-R-I-G-U-E-S","R-O-D-R-I-G-U-Z","R-O-D-R-I-Q-U-E-Z"],"answer":"R-O-D-R-I-G-U-E-Z","explanation":"El texto proporciona la ortografía exacta del apellido de Maria como R-O-D-R-I-G-U-E-Z."},{"question":"What are Maria's brothers' jobs?","options":["Teachers","Doctors","Engineers","Nurses"],"answer":"Engineers","explanation":"El pasaje menciona que ambos hermanos de Maria son ingenieros."},{"question":"True or False: Maria works as a doctor.","options":["True","False"],"answer":"False","explanation":"Maria no es doctora, es enfermera, como se indica en el texto."},{"question":"What is the main idea of the passage?","options":["Maria's weekend activities","Maria's job and personal life","Maria's love for music","Maria's family history"],"answer":"Maria's job and personal life","explanation":"El pasaje principalmente describe el trabajo de Maria y aspectos de su vida personal."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex8',
  'a2-m1-l2',
  8,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Intonation$_$,
  $_${"title":"Práctica de pronunciación: Intonation","instructions":"Practica la entonación en inglés con las siguientes oraciones. Presta atención a la melodía de la oración para mejorar tu comprensión y pronunciación. Escucha el audio y repite cada oración varias veces.","targetSentences":[{"english":"Is this your first visit to the doctor?","spanish":"¿Es esta tu primera visita al médico?","phonetic":"/ɪz ðɪs jɔːr fɜːrst ˈvɪzɪt tuː ðə ˈdɒktə/","audioModel":"URL_placeholder_1","pronunciationTips":"La entonación sube al inicio de la pregunta y baja al final.","focusPoints":"Rising intonation at the start, falling at the end","commonMistakes":"Evitar mantener la misma entonación durante toda la oración."},{"english":"Did you go to the movies yesterday?","spanish":"¿Fuiste al cine ayer?","phonetic":"/dɪd juː ɡəʊ tuː ðə ˈmuːviz ˈjɛstədeɪ/","audioModel":"URL_placeholder_2","pronunciationTips":"Sube ligeramente la entonación en 'movies' para indicar una pregunta.","focusPoints":"Slightly rising intonation on 'movies'","commonMistakes":"No pronunciar 'movies' con una entonación plana."},{"english":"You should call the bank tomorrow.","spanish":"Deberías llamar al banco mañana.","phonetic":"/juː ʃʊd kɔːl ðə bæŋk təˈmɒrəʊ/","audioModel":"URL_placeholder_3","pronunciationTips":"La entonación baja al final para dar una sugerencia.","focusPoints":"Falling intonation at the end","commonMistakes":"Evitar subir la entonación al final de una sugerencia."},{"english":"Will it rain this weekend?","spanish":"¿Lloverá este fin de semana?","phonetic":"/wɪl ɪt reɪn ðɪs ˈwiːkˌɛnd/","audioModel":"URL_placeholder_4","pronunciationTips":"La entonación sube al final de la pregunta.","focusPoints":"Rising intonation at the end","commonMistakes":"No finalizar la pregunta con una entonación plana."},{"english":"I have to visit my friend in the hospital.","spanish":"Tengo que visitar a mi amigo en el hospital.","phonetic":"/aɪ hæv tuː ˈvɪzɪt maɪ frɛnd ɪn ðə ˈhɒspɪtl/","audioModel":"URL_placeholder_5","pronunciationTips":"Mantén una entonación constante, pero baja al final.","focusPoints":"Consistent intonation with a slight fall at the end","commonMistakes":"No elevar la entonación al final de una declaración."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex9',
  'a2-m1-l2',
  9,
  'multiple-choice',
  $_$Personal Information: Question Words and Be Verb$_$,
  $_${"title":"Personal Information: Question Words and Be Verb","instructions":"Elige la opción correcta para cada pregunta. Las explicaciones están en español para ayudarte a entender mejor.","questions":[{"id":"q1","question":"What is your brother's job?","options":["A. He is a teacher","B. He is not teacher","C. He teacher","D. He a teacher"],"correctAnswer":"A","explanation":"La respuesta correcta es 'He is a teacher' porque usamos el verbo 'be' (is) para describir la ocupación de alguien. Las otras opciones no son gramaticalmente correctas."},{"id":"q2","question":"How do you spell your last name?","options":["A. S-M-I-T-H","B. S-M-E-T-H","C. S-I-M-T-H","D. S-M-T-H"],"correctAnswer":"A","explanation":"La respuesta correcta es 'S-M-I-T-H'. El deletreo correcto del apellido 'Smith' es importante para asegurar la precisión en la información personal."},{"id":"q3","question":"Is she a doctor?","options":["A. No, she not","B. Yes, she is","C. She doctor","D. She is doctor"],"correctAnswer":"B","explanation":"La respuesta correcta es 'Yes, she is' porque es la forma correcta de responder afirmativamente a una pregunta con el verbo 'be'. Las otras opciones son gramaticalmente incorrectas."},{"id":"q4","question":"What is her name?","options":["A. Her name Anna","B. Her name's Anna","C. She name Anna","D. Name her Anna"],"correctAnswer":"B","explanation":"La respuesta correcta es 'Her name's Anna' porque usamos la contracción 'name's' para indicar posesión de manera correcta en inglés. Las otras opciones tienen errores en la estructura."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex10',
  'a2-m1-l2',
  10,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"My brother is _____ than my sister, but they are both very intelligent.","options":["taller","tallest","most tall"],"answer":"taller","explanation":"En inglés, usamos el comparativo 'taller' para comparar dos personas o cosas. 'Tallest' es para el superlativo (el más alto de todos).","points":1},{"sentence":"There aren't _____ apples in the basket.","options":["many","much","a lot of"],"answer":"many","explanation":"'Many' se usa con sustantivos contables en plural como 'apples'. 'Much' se usaría con sustantivos incontables.","points":1},{"sentence":"Is there _____ milk in the fridge?","options":["some","any","a lot of"],"answer":"any","explanation":"En preguntas, usamos 'any' para sustantivos incontables como 'milk'. 'Some' se usa generalmente en afirmaciones.","points":1},{"sentence":"You mustn't _____ in the library. It's a quiet place.","options":["talk","to talk","talking"],"answer":"talk","explanation":"Después de 'mustn't', usamos el verbo en su forma base, sin 'to'.","points":1},{"sentence":"This coffee is too hot. I can't drink it _____.","options":["now","later","soon"],"answer":"now","explanation":"'Too' indica que algo es más de lo necesario, y 'now' se refiere al momento presente.","points":1},{"sentence":"We have _____ time to finish the project, so let's not rush.","options":["enough","too","many"],"answer":"enough","explanation":"'Enough' indica que hay suficiente cantidad. 'Too' sería para indicar más de lo necesario.","points":1},{"sentence":"_____ books did you buy yesterday?","options":["How many","How much","Many"],"answer":"How many","explanation":"Usamos 'How many' para preguntar sobre la cantidad de sustantivos contables en plural, como 'books'.","points":1},{"sentence":"They _____ have to work on Sunday, it's their day off.","options":["don't","doesn't","aren't"],"answer":"don't","explanation":"Usamos 'don't' para la negación en presente simple con 'they'. 'Doesn't' se usaría con 'he/she/it'.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex11',
  'a2-m1-l2',
  11,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["isn't","He","doctor","a","."],"correctOrder":["He","isn't","a","doctor","."],"translation":"Él no es un doctor.","hint":"Sujeto + 'isn't' + artículo + sustantivo.","points":2},{"id":2,"words":["your","spell","Can","name","you","?","please"],"correctOrder":["Can","you","spell","your","name","please","?"],"translation":"¿Puedes deletrear tu nombre, por favor?","hint":"Pregunta con 'Can' + sujeto + verbo.","points":2},{"id":3,"words":["What","is","job","your","?","title"],"correctOrder":["What","is","your","job","title","?"],"translation":"¿Cuál es tu título de trabajo?","hint":"Pregunta con 'What' + verbo.","points":2},{"id":4,"words":["is","He","not","engineer","an","."],"correctOrder":["He","is","not","an","engineer","."],"translation":"Él no es un ingeniero.","hint":"Sujeto + 'is not' + artículo + sustantivo.","points":2},{"id":5,"words":["Where","you","from","are","?"],"correctOrder":["Where","are","you","from","?"],"translation":"¿De dónde eres?","hint":"Pregunta con 'Where' + verbo.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex12',
  'a2-m1-l2',
  12,
  'speaking-analysis',
  $_$Speaking Analysis: Personal Information$_$,
  $_${"title":"Speaking Analysis: Personal Information","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are meeting someone for the first time and want to know what they do for work.","question":"Which question would you ask?","options":["A: What do you do?","B: What is your working?","C: What do you working?","D: What be your job?"],"correctAnswer":"A","explanation":"La opción A es la forma correcta de preguntar sobre el trabajo de alguien en inglés.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You didn't hear the person's name clearly. What would you say?","question":"Which is the most polite way to ask them to repeat their name?","options":["A: What?","B: Say again?","C: Could you repeat your name, please?","D: Name again?"],"correctAnswer":"C","explanation":"La opción C es la forma más cortés de pedir a alguien que repita su nombre.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You want to make sure you spell someone's name correctly.","question":"How would you ask them to spell their name?","options":["A: Spell your name?","B: Can you spell your name, please?","C: How spell your name?","D: Is your name spelled?"],"correctAnswer":"B","explanation":"La opción B es una pregunta clara y cortés para pedir la ortografía de un nombre.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are talking to a colleague about a friend who is a teacher.","question":"Which sentence correctly describes your friend's job?","options":["A: She is teacher.","B: She be a teacher.","C: She is a teacher.","D: She are a teacher."],"correctAnswer":"C","explanation":"La opción C es gramaticalmente correcta. Se usa 'is' para describir el trabajo de alguien.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are introducing yourself in a formal meeting.","question":"Which introduction is most appropriate?","options":["A: Hi, I'm Ana.","B: Hey, name's Ana.","C: Good morning, my name is Ana.","D: Hello, I Ana."],"correctAnswer":"C","explanation":"La opción C es la introducción más formal y adecuada para una reunión.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are asked where you live during a casual conversation.","question":"How would you respond informally?","options":["A: I am living in Madrid.","B: I live in Madrid.","C: Living in Madrid.","D: Be living in Madrid."],"correctAnswer":"B","explanation":"La opción B es la respuesta más informal y común para indicar dónde vives.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex13',
