  'word-search',
  $_$Word Search: Personal Information$_$,
  $_${"title":"Word Search: Personal Information","instructions":"Find the hidden words in the grid","words":["WHO","WHAT","WHERE","AREN'T","ISN'T","JOB","TEACHER","DOCTOR","SPELL"],"gridSize":10,"clues":[{"word":"WHO","clue":"¿Quién?"},{"word":"WHAT","clue":"¿Qué?"},{"word":"WHERE","clue":"¿Dónde?"},{"word":"AREN'T","clue":"Forma negativa del verbo 'be' (plural)"},{"word":"ISN'T","clue":"Forma negativa del verbo 'be' (singular)"},{"word":"JOB","clue":"Palabra para 'trabajo' o 'empleo'"},{"word":"TEACHER","clue":"Persona que enseña"},{"word":"DOCTOR","clue":"Persona que cuida de la salud"},{"word":"SPELL","clue":"Decir las letras de una palabra en orden"}]}$_$
