INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex14',
  'a2-m1-l2',
  14,
  'crossword',
  $_$Crossword: Personal Information$_$,
  $_${"title":"Crossword: Personal Information","instructions":"Complete the crossword puzzle","items":[{"word":"WHO","clue":"Question word to ask about a person","direction":"across","row":0,"col":0},{"word":"IS","clue":"Be verb used in questions (third person singular)","direction":"down","row":0,"col":2},{"word":"DOCTOR","clue":"A person who helps sick people","direction":"across","row":1,"col":0},{"word":"WHERE","clue":"Question word to ask about a place","direction":"down","row":0,"col":5},{"word":"JANE","clue":"A common female name, spell it","direction":"across","row":3,"col":0},{"word":"TEACHER","clue":"A person who works in a school","direction":"down","row":1,"col":7},{"word":"NOT","clue":"Negative form of the verb 'be'","direction":"across","row":4,"col":5}]}$_$
