INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex15',
  'a2-m1-l2',
  15,
  'reading-comprehension',
  $_$Personal Information: Meet Mary$_$,
  $_${"title":"Personal Information: Meet Mary","textPassage":"Mary is a friendly woman who works as a nurse. She is not a teacher. Mary loves helping people and she works in a big hospital in the city. Every day, she meets many patients and their families. Her job is not easy, but she enjoys it very much. Mary's last name is Johnson. You spell it J-O-H-N-S-O-N. Her first name is M-A-R-Y. Mary is not married, but she has a pet dog named Max. Mary always says, 'I am not a doctor, I am a nurse!' because sometimes people get confused. She likes her job and thinks it's important to be kind. Mary believes in helping others and being happy at work.","wordCount":117,"estimatedReadingTime":2,"questions":[{"question":"What is Mary's job?","options":["Teacher","Doctor","Nurse","Engineer"],"correctAnswer":"Nurse","explanation":"Mary trabaja como enfermera, no como profesora o doctora."},{"question":"Where does Mary work?","options":["In a school","In a hospital","In a park","In a supermarket"],"correctAnswer":"In a hospital","explanation":"Mary trabaja en un hospital grande de la ciudad."},{"question":"Is Mary married?","options":["Yes","No","It's not mentioned","Maybe"],"correctAnswer":"No","explanation":"Mary no está casada, pero tiene un perro llamado Max."},{"question":"What is Mary's pet's name?","options":["Sam","Max","Leo","Tom"],"correctAnswer":"Max","explanation":"El perro de Mary se llama Max."},{"question":"Mary's last name is spelled:","options":["J-O-H-N-S-O-N","J-O-N-H-S-O-N","J-O-H-N-S-I-N","J-O-H-N-S-O-M"],"correctAnswer":"J-O-H-N-S-O-N","explanation":"El apellido de Mary se deletrea J-O-H-N-S-O-N."},{"question":"Mary says, 'I am not a doctor, I am a nurse!' Why does she say this?","options":["She wants to be a doctor.","People often confuse her job.","She doesn't like being a nurse.","She wants to change jobs."],"correctAnswer":"People often confuse her job.","explanation":"Mary dice esto porque a veces las personas se confunden con su trabajo."},{"question":"True or False: Mary believes her job is important.","options":["True","False"],"correctAnswer":"True","explanation":"Mary cree que su trabajo es importante y que es importante ser amable."},{"question":"Which of the following is NOT mentioned about Mary?","options":["She likes her job.","She has a cat.","She helps people.","She is not married."],"correctAnswer":"She has a cat.","explanation":"No se menciona que Mary tenga un gato. Se menciona que tiene un perro llamado Max."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex16',
  'a2-m1-l2',
  16,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Enfoque en el Estrés de Palabras$_$,
  $_${"title":"Práctica de Pronunciación: Enfoque en el Estrés de Palabras","instructions":"Escucha las oraciones y presta atención al estrés de las palabras en inglés. Practica repitiendo cada oración y concéntrate en el estrés de las palabras resaltadas.","targetSentences":[{"english":"The doctor said you should rest today.","spanish":"El médico dijo que deberías descansar hoy.","phonetic":"ˈdɒk.tər","audioModelUrl":"URL_placeholder","pronunciationTips":"Enfoca el estrés en 'doctor'. Asegúrate de dar más énfasis en la primera sílaba.","focusPoints":"Palabra clave 'doctor' con énfasis en la primera sílaba.","commonMistakes":"Evitar pronunciar 'doctor' sin enfatizar correctamente la primera sílaba."},{"english":"I have a meeting with the manager tomorrow.","spanish":"Tengo una reunión con el gerente mañana.","phonetic":"ˈmiː.tɪŋ","audioModelUrl":"URL_placeholder","pronunciationTips":"Enfoca el estrés en 'meeting'. La primera sílaba recibe más énfasis.","focusPoints":"Palabra clave 'meeting' con énfasis en la primera sílaba.","commonMistakes":"No omitir el estrés en la primera sílaba de 'meeting'."},{"english":"You must call the nurse if you feel sick.","spanish":"Debes llamar a la enfermera si te sientes mal.","phonetic":"nɜːs","audioModelUrl":"URL_placeholder","pronunciationTips":"En 'nurse', asegúrate de pronunciar la 'r' suavemente.","focusPoints":"Palabra clave 'nurse' con una pronunciación clara de la 'r'.","commonMistakes":"Evitar pronunciar 'nurse' sin la 'r' suave."},{"english":"She works as a dentist in the city.","spanish":"Ella trabaja como dentista en la ciudad.","phonetic":"ˈden.tɪst","audioModelUrl":"URL_placeholder","pronunciationTips":"Enfoca el estrés en 'dentist'. La primera sílaba es más fuerte.","focusPoints":"Palabra clave 'dentist' con énfasis en la primera sílaba.","commonMistakes":"No pronunciar 'dentist' con el estrés en la segunda sílaba."},{"english":"He is a friendly teacher.","spanish":"Él es un profesor amable.","phonetic":"ˈfriːnd.li","audioModelUrl":"URL_placeholder","pronunciationTips":"Enfoca el estrés en 'friendly'. La primera sílaba es más larga.","focusPoints":"Palabra clave 'friendly' con énfasis en la primera sílaba.","commonMistakes":"Evitar pronunciar 'friendly' sin enfatizar la primera sílaba."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex17',
  'a2-m1-l2',
  17,
  'multiple-choice',
  $_$Personal Information Practice$_$,
  $_${"title":"Personal Information Practice","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"What is her job? She is a ______.","options":["A teacher","B doctor","C engineer","D pilot"],"correctAnswer":"B doctor","explanation":"La palabra 'doctor' es un título de trabajo común. El contexto sugiere que estamos hablando de una profesión, por lo que 'doctor' es la respuesta correcta."},{"id":"q2","question":"Is he a student? No, he _____ a student.","options":["isn't","aren't","doesn't","not"],"correctAnswer":"isn't","explanation":"'Isn't' es la contracción de 'is not', que se usa para negar en tercera persona del singular. Las otras opciones no son correctas en este contexto."},{"id":"q3","question":"How do you spell your name? It's ______.","options":["A-L-I-C-E","E-L-L-I-S","J-O-H-N","S-A-R-A"],"correctAnswer":"A-L-I-C-E","explanation":"La pregunta pide cómo se deletrea el nombre 'Alice'. La opción A proporciona el deletreo correcto."},{"id":"q4","question":"Where is the manager? ______ is in the office.","options":["He","She","They","It"],"correctAnswer":"She","explanation":"La palabra 'manager' puede ser tanto masculina como femenina, pero el contexto de este ejercicio implica que 'She' es la respuesta correcta porque estamos hablando de una mujer en este caso."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex18',
  'a2-m1-l2',
  18,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"You ______ like to visit the new restaurant in town?","options":["Would","Should","Must"],"answer":"Would","explanation":"Usamos 'would like to' para expresar deseos o preferencias. Aquí, 'would' es correcto para formular una pregunta sobre un deseo de visitar.","points":1},{"sentence":"We ______ go to the park today because it might rain.","options":["shouldn't","wouldn't","mustn't"],"answer":"shouldn't","explanation":"'Shouldn't' se usa para dar un consejo negativo, sugiriendo que no es una buena idea ir al parque debido a la posibilidad de lluvia.","points":1},{"sentence":"I ______ to eat more vegetables for my health.","options":["must","would like","mustn't"],"answer":"would like","explanation":"Aquí, 'would like' se utiliza para expresar un deseo o preferencia personal de comer más vegetales.","points":1},{"sentence":"You ______ take pictures in the museum. It is not allowed.","options":["mustn't","shouldn't","wouldn't"],"answer":"mustn't","explanation":"'Mustn't' se utiliza para indicar prohibición, en este caso, la prohibición de tomar fotos en el museo.","points":1},{"sentence":"My brother is ______ studying for his exams tomorrow.","options":["is","are","am"],"answer":"is","explanation":"Usamos 'is' con 'my brother' porque es tercera persona singular y está en presente continuo para indicar una acción futura planificada.","points":1},{"sentence":"I think you ______ go to the doctor if you feel sick.","options":["should","must","would"],"answer":"should","explanation":"'Should' se utiliza para dar consejos en inglés. La frase sugiere que ir al médico es una buena idea si te sientes enfermo.","points":1},{"sentence":"There ______ any milk left in the fridge.","options":["isn't","aren't","doesn't"],"answer":"isn't","explanation":"Usamos 'isn't' para negar la existencia de leche en la nevera, ya que 'milk' es un sustantivo incontable.","points":1},{"sentence":"She ______ like to travel to Japan next year.","options":["would","should","must"],"answer":"would","explanation":"'Would like to' se utiliza para expresar un deseo o interés futuro de viajar a Japón.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex19',
  'a2-m1-l2',
  19,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["do","How","many","students","are","in","your","class","?"],"correctOrder":["How","many","students","are","in","your","class","?"],"translation":"¿Cuántos estudiantes hay en tu clase?","hint":"Utiliza 'How many' para preguntar por cantidad de contables.","points":2},{"id":2,"words":["mustn't","You","forget","to","bring","your","ID","."],"correctOrder":["You","mustn't","forget","to","bring","your","ID","."],"translation":"No debes olvidar traer tu identificación.","hint":"Usa 'mustn't' para expresar prohibición.","points":2},{"id":3,"words":["doesn't","She","have","to","work","on","Sundays","."],"correctOrder":["She","doesn't","have","to","work","on","Sundays","."],"translation":"Ella no tiene que trabajar los domingos.","hint":"Usa 'doesn't have to' para expresar falta de obligación.","points":2},{"id":4,"words":["more","This","book","is","interesting","than","that","one","."],"correctOrder":["This","book","is","more","interesting","than","that","one","."],"translation":"Este libro es más interesante que ese.","hint":"Para comparativos de superioridad con adjetivos largos, usa 'more'.","points":2},{"id":5,"words":["much","How","sugar","do","you","need","for","the","cake","?"],"correctOrder":["How","much","sugar","do","you","need","for","the","cake","?"],"translation":"¿Cuánta azúcar necesitas para el pastel?","hint":"Utiliza 'How much' para preguntar por cantidad de no contables.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex20',
  'a2-m1-l2',
  20,
  'speaking-analysis',
  $_$Speaking Analysis: Personal Information$_$,
  $_${"title":"Speaking Analysis: Personal Information","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are meeting a new colleague for the first time and want to ask about their job.","question":"Which question is correct?","options":["A) What job do you have?","B) What are you do?","C) What you do?","D) What do you work?"],"correctAnswer":"A","explanation":"La opción A es correcta porque utiliza la estructura correcta del presente simple para hacer preguntas sobre el trabajo de alguien.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You want to confirm the spelling of a name over the phone.","question":"Which phrase would you use?","options":["A) How you spell that?","B) Could you spell that, please?","C) What is the spelling?","D) Spell this."],"correctAnswer":"B","explanation":"La opción B es la correcta porque es una manera educada de pedir a alguien que deletree un nombre.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are introducing yourself at a party and someone asks what you do.","question":"Which response is appropriate?","options":["A) I am an engineer.","B) I am not teacher.","C) I do engineer.","D) I does engineer."],"correctAnswer":"A","explanation":"La opción A es correcta porque usa la estructura correcta para expresar una ocupación.","points":2},{"id":"q4","type":"multiple-choice","scenario":"A friend asks if you are a teacher, but you are not.","question":"How do you respond?","options":["A) I not a teacher.","B) I am not a teacher.","C) I don't teacher.","D) I am teacher."],"correctAnswer":"B","explanation":"La opción B es correcta porque usa correctamente el verbo 'be' en su forma negativa para responder que no eres maestro.","points":2},{"id":"q5","type":"multiple-choice","scenario":"Someone asks for your name and you need to spell it.","question":"What do you say?","options":["A) My name spells J-O-H-N.","B) It spells J-O-H-N.","C) It's J-O-H-N.","D) My name is spelled J-O-H-N."],"correctAnswer":"D","explanation":"La opción D es correcta porque utiliza la estructura adecuada para indicar cómo se deletrea un nombre.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are filling out a form and are asked about your occupation.","question":"Which phrase is correct?","options":["A) I am a doctor.","B) I doctor.","C) I is a doctor.","D) Am doctor."],"correctAnswer":"A","explanation":"La opción A es correcta porque usa la estructura adecuada para expresar una ocupación.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex21',
  'a2-m1-l2',
  21,
  'word-search',
  $_$Word Search: Personal Information$_$,
  $_${"title":"Word Search: Personal Information","instructions":"Find the hidden words in the grid","words":["WHAT","WHO","ISN'T","AREN'T","JAMES","DOCTOR","TEACHER","ALICE","WHERE","WHY"],"gridSize":10,"clues":[{"word":"WHAT","clue":"¿Qué?"},{"word":"WHO","clue":"¿Quién?"},{"word":"ISN'T","clue":"Forma negativa del verbo 'to be' en singular"},{"word":"AREN'T","clue":"Forma negativa del verbo 'to be' en plural"},{"word":"JAMES","clue":"Un nombre común de chico"},{"word":"DOCTOR","clue":"Trabaja en un hospital"},{"word":"TEACHER","clue":"Enseña en la escuela"},{"word":"ALICE","clue":"Un nombre común de chica"},{"word":"WHERE","clue":"¿Dónde?"},{"word":"WHY","clue":"¿Por qué?"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex22',
  'a2-m1-l2',
  22,
  'crossword',
  $_$Crossword: Personal Information$_$,
  $_${"title":"Crossword: Personal Information","instructions":"Complete the crossword puzzle","items":[{"word":"TEACHER","clue":"Someone who works in a school","direction":"across","row":0,"col":0},{"word":"STUDENT","clue":"A person who studies at school","direction":"down","row":0,"col":7},{"word":"WHERE","clue":"A question word for asking about places","direction":"across","row":2,"col":1},{"word":"NOT","clue":"This word makes a sentence negative","direction":"down","row":1,"col":3},{"word":"ENGINEER","clue":"A person who designs and builds things","direction":"across","row":4,"col":0},{"word":"IS","clue":"A form of 'be' used with he, she, it","direction":"down","row":3,"col":5},{"word":"WHY","clue":"A question word for asking reasons","direction":"across","row":6,"col":4}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex23',
  'a2-m1-l2',
  23,
  'reading-comprehension',
  $_$Understanding Personal Information$_$,
  $_${"title":"Understanding Personal Information","textPassage":"Maria works at a local library as a librarian. Her job is to help people find books and organize events for children. Maria loves her job because she enjoys reading and meeting new people. Every day, she answers questions from visitors. People often ask, 'Where is the history section?' or 'How do you spell this author's name?' Maria is patient and always ready to help. Her favorite author is J.K. Rowling, and she can spell her name without hesitation. Maria's manager, Mr. Brown, is not always in the office because he travels a lot for meetings. When he is not there, Maria takes care of everything. She is happy to assist anyone who needs help and feels proud of her work. Maria's job is not easy, but she finds it very rewarding.","wordCount":130,"estimatedReadingTime":2,"questions":[{"question":"What is Maria's job?","options":["A teacher","A librarian","A doctor","A manager"],"answer":"A librarian","explanation":"La pregunta pregunta sobre el trabajo de Maria. En el texto se menciona que ella trabaja en una biblioteca como bibliotecaria, por lo tanto, la respuesta correcta es 'A librarian'."},{"question":"Why does Maria love her job?","options":["Because she likes organizing books","Because she likes reading and meeting new people","Because she travels a lot","Because she works with Mr. Brown"],"answer":"Because she likes reading and meeting new people","explanation":"El texto indica que a Maria le encanta su trabajo porque disfruta leer y conocer gente nueva, lo que coincide con la opción correcta."},{"question":"Maria's manager, Mr. Brown, is not always in the office because he:","options":["Is on vacation","Travels a lot for meetings","Works from home","Is retired"],"answer":"Travels a lot for meetings","explanation":"El texto explica que el Sr. Brown no siempre está en la oficina porque viaja mucho para reuniones, lo cual es la razón correcta."},{"question":"True or False: Maria finds her job easy.","answer":"False","explanation":"El texto afirma que el trabajo de Maria no es fácil, pero ella lo encuentra muy gratificante. Así que la afirmación de que su trabajo es fácil es falsa."},{"question":"What does Maria often do at her job?","options":["She cooks meals","She answers questions from visitors","She writes books","She paints pictures"],"answer":"She answers questions from visitors","explanation":"El texto menciona que Maria responde preguntas de los visitantes como parte de sus tareas diarias en su trabajo."},{"question":"Which author's name can Maria spell without hesitation?","options":["J.K. Rowling","Stephen King","Agatha Christie","George Orwell"],"answer":"J.K. Rowling","explanation":"El texto especifica que el autor favorito de Maria es J.K. Rowling y que puede deletrear su nombre sin vacilar."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex24',
  'a2-m1-l2',
  24,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Información Personal$_$,
  $_${"title":"Práctica de Pronunciación: Información Personal","instructions":"Escucha y repite las oraciones en inglés. Presta atención a los puntos de pronunciación específicos indicados para cada una.","targetSentences":[{"englishSentence":"My name is Anna Brown.","spanishTranslation":"Mi nombre es Anna Brown.","phoneticTranscription":"/maɪ neɪm ɪz ˈænə braʊn/","audioModelURL":"https://example.com/audio1","pronunciationTips":"Asegúrate de pronunciar claramente el sonido /n/ en 'name' y 'Anna'.","focusPoints":"Stress on the first syllable of 'Anna'.","commonMistakes":"Evitar decir 'An-na' con un sonido /æ/ muy largo."},{"englishSentence":"I work as a nurse.","spanishTranslation":"Trabajo como enfermera.","phoneticTranscription":"/aɪ wɜrk æz ə nɜrs/","audioModelURL":"https://example.com/audio2","pronunciationTips":"Concentra el estrés en la palabra 'nurse'.","focusPoints":"Emphasize the /nɜrs/ sound.","commonMistakes":"No cambiar 'nurse' por 'ners'."},{"englishSentence":"He is not a student.","spanishTranslation":"Él no es un estudiante.","phoneticTranscription":"/hi ɪz nɑt ə ˈstuːdənt/","audioModelURL":"https://example.com/audio3","pronunciationTips":"El sonido /st/ en 'student' debe ser claro.","focusPoints":"Emphasize 'not' to highlight negation.","commonMistakes":"No decir 'studént' con el estrés al final."},{"englishSentence":"We are doctors.","spanishTranslation":"Somos doctores.","phoneticTranscription":"/wi ɑr ˈdɑktərz/","audioModelURL":"https://example.com/audio4","pronunciationTips":"Asegúrate de que el sonido /d/ en 'doctors' sea fuerte.","focusPoints":"Stress the first syllable in 'doctors'.","commonMistakes":"No omitir el sonido /s/ al final."},{"englishSentence":"She is a teacher.","spanishTranslation":"Ella es maestra.","phoneticTranscription":"/ʃi ɪz ə ˈtiːtʃər/","audioModelURL":"https://example.com/audio5","pronunciationTips":"Pronuncia bien el sonido /tʃ/ en 'teacher'.","focusPoints":"Emphasize 'teacher'.","commonMistakes":"No cambiar 'teacher' por 'teacer' sin el sonido /tʃ/."},{"englishSentence":"They are not engineers.","spanishTranslation":"Ellos no son ingenieros.","phoneticTranscription":"/ðeɪ ɑr nɑt ˌɛnʤəˈnɪrz/","audioModelURL":"https://example.com/audio6","pronunciationTips":"El sonido /nɪr/ en 'engineers' debe ser claro.","focusPoints":"Stress on the last syllable of 'engineers'.","commonMistakes":"No omitir el sonido /s/ al final."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex25',
  'a2-m1-l2',
  25,
  'multiple-choice',
  $_$Personal Information Exercise$_$,
  $_${"title":"Personal Information Exercise","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"How many letters are there in the English alphabet?","options":["A. 24","B. 25","C. 26","D. 27"],"correctAnswer":"C","explanation":"La respuesta correcta es '26' porque el alfabeto inglés tiene 26 letras, desde la A hasta la Z."},{"id":"q2","question":"What is the job title of a person who helps sick animals?","options":["A. Doctor","B. Teacher","C. Farmer","D. Veterinarian"],"correctAnswer":"D","explanation":"La respuesta correcta es 'Veterinarian' porque un veterinario es una persona que cuida y trata animales enfermos."},{"id":"q3","question":"Which question word is used to ask about the quantity of something?","options":["A. How much","B. How many","C. What","D. When"],"correctAnswer":"B","explanation":"La respuesta correcta es 'How many' porque se usa para preguntar sobre la cantidad de algo contable."},{"id":"q4","question":"Which sentence is correct?","options":["A. She not is a teacher.","B. She isn't a teacher.","C. She doesn't a teacher.","D. She not teacher."],"correctAnswer":"B","explanation":"La respuesta correcta es 'She isn't a teacher.' porque es la forma negativa correcta utilizando el verbo 'be'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex26',
  'a2-m1-l2',
  26,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"¿________ is your favorite color?","gap":"What","explanation":"Usamos 'What' para preguntar sobre cosas o información general.","points":1},{"sentence":"She ________ a teacher, she is a doctor.","gap":"isn't","explanation":"Utilizamos 'isn't' como la forma negativa de 'be' en tercera persona singular.","points":1},{"sentence":"______ they coming to the party tomorrow?","gap":"Are","explanation":"Usamos 'Are' para hacer preguntas con el verbo 'be' en presente continuo.","points":1},{"sentence":"Can you spell your name, please? It's ________.","gap":"J-O-H-N","explanation":"Cuando pedimos que deletreen un nombre, se responde con las letras separadas por guiones.","points":1},{"sentence":"He ________ enough money to buy the car.","gap":"doesn't have","explanation":"Utilizamos 'doesn't have' como negación en tercera persona del singular con el verbo 'have'.","points":1},{"sentence":"________ is her job? She is a nurse.","gap":"What","explanation":"Usamos 'What' para preguntar sobre la profesión o trabajo de alguien.","points":1},{"sentence":"________ you at home yesterday?","gap":"Were","explanation":"Usamos 'Were' para preguntar en el pasado simple con el verbo 'be'.","points":1},{"sentence":"He should ________ more vegetables to stay healthy.","gap":"eat","explanation":"Después de 'should' el verbo va en forma base para dar consejos.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex27',
  'a2-m1-l2',
  27,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["is","biggest","London","city","the","in","."],"correctOrder":["London","is","the","biggest","city","in","."],"translation":"Londres es la ciudad más grande.","hint":"Utiliza 'is' seguido del superlativo 'the biggest'.","points":2},{"id":2,"words":["have","to","You","wear","a","uniform","."],"correctOrder":["You","have","to","wear","a","uniform","."],"translation":"Tienes que llevar un uniforme.","hint":"Comienza con 'You' seguido de 'have to'.","points":2},{"id":3,"words":["going","We","visit","are","to","museum","the","."],"correctOrder":["We","are","going","to","visit","the","museum","."],"translation":"Vamos a visitar el museo.","hint":"Comienza con 'We are going to'.","points":2},{"id":4,"words":["mustn't","You","phone","use","your","class","in","."],"correctOrder":["You","mustn't","use","your","phone","in","class","."],"translation":"No debes usar tu teléfono en clase.","hint":"Utiliza 'You mustn't' seguido de la acción.","points":2},{"id":5,"words":["is","more","interesting","History","than","Math","."],"correctOrder":["History","is","more","interesting","than","Math","."],"translation":"La historia es más interesante que las matemáticas.","hint":"Comienza con 'History is more interesting than'.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex28',
  'a2-m1-l2',
  28,
  'speaking-analysis',
  $_$Speaking Analysis: Personal Information$_$,
  $_${"title":"Speaking Analysis: Personal Information","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are introducing yourself at a new job and someone asks your full name.","question":"How do you spell your last name?","options":["A: My last name is spelled S-M-I-T-H.","B: It is spelled S-M-Y-T-H.","C: Spell my name as S-M-I-T-H-E.","D: You spell it as S-M-E-I-T-H."],"correctAnswer":"A","explanation":"En esta situación, la opción A es correcta porque es una respuesta directa y clara a la pregunta sobre la ortografía del apellido.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are talking to a friend about a new colleague.","question":"What does your colleague do?","options":["A: He is a teacher.","B: He do a teacher.","C: He are a teacher.","D: He will a teacher."],"correctAnswer":"A","explanation":"La opción A es correcta porque utiliza la estructura gramatical adecuada del verbo 'to be' en presente simple.","points":2},{"id":"q3","type":"multiple-choice","scenario":"A friend asks you why you are not at the party.","question":"Why aren't you at the party?","options":["A: I am not feeling well.","B: I is not feeling well.","C: I am not feel well.","D: Am not feeling well."],"correctAnswer":"A","explanation":"La opción A es correcta porque utiliza la forma negativa del verbo 'to be' en presente simple correctamente.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You meet someone new and they say their name quickly.","question":"Could you spell your name, please?","options":["A: Yes, it's J-O-H-N.","B: Yes, J-O-H-N is.","C: Yes, spell J-H-O-N.","D: Yes, is J-O-H-N."],"correctAnswer":"A","explanation":"La opción A es correcta porque proporciona una respuesta clara y correcta sobre cómo deletrear el nombre.","points":2},{"id":"q5","type":"multiple-choice","scenario":"Someone asks you if your friend is a doctor.","question":"Is your friend a doctor?","options":["A: No, he isn't.","B: No, he not is.","C: No, is not he.","D: No, he is not is."],"correctAnswer":"A","explanation":"La opción A es correcta porque es la forma negativa correcta del verbo 'to be' en presente simple para responder a la pregunta.","points":2},{"id":"q6","type":"multiple-choice","scenario":"At a job interview, you are asked about your occupation.","question":"What is your job title?","options":["A: I am an engineer.","B: I be an engineer.","C: I engineer.","D: Engineer I am."],"correctAnswer":"A","explanation":"La opción A es correcta porque utiliza la estructura gramatical adecuada para decir la profesión de uno en inglés.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex29',
  'a2-m1-l2',
  29,
  'word-search',
  $_$Word Search: Personal Information$_$,
  $_${"title":"Word Search: Personal Information","instructions":"Find the hidden words in the grid","words":["WHAT","WHERE","WHO","ISN'T","AREN'T","NAME","SPELL","JOB","TEACHER","DOCTOR"],"gridSize":10,"clues":[{"word":"WHAT","clue":"¿Qué?"},{"word":"WHERE","clue":"¿Dónde?"},{"word":"WHO","clue":"¿Quién?"},{"word":"ISN'T","clue":"No es (contracción)"},{"word":"AREN'T","clue":"No son (contracción)"},{"word":"NAME","clue":"Nombre"},{"word":"SPELL","clue":"Deletrear"},{"word":"JOB","clue":"Trabajo"},{"word":"TEACHER","clue":"Profesor/a"},{"word":"DOCTOR","clue":"Doctor/a"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex30',
  'a2-m1-l2',
  30,
  'crossword',
  $_$Crossword: Personal Information$_$,
  $_${"title":"Crossword: Personal Information","instructions":"Complete the crossword puzzle","items":[{"word":"NAME","clue":"What is your _____? (question word)","direction":"across","row":0,"col":0},{"word":"DOCTOR","clue":"A person who helps sick people","direction":"down","row":0,"col":4},{"word":"WHERE","clue":"_____ do you live? (question word)","direction":"across","row":2,"col":0},{"word":"NO","clue":"The opposite of 'yes' (negative form)","direction":"down","row":1,"col":3},{"word":"NURSE","clue":"A person who assists doctors","direction":"across","row":4,"col":0},{"word":"HOW","clue":"_____ are you? (question word)","direction":"down","row":2,"col":2},{"word":"TEACHER","clue":"A person who educates students","direction":"across","row":6,"col":0}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex31',
  'a2-m1-l2',
  31,
  'reading-comprehension',
  $_$Personal Information: Meet John$_$,
  $_${"title":"Personal Information: Meet John","textPassage":"Hello! My name is John Smith. I am not a doctor, but I am a teacher. I work at a school in London. My students often ask me questions about my job. They say, 'Are you happy to be a teacher?' I always answer, 'Yes, I am!' I love my job because I enjoy helping students learn new things every day. \n\nSometimes, people ask me, 'Is your name Jonathan?' I say, 'No, my name is John, J-O-H-N.' I like my name because it is short and easy to spell. \n\nMy best friend is a nurse. Her name is Emily. She works in a hospital. Emily and I often talk about our jobs. She says, 'Being a nurse is not easy, but I love helping people.' We both enjoy our work, and we often share stories about our day. \n\nI have a brother, and his name is Michael. He is not a teacher or a nurse. He is an engineer. He works with computers, and he likes his job very much too. My family is very important to me, and I am happy to share my life with them.","wordCount":171,"estimatedReadingTime":2,"questions":[{"question":"What is John's job?","options":["Doctor","Teacher","Engineer","Nurse"],"answer":"Teacher","explanation":"La pregunta se refiere al trabajo de John. En el texto, John menciona que no es médico, pero sí es profesor."},{"question":"Is John's name Jonathan?","options":["Yes","No","Sometimes","Not mentioned"],"answer":"No","explanation":"John aclara que su nombre no es Jonathan, sino John, y explica cómo se deletrea."},{"question":"Who is Emily?","options":["John's sister","John's friend","John's teacher","John's student"],"answer":"John's friend","explanation":"Emily es la mejor amiga de John, y trabaja como enfermera."},{"question":"What job does Michael have?","options":["Teacher","Nurse","Engineer","Doctor"],"answer":"Engineer","explanation":"Michael, el hermano de John, es ingeniero y trabaja con computadoras."},{"question":"True or False: John does not like his job.","options":["True","False"],"answer":"False","explanation":"John expresa que le encanta su trabajo porque disfruta ayudar a los estudiantes a aprender cosas nuevas cada día."},{"question":"Why does John like his name?","options":["Because it is long","Because it is easy to spell","Because it is unusual","Because it is a common name"],"answer":"Because it is easy to spell","explanation":"John menciona que le gusta su nombre porque es corto y fácil de deletrear."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex32',
  'a2-m1-l2',
  32,
  'pronunciation-practice',
  $_$Práctica de pronunciación: ¡Enfócate en la información personal!$_$,
  $_${"title":"Práctica de pronunciación: ¡Enfócate en la información personal!","instructions":"Practica la pronunciación de estas oraciones en inglés. Presta atención a las palabras clave y sigue los consejos de pronunciación. Luego, intenta repetir las oraciones en voz alta.","targetSentences":[{"englishSentence":"What is your favorite color?","spanishTranslation":"¿Cuál es tu color favorito?","phoneticTranscription":"/wɒt/ /ɪz/ /jɔːr/ /ˈfeɪvərɪt/ /ˈkʌlər/","audioModelURL":"https://audio-placeholder.com/color","pronunciationTips":"Asegúrate de enfatizar la palabra 'favorite' y pronuncia claramente la 'r'.","focusPoints":"Enfoca en la palabra 'favorite' y la pronunciación de la 'r'.","commonMistakes":"Evita deslizar la 'r' al final de 'your' y 'color'."},{"englishSentence":"Where do you work?","spanishTranslation":"¿Dónde trabajas?","phoneticTranscription":"/weər/ /duː/ /juː/ /wɜːrk/","audioModelURL":"https://audio-placeholder.com/work","pronunciationTips":"Practica el sonido 'w' en 'where' y 'work'.","focusPoints":"Enfoca en los sonidos 'w' y la clara pronunciación de 'work'.","commonMistakes":"No omitas el sonido 'w' al principio de las palabras."},{"englishSentence":"How do you spell your name?","spanishTranslation":"¿Cómo se deletrea tu nombre?","phoneticTranscription":"/haʊ/ /duː/ /juː/ /spɛl/ /jɔːr/ /neɪm/","audioModelURL":"https://audio-placeholder.com/spell","pronunciationTips":"Enfatiza bien cada letra al deletrear.","focusPoints":"Enfoca en la claridad al pronunciar cada letra.","commonMistakes":"Evita pronunciar de manera rápida sin claridad al deletrear."},{"englishSentence":"She is not a teacher.","spanishTranslation":"Ella no es profesora.","phoneticTranscription":"/ʃiː/ /ɪz/ /nɒt/ /ə/ /ˈtiːtʃər/","audioModelURL":"https://audio-placeholder.com/teacher","pronunciationTips":"Enfatiza la negación 'not' y el sonido 'ch' en 'teacher'.","focusPoints":"Enfoca en la negación y el sonido 'ch'.","commonMistakes":"No omitas el sonido 'ch' o el énfasis en 'not'."},{"englishSentence":"What is your job?","spanishTranslation":"¿Cuál es tu trabajo?","phoneticTranscription":"/wɒt/ /ɪz/ /jɔːr/ /dʒɒb/","audioModelURL":"https://audio-placeholder.com/job","pronunciationTips":"Practica el sonido 'j' en 'job' y enfatiza 'your'.","focusPoints":"Enfoca en la claridad del sonido 'j' y el énfasis en 'your'.","commonMistakes":"Evita pronunciar 'job' como 'yob'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex33',
  'a2-m1-l2',
  33,
  'multiple-choice',
  $_$Personal Information: Questions and Be Verb$_$,
  $_${"title":"Personal Information: Questions and Be Verb","instructions":"Selecciona la respuesta correcta en cada pregunta.","questions":[{"id":"q1","question":"What is your job?","options":["A) I'm a teacher.","B) I'm not tired.","C) I'm at home.","D) I'm 25 years old."],"correctAnswer":"A","explanation":"La opción A es correcta porque responde directamente a la pregunta sobre el trabajo. Las otras opciones no están relacionadas con el trabajo."},{"id":"q2","question":"How do you spell your last name?","options":["A) It's J-O-H-N.","B) I'm a doctor.","C) At 6 PM.","D) I live in London."],"correctAnswer":"A","explanation":"La opción A es correcta porque proporciona la ortografía del apellido, que es lo que se pregunta. Las otras opciones no responden a la pregunta sobre cómo se deletrea un nombre."},{"id":"q3","question":"Are you a student?","options":["A) Yes, I am.","B) No, I'm not hungry.","C) My name is Anna.","D) I like apples."],"correctAnswer":"A","explanation":"La opción A es la respuesta adecuada a una pregunta sobre ser estudiante. Las otras opciones no responden correctamente a la pregunta ya que proporcionan información irrelevante."},{"id":"q4","question":"Where is your office?","options":["A) It's on Main Street.","B) I'm a lawyer.","C) I have a cat.","D) I was at the park."],"correctAnswer":"A","explanation":"La opción A es correcta porque responde dónde está la oficina, que es lo que se pregunta. Las otras opciones no están relacionadas con la ubicación de una oficina."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex34',
  'a2-m1-l2',
  34,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I have two tickets for the concert. How ______ do they cost?","gap":"much","explanation":"En inglés, usamos 'how much' para preguntar por el precio o cantidad de algo que no se puede contar. Aquí se pregunta por el precio de las entradas.","points":1},{"sentence":"Are you ______ to travel to London next month?","gap":"going","explanation":"Usamos 'going to' para hablar sobre planes futuros. Aquí se pregunta sobre un plan de viaje.","points":1},{"sentence":"You ______ take an umbrella today. It won't rain.","gap":"don't have to","explanation":"La expresión 'don't have to' se usa para indicar que algo no es necesario. Aquí se indica que no es necesario llevar un paraguas.","points":1},{"sentence":"You ______ eat so much candy. It's not good for your teeth.","gap":"shouldn't","explanation":"Usamos 'shouldn't' para dar consejos o recomendaciones. Aquí se recomienda no comer tanto dulce.","points":1},{"sentence":"I think it ______ rain tomorrow.","gap":"will","explanation":"Usamos 'will' para hacer predicciones sobre el futuro. Aquí se predice que lloverá mañana.","points":1},{"sentence":"How ______ apples are in the basket?","gap":"many","explanation":"Usamos 'how many' para preguntar por la cantidad de cosas que se pueden contar, como manzanas.","points":1},{"sentence":"We ______ go to the park if it rains.","gap":"won't","explanation":"Usamos 'won't' como la forma negativa de 'will' para decir que no haremos algo en el futuro. Aquí se dice que no irán al parque si llueve.","points":1},{"sentence":"She ______ to study tonight because the exam is tomorrow.","gap":"has","explanation":"Usamos 'has to' para indicar obligación. Aquí se indica que ella tiene que estudiar porque el examen es mañana.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex35',
  'a2-m1-l2',
  35,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["job","Emma","a","isn't","nurse","."],"correctOrder":["Emma","isn't","a","nurse","."],"translation":"Emma no es una enfermera.","hint":"Estructura de oración negativa con el verbo 'be'.","points":2},{"id":2,"words":["your","How","is","last","spell","name","?"],"correctOrder":["How","is","your","last","name","spelled","?"],"translation":"¿Cómo se deletrea tu apellido?","hint":"Pregunta usando 'How' para pedir información sobre el deletreo.","points":2},{"id":3,"words":["he","Is","an","architect","?","not"],"correctOrder":["Is","he","not","an","architect","?"],"translation":"¿No es él un arquitecto?","hint":"Pregunta negativa con el verbo 'be'.","points":2},{"id":4,"words":["John","is","teacher","a","not","."],"correctOrder":["John","is","not","a","teacher","."],"translation":"John no es un maestro.","hint":"Oración negativa con el verbo 'be'.","points":2},{"id":5,"words":["How","your","do","spell","you","first","name","?"],"correctOrder":["How","do","you","spell","your","first","name","?"],"translation":"¿Cómo deletreas tu nombre?","hint":"Pregunta usando 'How' para pedir el deletreo.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex36',
  'a2-m1-l2',
  36,
  'speaking-analysis',
  $_$Speaking Analysis: Personal Information$_$,
  $_${"title":"Speaking Analysis: Personal Information","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You meet someone new at a party and want to know their job.","question":"What is the most natural question to ask about their job?","options":["A. Who are you?","B. Where do you work?","C. What do you do?","D. How do you do?"],"correctAnswer":"C","explanation":"La opción C 'What do you do?' es la pregunta más común para preguntar sobre la profesión de alguien en inglés.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You need to spell your name over the phone.","question":"How would you say the letter 'B' to ensure it is understood correctly?","options":["A. D as in dog","B. V as in violin","C. B as in ball","D. P as in pen"],"correctAnswer":"C","explanation":"La opción C es correcta porque 'B as in ball' ayuda a clarificar la letra 'B' usando una palabra común.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are writing an email and need to use a person's job title.","question":"What is the correct way to write a job title?","options":["A. Head of Department","B. head of department","C. Head Of Department","D. Head of department"],"correctAnswer":"A","explanation":"La opción A 'Head of Department' es correcta porque los títulos de trabajo se capitalizan adecuadamente en inglés.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are in a formal meeting and need to ask about the time.","question":"What is the most appropriate way to ask the time?","options":["A. What's the time?","B. Do you know what time it is?","C. Time please?","D. Have you got the time?"],"correctAnswer":"B","explanation":"La opción B 'Do you know what time it is?' es la más formal y apropiada para un entorno de reunión.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are introducing yourself to a new colleague.","question":"Which introduction is most appropriate?","options":["A. Hi, I'm working here.","B. Hello, I'm your colleague.","C. Hi, my name is John.","D. I'm John, who are you?"],"correctAnswer":"C","explanation":"La opción C 'Hi, my name is John.' es la forma más clara y directa de presentarse.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are correcting someone who said your name wrong.","question":"What is the polite way to correct them?","options":["A. No, that's wrong.","B. Actually, it's John, not Jon.","C. You said it wrong, it's John.","D. It's John, okay?"],"correctAnswer":"B","explanation":"La opción B 'Actually, it's John, not Jon.' es la forma más educada y clara de corregir el error.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex37',
  'a2-m1-l2',
  37,
  'word-search',
  $_$Word Search: Personal Information$_$,
  $_${"title":"Word Search: Personal Information","instructions":"Find the hidden words in the grid","words":["NAME","WHO","WHERE","NOT","ARE","IS","DOCTOR","TEACHER"],"gridSize":10,"clues":[{"word":"NAME","clue":"The word used to identify a person"},{"word":"WHO","clue":"Question word to ask about a person"},{"word":"WHERE","clue":"Question word to ask about a place"},{"word":"NOT","clue":"Used to make sentences negative"},{"word":"ARE","clue":"Form of 'be' used with 'you', 'we', 'they'"},{"word":"IS","clue":"Form of 'be' used with 'he', 'she', 'it'"},{"word":"DOCTOR","clue":"A person who treats sick people"},{"word":"TEACHER","clue":"A person who helps students learn"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex38',
  'a2-m1-l2',
  38,
  'crossword',
  $_$Crossword: Personal Information$_$,
  $_${"title":"Crossword: Personal Information","instructions":"Complete the crossword puzzle","items":[{"word":"NAME","clue":"What is your first word?","direction":"across","row":0,"col":0},{"word":"TEACHER","clue":"Someone who works at a school.","direction":"down","row":1,"col":2},{"word":"DOCTOR","clue":"A person who helps sick people.","direction":"across","row":2,"col":0},{"word":"WHERE","clue":"A question word for location.","direction":"down","row":0,"col":4},{"word":"ISN'T","clue":"The negative form of 'is'.","direction":"across","row":4,"col":0},{"word":"SPELL","clue":"To say or write the letters of a word.","direction":"down","row":3,"col":1},{"word":"WHAT","clue":"A question word used for things.","direction":"across","row":5,"col":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex39',
  'a2-m1-l2',
  39,
  'reading-comprehension',
  $_$Personal Information$_$,
  $_${"title":"Personal Information","textPassage":"Maria is a friendly woman who works in a post office. Her job title is 'Postal Clerk'. She loves helping people with their mail and packages. Maria's last name is Smith, which she spells as S-M-I-T-H. She often gets letters from friends and family. Maria is not married, and she does not have children. Her best friend, Anna, is a teacher. Maria and Anna enjoy going to the cinema together. On weekends, Maria likes to read books or visit her parents. Maria's favorite book is 'The Little Prince'. She believes that staying connected with loved ones is very important.","wordCount":100,"estimatedReadingTime":2,"questions":[{"question":"What is Maria's job?","options":["Teacher","Postal Clerk","Nurse","Cashier"],"answer":"Postal Clerk","explanation":"La respuesta es 'Postal Clerk' porque el texto dice que Maria trabaja en una oficina de correos y su trabajo es ayudar a las personas con su correo y paquetes."},{"question":"How does Maria spell her last name?","options":["S-M-Y-T-H","S-M-I-T-H","S-M-I-T-E","S-M-I-G-H"],"answer":"S-M-I-T-H","explanation":"La respuesta es 'S-M-I-T-H' porque el texto menciona que Maria deletrea su apellido de esa manera."},{"question":"True or False: Maria is married and has children.","options":["True","False"],"answer":"False","explanation":"La respuesta es 'False' porque el texto claramente dice que Maria no está casada y no tiene hijos."},{"question":"Who is Maria's best friend?","options":["Anna","Her mother","Her sister","Her coworker"],"answer":"Anna","explanation":"La respuesta es 'Anna' porque el texto menciona que Anna es la mejor amiga de Maria y disfrutan de ir al cine juntas."},{"question":"What does Maria like to do on weekends?","options":["Go shopping","Read books","Play sports","Cook"],"answer":"Read books","explanation":"La respuesta es 'Read books' porque el texto dice que a Maria le gusta leer libros o visitar a sus padres los fines de semana."},{"question":"What is NOT mentioned in the text?","options":["Maria's favorite book","Maria's favorite movie","Maria's job title","Maria's best friend"],"answer":"Maria's favorite movie","explanation":"La respuesta es 'Maria's favorite movie' porque el texto no menciona cuál es la película favorita de Maria, solo menciona su libro favorito y otras cosas."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex40',
  'a2-m1-l2',
  40,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Información Personal$_$,
  $_${"title":"Práctica de Pronunciación: Información Personal","instructions":"Practica la pronunciación de las siguientes oraciones enfocándote en el uso correcto de las palabras clave. Escucha atentamente el modelo de audio y repite cada oración. Usa los consejos de pronunciación para mejorar tu habilidad.","targetSentences":[{"englishSentence":"My name is Anna, I am a teacher.","spanishTranslation":"Me llamo Anna, soy profesora.","phoneticTranscription":"/maɪ neɪm ɪz ˈænə, aɪ æm ə ˈtiːtʃər/","audioModelUrl":"URL_DEL_AUDIO_MODELO_1","pronunciationTips":"Concéntrate en la pronunciación de 'teacher' /ˈtiːtʃər/, asegurándote de enfatizar la primera sílaba.","focusPoints":"Palabras clave: name, teacher","commonMistakes":"Pronunciar 'teacher' como /tɪtʃər/ en lugar de /ˈtiːtʃər/"},{"englishSentence":"She is not a doctor, she is a nurse.","spanishTranslation":"Ella no es doctora, es enfermera.","phoneticTranscription":"/ʃiː ɪz nɒt ə ˈdɒktər, ʃiː ɪz ə nɜːrs/","audioModelUrl":"URL_DEL_AUDIO_MODELO_2","pronunciationTips":"Presta atención a 'nurse' /nɜːrs/, asegurándote de que suene claro.","focusPoints":"Palabras clave: doctor, nurse","commonMistakes":"Confundir 'doctor' y 'nurse' en la pronunciación. Asegúrate de que 'nurse' no suene como 'nors'."},{"englishSentence":"His last name is Brown, spelled B-R-O-W-N.","spanishTranslation":"Su apellido es Brown, se deletrea B-R-O-W-N.","phoneticTranscription":"/hɪz læst neɪm ɪz braʊn, spɛld biː-ɑːr-oʊ-double-uː-ɛn/","audioModelUrl":"URL_DEL_AUDIO_MODELO_3","pronunciationTips":"Enfatiza cada letra al deletrear, especialmente 'B' y 'W'.","focusPoints":"Palabras clave: last name, Brown","commonMistakes":"Olvidar el sonido /b/ en 'Brown'. Asegúrate de pronunciar todas las letras claramente."},{"englishSentence":"I work at a bank in the city.","spanishTranslation":"Trabajo en un banco en la ciudad.","phoneticTranscription":"/aɪ wɜːrk æt ə bæŋk ɪn ðə ˈsɪti/","audioModelUrl":"URL_DEL_AUDIO_MODELO_4","pronunciationTips":"Céntrate en la pronunciación de 'bank' /bæŋk/ y asegúrate de no omitir el sonido /ŋ/.","focusPoints":"Palabras clave: work, bank, city","commonMistakes":"Decir 'bank' sin el sonido nasal /ŋ/."},{"englishSentence":"They are going to the post office now.","spanishTranslation":"Ellos van a la oficina de correos ahora.","phoneticTranscription":"/ðeɪ ɑːr ˈɡoʊɪŋ tu ðə poʊst ˈɒfɪs naʊ/","audioModelUrl":"URL_DEL_AUDIO_MODELO_5","pronunciationTips":"Asegúrate de que 'post office' suene como dos palabras distintas, con una pausa clara entre ellas.","focusPoints":"Palabras clave: post office","commonMistakes":"Pronunciar 'post office' como una sola palabra rápida. Separa claramente ambas palabras."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex41',
  'a2-m1-l2',
  41,
  'multiple-choice',
  $_$Personal Information Practice$_$,
  $_${"title":"Personal Information Practice","instructions":"Elige la respuesta correcta para cada pregunta.","questions":[{"id":"q1","question":"What is her job title?","options":["A) She is a manager","B) She are a manager","C) She is manager","D) She managers"],"correctAnswer":"A) She is a manager","explanation":"La respuesta correcta es 'She is a manager' porque usamos 'is' como el verbo 'be' correcto para 'she'. Además, 'a manager' es la forma correcta de indicar el título de trabajo."},{"id":"q2","question":"How do you spell the name 'Emily'?","options":["A) E-M-L-I-Y","B) E-M-I-L-I-E","C) E-M-I-L-Y","D) E-M-E-L-Y"],"correctAnswer":"C) E-M-I-L-Y","explanation":"La respuesta correcta es 'E-M-I-L-Y'. Es importante conocer el orden correcto de las letras para deletrear nombres."},{"id":"q3","question":"Is he a teacher?","options":["A) Yes, he is","B) No, he not","C) Yes, he are","D) No, he isn't"],"correctAnswer":"D) No, he isn't","explanation":"La respuesta correcta es 'No, he isn't' porque utilizamos 'isn't' para la negación del verbo 'be' en tercera persona singular."},{"id":"q4","question":"Who is your favorite actor?","options":["A) My favorite actor is Tom","B) My favorite actor are Tom","C) My favorite actor am Tom","D) My favorite actor be Tom"],"correctAnswer":"A) My favorite actor is Tom","explanation":"La respuesta correcta es 'My favorite actor is Tom' porque usamos 'is' para el verbo 'be' en tercera persona singular cuando describimos algo o alguien."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex42',
  'a2-m1-l2',
  42,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"________ (How much / How many) apples do you eat every day?","correctAnswers":["How many"],"explanation":"Usamos 'How many' para preguntar por la cantidad de objetos contables, como 'apples'.","points":1},{"sentence":"You ________ (must / mustn't) talk during the exam.","correctAnswers":["mustn't"],"explanation":"El verbo modal 'mustn't' se usa para expresar prohibiciones, en este caso, no se debe hablar durante el examen.","points":1},{"sentence":"She ________ (have to / don't have to) wear a uniform at school.","correctAnswers":["has to"],"explanation":"Usamos 'has to' para expresar obligación. En inglés británico, 'have to' cambia a 'has to' en tercera persona del singular.","points":1},{"sentence":"I ________ (would like to / would like) visit the new museum this weekend.","correctAnswers":["would like to"],"explanation":"La estructura 'would like to' se utiliza para expresar deseos o preferencias seguidos de un verbo.","points":1},{"sentence":"We ________ (are / are not) going to the park tomorrow because it's going to rain.","correctAnswers":["are not"],"explanation":"Usamos 'are not' para negar una acción en el futuro cercano usando el presente continuo.","points":1},{"sentence":"________ (How much / How many) milk do you need for the recipe?","correctAnswers":["How much"],"explanation":"Utilizamos 'How much' para preguntar por cantidades de cosas no contables, como 'milk'.","points":1},{"sentence":"They ________ (are / are not) visiting their grandparents this weekend.","correctAnswers":["are"],"explanation":"Usamos 'are' en presente continuo para indicar un plan futuro.","points":1},{"sentence":"You ________ (have to / don't have to) bring your own lunch to the picnic.","correctAnswers":["have to"],"explanation":"Usamos 'have to' para expresar obligación. En este caso, se indica que es necesario traer el almuerzo al picnic.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex43',
  'a2-m1-l2',
  43,
  'sentence-building',
  $_$Exercise$_$,
  $_${"exercises":[{"id":1,"words":["her","is","teacher","a","not","."],"correctOrder":["She","is","not","a","teacher","."],"translation":"Ella no es profesora.","hint":"Usa 'be' para negar.","points":2},{"id":2,"words":["many","students","are","there","?"],"correctOrder":["How","many","students","are","there","?"],"translation":"¿Cuántos estudiantes hay?","hint":"Pregunta sobre cantidad usando 'how many'.","points":2},{"id":3,"words":["you","are","a","doctor","?"],"correctOrder":["Are","you","a","doctor","?"],"translation":"¿Eres médico?","hint":"Forma una pregunta con 'be'.","points":2},{"id":4,"words":["her","is","name","what","?"],"correctOrder":["What","is","her","name","?"],"translation":"¿Cuál es su nombre?","hint":"Usa 'what' para preguntar por información personal.","points":2},{"id":5,"words":["you","like","would","some","coffee","?"],"correctOrder":["Would","you","like","some","coffee","?"],"translation":"¿Te gustaría un poco de café?","hint":"Usa 'would like' para ofrecer.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex44',
  'a2-m1-l2',
  44,
  'speaking-analysis',
  $_$Speaking Analysis: Personal Information$_$,
  $_${"title":"Speaking Analysis: Personal Information","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You meet someone new at a party and they introduce themselves.","question":"What is the most natural question to ask next?","options":["A. How you are?","B. What do you do?","C. Where you from?","D. Who you are?"],"correctAnswer":"B","explanation":"La opción B es la pregunta más natural para preguntar sobre la ocupación de alguien después de una presentación. Las otras opciones tienen errores gramaticales.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You need to ask for someone's name again at a formal event.","question":"Which is the most polite way to ask?","options":["A. What's your name again?","B. Can I have your name?","C. Sorry, what's your name, please?","D. Remind me your name."],"correctAnswer":"C","explanation":"La opción C es la más cortés porque usa 'sorry' y 'please', que son palabras clave de cortesía en inglés.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are talking to a friend about someone's job.","question":"Which question is correct?","options":["A. What is she do?","B. What does she do?","C. What is she doing?","D. What she do?"],"correctAnswer":"B","explanation":"La opción B es correcta porque usa la estructura 'does' para hacer preguntas en presente simple sobre ocupaciones.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are spelling your name over the phone.","question":"How do you say the letter 'H' in English?","options":["A. Aitch","B. Hech","C. Haitch","D. Eitch"],"correctAnswer":"A","explanation":"La opción A es la pronunciación correcta de la letra 'H' en inglés.","points":2},{"id":"q5","type":"multiple-choice","scenario":"A friend asks you if you are a teacher.","question":"What is the correct negative response?","options":["A. I am not.","B. I not am.","C. I'm no.","D. No, am not."],"correctAnswer":"A","explanation":"La opción A es la respuesta negativa correcta usando 'am not'.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You forgot someone's job title and want to ask again.","question":"Which is the most appropriate question?","options":["A. What is your job?","B. What do you do?","C. What's your job title?","D. You are what?"],"correctAnswer":"C","explanation":"La opción C es más específica y adecuada para preguntar por el título laboral de una persona.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex45',
  'a2-m1-l2',
  45,
  'word-search',
  $_$Word Search: Personal Information$_$,
  $_${"title":"Word Search: Personal Information","instructions":"Find the hidden words in the grid","words":["WHO","WHAT","WHERE","NOT","IS","ARE","ALICE","ENGINEER","DOCTOR","TEACHER"],"gridSize":10,"clues":[{"word":"WHO","clue":"¿Quién?"},{"word":"WHAT","clue":"¿Qué?"},{"word":"WHERE","clue":"¿Dónde?"},{"word":"NOT","clue":"Negación del verbo 'to be'"},{"word":"IS","clue":"Forma del verbo 'to be' para él, ella"},{"word":"ARE","clue":"Forma del verbo 'to be' para tú, ellos"},{"word":"ALICE","clue":"Nombre común, empieza con A"},{"word":"ENGINEER","clue":"Título de trabajo, trabaja con tecnología"},{"word":"DOCTOR","clue":"Título de trabajo, cuida la salud"},{"word":"TEACHER","clue":"Título de trabajo, enseña en la escuela"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex46',
  'a2-m1-l2',
  46,
  'crossword',
  $_$Crossword: Personal Information$_$,
  $_${"title":"Crossword: Personal Information","instructions":"Complete the crossword puzzle","items":[{"word":"TEACHER","clue":"A person who works in a school","direction":"across","row":0,"col":0},{"word":"DOCTOR","clue":"A person who helps sick people","direction":"down","row":0,"col":5},{"word":"WHERE","clue":"Question word for asking about place","direction":"across","row":2,"col":1},{"word":"ISN'T","clue":"Short form of 'is not'","direction":"down","row":1,"col":3},{"word":"HELLO","clue":"A common way to greet someone","direction":"across","row":4,"col":0},{"word":"NURSE","clue":"A person who helps doctors","direction":"down","row":4,"col":2},{"word":"SPELL","clue":"To say the letters of a word","direction":"across","row":6,"col":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex47',
  'a2-m1-l2',
  47,
  'reading-comprehension',
  $_$Understanding Personal Information$_$,
  $_${"title":"Understanding Personal Information","textPassage":"Hello! I'm Sarah and I am a student at Greenfield College. Many people ask me about my life and what I do. I am not a doctor or a teacher; I am studying engineering. My best friend, Lisa, is in the same college. She is not studying engineering, she is studying art. My favorite subject is mathematics because it is both interesting and challenging. Lisa prefers painting and drawing. We usually study together in the library. It's a quiet place, perfect for concentrating. Lisa's last name is Brown, and mine is White. People sometimes find it funny that our last names are colors. We often laugh about it. My email address is sarah.white@greenfield.edu if you want to contact me for more information. Have a great day!","wordCount":128,"estimatedReadingTime":3,"questions":[{"question":"What is the main idea of the passage?","options":["Sarah's career plans.","Information about Sarah and her friend Lisa.","A typical day in Sarah's life.","The subjects Sarah dislikes."],"answer":"Information about Sarah and her friend Lisa.","explanation":"El párrafo principalmente proporciona información sobre Sarah y Lisa, sus estudios y el lugar donde estudian."},{"question":"What is Sarah studying at Greenfield College?","options":["Art","Mathematics","Engineering","Medicine"],"answer":"Engineering","explanation":"Sarah está estudiando ingeniería, como se menciona en la segunda frase del texto."},{"question":"Which statement is TRUE about Lisa?","options":["She is studying engineering.","She is a teacher.","She prefers mathematics.","She is studying art."],"answer":"She is studying art.","explanation":"El texto menciona que Lisa está estudiando arte, no ingeniería."},{"question":"Why do Sarah and Lisa find their last names funny?","options":["They are both names of animals.","They are both colors.","They are difficult to spell.","They are very common."],"answer":"They are both colors.","explanation":"Sus apellidos son 'Brown' y 'White', que son nombres de colores."},{"question":"Where do Sarah and Lisa usually study?","options":["In the classroom","At home","In the library","In a cafe"],"answer":"In the library","explanation":"Estudian en la biblioteca porque es un lugar tranquilo, ideal para concentrarse."},{"question":"Sarah's favorite subject is NOT mentioned as:","options":["Interesting","Boring","Challenging","Mathematics"],"answer":"Boring","explanation":"Sarah describe las matemáticas como interesantes y desafiantes, pero nunca aburridas."},{"question":"Sarah's email address includes:","options":["greenfield.com","sarah.brown","sarah.white","lisa.white"],"answer":"sarah.white","explanation":"La dirección de correo electrónico de Sarah es sarah.white@greenfield.edu."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex48',
  'a2-m1-l2',
  48,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Información Personal$_$,
  $_${"title":"Práctica de Pronunciación: Información Personal","instructions":"Practica la pronunciación de las siguientes frases relacionadas con información personal. Escucha los modelos de audio, repite y presta atención a los consejos de pronunciación.","targetSentences":[{"englishSentence":"What is your name?","spanishTranslation":"¿Cuál es tu nombre?","phoneticTranscription":"/wɒt ɪz jɔːr neɪm/","audioModelURL":"https://example.com/audio1","pronunciationTips":"Asegúrate de enfatizar 'name' al final de la frase.","focusPoints":"Intonation rising at the end of the question.","commonMistakes":"Avoid dropping the 't' in 'what'."},{"englishSentence":"I am a teacher.","spanishTranslation":"Soy profesor.","phoneticTranscription":"/aɪ æm ə ˈtiːʧər/","audioModelURL":"https://example.com/audio2","pronunciationTips":"Mantén la 't' suave en 'teacher'.","focusPoints":"Correct stress on 'teacher'.","commonMistakes":"Do not confuse 'teacher' with 'teether'."},{"englishSentence":"She is not a student.","spanishTranslation":"Ella no es estudiante.","phoneticTranscription":"/ʃiː ɪz nɒt ə ˈstjuːdənt/","audioModelURL":"https://example.com/audio3","pronunciationTips":"Asegúrate de pronunciar la 's' en 'student'.","focusPoints":"Negative form with 'not'.","commonMistakes":"Avoid blending 'she is' into 'shiz'."},{"englishSentence":"He is from Spain.","spanishTranslation":"Él es de España.","phoneticTranscription":"/hiː ɪz frɒm speɪn/","audioModelURL":"https://example.com/audio4","pronunciationTips":"Enfatiza la 'n' en 'Spain'.","focusPoints":"Clear pronunciation of 'from'.","commonMistakes":"Do not shorten 'from' to 'frum'."},{"englishSentence":"I have two brothers.","spanishTranslation":"Tengo dos hermanos.","phoneticTranscription":"/aɪ hæv tuː ˈbrʌðərz/","audioModelURL":"https://example.com/audio5","pronunciationTips":"Asegúrate de que 'brothers' suene claro y con la 'r'.","focusPoints":"Stress on 'brothers'.","commonMistakes":"Do not say 'bothers' instead of 'brothers'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex49',
  'a2-m1-l2',
  49,
  'multiple-choice',
  $_$Personal Information Practice$_$,
  $_${"title":"Personal Information Practice","instructions":"Selecciona la respuesta correcta para cada pregunta. Usa las palabras de pregunta, el verbo 'be' en negativo y preguntas, el alfabeto, la ortografía de nombres y títulos de trabajo.","questions":[{"id":"q1","question":"What job does Sarah's brother have?","options":["A. Doctor","B. Teacher","C. Engineer","D. Chef"],"correctAnswer":"B","explanation":"La respuesta correcta es 'Teacher' (maestro) porque Sarah mencionó que su hermano trabaja en una escuela, lo cual es típico de un maestro."},{"id":"q2","question":"How do you spell the name 'Michael'?","options":["A. M-I-K-A-L","B. M-Y-C-H-A-L","C. M-I-C-H-E-A-L","D. M-I-C-H-A-E-L"],"correctAnswer":"D","explanation":"La respuesta correcta es 'M-I-C-H-A-E-L' porque es la ortografía correcta del nombre 'Michael'."},{"id":"q3","question":"Which question word is used to ask about someone's job?","options":["A. Where","B. Who","C. What","D. How"],"correctAnswer":"C","explanation":"La respuesta correcta es 'What' porque usamos 'What' para preguntar sobre la ocupación de alguien, como en 'What do you do for a living?'."},{"id":"q4","question":"Is your father a teacher?","options":["A. Yes, he is.","B. No, he are not.","C. Yes, he are.","D. No, he isn't."],"correctAnswer":"D","explanation":"La respuesta correcta es 'No, he isn't.' porque es la forma negativa correcta del verbo 'be' al responder una pregunta sobre ocupaciones."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l2-ex50',
  'a2-m1-l2',
  50,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"Where ____ you from?","acceptableAnswers":["are"],"explanation":"Usamos 'are' para formar preguntas con 'where' sobre el origen de alguien.","points":1},{"sentence":"She ____ a doctor, she is a nurse.","acceptableAnswers":["isn't","is not"],"explanation":"Para negar el verbo 'be', usamos 'isn't' o 'is not'. Aquí decimos que ella no es doctora.","points":1},{"sentence":"____ is your favorite color?","acceptableAnswers":["What"],"explanation":"Usamos 'What' para preguntar sobre preferencias o información general.","points":1},{"sentence":"He ____ at home right now.","acceptableAnswers":["isn't","is not"],"explanation":"Para negar la presencia de alguien en un lugar, usamos 'isn't' o 'is not'.","points":1},{"sentence":"____ you like to go to the park?","acceptableAnswers":["Do"],"explanation":"Usamos 'Do' para formar preguntas con verbos en presente simple.","points":1},{"sentence":"My name is spelled J-O-H-N. How ____ you spell your name?","acceptableAnswers":["do"],"explanation":"Usamos 'do' para preguntar cómo se hace algo, en este caso, cómo se deletrea un nombre.","points":1},{"sentence":"She ____ a teacher, she works in a bank.","acceptableAnswers":["isn't","is not"],"explanation":"Para negar una ocupación, usamos 'isn't' o 'is not'. En este caso, decimos que ella no es maestra.","points":1},{"sentence":"____ is your brother's job?","acceptableAnswers":["What"],"explanation":"Usamos 'What' para preguntar sobre el trabajo o ocupación de alguien.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;

