INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex1',
  'a2-m1-l3',
  1,
  'multiple-choice',
  $_$Family Members and Possessive Adjectives$_$,
  $_${"title":"Family Members and Possessive Adjectives","instructions":"Choose the correct option to complete each sentence about family members and relationships.","questions":[{"id":"q1","question":"This is my brother. ___ name is John.","options":["A) His","B) Her","C) Their","D) Its"],"correctAnswer":"A","explanation":"La respuesta correcta es 'His' porque estamos hablando del hermano, que es masculino, por lo tanto usamos el adjetivo posesivo 'His'."},{"id":"q2","question":"Maria is my aunt. She is my mother's sister. She is ___ aunt.","options":["A) my","B) their","C) our","D) her"],"correctAnswer":"A","explanation":"La respuesta correcta es 'my' porque estamos hablando de 'mi' tía, lo que indica posesión desde el punto de vista del hablante."},{"id":"q3","question":"This is my parents' house. It's ___ house.","options":["A) their","B) her","C) his","D) our"],"correctAnswer":"A","explanation":"La respuesta correcta es 'their' porque 'parents' es plural y el adjetivo posesivo 'their' se usa para indicar posesión de algo que pertenece a más de una persona."},{"id":"q4","question":"Julia's husband is my uncle. He is ___ uncle.","options":["A) my","B) his","C) your","D) our"],"correctAnswer":"A","explanation":"La respuesta correcta es 'my' porque estamos hablando de 'mi' tío, lo que indica posesión desde el punto de vista del hablante."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex2',
  'a2-m1-l3',
  2,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"You should eat ___ vegetables every day to stay healthy.","answer":["some"],"explanation":"Usamos 'some' cuando hablamos de cantidades no específicas en afirmaciones. En este caso, se recomienda comer algunas verduras diariamente.","points":1},{"sentence":"I don't have ___ money, so I can't buy the book.","answer":["any"],"explanation":"Usamos 'any' en oraciones negativas para indicar que no hay cantidad de algo. Aquí, no tengo dinero para comprar el libro.","points":1},{"sentence":"She is going to visit her grandparents ___ the weekend.","answer":["at"],"explanation":"Usamos 'at' para hablar de momentos específicos, como 'at the weekend'. Indica el tiempo en que ella planea visitar a sus abuelos.","points":1},{"sentence":"He has to clean his room before his friends arrive.","answer":["has to"],"explanation":"'Has to' se usa para mostrar obligación personal o externa. En este caso, él debe limpiar su habitación antes de que lleguen sus amigos.","points":1},{"sentence":"There aren't ___ chairs in the room.","answer":["many"],"explanation":"Usamos 'many' con sustantivos contables en plural en oraciones negativas o preguntas. Aquí, no hay muchas sillas en la habitación.","points":1},{"sentence":"We are going to have a picnic if it doesn't rain.","answer":["going to"],"explanation":"Usamos 'going to' para planes futuros ya decididos. Aquí, planeamos hacer un picnic si no llueve.","points":1},{"sentence":"Is there ___ milk in the fridge?","answer":["any"],"explanation":"Usamos 'any' en preguntas para preguntar sobre la cantidad de algo. Aquí, se pregunta si hay leche en la nevera.","points":1},{"sentence":"This is the most interesting book I have ever read.","answer":["most interesting"],"explanation":"Usamos superlativos para expresar el grado más alto de una cualidad entre varios elementos. En este caso, es el libro más interesante que ha leído.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex3',
  'a2-m1-l3',
  3,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["have","to","a","You","lot","of","family","visit","."],"correctOrder":["You","have","to","visit","a","lot","of","family","."],"translation":"Tienes que visitar a muchos familiares.","hint":"Use 'have to' followed by the base form of the verb.","points":2},{"id":2,"words":["enough","money","We","don't","have","to","buy","a","big","house","."],"correctOrder":["We","don't","have","enough","money","to","buy","a","big","house","."],"translation":"No tenemos suficiente dinero para comprar una casa grande.","hint":"Use 'enough' before the noun it qualifies.","points":2},{"id":3,"words":["must","You","not","forget","your","cousin's","birthday","."],"correctOrder":["You","must","not","forget","your","cousin's","birthday","."],"translation":"No debes olvidar el cumpleaños de tu primo.","hint":"Use 'must not' to express prohibition.","points":2},{"id":4,"words":["is","Her","really","family","big","."],"correctOrder":["Her","family","is","really","big","."],"translation":"Su familia es realmente grande.","hint":"Use 'really' to emphasize the adjective.","points":2},{"id":5,"words":["My","aunt's","house","is","too","far","."],"correctOrder":["My","aunt's","house","is","too","far","."],"translation":"La casa de mi tía está demasiado lejos.","hint":"Use 'too' before an adjective to express excess.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex4',
  'a2-m1-l3',
  4,
  'speaking-analysis',
  $_$Speaking Analysis: Family Members$_$,
  $_${"title":"Speaking Analysis: Family Members","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You meet a friend's family for the first time. They introduce you to their sister.","question":"What is the most appropriate response to the introduction?","options":["A) Nice to meet your sister.","B) Nice to meet you, sister.","C) Hello, sister.","D) Hi, who is this?"],"correctAnswer":"A","explanation":"La opción A es la respuesta más adecuada porque utiliza un saludo apropiado para conocer a un miembro de la familia de un amigo.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are talking to your friend's mother in a formal setting.","question":"Which register is most appropriate for this situation?","options":["A) Hi, how's it going?","B) Hello, how do you do?","C) Hey, what's up?","D) Hiya, how are you?"],"correctAnswer":"B","explanation":"La opción B es la respuesta más formal y adecuada para una conversación con la madre de un amigo en un ambiente formal.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are describing your cousin's relationship with your brother.","question":"Which phrase correctly uses the Saxon genitive?","options":["A) My cousin brother's friend.","B) My brother's cousin's friend.","C) Cousin of my brother's friend.","D) Friend of my brother cousin's."],"correctAnswer":"B","explanation":"La opción B usa correctamente el genitivo sajón para describir la relación entre el hermano y el primo.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are talking about your uncle's marital status.","question":"Which sentence correctly describes his status?","options":["A) My uncle is married with two children.","B) My uncle's is married with two children.","C) My uncle married with two children.","D) My uncle, he is married with two children."],"correctAnswer":"A","explanation":"La opción A es la forma correcta de expresar el estado civil de tu tío.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are introducing your family members to a friend.","question":"Which informal expression fits the casual context?","options":["A) This is my father, pleased to meet you.","B) Here's my dad, nice to meet ya!","C) Let me introduce you to my father.","D) Allow me to present my father."],"correctAnswer":"B","explanation":"La opción B es una expresión informal adecuada para presentar a tu padre en un contexto casual.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are describing your aunt's job change to a colleague.","question":"Which discourse marker best introduces the topic?","options":["A) However, my aunt has a new job.","B) Therefore, my aunt has a new job.","C) By the way, my aunt has a new job.","D) Nonetheless, my aunt has a new job."],"correctAnswer":"C","explanation":"La opción C utiliza el marcador discursivo 'By the way' que es adecuado para introducir un nuevo tema de conversación.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
