INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex6',
  'a2-m1-l3',
  6,
  'crossword',
  $_$Crossword: Family Members$_$,
  $_${"title":"Crossword: Family Members","instructions":"Complete the crossword puzzle","items":[{"word":"MOTHER","clue":"My father's wife is my ____.","direction":"across","row":0,"col":0},{"word":"BROTHER","clue":"My mother's son is my ____.","direction":"down","row":0,"col":2},{"word":"FATHER","clue":"My mother's husband is my ____.","direction":"across","row":2,"col":0},{"word":"SISTER","clue":"My father's daughter is my ____.","direction":"down","row":1,"col":4},{"word":"UNCLE","clue":"My father's brother is my ____.","direction":"across","row":4,"col":0},{"word":"COUSIN","clue":"My uncle's child is my ____.","direction":"across","row":6,"col":0},{"word":"HUSBAND","clue":"My sister's spouse is her ____.","direction":"across","row":8,"col":0},{"word":"WIFE","clue":"My brother's spouse is his ____.","direction":"down","row":4,"col":6}]}$_$
