INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex7',
  'a2-m1-l3',
  7,
  'reading-comprehension',
  $_$Meet My Family$_$,
  $_${"title":"Meet My Family","textPassage":"I live in a small town with my family. My father's name is John, and he is a doctor. My mother, Susan, is a teacher at the local school. I have one brother and one sister. My brother's name is Alex, and he is twelve years old. My sister's name is Emma, and she is ten. We also have a pet dog called Max. Every evening, our family has dinner together. My mother's cooking is delicious, and we all love her spaghetti. After dinner, my father helps us with homework. On weekends, we visit my grandparents. My grandfather's stories are always interesting, and my grandmother's garden is beautiful. We love spending time with them.","wordCount":120,"estimatedReadingTime":2,"questions":[{"question":"What is the main idea of the passage?","options":["A description of the speaker's family and activities.","A detailed story about a trip.","A conversation between friends.","An explanation of a school project."],"answer":"A description of the speaker's family and activities.","explanation":"La idea principal es sobre la familia del hablante y sus actividades cotidianas."},{"question":"How many siblings does the speaker have?","options":["One brother and two sisters.","Two brothers.","One brother and one sister.","No siblings."],"answer":"One brother and one sister.","explanation":"El hablante menciona tener un hermano y una hermana."},{"question":"What is the profession of the speaker's father?","options":["A teacher.","A doctor.","An engineer.","A chef."],"answer":"A doctor.","explanation":"El texto dice que el padre del hablante es doctor."},{"question":"True or False: The family has dinner together every evening.","options":["True","False"],"answer":"True","explanation":"El texto menciona que la familia cena junta todas las noches."},{"question":"What activity do they do on weekends?","options":["Visit friends.","Go to the park.","Visit their grandparents.","Go to the cinema."],"answer":"Visit their grandparents.","explanation":"El texto indica que los fines de semana visitan a sus abuelos."},{"question":"What is the name of the speaker's pet?","options":["Bella","Max","Charlie","Buddy"],"answer":"Max","explanation":"El texto menciona que tienen un perro llamado Max."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex8',
  'a2-m1-l3',
  8,
  'pronunciation-practice',
  $_$Practica de Pronunciación: Énfasis en el Estrés de las Palabras$_$,
  $_${"title":"Practica de Pronunciación: Énfasis en el Estrés de las Palabras","instructions":"Practica la pronunciación enfocándote en el estrés de las palabras en las siguientes oraciones. Escucha el audio y repite cada oración, prestando atención a cómo se enfatizan ciertas palabras. Usa las transcripciones fonéticas como guía.","targetSentences":[{"sentence":"This is my mother's favorite book.","translation":"Este es el libro favorito de mi madre.","phoneticTranscription":"ˈmʌðərz ˈfeɪvərɪt bʊk","audioModelURL":"https://example.com/audio1","pronunciationTips":"Enfoca el estrés en 'mother's' y 'favorite'.","focusPoints":"El estrés recae en las palabras posesivas y adjetivos.","commonMistakes":"No enfatizar correctamente las palabras posesivas."},{"sentence":"My brother's car is very fast.","translation":"El coche de mi hermano es muy rápido.","phoneticTranscription":"ˈbrʌðərz kɑːr ˈveri fɑːst","audioModelURL":"https://example.com/audio2","pronunciationTips":"Asegúrate de enfatizar 'brother's' y 'fast'.","focusPoints":"El estrés en posesiones y adjetivos comparativos.","commonMistakes":"No hacer una pausa clara entre 'car' y 'is'."},{"sentence":"Our family's vacation was amazing.","translation":"Las vacaciones de nuestra familia fueron increíbles.","phoneticTranscription":"ˈfæmɪliz vəˈkeɪʃən əˈmeɪzɪŋ","audioModelURL":"https://example.com/audio3","pronunciationTips":"Enfoca en 'family's' y 'amazing'.","focusPoints":"El estrés en los sustantivos posesivos y adjetivos calificativos.","commonMistakes":"Subestimar el énfasis en 'vacation'."},{"sentence":"Her husband's job is interesting.","translation":"El trabajo de su esposo es interesante.","phoneticTranscription":"ˈhʌzbəndz dʒɒb ˈɪntrəstɪŋ","audioModelURL":"https://example.com/audio4","pronunciationTips":"Pon atención al estrés en 'husband's' y 'interesting'.","focusPoints":"El estrés en los posesivos y adjetivos descriptivos.","commonMistakes":"Olvidar el estrés en 'job'."},{"sentence":"My sister's friend is a teacher.","translation":"La amiga de mi hermana es maestra.","phoneticTranscription":"ˈsɪstərz frend ə ˈtiːtʃər","audioModelURL":"https://example.com/audio5","pronunciationTips":"Asegúrate de enfatizar 'sister's' y 'teacher'.","focusPoints":"El estrés en los posesivos y profesiones.","commonMistakes":"No enfatizar 'teacher' correctamente."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex9',
  'a2-m1-l3',
  9,
  'multiple-choice',
  $_$Family Members and Possessive Adjectives$_$,
  $_${"title":"Family Members and Possessive Adjectives","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"Who is Sarah's brother?","options":["A) My brother","B) Her brother","C) Their brother","D) His brother"],"correctAnswer":"B","explanation":"La pregunta se refiere al hermano de Sarah, por lo tanto, usamos 'her' porque es el adjetivo posesivo que corresponde a la tercera persona del singular femenino."},{"id":"q2","question":"Is this your parents' house?","options":["A) Yes, it's mine.","B) No, it's theirs.","C) Yes, it's ours.","D) No, it's his."],"correctAnswer":"B","explanation":"La pregunta utiliza 'your parents', lo que significa que la casa no pertenece a la persona que responde, sino a los padres de alguien más. Por eso, la respuesta correcta es 'theirs'."},{"id":"q3","question":"Jessica's father is married. Who is his wife?","options":["A) Jessica's aunt","B) Jessica's mother","C) Jessica's cousin","D) Jessica's sister"],"correctAnswer":"B","explanation":"El padre de Jessica está casado, por lo tanto, su esposa sería la madre de Jessica. 'Married' indica el estado civil, y 'wife' se refiere a la esposa."},{"id":"q4","question":"Whose book is this? It's my ___.","options":["A) brother","B) brother's","C) brothers","D) brothers'"],"correctAnswer":"B","explanation":"Para indicar posesión, utilizamos el genitivo sajón. En este caso, 'brother's' muestra que el libro pertenece al hermano."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex10',
  'a2-m1-l3',
  10,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I would like to visit ___ (mi) grandmother this weekend.","gap":"my","explanation":"Usamos 'my' para referirnos a algo que nos pertenece. En este caso, 'mi abuela'.","points":1},{"sentence":"She should call ___ (su) brother before dinner.","gap":"her","explanation":"Usamos 'her' para referirnos a algo que pertenece a ella. En este caso, 'su hermano'.","points":1},{"sentence":"John's father is a doctor. ___ (Su) clinic is in the city.","gap":"His","explanation":"'His' se usa porque estamos hablando de algo que pertenece a John. En este caso, 'su clínica'.","points":1},{"sentence":"My parents' house is bigger than ___ (sus) house.","gap":"their","explanation":"Usamos 'their' para hablar de algo que pertenece a ellos. En este caso, 'su casa'.","points":1},{"sentence":"We have to visit ___ (nuestros) cousins during the holidays.","gap":"our","explanation":"Usamos 'our' para referirnos a algo que nos pertenece a nosotros. En este caso, 'nuestros primos'.","points":1},{"sentence":"Is this ___ (tu) sister's book?","gap":"your","explanation":"Usamos 'your' para referirnos a algo que pertenece a la persona con la que hablamos. En este caso, 'tu hermana'.","points":1},{"sentence":"They should visit ___ (sus) grandparents more often.","gap":"their","explanation":"Usamos 'their' para hablar de algo que pertenece a ellos. En este caso, 'sus abuelos'.","points":1},{"sentence":"___ (Mi) brother's guitar is very old.","gap":"My","explanation":"Usamos 'my' para hablar de algo que es mío. En este caso, 'la guitarra de mi hermano'.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex11',
  'a2-m1-l3',
  11,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["some","a","have","of","I","cousins","."],"correctOrder":["I","have","some","cousins","."],"translation":"Tengo algunos primos.","hint":"Use 'some' for an unspecified amount.","points":2},{"id":2,"words":["she","is","wedding","a","planning","her","for","next","week","."],"correctOrder":["She","is","planning","a","wedding","for","her","next","week","."],"translation":"Ella está planeando una boda para la próxima semana.","hint":"Use 'is planning' to talk about future arrangements.","points":2},{"id":3,"words":["How","many","brothers","do","you","have","?"],"correctOrder":["How","many","brothers","do","you","have","?"],"translation":"¿Cuántos hermanos tienes?","hint":"Use 'How many' for countable items.","points":2},{"id":4,"words":["would","you","like","to","meet","my","parents","?"],"correctOrder":["Would","you","like","to","meet","my","parents","?"],"translation":"¿Te gustaría conocer a mis padres?","hint":"Use 'Would like to' for polite offers or requests.","points":2},{"id":5,"words":["My","sister","is","really","intelligent","."],"correctOrder":["My","sister","is","really","intelligent","."],"translation":"Mi hermana es realmente inteligente.","hint":"Use 'really' to emphasize an adjective.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex12',
  'a2-m1-l3',
  12,
  'speaking-analysis',
  $_$Speaking Analysis: Family Members$_$,
  $_${"title":"Speaking Analysis: Family Members","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking about your family to a new friend.","question":"Which is the correct possessive adjective to use? '___ brother is older than me.'","options":["A. My","B. Your","C. His","D. Their"],"correctAnswer":"A","explanation":"La frase habla sobre 'tu' hermano, por lo que el adjetivo posesivo correcto es 'my' para indicar posesión.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You're introducing your cousin to a group of friends.","question":"Which phrase is correct? 'This is ___ cousin, Alex.'","options":["A. my","B. the","C. a","D. an"],"correctAnswer":"A","explanation":"Se utiliza 'my' para mostrar que Alex es 'tu' primo.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are talking about your uncle's house.","question":"Which is the correct way to express possession? 'This is my ___ house.'","options":["A. uncle's","B. uncles'","C. uncle","D. uncles"],"correctAnswer":"A","explanation":"Se usa el genitivo sajón ('s) para indicar que la casa pertenece a tu tío.","points":2},{"id":"q4","type":"multiple-choice","scenario":"Discussing your sister's marital status with a friend.","question":"Which statement is correct? 'My sister ___ married.'","options":["A. is","B. are","C. has","D. have"],"correctAnswer":"A","explanation":"La forma correcta del verbo 'to be' en tercera persona singular es 'is'.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are describing your family to someone new.","question":"Choose the correct word: 'My parents' ___ is very strong.'","options":["A. relationship","B. relationships","C. relation","D. relatives"],"correctAnswer":"A","explanation":"La palabra correcta es 'relationship' ya que se refiere a la conexión entre tus padres.","points":2},{"id":"q6","type":"multiple-choice","scenario":"Talking about your brother's new job.","question":"Which is the correct sentence? 'My brother's job is ___ interesting.'","options":["A. very","B. much","C. many","D. more"],"correctAnswer":"A","explanation":"Se usa 'very' para intensificar adjetivos como 'interesting'.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex13',
  'a2-m1-l3',
  13,
  'word-search',
  $_$Word Search: Family Members$_$,
  $_${"title":"Word Search: Family Members","instructions":"Find the hidden words in the grid","words":["MOTHER","FATHER","BROTHER","SISTER","UNCLE","AUNT","COUSIN","GRANDMA","GRANDPA","HUSBAND","WIFE","SON"],"gridSize":10,"clues":[{"word":"MOTHER","clue":"The female parent"},{"word":"FATHER","clue":"The male parent"},{"word":"BROTHER","clue":"A male sibling"},{"word":"SISTER","clue":"A female sibling"},{"word":"UNCLE","clue":"Your parent's brother"},{"word":"AUNT","clue":"Your parent's sister"},{"word":"COUSIN","clue":"Your uncle's or aunt's child"},{"word":"GRANDMA","clue":"Your mother's or father's mother"},{"word":"GRANDPA","clue":"Your mother's or father's father"},{"word":"HUSBAND","clue":"A married man"},{"word":"WIFE","clue":"A married woman"},{"word":"SON","clue":"A male child"}]}$_$
