INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex14',
  'a2-m1-l3',
  14,
  'crossword',
  $_$Crossword: Family Members$_$,
  $_${"title":"Crossword: Family Members","instructions":"Complete the crossword puzzle","items":[{"word":"SISTER","clue":"Your mother's daughter is your ___.","direction":"across","row":0,"col":0},{"word":"MOTHER","clue":"She is my father's wife.","direction":"down","row":0,"col":0},{"word":"BROTHER","clue":"Your father's son is your ___.","direction":"across","row":2,"col":0},{"word":"UNCLE","clue":"My father's brother is my ___.","direction":"down","row":0,"col":4},{"word":"COUSIN","clue":"My uncle's son is my ___.","direction":"across","row":4,"col":0},{"word":"WIFE","clue":"A married woman is a ___.","direction":"down","row":0,"col":6},{"word":"HUSBAND","clue":"A married man is a ___.","direction":"across","row":6,"col":0}]}$_$
