INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex15',
  'a2-m1-l3',
  15,
  'reading-comprehension',
  $_$A Day with My Family$_$,
  $_${"title":"A Day with My Family","textPassage":"Yesterday, I spent a wonderful day with my family. My brother, Tom, and his wife, Anna, invited us all for lunch at their house. Tom's house is very big, and it has a lovely garden. My parents, my sister Lucy, and I went together. My mom brought a delicious cake, and my dad helped Tom with the barbecue. Lucy played with Tom and Anna's children, who are very energetic. After lunch, we all sat in the garden and talked about many things. Tom's dog, Max, ran around and made us laugh. It was a sunny day, and everyone was happy. Before leaving, Anna showed us her new paintings. She is very talented. We had a great time, and I am grateful for my family.","wordCount":127,"estimatedReadingTime":1,"questions":[{"question":"What is the main idea of the passage?","options":["A family reunion at Tom's house","A day at the beach","A visit to the city","A shopping trip"],"answer":"A family reunion at Tom's house","explanation":"El texto describe un día que la familia pasó junta en la casa de Tom, destacando las actividades que realizaron."},{"question":"Who brought a cake?","options":["Anna","Tom","Mom","Lucy"],"answer":"Mom","explanation":"En el texto se menciona que la mamá llevó un pastel delicioso."},{"question":"What did Lucy do at Tom's house?","options":["She cooked the barbecue","She watched TV","She played with Tom and Anna's children","She painted with Anna"],"answer":"She played with Tom and Anna's children","explanation":"El texto dice que Lucy jugó con los hijos de Tom y Anna."},{"question":"TRUE or FALSE: Tom's house has a small garden.","options":["TRUE","FALSE"],"answer":"FALSE","explanation":"El texto menciona que la casa de Tom tiene un jardín encantador, no pequeño."},{"question":"What kind of day was it?","options":["Rainy","Cloudy","Sunny","Windy"],"answer":"Sunny","explanation":"Se menciona que fue un día soleado, lo que hizo que todos estuvieran felices."},{"question":"What can we infer about Anna?","options":["She is a professional chef","She likes gardening","She is an artist","She is a teacher"],"answer":"She is an artist","explanation":"Al final del texto se menciona que Anna mostró sus nuevas pinturas, lo que sugiere que es una artista."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex16',
  'a2-m1-l3',
  16,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Enfoque en el Estrés de las Oraciones$_$,
  $_${"title":"Práctica de Pronunciación: Enfoque en el Estrés de las Oraciones","instructions":"Practiquemos la pronunciación enfocados en el estrés de las oraciones. Escucha los ejemplos y repite cada oración en inglés. Observa dónde cae el énfasis en cada oración.","targetSentences":[{"englishSentence":"My brother's wife is coming to visit.","spanishTranslation":"La esposa de mi hermano viene de visita.","phoneticTranscription":"maɪ ˈbrʌðərz waɪf ɪz ˈkʌmɪŋ tə ˈvɪzɪt","audioModelURL":"https://example.com/audio1","pronunciationTips":"Pon énfasis en 'brother's' y 'wife' para resaltar la relación familiar.","focusPoints":"Enfatizar 'brother's' y 'wife'","commonMistakes":"No omitir el sonido 'z' en 'brother's' que indica posesión."},{"englishSentence":"Their daughter just got married.","spanishTranslation":"Su hija acaba de casarse.","phoneticTranscription":"ðeər ˈdɔːtər dʒʌst gɒt ˈmærid","audioModelURL":"https://example.com/audio2","pronunciationTips":"Acentúa 'daughter' y 'married' para enfatizar la acción reciente.","focusPoints":"Resaltar 'daughter' y 'married'","commonMistakes":"No confundir el sonido 'ð' en 'their' con un 'd' fuerte."},{"englishSentence":"Our grandparents are visiting tomorrow.","spanishTranslation":"Nuestros abuelos nos visitan mañana.","phoneticTranscription":"aʊər ˈɡrændˌpɛrənts ɑːr ˈvɪzɪtɪŋ təˈmɒroʊ","audioModelURL":"https://example.com/audio3","pronunciationTips":"Enfatiza 'grandparents' y 'tomorrow' para indicar quién y cuándo.","focusPoints":"Destacar 'grandparents' y 'tomorrow'","commonMistakes":"Evitar la pronunciación incorrecta de 'grandparents' como 'grandparentses'."},{"englishSentence":"Her uncle has a new car.","spanishTranslation":"Su tío tiene un coche nuevo.","phoneticTranscription":"hɜːr ˈʌŋkəl hæz ə njuː kɑːr","audioModelURL":"https://example.com/audio4","pronunciationTips":"Subraya 'uncle' y 'new' para señalar el objeto de interés.","focusPoints":"Enfatizar 'uncle' y 'new'","commonMistakes":"No olvidar la 'h' en 'her' al inicio de la oración."},{"englishSentence":"My cousin's family is moving next week.","spanishTranslation":"La familia de mi primo se muda la próxima semana.","phoneticTranscription":"maɪ ˈkʌzɪnz ˈfæmɪli ɪz ˈmuːvɪŋ nɛkst wiːk","audioModelURL":"https://example.com/audio5","pronunciationTips":"Acentúa 'cousin's' y 'moving' para destacar el evento.","focusPoints":"Resaltar 'cousin's' y 'moving'","commonMistakes":"No omitir el sonido 'z' en 'cousin's'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex17',
  'a2-m1-l3',
  17,
  'multiple-choice',
  $_$Family Relationships and Grammar Exercise$_$,
  $_${"title":"Family Relationships and Grammar Exercise","instructions":"Choose the correct answer for each question about family and grammar.","questions":[{"id":"q1","question":"Who is Lisa's brother?","options":["A) Lisa's sister is Tom","B) Tom is Lisa's brother","C) Lisa's brother is her aunt","D) Tom is Lisa's cousin"],"correctAnswer":"B","explanation":"Tom es el hermano de Lisa. La pregunta busca identificar la relación familiar correcta, y 'Tom is Lisa's brother' es la opción correcta."},{"id":"q2","question":"Whose car is parked outside? It's _____ car.","options":["A) my parents'","B) my parent's","C) my parents","D) my parents's"],"correctAnswer":"A","explanation":"La forma correcta del genitivo sajón para indicar posesión de más de una persona es añadir un apóstrofo después de la 's', formando 'my parents''."},{"id":"q3","question":"Sarah is married. Her husband's name is John. What is John's marital status?","options":["A) Sarah is single","B) John is married","C) John's wife is his sister","D) John is divorced"],"correctAnswer":"B","explanation":"John está casado con Sarah, por lo que su estado civil es 'married'. La opción correcta es 'John is married'."},{"id":"q4","question":"My uncle's daughter is my _____.","options":["A) cousin","B) niece","C) sister","D) aunt"],"correctAnswer":"A","explanation":"La hija de mi tío es mi prima. La palabra correcta es 'cousin'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex18',
  'a2-m1-l3',
  18,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"My brother is going to visit ___ grandmother this weekend.","options":["our","your","his"],"answer":"our","explanation":"La oración habla sobre 'mi hermano' y su relación con 'nuestra abuela', por lo que el adjetivo posesivo correcto es 'our' para indicar que la abuela es de ambos.","points":1},{"sentence":"Sarah is talking to ___ cousin about the family picnic.","options":["her","his","my"],"answer":"her","explanation":"La oración menciona a Sarah, por lo que el adjetivo posesivo correcto es 'her' para indicar que el primo es de Sarah.","points":1},{"sentence":"___ parents are planning a trip to Paris next month.","options":["Their","My","Her"],"answer":"My","explanation":"La oración debe referirse a los padres del hablante, por lo que el adjetivo posesivo correcto es 'My'.","points":1},{"sentence":"John's sister is getting married, and ___ wedding is next Saturday.","options":["her","his","their"],"answer":"her","explanation":"La oración menciona a la hermana de John, así que el adjetivo posesivo correcto es 'her' para referirse a la boda de la hermana.","points":1},{"sentence":"We are going to meet ___ aunt at the airport tomorrow.","options":["our","their","his"],"answer":"our","explanation":"La oración habla sobre 'nosotros' y su relación con 'nuestra tía', así que el adjetivo posesivo correcto es 'our'.","points":1},{"sentence":"___ father's new job is in a different city.","options":["Their","His","Our"],"answer":"Their","explanation":"La oración habla sobre el padre de un grupo (no especificado), por lo que el adjetivo posesivo correcto es 'Their'.","points":1},{"sentence":"Jessica and Tom are excited because ___ baby is due soon.","options":["their","her","his"],"answer":"their","explanation":"La oración menciona a Jessica y Tom, por lo que el adjetivo posesivo correcto es 'their' para referirse al bebé de ambos.","points":1},{"sentence":"___ grandparents live in a small village by the sea.","options":["My","His","Their"],"answer":"My","explanation":"La oración debe referirse a los abuelos del hablante, por lo que el adjetivo posesivo correcto es 'My'.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex19',
  'a2-m1-l3',
  19,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["have","I","since","my","known","friend","2019","."],"correctOrder":["I","have","known","my","friend","since","2019","."],"translation":"Conozco a mi amigo desde 2019.","hint":"Usa el presente perfecto con 'since' para indicar el inicio de una acción en el pasado.","points":2},{"id":2,"words":["is","My","than","older","me","brother","."],"correctOrder":["My","brother","is","older","than","me","."],"translation":"Mi hermano es mayor que yo.","hint":"Usa el comparativo para comparar dos personas.","points":2},{"id":3,"words":["will","be","They","next","week","married","."],"correctOrder":["They","will","be","married","next","week","."],"translation":"Estarán casados la próxima semana.","hint":"Usa 'will' para hablar sobre un evento futuro.","points":2},{"id":4,"words":["want","My","to","visit","cousins","us","this","summer","."],"correctOrder":["My","cousins","want","to","visit","us","this","summer","."],"translation":"Mis primos quieren visitarnos este verano.","hint":"Sujeto + 'want to' + verbo en infinitivo para expresar un deseo.","points":2},{"id":5,"words":["The","is","bigger","than","our","house","ours","."],"correctOrder":["The","house","is","bigger","than","ours","."],"translation":"La casa es más grande que la nuestra.","hint":"Usa el comparativo 'bigger than' para comparar dos objetos.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex20',
  'a2-m1-l3',
  20,
  'speaking-analysis',
  $_$Speaking Analysis: Family Members$_$,
  $_${"title":"Speaking Analysis: Family Members","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are speaking to your friend about your sister's new job. Your friend asks how she likes it.","question":"What is the most natural response?","options":["A: My sister enjoys her new job a lot.","B: My sister enjoy her new job a lot.","C: My sister's enjoys her new job a lot.","D: My sister's enjoy her new job a lot."],"correctAnswer":"A","explanation":"La opción A es correcta porque 'enjoys' está en tercera persona del singular. 'My sister' es singular, por lo que el verbo lleva 's'.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You're telling a friend about a family gathering.","question":"What is the correct possessive structure?","options":["A: The house of my uncle is big.","B: The uncle's house is big.","C: The house's my uncle is big.","D: The house my uncle's is big."],"correctAnswer":"B","explanation":"La opción B usa el genitivo sajón correctamente para indicar posesión: 'The uncle's house'.","points":2},{"id":"q3","type":"multiple-choice","scenario":"Someone asks you if your brother is married.","question":"What is the most appropriate response in a formal context?","options":["A: Nah, he's single.","B: No, he's not married.","C: Nope, he's still free.","D: Yeah, he's not taken."],"correctAnswer":"B","explanation":"La opción B es la más formal y apropiada. 'No, he's not married.' es una respuesta directa y clara.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You're describing your cousin's relationship with her partner.","question":"Which expression is used correctly?","options":["A: My cousin is tying the knot next month.","B: My cousin is breaking the ice next month.","C: My cousin is cutting the mustard next month.","D: My cousin is hitting the sack next month."],"correctAnswer":"A","explanation":"La opción A es correcta. 'Tying the knot' es una expresión idiomática que significa casarse.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You're talking to a friend about your grandparents.","question":"Which phrase is appropriate to start your response?","options":["A: Well, you see, my grandparents...","B: Hey, you know, my grandparents...","C: Listen, my grandparents...","D: Uhm, basically, my grandparents..."],"correctAnswer":"A","explanation":"La opción A usa 'Well, you see,' como marcador de discurso que es apropiado para iniciar una explicación o historia.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You're in a casual conversation about your cousin's wedding.","question":"Which informal expression fits?","options":["A: My cousin's getting hitched soon.","B: My cousin's celebrating matrimony soon.","C: My cousin's entering wedlock soon.","D: My cousin's getting married soon."],"correctAnswer":"A","explanation":"La opción A es la más informal y adecuada para una conversación casual. 'Getting hitched' es una expresión coloquial para casarse.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex21',
  'a2-m1-l3',
  21,
  'word-search',
  $_$Word Search: Family Members$_$,
  $_${"title":"Word Search: Family Members","instructions":"Find the hidden words in the grid","words":["MOTHER","FATHER","BROTHER","SISTER","GRANDPA","GRANDMA","COUSIN","HUSBAND","WIFE","UNCLE","AUNT","SON"],"gridSize":10,"clues":[{"word":"MOTHER","clue":"Your mom"},{"word":"FATHER","clue":"Your dad"},{"word":"BROTHER","clue":"Your male sibling"},{"word":"SISTER","clue":"Your female sibling"},{"word":"GRANDPA","clue":"Your mom or dad's father"},{"word":"GRANDMA","clue":"Your mom or dad's mother"},{"word":"COUSIN","clue":"Your aunt or uncle's child"},{"word":"HUSBAND","clue":"Your married male partner"},{"word":"WIFE","clue":"Your married female partner"},{"word":"UNCLE","clue":"Your mom or dad's brother"},{"word":"AUNT","clue":"Your mom or dad's sister"},{"word":"SON","clue":"Your male child"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex22',
  'a2-m1-l3',
  22,
  'crossword',
  $_$Crossword: Family Members$_$,
  $_${"title":"Crossword: Family Members","instructions":"Complete the crossword puzzle","items":[{"word":"BROTHER","clue":"Your parent's son is your ______. (Across)","direction":"across","row":0,"col":0},{"word":"MOTHER","clue":"Your father's wife. (Down)","direction":"down","row":0,"col":0},{"word":"COUSIN","clue":"Your uncle's child is your ______. (Across)","direction":"across","row":2,"col":2},{"word":"SISTER","clue":"Your parent's daughter is your ______. (Down)","direction":"down","row":1,"col":5},{"word":"FAMILY","clue":"A group of people related by blood or marriage. (Across)","direction":"across","row":4,"col":1},{"word":"HUSBAND","clue":"A married man in relation to his spouse. (Across)","direction":"across","row":6,"col":0},{"word":"NIECE","clue":"Your brother's daughter is your ______. (Down)","direction":"down","row":3,"col":4}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex23',
  'a2-m1-l3',
  23,
  'reading-comprehension',
  $_$Family Connections: A Simple Introduction$_$,
  $_${"title":"Family Connections: A Simple Introduction","textPassage":"Hello! My name is Laura, and I want to tell you about my family. I have a big family, and we all live in the same city. My father's name is Peter, and he is married to my mother, Jane. They have been together for 30 years! My brother, Tom, is younger than me, and he is still studying at university. Then, there is my sister, Anna. She is older than me and has two children. Her husband's name is Mike. They have a lovely home and often invite us for dinner on weekends. My grandparents, who are my mother's parents, live nearby. We visit them every Sunday. My grandfather loves to tell stories from the past, and my grandmother always makes delicious cakes. I love my family very much, and we enjoy spending time together. What about your family? Do you have a big family too?","wordCount":154,"estimatedReadingTime":2,"questions":[{"question":"What is the main idea of the passage?","options":["Laura's family lives in different countries.","Laura's family enjoys traveling together.","Laura describes her family and their relationships.","Laura's family does not get along well."],"answer":"Laura describes her family and their relationships.","explanation":"La pregunta pregunta sobre la idea principal del pasaje. Laura está hablando de su familia y las relaciones entre los miembros de su familia."},{"question":"Who is married to Jane?","options":["Tom","Peter","Mike","Anna"],"answer":"Peter","explanation":"La pregunta trata de los detalles específicos. Jane está casada con Peter, según el texto."},{"question":"Which family member is still studying at university?","options":["Anna","Tom","Laura","Mike"],"answer":"Tom","explanation":"La pregunta busca un detalle específico sobre la familia. Tom es el hermano que todavía está estudiando en la universidad."},{"question":"True or False: Laura's grandparents live far away.","options":["True","False"],"answer":"False","explanation":"La pregunta es sobre un detalle específico. Los abuelos de Laura viven cerca, ya que se menciona que visitan cada domingo."},{"question":"What does Laura's grandmother love to do?","options":["Go hiking","Make cakes","Sing songs","Read books"],"answer":"Make cakes","explanation":"La pregunta se centra en un detalle específico. La abuela de Laura siempre hace pasteles deliciosos, según el texto."},{"question":"What can be inferred about Laura's family?","options":["They are not very close.","They enjoy spending time together.","They rarely see each other.","They live in different countries."],"answer":"They enjoy spending time together.","explanation":"La pregunta requiere inferencia. Laura menciona que disfruta pasar tiempo con su familia, lo que sugiere que tienen una relación cercana."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex24',
  'a2-m1-l3',
  24,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Enfoque en la Entonación$_$,
  $_${"title":"Práctica de Pronunciación: Enfoque en la Entonación","instructions":"Lee las siguientes oraciones en inglés, prestando atención a la entonación. Luego, escúchalas usando el enlace proporcionado y repítelas en voz alta. Concéntrate en cómo sube y baja la voz natural al hablar.","targetSentences":[{"englishSentence":"My brother's favorite game is chess.","spanishTranslation":"El juego favorito de mi hermano es el ajedrez.","phoneticTranscription":"/ˈbrʌðərz/ /ˈfeɪvərɪt/ /ɡeɪm/ /ɪz/ /tʃes/","audioModelURL":"https://example.com/audio1","pronunciationTips":"Escucha cómo se enfatiza 'brother's' y 'favorite' en esta oración. La entonación sube en 'favorite' y baja al final en 'chess'.","focusPoints":"Emphasize 'brother's' and 'favorite'.","commonMistakes":"Avoid monotone delivery; make sure 'favorite' has a higher pitch."},{"englishSentence":"Our parents have a new car.","spanishTranslation":"Nuestros padres tienen un coche nuevo.","phoneticTranscription":"/ˈpɛrənts/ /hæv/ /ə/ /njuː/ /kɑːr/","audioModelURL":"https://example.com/audio2","pronunciationTips":"Presta atención a la subida de entonación en 'new' para destacar la novedad.","focusPoints":"Emphasize 'new'.","commonMistakes":"Don't rush through 'new'; give it a slight emphasis."},{"englishSentence":"My sister's wedding is in June.","spanishTranslation":"La boda de mi hermana es en junio.","phoneticTranscription":"/ˈsɪstərz/ /ˈwɛdɪŋ/ /ɪz/ /ɪn/ /dʒuːn/","audioModelURL":"https://example.com/audio3","pronunciationTips":"La palabra 'wedding' se enfatiza naturalmente. Practica la entonación ascendente en 'June'.","focusPoints":"Emphasize 'wedding' and 'June'.","commonMistakes":"Don't let 'June' sound flat; it should have a rising intonation."},{"englishSentence":"My cousin's daughter is very tall.","spanishTranslation":"La hija de mi primo es muy alta.","phoneticTranscription":"/ˈkʌzɪnz/ /ˈdɔːtər/ /ɪz/ /ˈvɛri/ /tɔːl/","audioModelURL":"https://example.com/audio4","pronunciationTips":"Escucha cómo la entonación sube en 'very' y baja suavemente al final de la oración.","focusPoints":"Emphasize 'very'.","commonMistakes":"Avoid ending flatly; ensure 'tall' has a slight drop in intonation."},{"englishSentence":"Do you know my uncle's wife?","spanishTranslation":"¿Conoces a la esposa de mi tío?","phoneticTranscription":"/duː/ /juː/ /nəʊ/ /maɪ/ /ˈʌŋkəlz/ /waɪf/","audioModelURL":"https://example.com/audio5","pronunciationTips":"La entonación sube en 'know' y desciende en 'wife' al ser una pregunta cerrada.","focusPoints":"Emphasize 'know'.","commonMistakes":"Ensure 'wife' has a downward intonation, typical of yes/no questions."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex25',
  'a2-m1-l3',
  25,
  'multiple-choice',
  $_$Family Members and Possessive Adjectives$_$,
  $_${"title":"Family Members and Possessive Adjectives","instructions":"Choose the correct answer for each question. Use possessive adjectives and family vocabulary.","questions":[{"id":"q1","question":"Whose book is this? It's _____ book.","options":["my","I","me","mine"],"correctAnswer":"my","explanation":"La respuesta correcta es 'my' porque es el adjetivo posesivo que indica que el libro pertenece al hablante."},{"id":"q2","question":"Anna is my mother's sister. She is my _____.","options":["aunt","niece","cousin","grandmother"],"correctAnswer":"aunt","explanation":"La respuesta correcta es 'aunt' porque es el término para la hermana de la madre."},{"id":"q3","question":"John's brother is married. His _____ is very nice.","options":["wife","son","daughter","husband"],"correctAnswer":"wife","explanation":"La respuesta correcta es 'wife' porque se refiere a la esposa del hermano de John."},{"id":"q4","question":"Peter is married. _____ wife is named Sarah.","options":["His","Her","Their","Your"],"correctAnswer":"His","explanation":"La respuesta correcta es 'His' porque es el adjetivo posesivo que indica que la esposa es de Peter."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex26',
  'a2-m1-l3',
  26,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"This is ______ brother. He is ten years old.","gap":"my","acceptableAnswers":["my"],"explanation":"Use 'my' para indicar que el hermano es mío.","points":1},{"sentence":"Is this ______ sister? She looks very nice.","gap":"your","acceptableAnswers":["your"],"explanation":"Usa 'your' para preguntar si la hermana es de la persona con la que hablas.","points":1},{"sentence":"That is Sarah's book. It is ______ book.","gap":"her","acceptableAnswers":["her"],"explanation":"Usa 'her' para indicar que el libro pertenece a Sarah.","points":1},{"sentence":"We visited ______ grandparents last weekend.","gap":"our","acceptableAnswers":["our"],"explanation":"Usa 'our' para hablar de los abuelos que son de nosotros.","points":1},{"sentence":"Tom is married. ______ wife is a doctor.","gap":"His","acceptableAnswers":["His"],"explanation":"Usa 'His' para indicar que la esposa es de Tom.","points":1},{"sentence":"This is my parents' house. ______ garden is very big.","gap":"Their","acceptableAnswers":["Their"],"explanation":"Usa 'Their' para indicar que el jardín pertenece a mis padres.","points":1},{"sentence":"Is this ______ uncle's car? It's very old.","gap":"your","acceptableAnswers":["your"],"explanation":"Usa 'your' para preguntar si el coche es del tío de la persona con la que hablas.","points":1},{"sentence":"The dog's tail is wagging. ______ tail is very fluffy.","gap":"Its","acceptableAnswers":["Its"],"explanation":"Usa 'Its' para hablar de la cola del perro.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex27',
  'a2-m1-l3',
  27,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["don't","have","You","to","visit","your","every","cousin","week","."],"correctOrder":["You","don't","have","to","visit","your","cousin","every","week","."],"translation":"No tienes que visitar a tu primo cada semana.","hint":"Estructura para expresar falta de obligación con 'have to'.","points":2},{"id":2,"words":["are","going","We","to","our","grandparents'","house","on","Sunday","."],"correctOrder":["We","are","going","to","our","grandparents'","house","on","Sunday","."],"translation":"Vamos a la casa de nuestros abuelos el domingo.","hint":"Usa 'going to' para hablar de planes futuros.","points":2},{"id":3,"words":["should","You","help","your","sister","with","her","homework","."],"correctOrder":["You","should","help","your","sister","with","her","homework","."],"translation":"Deberías ayudar a tu hermana con su tarea.","hint":"Estructura de 'should' para dar consejos.","points":2},{"id":4,"words":["have","to","My","brother","doesn't","help","our","parents","every","day","."],"correctOrder":["My","brother","doesn't","have","to","help","our","parents","every","day","."],"translation":"Mi hermano no tiene que ayudar a nuestros padres todos los días.","hint":"Negación de obligación con 'don't have to'.","points":2},{"id":5,"words":["My","aunt's","dog","is","very","friendly","."],"correctOrder":["My","aunt's","dog","is","very","friendly","."],"translation":"El perro de mi tía es muy amigable.","hint":"Usa el genitivo sajón para expresar posesión.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex28',
  'a2-m1-l3',
  28,
  'speaking-analysis',
  $_$Speaking Analysis: Family Members$_$,
  $_${"title":"Speaking Analysis: Family Members","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are at a family gathering, and your cousin introduces you to their spouse.","question":"What is the most natural response?","options":["A: Nice to meet you, I'm Alex's cousin.","B: Pleased to meet your acquaintance.","C: How do you do? I am the cousin of Alex.","D: Greetings! Alex's my cousin."],"correctAnswer":"A","explanation":"La respuesta A es la más natural y común en situaciones informales como conocer al cónyuge de un primo. Combina educación con un tono amigable.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are speaking with your friend's father at a formal event.","question":"Which response is appropriate?","options":["A: Hey! What's up?","B: Hello, Mr. Smith. It's a pleasure to meet you.","C: Hiya! How's it going?","D: Yo! How are you?"],"correctAnswer":"B","explanation":"La respuesta B es adecuada para un evento formal. 'Mr. Smith' es una manera respetuosa de dirigirse al padre de un amigo.","points":2},{"id":"q3","type":"multiple-choice","scenario":"Your uncle is talking about his new job, and you want to show interest.","question":"Which idiomatic expression shows interest?","options":["A: That's a piece of cake!","B: That's over the moon!","C: That's music to my ears!","D: That's the last straw!"],"correctAnswer":"C","explanation":"La expresión C, 'That's music to my ears', se utiliza para mostrar que algo es agradable de escuchar, mostrando interés en lo que dice tu tío.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You're discussing your family's weekend plans with a friend.","question":"Which discourse marker is most appropriate?","options":["A: So, we're going to the park.","B: However, we're going to the park.","C: Therefore, we're going to the park.","D: Nevertheless, we're going to the park."],"correctAnswer":"A","explanation":"'So' se usa comúnmente para introducir lo que viene después, en este caso, los planes de fin de semana. Es un conector casual y adecuado.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You need to ask your friend's sibling to pass you something at the dining table.","question":"Which is the most polite way to ask?","options":["A: Pass me the salt.","B: Could you please pass me the salt?","C: You there, pass the salt!","D: Salt, please."],"correctAnswer":"B","explanation":"La opción B es la más cortés, usando 'could' y 'please', mostrando respeto y amabilidad.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You're at a casual family barbecue and want to ask your cousin to continue speaking.","question":"Which informal expression fits this casual context?","options":["A: Carry on.","B: Continue, please.","C: Keep going.","D: Proceed."],"correctAnswer":"C","explanation":"La opción C, 'Keep going', es una expresión informal adecuada para un ambiente casual como una barbacoa familiar.","points":2},{"id":"q7","type":"multiple-choice","scenario":"Your aunt is telling a story, and you want to encourage her to elaborate.","question":"Which turn-taking phrase is appropriate?","options":["A: Let's move on.","B: Go on.","C: Hold on.","D: Stop there."],"correctAnswer":"B","explanation":"La opción B, 'Go on', es una frase de toma de turnos que anima a la otra persona a seguir hablando o a proporcionar más detalles.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex29',
