INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex31',
  'a2-m1-l3',
  31,
  'reading-comprehension',
  $_$Family Members and Possessive Adjectives$_$,
  $_${"title":"Family Members and Possessive Adjectives","textPassage":"My family is very important to me. My mother, Sarah, is a teacher and she loves her job. My father, John, works in a bank. He enjoys his work because he likes helping people. I have one sister, Emily, and she is younger than me. Emily is a student and she loves her school. Our family also has a pet dog, Max. Max is very friendly and he is part of our family too. On weekends, we visit our grandparents. Grandpa's house is big, and we all love playing in his garden. Grandma's cooking is delicious, and she always makes our favorite meals. We are a happy family and we love spending time together.","wordCount":108,"estimatedReadingTime":1,"questions":[{"question":"What is the main idea of the passage?","options":["The family has a big house.","Emily is a student.","The family enjoys spending time together.","Max is a friendly dog."],"answer":"The family enjoys spending time together.","explanation":"La idea principal del pasaje es que la familia disfruta pasando tiempo junta, lo cual se menciona varias veces en el texto."},{"question":"What does Sarah do?","options":["She is a teacher.","She works in a bank.","She is a student.","She cooks delicious meals."],"answer":"She is a teacher.","explanation":"El texto dice que Sarah es profesora, lo cual es una información detallada en el pasaje."},{"question":"Who is younger than the speaker?","options":["John","Sarah","Emily","Max"],"answer":"Emily","explanation":"Emily es la hermana menor del hablante, como se menciona en el texto."},{"question":"True or False: Max is part of the family.","answer":"True","explanation":"El texto afirma que Max es parte de la familia, lo que hace esta afirmación verdadera."},{"question":"What can you infer about Grandma's cooking?","options":["It is not very good.","It is delicious.","It is only for holidays.","It is very spicy."],"answer":"It is delicious.","explanation":"El texto menciona que la cocina de la abuela es deliciosa, lo que permite inferir que es muy buena."},{"question":"What does the family do on weekends?","options":["They visit the park.","They go shopping.","They visit their grandparents.","They travel to the city."],"answer":"They visit their grandparents.","explanation":"Los fines de semana, la familia visita a sus abuelos, como se menciona específicamente en el texto."},{"question":"Which of the following is NOT mentioned in the passage?","options":["Grandpa's house is big.","Max is a cat.","Grandma makes favorite meals.","Emily loves her school."],"answer":"Max is a cat.","explanation":"El texto menciona que Max es un perro, no un gato, por lo que esta opción no se menciona en el pasaje."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex32',
  'a2-m1-l3',
  32,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Enfocando en el Estrés de las Oraciones$_$,
  $_${"title":"Práctica de Pronunciación: Enfocando en el Estrés de las Oraciones","instructions":"Practica la pronunciación con las siguientes oraciones. Concéntrate en el estrés de las palabras en las oraciones. Usa los consejos proporcionados para mejorar tu pronunciación.","targetSentences":[{"englishSentence":"My son's birthday is in March.","spanishTranslation":"El cumpleaños de mi hijo es en marzo.","phoneticTranscription":"maɪ sʌn's ˈbɜːθ.deɪ ɪz ɪn mɑːrtʃ.","audioModelURL":"URL_placeholder_1","pronunciationTips":"Enfatiza 'son's' y 'birthday'.","focusPoints":"En el inglés, las palabras posesivas como 'son's' a menudo llevan el estrés en una oración.","commonMistakes":"No omitas el sonido 's' al final de 'son's'."},{"englishSentence":"Your sister and her husband have a new house.","spanishTranslation":"Tu hermana y su esposo tienen una casa nueva.","phoneticTranscription":"jɔːr ˈsɪs.tər ənd hɜːr ˈhʌz.bənd hæv ə njuː haʊs.","audioModelURL":"URL_placeholder_2","pronunciationTips":"Focaliza en 'sister' y 'husband'.","focusPoints":"Las palabras clave como 'sister' y 'husband' suelen llevar más énfasis.","commonMistakes":"No pronuncies 'husband' sin la 'h'."},{"englishSentence":"My parents' car is very old.","spanishTranslation":"El coche de mis padres es muy viejo.","phoneticTranscription":"maɪ ˈpeə.rənts kɑːr ɪz ˈve.ri əʊld.","audioModelURL":"URL_placeholder_3","pronunciationTips":"Asegúrate de enfatizar 'parents'' y 'car'.","focusPoints":"El estrés en 'parents'' ayuda a indicar posesión.","commonMistakes":"No olvides el sonido 's' en 'parents''."},{"englishSentence":"Her cousin's wedding was beautiful.","spanishTranslation":"La boda de su primo fue hermosa.","phoneticTranscription":"hɜːr ˈkʌz.ɪnz ˈwɛd.ɪŋ wəz ˈbjuː.tɪ.fəl.","audioModelURL":"URL_placeholder_4","pronunciationTips":"Pon atención en 'cousin's' y 'wedding'.","focusPoints":"Las palabras posesivas como 'cousin's' son importantes para el estrés de la oración.","commonMistakes":"Asegúrate de no omitir la 'g' en 'wedding'."},{"englishSentence":"Our grandparents live in a big city.","spanishTranslation":"Nuestros abuelos viven en una gran ciudad.","phoneticTranscription":"aʊər ˈɡræn.peə.rənts lɪv ɪn ə bɪɡ ˈsɪ.ti.","audioModelURL":"URL_placeholder_5","pronunciationTips":"Enfatiza 'grandparents' y 'city'.","focusPoints":"Las palabras clave en información personal, como 'grandparents', llevan más estrés.","commonMistakes":"No olvides pronunciar la 't' en 'city'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex33',
  'a2-m1-l3',
  33,
  'multiple-choice',
  $_$Family Members and Possessive Adjectives$_$,
  $_${"title":"Family Members and Possessive Adjectives","instructions":"Choose the correct option to complete the sentences about family members and relationships.","questions":[{"id":"q1","question":"This is _____ brother. He is taller than me.","options":["my","your","his","her"],"correctAnswer":"my","explanation":"Usamos 'my' para indicar que el hermano es mío. Es un adjetivo posesivo que se utiliza para mostrar posesión o relación."},{"id":"q2","question":"That is Maria's father. _____ name is Mr. Smith.","options":["His","Her","Their","Its"],"correctAnswer":"His","explanation":"Usamos 'his' porque estamos hablando del padre de Maria, que es un hombre. 'His' es el adjetivo posesivo correcto para referirse a un hombre."},{"id":"q3","question":"Paul is _____ uncle. He is married to Aunt Susan.","options":["my","your","our","their"],"correctAnswer":"our","explanation":"Usamos 'our' para indicar que Paul es el tío de nosotros. 'Our' se usa para mostrar posesión compartida entre el hablante y otras personas."},{"id":"q4","question":"_____ cousin is very funny. She always tells jokes.","options":["My","Your","His","Their"],"correctAnswer":"My","explanation":"Usamos 'my' para indicar que estamos hablando de mi prima. 'My' es el adjetivo posesivo adecuado para referirse a algo que pertenece a la persona que habla."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex34',
  'a2-m1-l3',
  34,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"This is ___ brother. He is very tall.","gap":"my","acceptable_answers":["my"],"explanation":"Usamos 'my' para indicar que el hermano es mío.","points":1},{"sentence":"___ parents are going to the cinema tonight.","gap":"Their","acceptable_answers":["Their"],"explanation":"Usamos 'Their' para referirnos a los padres de ellos.","points":1},{"sentence":"Is this ___ car? It looks new.","gap":"your","acceptable_answers":["your"],"explanation":"Usamos 'your' para preguntar si el coche pertenece a la persona con la que hablamos.","points":1},{"sentence":"That is ___ mother's house. She loves gardening.","gap":"my","acceptable_answers":["my"],"explanation":"Usamos 'my' para indicar que la casa es de mi madre.","points":1},{"sentence":"___ sister's name is Anna. She is a teacher.","gap":"His","acceptable_answers":["His"],"explanation":"Usamos 'His' para indicar que la hermana es de él.","points":1},{"sentence":"___ cousin's wedding is on Saturday. We are excited.","gap":"Our","acceptable_answers":["Our"],"explanation":"Usamos 'Our' para indicar que la boda es de nuestro primo.","points":1},{"sentence":"Is that ___ grandfather's watch? It looks old.","gap":"her","acceptable_answers":["her"],"explanation":"Usamos 'her' para preguntar si el reloj pertenece al abuelo de ella.","points":1},{"sentence":"___ uncle's dog is very friendly. We love playing with him.","gap":"Their","acceptable_answers":["Their"],"explanation":"Usamos 'Their' para indicar que el perro es del tío de ellos.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex35',
  'a2-m1-l3',
  35,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["my","lives","brother","with","his","wife","."],"correctOrder":["My","brother","lives","with","his","wife","."],"translation":"Mi hermano vive con su esposa.","hint":"Usa un adjetivo posesivo para cada persona.","points":2},{"id":2,"words":["is","Peter's","brother","John","younger","."],"correctOrder":["John","is","Peter's","younger","brother","."],"translation":"John es el hermano menor de Peter.","hint":"Usa el genitivo sajón para indicar posesión.","points":2},{"id":3,"words":["father","my","is","married","to","my","mother","."],"correctOrder":["My","father","is","married","to","my","mother","."],"translation":"Mi padre está casado con mi madre.","hint":"Usa adjetivos posesivos para ambos personajes.","points":2},{"id":4,"words":["cousins","always","my","visit","us","summer","in","."],"correctOrder":["My","cousins","always","visit","us","in","summer","."],"translation":"Mis primos siempre nos visitan en verano.","hint":"Empieza con el sujeto y sigue con el verbo.","points":2},{"id":5,"words":["is","younger","than","sister","my","me","."],"correctOrder":["My","sister","is","younger","than","me","."],"translation":"Mi hermana es menor que yo.","hint":"Sigue el orden sujeto-verbo-complemento.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex36',
  'a2-m1-l3',
  36,
  'speaking-analysis',
  $_$Speaking Analysis: Family Members$_$,
  $_${"title":"Speaking Analysis: Family Members","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking to your friend about your sister's new job.","question":"What is the most natural response if your friend asks, 'How's your sister's new job?'","options":["A. My sister's job is very interesting.","B. The job of my sister is very interesting.","C. Sister's my job is very interesting.","D. Job of my sister's is very interesting."],"correctAnswer":"A","explanation":"La respuesta A es correcta porque usa el genitivo sajón correctamente ('my sister's job') para indicar posesión.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You meet your cousin's teacher at a family event.","question":"What would be a polite way to introduce your cousin?","options":["A. This is cousin's my teacher.","B. This is my cousin's teacher.","C. This teacher is my cousin.","D. Teacher my cousin's this is."],"correctAnswer":"B","explanation":"La opción B es la más adecuada porque mantiene la estructura correcta del genitivo sajón ('my cousin's teacher').","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are at a family gathering and someone asks about your brother's marital status.","question":"What is the most appropriate response?","options":["A. My brother's is married.","B. Brother my is married.","C. My brother is married.","D. Married is my brother."],"correctAnswer":"C","explanation":"La opción C es correcta porque usa correctamente el adjetivo posesivo ('my') y el verbo 'is' para describir el estado civil de tu hermano.","points":2},{"id":"q4","type":"multiple-choice","scenario":"While chatting with a friend about family, they mention their parents' house.","question":"Which is the most natural way to refer to the house?","options":["A. My parent's house.","B. The house of my parents.","C. My parents' house.","D. Parents' my house."],"correctAnswer":"C","explanation":"La opción C es correcta ya que utiliza el genitivo sajón correctamente para indicar posesión ('my parents' house').","points":2},{"id":"q5","type":"multiple-choice","scenario":"Your friend asks about your grandparents' health.","question":"What is the best way to reply?","options":["A. My grandparents' health is good.","B. Health of my grandparents is good.","C. My health grandparents' is good.","D. Grandparents health my is good."],"correctAnswer":"A","explanation":"La opción A es la correcta porque usa el genitivo sajón apropiadamente ('my grandparents' health').","points":2},{"id":"q6","type":"multiple-choice","scenario":"Discussing family vacations with a colleague.","question":"What is the most suitable response when asked, 'How was your family's vacation?'","options":["A. Vacation of my family was great.","B. My family vacation was great.","C. The family vacation's mine was great.","D. Vacation my family's was great."],"correctAnswer":"B","explanation":"La opción B es la más adecuada porque utiliza correctamente el adjetivo posesivo ('my') para referirse a la vacación de la familia.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
