  $_${"title":"Crossword: Family Members","instructions":"Complete the crossword puzzle","items":[{"word":"MOTHER","clue":"This is my mom's title.","direction":"across","row":0,"col":0},{"word":"FATHER","clue":"My dad's title.","direction":"down","row":0,"col":2},{"word":"SISTER","clue":"My brother's female sibling.","direction":"across","row":2,"col":0},{"word":"BROTHER","clue":"Your sister's male sibling.","direction":"down","row":1,"col":5},{"word":"UNCLE","clue":"My father's brother is my ___.","direction":"across","row":4,"col":0},{"word":"AUNT","clue":"Your mother's sister is your ___.","direction":"down","row":3,"col":6},{"word":"COUSIN","clue":"My uncle's child is my ___.","direction":"across","row":6,"col":2},{"word":"GRANDMA","clue":"Your mother's mother is your ___.","direction":"down","row":2,"col":4}]}$_$
