INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex39',
  'a2-m1-l3',
  39,
  'reading-comprehension',
  $_$Family Ties$_$,
  $_${"title":"Family Ties","textPassage":"My name is Anna, and I want to tell you about my family. I live with my parents and my little brother. My father's name is John, and he is a doctor. My mother's name is Sarah, and she is a teacher. My brother's name is Tom, and he is ten years old. We have a pet dog named Max. Our family enjoys spending weekends together. Sometimes, we visit my grandparents. My grandmother's name is Mary, and my grandfather's name is George. They live in a small village. My uncle, whose name is Peter, also lives nearby with his wife, Linda. They have two children, my cousins, Lucy and Jack. Lucy is my age, and we are very close. We often play together when I visit. My aunt and uncle's house is always full of laughter and fun. I am happy to have such a wonderful family.","wordCount":145,"estimatedReadingTime":2,"questions":[{"question":"Who is Anna's father?","options":["Tom","John","George","Peter"],"answer":"John","explanation":"El padre de Anna se llama John, como se menciona en el pasaje: 'My father's name is John'."},{"question":"How old is Anna's brother?","options":["Five","Ten","Twelve","Eight"],"answer":"Ten","explanation":"El pasaje menciona que el hermano de Anna, Tom, tiene diez años: 'My brother's name is Tom, and he is ten years old'."},{"question":"What is the name of Anna's dog?","options":["Max","Lucy","Tom","Jack"],"answer":"Max","explanation":"El nombre del perro de Anna es Max, como se indica en el pasaje: 'We have a pet dog named Max'."},{"question":"True or False: Anna's grandparents live in a big city.","answer":"False","explanation":"El pasaje menciona que los abuelos de Anna viven en una pequeña aldea: 'They live in a small village'."},{"question":"Who is Anna's cousin that is the same age as her?","options":["Jack","Lucy","Tom","Linda"],"answer":"Lucy","explanation":"Lucy es la prima de Anna que tiene la misma edad que ella, como se menciona: 'Lucy is my age, and we are very close'."},{"question":"What is NOT mentioned about Anna's family?","options":["Their weekend activities","Her mother's job","Her pet cat","Her uncle's children"],"answer":"Her pet cat","explanation":"El pasaje no menciona que Anna tenga un gato como mascota, sólo se menciona un perro llamado Max."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex40',
  'a2-m1-l3',
  40,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Intonation$_$,
  $_${"title":"Práctica de pronunciación: Intonation","instructions":"Practica la entonación en inglés con las siguientes oraciones. Escucha cómo cambia la entonación según el significado y la emoción detrás de cada oración.","targetSentences":[{"englishSentence":"My brother's friend is visiting us tomorrow.","spanishTranslation":"El amigo de mi hermano nos visitará mañana.","phoneticTranscription":"/ˈbrʌðərz ˈfrɛnd/","audioModelUrlPlaceholder":"URL de audio aquí","pronunciationTips":"Enfatiza 'brother's' para mostrar posesión. Asegúrate de elevar la voz al final para indicar el tiempo futuro.","focusPoints":"Poseción con 's, entonación ascendente para el futuro","commonMistakes":"Evitar pronunciar 'brother's' sin el sonido /z/ al final."},{"englishSentence":"Will you help my sister with her homework?","spanishTranslation":"¿Ayudarás a mi hermana con su tarea?","phoneticTranscription":"/wɪl jʊ hɛlp/","audioModelUrlPlaceholder":"URL de audio aquí","pronunciationTips":"Usa un tono ascendente en 'help my sister' para enfatizar la solicitud de ayuda.","focusPoints":"Entonación en preguntas, claridad en 'help my sister'","commonMistakes":"No dejes caer la voz al final de la pregunta; debe sonar como una solicitud."},{"englishSentence":"Our parents have to work on the weekend.","spanishTranslation":"Nuestros padres tienen que trabajar el fin de semana.","phoneticTranscription":"/ˈpɛrənts hæv tə/","audioModelUrlPlaceholder":"URL de audio aquí","pronunciationTips":"Enfatiza 'have to' para indicar obligación. Mantén un tono estable para mostrar rutina.","focusPoints":"Entonación de obligación, pronunciación clara de 'parents'","commonMistakes":"Asegúrate de no reducir 'have to' a un solo sonido rápido como /həftə/."},{"englishSentence":"Grandma baked a cake for my birthday.","spanishTranslation":"La abuela horneó un pastel para mi cumpleaños.","phoneticTranscription":"/ˈɡrænˌmɑ beɪkt/","audioModelUrlPlaceholder":"URL de audio aquí","pronunciationTips":"Enfatiza 'baked a cake' para destacar la acción. Suaviza la entonación en 'for my birthday' para concluir.","focusPoints":"Entonación de acciones, claridad en el pasado","commonMistakes":"Evita omitir el sonido /t/ en 'baked'."},{"englishSentence":"His cousin's wedding was beautiful.","spanishTranslation":"La boda de su primo fue hermosa.","phoneticTranscription":"/ˈkʌzənz ˈwɛdɪŋ/","audioModelUrlPlaceholder":"URL de audio aquí","pronunciationTips":"Enfatiza 'wedding' para resaltar el evento. Mantén un tono de admiración en 'was beautiful'.","focusPoints":"Poseción con 's, entonación de admiración","commonMistakes":"No omitas el sonido /z/ en 'cousin's'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex41',
  'a2-m1-l3',
  41,
  'multiple-choice',
  $_$Understanding Family Members$_$,
  $_${"title":"Understanding Family Members","instructions":"Choose the correct answer for each question. Use possessive adjectives and Saxon genitive to talk about family relationships. Las explicaciones están en español.","questions":[{"id":"q1","question":"Who is your father's brother?","options":["A. My uncle","B. My aunt","C. My cousin","D. My nephew"],"correctAnswer":"A","explanation":"La respuesta correcta es 'My uncle' porque 'el hermano de tu padre' es tu tío."},{"id":"q2","question":"What is your mother's sister's daughter called to you?","options":["A. My niece","B. My cousin","C. My sister","D. My aunt"],"correctAnswer":"B","explanation":"La respuesta correcta es 'My cousin' porque la hija de la hermana de tu madre es tu prima."},{"id":"q3","question":"How do you refer to your brother's son?","options":["A. My son","B. My nephew","C. My uncle","D. My cousin"],"correctAnswer":"B","explanation":"La respuesta correcta es 'My nephew' porque el hijo de tu hermano es tu sobrino."},{"id":"q4","question":"What is the correct possessive adjective for 'Sarah's husband'?","options":["A. My","B. Their","C. Her","D. His"],"correctAnswer":"C","explanation":"La respuesta correcta es 'Her' porque 'Sarah's husband' se refiere al esposo de ella, usando 'her'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex42',
  'a2-m1-l3',
  42,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"This is ______ sister. She is very kind.","gap":"your","explanation":"En inglés, usamos 'your' para referirnos a algo que pertenece a la persona a quien hablamos. Aquí, estamos hablando de la hermana de la persona que escucha.","points":1},{"sentence":"______ brother is taller than mine.","gap":"My","explanation":"Usamos 'my' para hablar de algo que pertenece a la persona que habla. En este caso, hablamos del hermano de la persona que está hablando.","points":1},{"sentence":"This is ______ parents' house. They have lived here for ten years.","gap":"my","explanation":"Usamos 'my' para referirnos a algo que pertenece a la persona que habla. Aquí, es la casa de los padres de la persona que está hablando.","points":1},{"sentence":"______ uncle is visiting us next week.","gap":"Our","explanation":"'Our' se usa para referirse a algo que pertenece a un grupo que incluye a la persona que habla. Aquí, el tío pertenece a la familia del grupo que incluye al hablante.","points":1},{"sentence":"Is this ______ cousin's car?","gap":"your","explanation":"Se usa 'your' para indicar posesión de la persona a quien se le habla. Aquí, la pregunta es sobre el coche del primo de la persona que escucha.","points":1},{"sentence":"______ grandparents are celebrating their anniversary today.","gap":"Their","explanation":"Usamos 'their' para referirnos a algo que pertenece a un grupo de personas. Aquí, estamos hablando de los abuelos de un grupo (ellos) que celebran su aniversario.","points":1},{"sentence":"She is ______ father's favorite child.","gap":"her","explanation":"Usamos 'her' para indicar que algo pertenece a una persona femenina de la que estamos hablando. Aquí, es la hija favorita del padre de la chica.","points":1},{"sentence":"This is ______ brother's jacket. He left it here.","gap":"his","explanation":"Usamos 'his' para referirnos a algo que pertenece a una persona masculina de la que estamos hablando. Aquí, es la chaqueta del hermano del chico.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex43',
  'a2-m1-l3',
  43,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["very","grandma","My","is","old","."],"correctOrder":["My","grandma","is","very","old","."],"translation":"Mi abuela es muy vieja.","hint":"Recuerda que 'very' es un adverbio de grado que se coloca antes del adjetivo.","points":2},{"id":2,"words":["must","help","You","your","sister","."],"correctOrder":["You","must","help","your","sister","."],"translation":"Debes ayudar a tu hermana.","hint":"Usa 'must' para expresar obligación.","points":2},{"id":3,"words":["his","is","Her","brother","taller","than","."],"correctOrder":["Her","brother","is","taller","than","his","."],"translation":"Su hermano es más alto que el de él.","hint":"Usa adjetivos comparativos para hacer comparaciones.","points":2},{"id":4,"words":["has","a","She","beautiful","really","family","."],"correctOrder":["She","has","a","really","beautiful","family","."],"translation":"Ella tiene una familia realmente hermosa.","hint":"Coloca 'really' antes del adjetivo para intensificarlo.","points":2},{"id":5,"words":["Tom's","is","new","house","big","."],"correctOrder":["Tom's","new","house","is","big","."],"translation":"La nueva casa de Tom es grande.","hint":"Usa el genitivo sajón para mostrar posesión.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex44',
  'a2-m1-l3',
  44,
  'speaking-analysis',
  $_$Speaking Analysis: Family Members$_$,
  $_${"title":"Speaking Analysis: Family Members","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are introducing your parents to a friend. How do you refer to them?","question":"Which sentence is correct?","options":["This is my parents.","These are my parents.","This is my parent's.","These are my parent's."],"correctAnswer":"B","explanation":"La opción B es correcta porque 'parents' es plural, por lo que usamos 'these are' en lugar de 'this is'. Además, no se usa el genitivo sajón porque no estamos hablando de posesión.","points":2},{"id":"q2","type":"multiple-choice","scenario":"Your brother is getting married next month, and you're telling your friend.","question":"How do you describe the event?","options":["My brother's wedding is next month.","My brother wedding's is next month.","The wedding of my brother's is next month.","My brother's wedding's next month."],"correctAnswer":"A","explanation":"La opción A es correcta porque usa el genitivo sajón correctamente para indicar que es la boda de tu hermano.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are at a family gathering and want to introduce your sister to a colleague.","question":"Which introduction is appropriate?","options":["Meet my sister, she's a teacher.","Meet my sister, she's teacher.","Meet my sister, she teacher.","Meet sister, she's a teacher."],"correctAnswer":"A","explanation":"La opción A es correcta porque usa el artículo 'a' antes de la profesión, lo cual es necesario en inglés.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are talking about your cousin's new baby.","question":"How would you say your cousin has a new child?","options":["My cousin's baby is new.","My cousin has a new baby.","My cousin's has a baby new.","The baby of my cousin is new."],"correctAnswer":"B","explanation":"La opción B es correcta porque el adjetivo 'new' debe ir antes del sustantivo 'baby'.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You want to say that your uncle is married.","question":"Which statement is correct?","options":["My uncle married.","My uncle is married.","Uncle my is married.","My uncle has married."],"correctAnswer":"B","explanation":"La opción B es correcta porque se necesita el verbo 'is' para formar la oración correctamente.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are explaining that your aunt is not single.","question":"How do you express her marital status?","options":["My aunt's married.","My aunt married.","Aunt my is married.","My aunt is marryed."],"correctAnswer":"A","explanation":"La opción A es correcta porque 'aunt's' es una forma contraída de 'aunt is', que es gramaticalmente correcta.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex45',
  'a2-m1-l3',
  45,
  'word-search',
  $_$Word Search: Family Members$_$,
  $_${"title":"Word Search: Family Members","instructions":"Find the hidden words in the grid","words":["MOTHER","FATHER","BROTHER","SISTER","GRANDFATHER","GRANDMOTHER","UNCLE","AUNT","COUSIN","HUSBAND","WIFE","SON"],"gridSize":10,"clues":[{"word":"MOTHER","clue":"Your mom."},{"word":"FATHER","clue":"Your dad."},{"word":"BROTHER","clue":"Your male sibling."},{"word":"SISTER","clue":"Your female sibling."},{"word":"GRANDFATHER","clue":"Your father's or mother's father."},{"word":"GRANDMOTHER","clue":"Your father's or mother's mother."},{"word":"UNCLE","clue":"Your parent's brother."},{"word":"AUNT","clue":"Your parent's sister."},{"word":"COUSIN","clue":"Your uncle's or aunt's child."},{"word":"HUSBAND","clue":"The man you are married to."},{"word":"WIFE","clue":"The woman you are married to."},{"word":"SON","clue":"Your male child."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex46',
  'a2-m1-l3',
  46,
  'crossword',
  $_$Crossword: Family Members$_$,
  $_${"title":"Crossword: Family Members","instructions":"Complete the crossword puzzle","items":[{"word":"MOTHER","clue":"This is my dad's wife.","direction":"across","row":0,"col":0},{"word":"BROTHER","clue":"My father's son is my ___.","direction":"down","row":0,"col":5},{"word":"GRANDMA","clue":"My mom's mom is my ___.","direction":"down","row":1,"col":2},{"word":"SISTER","clue":"My mother's daughter is my ___.","direction":"across","row":3,"col":1},{"word":"FATHER","clue":"This is my mom's husband.","direction":"across","row":5,"col":0},{"word":"COUSIN","clue":"My aunt's child is my ___.","direction":"down","row":0,"col":7},{"word":"UNCLE","clue":"My father's brother is my ___.","direction":"across","row":7,"col":3},{"word":"HUSBAND","clue":"This is my wife's partner.","direction":"across","row":9,"col":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex47',
  'a2-m1-l3',
  47,
  'reading-comprehension',
  $_$My Family and Our Traditions$_$,
  $_${"title":"My Family and Our Traditions","text":"Hello! My name is Anna, and I want to tell you about my family and our traditions. My mother's name is Sarah, and my father's name is John. My parents have been married for 20 years. My brother's name is Tom, and he is two years younger than me. We all live in a small house in a quiet town.\n\nEvery Sunday, my family visits my grandparents' house. My grandfather's name is Robert, and my grandmother's name is Mary. They have a beautiful garden where we play games and have picnics. It's my favorite place to relax.\n\nOn special occasions, like birthdays and holidays, my aunt Lisa and my uncle David come to visit us. They always bring delicious food and funny stories to share. We love spending time together, and it makes us all very happy.\n\nIn my family, we have a tradition of baking cookies every Christmas. My mom teaches us how to make her secret recipe, and we decorate the cookies with colorful icing and sprinkles. It's a fun activity that we all enjoy!\n\nI am very proud of my family, and I love them very much. They are always there to support me and make me smile.","wordCount":193,"estimatedReadingTime":2,"questions":[{"question":"Who visits their grandparents every Sunday?","options":["Anna's family","Anna's friends","Anna alone","Only Anna's parents"],"answer":"Anna's family","explanation":"Anna menciona que todos los domingos su familia visita la casa de sus abuelos."},{"question":"What is the main idea of the passage?","options":["Anna's family traditions and relationships","Anna's favorite foods","Anna's school activities","Anna's future plans"],"answer":"Anna's family traditions and relationships","explanation":"El texto se centra en las tradiciones y relaciones familiares de Anna, como las visitas a los abuelos y la preparación de galletas en Navidad."},{"question":"What do Anna and her family do on Christmas?","options":["Visit the zoo","Bake cookies","Go to the beach","Watch movies"],"answer":"Bake cookies","explanation":"Anna explica que su familia tiene la tradición de hornear galletas cada Navidad."},{"question":"TRUE or FALSE: Anna's aunt and uncle visit every day.","options":["TRUE","FALSE"],"answer":"FALSE","explanation":"Anna dice que su tía y su tío los visitan en ocasiones especiales, no todos los días."},{"question":"Whose garden do they enjoy playing games in?","options":["Anna's garden","Tom's garden","Grandparents' garden","Neighbor's garden"],"answer":"Grandparents' garden","explanation":"Anna menciona que disfrutan jugar en el jardín de sus abuelos."},{"question":"Which of the following is NOT mentioned in the passage?","options":["Anna's pet","Anna's grandparents","Family's cookie tradition","Tom's age"],"answer":"Anna's pet","explanation":"En el texto no se menciona ninguna mascota de Anna."},{"question":"What does Anna feel about her family?","options":["She is proud and loves them","She is angry","She is indifferent","She is disappointed"],"answer":"She is proud and loves them","explanation":"Anna expresa que está muy orgullosa de su familia y los ama mucho."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex48',
  'a2-m1-l3',
  48,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Acentuación de Palabras$_$,
  $_${"title":"Práctica de Pronunciación: Acentuación de Palabras","instructions":"Practica la pronunciación enfocándote en la acentuación correcta de palabras en inglés. Por favor, escucha el modelo de audio y repite las frases en voz alta.","targetSentences":[{"englishSentence":"My brother's daughter is very friendly.","spanishTranslation":"La hija de mi hermano es muy amable.","phoneticTranscription":"ˈbrʌð.ərz ˈdɔː.tər","audioModelURL":"https://example.com/audio1","pronunciationTips":"Enfócate en el acento de la palabra 'brother's' y 'daughter'. Asegúrate de enfatizar la primera sílaba de 'brother's' y 'daughter'.","focusPoints":["Acentuación en 'brother's'","Acentuación en 'daughter'"],"commonMistakes":["No acentuar correctamente 'brother's'","Pronunciar débilmente 'daughter'"]},{"englishSentence":"Lisa's husband works in a hospital.","spanishTranslation":"El esposo de Lisa trabaja en un hospital.","phoneticTranscription":"ˈliː.səz ˈhʌz.bənd","audioModelURL":"https://example.com/audio2","pronunciationTips":"En 'Lisa's', la acentuación está en la primera sílaba. En 'husband', asegúrate de enfatizar 'hus'.","focusPoints":["Acentuación en 'Lisa's'","Acentuación en 'husband'"],"commonMistakes":["Decir 'Lisa's' sin acento","Pronunciar 'husband' como 'hosband'"]},{"englishSentence":"Their parents live in the countryside.","spanishTranslation":"Sus padres viven en el campo.","phoneticTranscription":"ˈpeə.rənts","audioModelURL":"https://example.com/audio3","pronunciationTips":"La palabra 'parents' tiene la acentuación en la primera sílaba. No olvides enfatizarla.","focusPoints":["Acentuación en 'parents'"],"commonMistakes":["No enfatizar la primera sílaba de 'parents'"]},{"englishSentence":"Her cousin loves playing the piano.","spanishTranslation":"Su primo ama tocar el piano.","phoneticTranscription":"ˈkʌz.ən","audioModelURL":"https://example.com/audio4","pronunciationTips":"En 'cousin', la acentuación está en la primera sílaba. Recuerda enfatizar 'cous'.","focusPoints":["Acentuación en 'cousin'"],"commonMistakes":["Pronunciar 'cousin' sin acento en la primera sílaba"]},{"englishSentence":"My sister's children are very active.","spanishTranslation":"Los hijos de mi hermana son muy activos.","phoneticTranscription":"ˈsɪs.tərz ˈtʃɪl.drən","audioModelURL":"https://example.com/audio5","pronunciationTips":"Recuerda acentuar 'sister's' en la primera sílaba y 'children' en 'chil'.","focusPoints":["Acentuación en 'sister's'","Acentuación en 'children'"],"commonMistakes":["Decir 'sister's' sin énfasis","Olvidar acentuar 'children'"]},{"englishSentence":"My aunt and uncle enjoy gardening.","spanishTranslation":"Mi tía y tío disfrutan de la jardinería.","phoneticTranscription":"ænt ənd ˈʌŋ.kəl","audioModelURL":"https://example.com/audio6","pronunciationTips":"En 'uncle', asegúrate de acentuar la primera sílaba 'un'.","focusPoints":["Acentuación en 'uncle'"],"commonMistakes":["Pronunciar 'uncle' sin acento en 'un'"]}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex49',
  'a2-m1-l3',
  49,
  'multiple-choice',
  $_$Family Members and Possessives Exercise$_$,
  $_${"title":"Family Members and Possessives Exercise","instructions":"Choose the correct option for each question about family members and possessive adjectives.","questions":[{"id":"q1","question":"This is my grandmother. _____ house is very big.","options":["A. Her","B. His","C. Their","D. Our"],"correctAnswer":"A","explanation":"La opción correcta es 'Her' porque se refiere a la casa de la abuela, usando el adjetivo posesivo para 'ella'."},{"id":"q2","question":"John is married. _____ wife is a teacher.","options":["A. Their","B. His","C. Her","D. Our"],"correctAnswer":"B","explanation":"La opción correcta es 'His' porque se refiere a la esposa de John, usando el adjetivo posesivo para 'él'."},{"id":"q3","question":"Maria and _____ brother are visiting their grandparents.","options":["A. its","B. her","C. his","D. their"],"correctAnswer":"B","explanation":"La opción correcta es 'her' porque se refiere al hermano de Maria, usando el adjetivo posesivo para 'ella'."},{"id":"q4","question":"This is Tom's car. _____ color is blue.","options":["A. Her","B. His","C. Its","D. Their"],"correctAnswer":"C","explanation":"La opción correcta es 'Its' porque se refiere al color del coche de Tom, usando el adjetivo posesivo para una cosa."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l3-ex50',
  'a2-m1-l3',
  50,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"How ________ brothers do you have?","answers":["many"],"explanation":"Usamos 'how many' para preguntar sobre cantidad de cosas contables, como 'brothers'.","points":1},{"sentence":"There are ________ apples in the basket.","answers":["some","a lot of"],"explanation":"Podemos usar 'some' o 'a lot of' para indicar una cantidad positiva. Ambas son correctas aquí.","points":1},{"sentence":"You ________ drink a lot of water every day.","answers":["should"],"explanation":"'Should' se usa para dar consejos. Aquí se aconseja beber mucha agua.","points":1},{"sentence":"This is the ________ cake I have ever tasted!","answers":["biggest"],"explanation":"Usamos 'biggest' como adjetivo superlativo para indicar que este es el pastel más grande en comparación con otros.","points":1},{"sentence":"I ________ like to visit my grandparents this weekend.","answers":["would"],"explanation":"Usamos 'would like to' para expresar deseos o preferencias.","points":1},{"sentence":"How ________ sugar do you need for the recipe?","answers":["much"],"explanation":"Usamos 'how much' para preguntar sobre la cantidad de cosas incontables, como 'sugar'.","points":1},{"sentence":"We don't have ________ milk left in the fridge.","answers":["any"],"explanation":"'Any' se usa en oraciones negativas para indicar ausencia de algo, en este caso, 'milk'.","points":1},{"sentence":"She ________ eat so much candy. It's not healthy.","answers":["shouldn't"],"explanation":"Usamos 'shouldn't' para dar un consejo negativo, indicando que no es recomendable comer tanto dulce.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;

