INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex1',
  'a2-m1-l4',
  1,
  'multiple-choice',
  $_$Describing People - Physical Appearance$_$,
  $_${"title":"Describing People - Physical Appearance","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"What color eyes has she got?","options":["A. Brown","B. Tall","C. Long","D. Small"],"correctAnswer":"A","explanation":"La pregunta se refiere al color de los ojos, y 'Brown' (marrón) es el único color entre las opciones."},{"id":"q2","question":"He has got ____ hair.","options":["A. blue","B. short","C. big","D. young"],"correctAnswer":"B","explanation":"La oración describe el tipo de cabello que tiene, y 'short' (corto) es el adjetivo correcto para describir el cabello. Las otras opciones no son adjetivos adecuados para el cabello."},{"id":"q3","question":"She is wearing a ____ dress.","options":["A. red beautiful","B. beautiful red","C. red and beautiful","D. beautiful and red"],"correctAnswer":"B","explanation":"En inglés, el orden correcto es adjetivo de opinión seguido del adjetivo de color: 'beautiful red'."},{"id":"q4","question":"They have got ____ eyes.","options":["A. green","B. curly","C. tall","D. old"],"correctAnswer":"A","explanation":"La pregunta se refiere al color de los ojos, y 'green' (verde) es el único color entre las opciones. Las otras opciones no son adjetivos adecuados para describir los ojos."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex2',
  'a2-m1-l4',
  2,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"Luis ____ (will) go to the beach tomorrow if it doesn't rain.","gap":"will","explanation":"Usamos 'will' para hacer predicciones sobre el futuro. Aquí se predice que Luis irá a la playa si no llueve.","points":1},{"sentence":"Maria ____ (is going) to visit her grandmother this weekend.","gap":"is going","explanation":"Usamos el presente continuo para hablar de planes futuros. En este caso, Maria tiene el plan de visitar a su abuela el fin de semana.","points":1},{"sentence":"This is the ____ (biggest) cake I have ever seen!","gap":"biggest","explanation":"Usamos el superlativo 'biggest' para indicar que este pastel es el más grande que alguien ha visto.","points":1},{"sentence":"There are ____ (some) apples in the basket.","gap":"some","explanation":"Usamos 'some' con sustantivos contables plurales para indicar que hay una cantidad no especificada de manzanas en la canasta.","points":1},{"sentence":"The soup is ____ (very) hot, be careful!","gap":"very","explanation":"Usamos 'very' como un adverbio de grado para intensificar el adjetivo 'hot', indicando que la sopa está muy caliente.","points":1},{"sentence":"We ____ (should) wear coats because it's cold outside.","gap":"should","explanation":"Usamos 'should' como un verbo modal para dar consejos. Aquí se aconseja usar abrigos porque hace frío.","points":1},{"sentence":"You ____ (have to) finish your homework before you can play.","gap":"have to","explanation":"Usamos 'have to' para indicar obligación. En esta oración, es necesario terminar la tarea antes de poder jugar.","points":1},{"sentence":"My sister ____ (has got) long brown hair and green eyes.","gap":"has got","explanation":"Usamos 'has got' para describir posesiones o características físicas. Aquí describe el cabello y los ojos de la hermana.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex3',
  'a2-m1-l4',
  3,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["He","got","has","hair","curly","."],"correctOrder":["He","has","got","curly","hair","."],"translation":"Él tiene el pelo rizado.","hint":"Recuerda que usamos 'have got' para descripciones de apariencia.","points":2},{"id":2,"words":["She","has","got","eyes","green","."],"correctOrder":["She","has","got","green","eyes","."],"translation":"Ella tiene ojos verdes.","hint":"Los adjetivos de color van antes del sustantivo.","points":2},{"id":3,"words":["They","have","got","a","dog","big","."],"correctOrder":["They","have","got","a","big","dog","."],"translation":"Ellos tienen un perro grande.","hint":"El adjetivo describe al sustantivo y va antes de él.","points":2},{"id":4,"words":["I","have","got","a","jacket","red","."],"correctOrder":["I","have","got","a","red","jacket","."],"translation":"Tengo una chaqueta roja.","hint":"Los adjetivos de color preceden al sustantivo.","points":2},{"id":5,"words":["The","cat","has","got","fur","white","."],"correctOrder":["The","cat","has","got","white","fur","."],"translation":"El gato tiene el pelaje blanco.","hint":"Usa 'have got' para describir características.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex4',
  'a2-m1-l4',
  4,
  'speaking-analysis',
  $_$Speaking Analysis: Describing Appearance$_$,
  $_${"title":"Speaking Analysis: Describing Appearance","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are meeting a new classmate for the first time. They ask about your friend who is joining you.","question":"How would you describe your friend to the classmate?","options":["A: She has got long brown hair and blue eyes.","B: She blue eyes and long brown has got hair.","C: Brown long hair and she has got blue eyes.","D: She has got hair long and eyes blue."],"correctAnswer":"A","explanation":"En inglés, el orden es: adjetivo + sustantivo. 'Long brown hair' y 'blue eyes' están en el orden correcto.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are at a party and someone asks you about the host.","question":"What is a polite way to describe the host's appearance?","options":["A: The host looks tired but is very charming.","B: The host tired looks but is charming very.","C: Looks tired the host but very charming is.","D: The tired host looks but charming is very."],"correctAnswer":"A","explanation":"El adjetivo va antes del sustantivo: 'tired' describe cómo se ve la persona, seguido de 'charming' como una característica permanente.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are telling a friend about someone you met who was very fashionable.","question":"How would you describe this person?","options":["A: She's got a great sense of style and her clothes are always colorful.","B: She's got style great sense a and colorful are always her clothes.","C: A sense great of style she's got and colorful always are clothes her.","D: She's style got a great sense of and clothes colorful are her always."],"correctAnswer":"A","explanation":"La estructura correcta es 'great sense of style' y 'clothes are always colorful'.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are describing your new teacher to your parents.","question":"Which is the best way to describe your teacher?","options":["A: My teacher has got short black hair and green eyes.","B: My teacher has short black hair got and green eyes.","C: Short black hair my teacher has got and eyes green.","D: My teacher black short hair has got and eyes green."],"correctAnswer":"A","explanation":"Para describir características físicas, el adjetivo va antes del sustantivo: 'short black hair' y 'green eyes'.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are at a family reunion and want to introduce your cousin to a relative.","question":"How would you introduce your cousin?","options":["A: This is my cousin. He has got curly brown hair and a friendly smile.","B: This is my cousin. Curly brown hair he has got and smile a friendly.","C: My cousin this is. He curly brown hair has got and smile friendly a.","D: This is cousin my. He has got brown curly hair and a smile friendly."],"correctAnswer":"A","explanation":"El orden correcto en inglés es 'curly brown hair' y 'a friendly smile'. El adjetivo precede al sustantivo.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
