) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex6',
  'a2-m1-l4',
  6,
  'crossword',
  $_$Crossword: Describing Appearance$_$,
  $_${"title":"Crossword: Describing Appearance","instructions":"Complete the crossword puzzle","items":[{"word":"BLONDE","clue":"This is a light hair color, often seen in people from northern Europe.","direction":"across","row":0,"col":0},{"word":"EYES","clue":"You use these to see, and they can be blue, green, or brown.","direction":"down","row":0,"col":2},{"word":"TALL","clue":"The opposite of short.","direction":"across","row":2,"col":0},{"word":"SMILE","clue":"When you are happy, you do this with your mouth.","direction":"down","row":0,"col":5},{"word":"BEARD","clue":"Some men have this on their face.","direction":"across","row":4,"col":1},{"word":"HAVEBROWN","clue":"Many people _ _ _ _ _ _ _ _ hair and eyes.","direction":"across","row":6,"col":0}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex7',
  'a2-m1-l4',
  7,
  'reading-comprehension',
  $_$Describing Appearance$_$,
  $_${"title":"Describing Appearance","textPassage":"Anna and her family are going to a family reunion. Anna has got long, dark hair and bright blue eyes. She is wearing a yellow dress today. Her brother, Tom, has got short, curly hair and green eyes. He likes to wear a red t-shirt and blue jeans. Their mother, Mrs. Smith, has got straight, blonde hair and brown eyes. She is wearing a simple white blouse and a black skirt. Mr. Smith, their father, has got grey hair and brown eyes. He always wears a blue suit when they go to special events. Anna's little sister, Lucy, has got short, brown hair and hazel eyes. She loves her pink dress and wears it whenever she can. Together, they make a colorful and happy family!","wordCount":120,"estimatedReadingTime":2,"questions":[{"question":"What color is Anna's dress?","options":["Blue","Yellow","Red","Pink"],"answer":"Yellow","explanation":"El texto menciona que Anna está usando un vestido amarillo."},{"question":"Who has got curly hair?","options":["Tom","Anna","Lucy","Mrs. Smith"],"answer":"Tom","explanation":"Tom tiene el cabello corto y rizado, según el texto."},{"question":"Mr. Smith always wears a blue suit. True or False?","options":["True","False"],"answer":"True","explanation":"El texto dice que Mr. Smith siempre usa un traje azul para eventos especiales."},{"question":"What color are Lucy's eyes?","options":["Blue","Green","Brown","Hazel"],"answer":"Hazel","explanation":"Lucy tiene ojos color avellana, como se menciona en el texto."},{"question":"What does Mrs. Smith wear?","options":["A red t-shirt","A black skirt","A blue dress","A pink dress"],"answer":"A black skirt","explanation":"El texto indica que Mrs. Smith lleva una blusa blanca simple y una falda negra."},{"question":"Who has got blonde hair?","options":["Anna","Tom","Mrs. Smith","Lucy"],"answer":"Mrs. Smith","explanation":"Mrs. Smith tiene el cabello rubio según la descripción en el texto."},{"question":"Which of the following is NOT mentioned in the text?","options":["Anna's favorite color","Tom's jeans color","Mrs. Smith's eye color","Mr. Smith's hair color"],"answer":"Anna's favorite color","explanation":"El texto no menciona cuál es el color favorito de Anna."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex8',
  'a2-m1-l4',
  8,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Énfasis en el Orden de Adjetivos y Sustantivos$_$,
  $_${"title":"Práctica de Pronunciación: Énfasis en el Orden de Adjetivos y Sustantivos","instructions":"Practica la pronunciación del orden correcto de adjetivos y sustantivos en inglés. Escucha los ejemplos, repite las frases y presta atención a cómo se enfatiza cada palabra.","targetSentences":[{"english":"She has got long brown hair.","spanish":"Ella tiene el pelo largo y castaño.","phonetic":"/ʃiː hæz ɡɒt lɒŋ braʊn heə(r)/","audioModelUrl":"https://example.com/audio1","pronunciationTips":"Asegúrate de pronunciar claramente el sonido 'h' en 'hair'.","focusPoints":"Enfatiza la palabra 'long' para describir la longitud antes del color 'brown'.","commonMistakes":"No omitas el artículo 'has got'."},{"english":"The young boy has blue eyes.","spanish":"El niño joven tiene ojos azules.","phonetic":"/ðə jʌŋ bɔɪ hæz bluː aɪz/","audioModelUrl":"https://example.com/audio2","pronunciationTips":"Cuida la pronunciación de 'blue' para que no suene como 'blew'.","focusPoints":"Enfoca en 'young' como adjetivo de edad antes de 'boy'.","commonMistakes":"Confundir 'has' con 'is'."},{"english":"He's got a small red car.","spanish":"Él tiene un coche pequeño y rojo.","phonetic":"/hiːz ɡɒt ə smɔːl rɛd kɑː(r)/","audioModelUrl":"https://example.com/audio3","pronunciationTips":"Pronuncia la 'r' en 'red' de forma clara.","focusPoints":"Acentúa 'small' antes del color 'red' para describir el tamaño.","commonMistakes":"No intercambiar 'small' y 'red'."},{"english":"She has got green eyes.","spanish":"Ella tiene ojos verdes.","phonetic":"/ʃiː hæz ɡɒt ɡriːn aɪz/","audioModelUrl":"https://example.com/audio4","pronunciationTips":"Asegúrate de que 'green' suene claro y no se mezcle con 'eyes'.","focusPoints":"Resalta el color 'green' antes de 'eyes'.","commonMistakes":"No omitir el verbo 'has got'."},{"english":"The tall man has a black hat.","spanish":"El hombre alto tiene un sombrero negro.","phonetic":"/ðə tɔːl mæn hæz ə blæk hæt/","audioModelUrl":"https://example.com/audio5","pronunciationTips":"Presta atención a la 'h' aspirada en 'hat'.","focusPoints":"Enfatiza 'tall' como descripción de altura antes de 'man'.","commonMistakes":"No cambiar el orden de 'tall' y 'man'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex9',
  'a2-m1-l4',
  9,
  'multiple-choice',
  $_$Describing Appearance: Have Got and Adjective + Noun Order$_$,
  $_${"title":"Describing Appearance: Have Got and Adjective + Noun Order","instructions":"Elija la mejor opción para completar las frases sobre la descripción física.","questions":[{"id":"q1","question":"Samantha _____ long, brown hair and green eyes.","options":["A) have got","B) has got","C) got","D) get"],"correctAnswer":"B","explanation":"La forma correcta del verbo en tercera persona del singular es 'has got' cuando hablamos de posesión."},{"id":"q2","question":"My brother has got a _____ shirt.","options":["A) blue beautiful","B) beautiful blue","C) blue big","D) big blue"],"correctAnswer":"B","explanation":"El orden correcto de los adjetivos en inglés es 'opinion + color + noun', por lo que 'beautiful blue' es correcto."},{"id":"q3","question":"She has got _____ eyes.","options":["A) blue big","B) big blue","C) blue and big","D) big and blue"],"correctAnswer":"B","explanation":"En inglés, el orden correcto de los adjetivos es 'size + color', por lo que 'big blue eyes' es correcto."},{"id":"q4","question":"Tom _____ a tall man with short hair.","options":["A) has got","B) have got","C) is got","D) are got"],"correctAnswer":"A","explanation":"En tercera persona del singular, usamos 'has got' para describir características físicas o posesiones."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex10',
  'a2-m1-l4',
  10,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"She ___ got a big blue house near the beach.","options":["have","has"],"answer":"has","explanation":"En inglés, usamos 'has' para la tercera persona del singular (he, she, it) cuando hablamos de posesiones o características.","points":1},{"sentence":"He ___ going to visit his grandparents tomorrow.","options":["is","are"],"answer":"is","explanation":"Para formar el futuro próximo con 'going to', utilizamos 'is' con 'he', 'she', 'it'.","points":1},{"sentence":"My brother is taller ___ my sister.","options":["than","then"],"answer":"than","explanation":"Usamos 'than' para comparaciones, indicando que una cosa es más que otra.","points":1},{"sentence":"Would you like ___ cup of tea?","options":["a","an"],"answer":"a","explanation":"Usamos 'a' antes de palabras que comienzan con un sonido consonante. 'Cup' comienza con 'c', que es un sonido consonante.","points":1},{"sentence":"The tall woman has ___ black hair.","options":["got","have"],"answer":"got","explanation":"En inglés británico, 'have got' se utiliza para hablar de posesiones. 'Has got' se usa con la tercera persona del singular.","points":1},{"sentence":"This room is not big ___ for the party.","options":["enough","too"],"answer":"enough","explanation":"Se utiliza 'enough' después de un adjetivo para indicar que algo es suficiente.","points":1},{"sentence":"I have to wear ___ uniform at school.","options":["a","an"],"answer":"a","explanation":"Usamos 'a' antes de palabras que comienzan con un sonido consonante. 'Uniform' comienza con 'u', que se pronuncia como 'ju', un sonido consonante.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex11',
  'a2-m1-l4',
  11,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["has","got","hair","Anna","brown","."],"correctOrder":["Anna","has","got","brown","hair","."],"translation":"Anna tiene el pelo marrón.","hint":"Usa 'have got' para describir posesión y adjetivos antes del sustantivo.","points":2},{"id":2,"words":["eyes","has","got","blue","Tom","."],"correctOrder":["Tom","has","got","blue","eyes","."],"translation":"Tom tiene los ojos azules.","hint":"Recuerda el orden adjetivo + sustantivo.","points":2},{"id":3,"words":["got","a","has","Maria","red","jacket","."],"correctOrder":["Maria","has","got","a","red","jacket","."],"translation":"Maria tiene una chaqueta roja.","hint":"Coloca 'have got' y luego el adjetivo antes del sustantivo.","points":2},{"id":4,"words":["is","Paul","tall","and","has","got","curly","hair","."],"correctOrder":["Paul","is","tall","and","has","got","curly","hair","."],"translation":"Paul es alto y tiene el pelo rizado.","hint":"Describe primero la altura, luego el cabello con 'have got'.","points":2},{"id":5,"words":["got","brown","eyes","and","Sarah","short","hair","has","."],"correctOrder":["Sarah","has","got","brown","eyes","and","short","hair","."],"translation":"Sarah tiene ojos marrones y pelo corto.","hint":"Usa 'have got' para cada parte de la descripción.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex12',
  'a2-m1-l4',
  12,
  'speaking-analysis',
  $_$Speaking Analysis: Describing Appearance$_$,
  $_${"title":"Speaking Analysis: Describing Appearance","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are describing a friend to someone who is going to meet her for the first time.","question":"How would you describe her hair?","options":["A. She has got long brown hair.","B. She have got brown long hair.","C. She got brown long hair.","D. She has got hair long brown."],"correctAnswer":"A","explanation":"En inglés, el orden correcto de los adjetivos es ADJETIVO + SUSTANTIVO. Por eso, la opción correcta es 'long brown hair'.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are talking about someone's eye color in a casual conversation.","question":"What is the best way to mention her eye color?","options":["A. She has gotten blue eyes.","B. She's got eyes blue.","C. She's got blue eyes.","D. She have blue eyes."],"correctAnswer":"C","explanation":"La estructura correcta es 'have/has got' seguida por el color y luego el sustantivo: 'blue eyes'.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are describing someone's appearance in a formal context.","question":"Which expression is most appropriate?","options":["A. He's got a bunch of tattoos.","B. He possesses numerous tattoos.","C. He has loads of tattoos.","D. He got many tattoos."],"correctAnswer":"B","explanation":"En un contexto formal, 'He possesses numerous tattoos' es más apropiado debido al uso de un lenguaje formal.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are describing a person's height and build in an informal chat.","question":"Which phrase fits best?","options":["A. He is a tall and slim man.","B. He's a tall slim man.","C. He tall slim is.","D. He is a slim man tall."],"correctAnswer":"B","explanation":"En conversaciones informales, se pueden omitir palabras como 'and' si el contexto es claro. 'Tall slim man' es correcto porque sigue el orden adjetivo + sustantivo.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You're describing someone's clothing in a friendly conversation.","question":"Which option sounds most natural?","options":["A. She's wearing a red beautiful dress.","B. She has got a beautiful red dress on.","C. She's got on a dress red beautiful.","D. She wear beautiful dress red."],"correctAnswer":"B","explanation":"El orden correcto en inglés para adjetivos es 'beautiful red dress'. 'Has got on' es una expresión común para describir lo que alguien lleva puesto.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are describing a person's facial features in a detailed manner.","question":"Which description is best?","options":["A. He has a square jaw and bright blue eyes.","B. He got a square jaw and eyes bright blue.","C. He has jaw square and eyes blue bright.","D. He has jaw square and blue bright eyes."],"correctAnswer":"A","explanation":"El adjetivo siempre va antes del sustantivo y el color antes del sustantivo en inglés: 'square jaw' y 'bright blue eyes'.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex13',
  'a2-m1-l4',
  13,
  'word-search',
  $_$Word Search: Describing Appearance$_$,
  $_${"title":"Word Search: Describing Appearance","instructions":"Find the hidden words in the grid","words":["EYES","HAIR","TALL","SHORT","BROWN","BLONDE","BEARD","MUSTACHE","GLASSES","FRECKLES"],"gridSize":10,"clues":[{"word":"EYES","clue":"What you use to see"},{"word":"HAIR","clue":"It grows on your head"},{"word":"TALL","clue":"The opposite of short"},{"word":"SHORT","clue":"The opposite of tall"},{"word":"BROWN","clue":"A common hair and eye color"},{"word":"BLONDE","clue":"A light hair color"},{"word":"BEARD","clue":"Facial hair on the chin"},{"word":"MUSTACHE","clue":"Facial hair above the lips"},{"word":"GLASSES","clue":"Worn to see better"},{"word":"FRECKLES","clue":"Small brown spots on skin"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex14',
  'a2-m1-l4',
  14,
  'crossword',
  $_$Crossword: Describing Appearance$_$,
  $_${"title":"Crossword: Describing Appearance","instructions":"Complete the crossword puzzle","items":[{"word":"BLONDE","clue":"She has got __________ hair. (fair color)","direction":"across","row":1,"col":0},{"word":"TALL","clue":"He is not short, he is __________.","direction":"across","row":3,"col":2},{"word":"BROWN","clue":"Her eyes are __________.","direction":"down","row":0,"col":5},{"word":"BEARD","clue":"He has got a thick __________ on his face.","direction":"across","row":5,"col":0},{"word":"GLASSES","clue":"He wears __________ to see better.","direction":"down","row":2,"col":3},{"word":"SHORT","clue":"She has got __________ hair, not long.","direction":"across","row":7,"col":1},{"word":"GREEN","clue":"His eyes are the color of grass. They are __________.","direction":"down","row":1,"col":7},{"word":"CURVY","clue":"She is not thin, she is __________.","direction":"across","row":4,"col":4}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex15',
  'a2-m1-l4',
  15,
  'reading-comprehension',
  $_$Describing Appearance$_$,
  $_${"title":"Describing Appearance","textPassage":"Maria has got long brown hair and bright blue eyes. She is quite tall and has a slim figure. Her favorite color is green, so she often wears green clothes. Today, she is wearing a green shirt and a pair of blue jeans. Maria's best friend, Anna, has got short black hair and warm brown eyes. Anna is not as tall as Maria, but she has a strong build. She loves wearing red dresses because she thinks they look elegant. Both Maria and Anna enjoy going to the park on weekends. They like to sit under the big old trees and read books. Maria prefers reading mystery novels, while Anna enjoys romantic stories. They have been friends since childhood and love spending time together.","wordCount":125,"estimatedReadingTime":1,"questions":[{"question":"What color are Maria's eyes?","options":["Brown","Green","Blue","Black"],"answer":"Blue","explanation":"En el texto se menciona que Maria tiene ojos azules brillantes. Es importante identificar los detalles específicos de la descripción física."},{"question":"What does Anna like to wear?","options":["Green shirts","Red dresses","Blue jeans","Black trousers"],"answer":"Red dresses","explanation":"Anna prefiere los vestidos rojos porque piensa que se ven elegantes, según el texto. Esto prueba la comprensión de detalles específicos."},{"question":"Who is taller, Maria or Anna?","options":["Maria","Anna","Both are the same height","Not mentioned"],"answer":"Maria","explanation":"El texto indica que Maria es más alta que Anna. Es necesario inferir información de la descripción dada."},{"question":"True or False: Maria and Anna like reading the same type of books.","options":["True","False"],"answer":"False","explanation":"Maria prefiere novelas de misterio y Anna disfruta de historias románticas. Esto demuestra cómo los detalles pueden diferenciarse en una lectura."},{"question":"What is Maria wearing today?","options":["A red dress","A green shirt and blue jeans","A black shirt and trousers","A yellow dress"],"answer":"A green shirt and blue jeans","explanation":"El texto describe específicamente la ropa de Maria hoy. Observar detalles precisos es crucial para la comprensión."},{"question":"What do Maria and Anna like to do at the park?","options":["Play sports","Read books","Have a picnic","Go jogging"],"answer":"Read books","explanation":"En el texto, se menciona que disfrutan leyendo libros bajo los árboles en el parque. Comprender actividades mencionadas es parte de la comprensión general."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex16',
  'a2-m1-l4',
  16,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Énfasis en las Palabras$_$,
  $_${"title":"Práctica de Pronunciación: Énfasis en las Palabras","instructions":"Practica la pronunciación de estas oraciones en inglés enfocándote en el énfasis correcto de las palabras. Lee cada oración, escucha el audio y repite. Usa las transcripciones fonéticas para ayudarte. Recuerda prestar atención a los consejos de pronunciación.","targetSentences":[{"englishSentence":"The young man has got curly hair.","spanishTranslation":"El joven tiene el pelo rizado.","phoneticTranscription":"/ðə jʌŋ mæn hæz gɒt ˈkɜː.li heə/","audioModelURL":"https://example.com/audio1","pronunciationTips":"Enfoca la pronunciación en 'curly' asegurándote de que suene claro.","focusPoints":"Enfatiza la palabra 'curly'.","commonMistakes":"Evita pronunciar 'curly' como 'cur-lee'."},{"englishSentence":"She has got blue eyes.","spanishTranslation":"Ella tiene ojos azules.","phoneticTranscription":"/ʃi hæz gɒt bluː aɪz/","audioModelURL":"https://example.com/audio2","pronunciationTips":"Asegúrate de enfatizar 'blue' con claridad.","focusPoints":"Enfatiza 'blue'.","commonMistakes":"No pronuncies 'blue' como 'blu'."},{"englishSentence":"They have got a new car.","spanishTranslation":"Ellos tienen un coche nuevo.","phoneticTranscription":"/ðeɪ hæv gɒt ə njuː kɑː/","audioModelURL":"https://example.com/audio3","pronunciationTips":"Resalta 'new' para que se escuche claramente.","focusPoints":"Enfatiza 'new'.","commonMistakes":"No pronuncies 'new' como 'nyu'."},{"englishSentence":"She has got long brown hair.","spanishTranslation":"Ella tiene el pelo largo y marrón.","phoneticTranscription":"/ʃi hæz gɒt lɒŋ braʊn heə/","audioModelURL":"https://example.com/audio4","pronunciationTips":"Enfoca en 'brown' para una pronunciación clara.","focusPoints":"Enfatiza 'brown'.","commonMistakes":"No pronuncies 'brown' como 'brawn'."},{"englishSentence":"He has got short black hair.","spanishTranslation":"Él tiene el pelo corto y negro.","phoneticTranscription":"/hi hæz gɒt ʃɔːt blæk heə/","audioModelURL":"https://example.com/audio5","pronunciationTips":"Asegúrate de que 'black' tenga el énfasis correcto.","focusPoints":"Enfatiza 'black'.","commonMistakes":"No pronuncies 'black' como 'blak'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex17',
  'a2-m1-l4',
  17,
  'multiple-choice',
  $_$Describe People: Appearance and Attributes$_$,
  $_${"title":"Describe People: Appearance and Attributes","instructions":"Choose the correct option to complete the sentences about people's appearance.","questions":[{"id":"q1","question":"Maria ____ long, brown hair and green eyes.","options":["A: have got","B: has got","C: is having","D: have"],"correctAnswer":"B","explanation":"La forma correcta es 'has got' porque 'Maria' es tercera persona singular."},{"id":"q2","question":"The man is wearing a ____ hat.","options":["A: black big","B: big black","C: black and big","D: big and black"],"correctAnswer":"B","explanation":"El orden correcto de los adjetivos es 'big black' porque el tamaño va antes del color."},{"id":"q3","question":"John ____ to wear glasses because he can't see well.","options":["A: has got","B: have to","C: is going","D: must"],"correctAnswer":"D","explanation":"Usamos 'must' para expresar obligación personal."},{"id":"q4","question":"She is a ____ woman with a friendly smile.","options":["A: tall","B: tall and","C: tall a","D: a tall"],"correctAnswer":"D","explanation":"El adjetivo 'tall' va antes del sustantivo 'woman', por lo tanto se usa 'a tall woman'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex18',
  'a2-m1-l4',
  18,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"María _____ (have got) a lot of books in her room.","gap":"have got","acceptableAnswers":["has got"],"explanation":"En inglés, se utiliza 'has got' para decir que alguien posee algo. En este caso, 'María' es tercera persona del singular, por lo tanto usamos 'has got'.","points":1},{"sentence":"There _____ (be) any cookies left in the jar?","gap":"be","acceptableAnswers":["aren't"],"explanation":"Para preguntas negativas en inglés, se usa 'aren't' cuando se pregunta sobre cosas en plural, como 'cookies'.","points":1},{"sentence":"You _____ (should) wear a coat; it's very cold outside.","gap":"should","acceptableAnswers":["should"],"explanation":"'Should' se utiliza para dar consejos. Aquí se aconseja usar un abrigo porque hace frío.","points":1},{"sentence":"How _____ (much/many) apples are in the basket?","gap":"much/many","acceptableAnswers":["many"],"explanation":"Usamos 'many' porque 'apples' es contable y estamos preguntando por la cantidad.","points":1},{"sentence":"They _____ (going to) visit their grandparents next weekend.","gap":"going to","acceptableAnswers":["are going to"],"explanation":"'Going to' se usa para hablar de planes futuros. Aquí, 'they' es plural, por lo que usamos 'are going to'.","points":1},{"sentence":"I _____ (have to) finish my homework before dinner.","gap":"have to","acceptableAnswers":["have to"],"explanation":"'Have to' indica obligación. Aquí la obligación es terminar la tarea antes de la cena.","points":1},{"sentence":"She _____ (not have to) clean the house today.","gap":"not have to","acceptableAnswers":["doesn't have to"],"explanation":"Para expresar que no hay obligación, usamos 'doesn't have to' en tercera persona singular.","points":1},{"sentence":"We _____ (shouldn't) eat too much candy.","gap":"shouldn't","acceptableAnswers":["shouldn't"],"explanation":"'Shouldn't' se usa para dar un consejo negativo, en este caso, no comer muchos dulces.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex19',
  'a2-m1-l4',
  19,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["has","got","She","eyes","green","."],"correctOrder":["She","has","got","green","eyes","."],"translation":"Ella tiene ojos verdes.","hint":"Sujeto + has got + adjetivo + sustantivo","points":2},{"id":2,"words":["brown","He","hair","got","has","."],"correctOrder":["He","has","got","brown","hair","."],"translation":"Él tiene el pelo castaño.","hint":"Sujeto + has got + adjetivo + sustantivo","points":2},{"id":3,"words":["a","is","man","tall","He","."],"correctOrder":["He","is","a","tall","man","."],"translation":"Él es un hombre alto.","hint":"Sujeto + is + a + adjetivo + sustantivo","points":2},{"id":4,"words":["got","hair","She","blonde","has","."],"correctOrder":["She","has","got","blonde","hair","."],"translation":"Ella tiene el pelo rubio.","hint":"Sujeto + has got + adjetivo + sustantivo","points":2},{"id":5,"words":["blue","are","His","eyes","."],"correctOrder":["His","eyes","are","blue","."],"translation":"Sus ojos son azules.","hint":"Posesivo + sustantivo + are + adjetivo","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex20',
  'a2-m1-l4',
  20,
  'speaking-analysis',
  $_$Speaking Analysis: Describing Appearance$_$,
  $_${"title":"Speaking Analysis: Describing Appearance","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking about your friend to someone who hasn't met them yet. You want to describe your friend's hair.","question":"What is the best way to describe your friend's hair?","options":["A) My friend has got long black hair.","B) My friend has got black long hair.","C) My friend got long hair black.","D) My friend have got black and long hair."],"correctAnswer":"A","explanation":"La opción A es correcta porque sigue el orden correcto de adjetivo + sustantivo en inglés: 'long black hair'.","points":2},{"id":"q2","type":"multiple-choice","scenario":"During a casual conversation at a café, someone mentions a woman with striking eyes.","question":"How might they describe her eyes?","options":["A) She has got big and blue eyes.","B) She got eyes blue and big.","C) She has got blue big eyes.","D) She have got blue and big eyes."],"correctAnswer":"A","explanation":"La opción A es correcta porque sigue las reglas de gramática: 'big and blue eyes'.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You want to tell your friend about a man you saw at the store who looks interesting.","question":"Which description is most appropriate?","options":["A) He has got green eyes and short brown hair.","B) He have got hair short and brown.","C) He got short and brown hair.","D) He has got short brown hair and green eyes."],"correctAnswer":"D","explanation":"La opción D es correcta y completa, describiendo tanto los ojos como el cabello de manera gramaticalmente correcta.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are complimenting your classmate on their appearance for a special event.","question":"What is the best compliment?","options":["A) You have got a beautiful dress.","B) You got a dress beautiful.","C) You have got dress beautiful.","D) You has got beautiful a dress."],"correctAnswer":"A","explanation":"La opción A es correcta porque sigue el orden correcto de adjetivo + sustantivo y usa 'have got'.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are describing a famous person during a conversation.","question":"How would you describe their appearance?","options":["A) They have got tall and slim.","B) They have got a tall and slim build.","C) They got build tall and slim.","D) They has got slim tall build."],"correctAnswer":"B","explanation":"La opción B es correcta porque utiliza 'have got' con un sustantivo y adjetivos en el orden correcto.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex21',
  'a2-m1-l4',
  21,
  'word-search',
  $_$Word Search: Describing Appearance$_$,
  $_${"title":"Word Search: Describing Appearance","instructions":"Find the hidden words in the grid","words":["blonde","brown","curly","eyes","glasses","hair","short","tall","blue","green","young","old"],"gridSize":10,"clues":[{"word":"blonde","clue":"Color of hair often light yellow."},{"word":"brown","clue":"Common eye and hair color."},{"word":"curly","clue":"Type of hair that is not straight."},{"word":"eyes","clue":"Part of the body used to see."},{"word":"glasses","clue":"Worn to help see better."},{"word":"hair","clue":"It grows on your head."},{"word":"short","clue":"Opposite of tall."},{"word":"tall","clue":"Opposite of short."},{"word":"blue","clue":"A color often seen in eyes."},{"word":"green","clue":"A rare eye color."},{"word":"young","clue":"Opposite of old."},{"word":"old","clue":"Opposite of young."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex22',
  'a2-m1-l4',
  22,
  'crossword',
  $_$Crossword: Describing Appearance$_$,
  $_${"title":"Crossword: Describing Appearance","instructions":"Complete the crossword puzzle","items":[{"word":"BLOND","clue":"A color of hair, similar to yellow.","direction":"across","row":0,"col":0},{"word":"TALL","clue":"The opposite of short.","direction":"down","row":0,"col":1},{"word":"EYES","clue":"We use these to see.","direction":"across","row":1,"col":2},{"word":"BEARD","clue":"Hair on a man's face, below the mouth.","direction":"down","row":1,"col":4},{"word":"BLUE","clue":"The color of the sky on a clear day.","direction":"across","row":3,"col":0},{"word":"GLASSES","clue":"You wear these to help you see better.","direction":"down","row":3,"col":3},{"word":"SMILE","clue":"What you do with your mouth when you are happy.","direction":"across","row":5,"col":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex23',
  'a2-m1-l4',
  23,
  'reading-comprehension',
  $_$Describing Appearance$_$,
  $_${"title":"Describing Appearance","text":"Anna and her brother Tom have got very different appearances. Anna has got long, straight brown hair and bright blue eyes. She is quite tall and slim. People often say she looks like her mother. Tom, on the other hand, has got short, curly black hair and dark brown eyes. He is shorter than Anna and a bit more muscular. Tom likes to wear colorful clothes, especially his favorite red jacket. Anna prefers to wear simple, comfortable clothes, like her green sweater and jeans. They both have got friendly smiles, and they are always ready to help others. Despite their different looks, they get along very well and enjoy spending time together.","wordCount":109,"estimatedReadingTime":1,"questions":[{"question":"What color are Anna's eyes?","options":["Brown","Blue","Green","Black"],"answer":"Blue","explanation":"Anna tiene ojos azules brillantes, como se menciona en el texto."},{"question":"How can we describe Tom's hair?","options":["Long and straight","Short and curly","Blonde and wavy","Bald"],"answer":"Short and curly","explanation":"Tom tiene el cabello corto y rizado, lo cual está claramente especificado en el texto."},{"question":"Who is taller, Anna or Tom?","options":["Anna","Tom","They are the same height","Not mentioned"],"answer":"Anna","explanation":"Anna es descrita como más alta que Tom, por lo que la respuesta correcta es que Anna es más alta."},{"question":"What does Tom like to wear?","options":["A green sweater","Jeans","A red jacket","A blue shirt"],"answer":"A red jacket","explanation":"A Tom le gusta llevar ropa colorida, especialmente su chaqueta roja favorita."},{"question":"TRUE or FALSE: Anna looks like her father.","options":["TRUE","FALSE"],"answer":"FALSE","explanation":"El texto indica que Anna se parece a su madre, no a su padre."},{"question":"What is the main idea of the text?","options":["Anna and Tom's favorite clothes","Anna and Tom's physical description","Anna and Tom's hobbies","Anna and Tom's favorite food"],"answer":"Anna and Tom's physical description","explanation":"El texto se centra principalmente en describir las apariencias físicas de Anna y Tom."},{"question":"What can we infer about Anna and Tom's relationship?","options":["They don't get along","They enjoy spending time together","They have many arguments","They live in different cities"],"answer":"They enjoy spending time together","explanation":"A pesar de sus diferencias, el texto menciona que disfrutan pasar tiempo juntos, lo que indica una buena relación."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex24',
  'a2-m1-l4',
  24,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Enfoque en el estrés de las palabras$_$,
  $_${"title":"Práctica de pronunciación: Enfoque en el estrés de las palabras","instructions":"Practiquemos el estrés de las palabras en inglés. Escucha cada oración y repite después de escuchar el audio. Presta atención al acento correcto en las palabras clave. Usa las transcripciones fonéticas para ayudarte.","targetSentences":[{"englishSentence":"She has got beautiful blue eyes.","spanishTranslation":"Ella tiene hermosos ojos azules.","phoneticTranscription":"[ˈbjuːtɪfl̩] [bluː] [aɪz]","audioModelURL":"URL_placeholder_1","pronunciationTips":"Enfatiza la primera sílaba de 'beautiful' y 'blue'.","focusPoints":"Acentuar la primera sílaba en 'beautiful' y 'blue'.","commonMistakes":"Evita acentuar la sílaba incorrecta en 'beautiful'."},{"englishSentence":"His hair is curly and brown.","spanishTranslation":"Su cabello es rizado y marrón.","phoneticTranscription":"[hɛə] [ˈkɜːli] [braʊn]","audioModelURL":"URL_placeholder_2","pronunciationTips":"Pon el énfasis en 'curly' y asegúrate de pronunciar claramente 'brown'.","focusPoints":"Acentuar correctamente 'curly'.","commonMistakes":"No omitas el sonido final en 'brown'."},{"englishSentence":"They have got a small red car.","spanishTranslation":"Ellos tienen un coche rojo pequeño.","phoneticTranscription":"[smɔːl] [rɛd] [kɑː]","audioModelURL":"URL_placeholder_3","pronunciationTips":"Destaca la primera sílaba en 'small' y 'red'.","focusPoints":"Acentuar correctamente 'small' y 'red'.","commonMistakes":"No mezclar el acento en 'red'."},{"englishSentence":"The woman is tall and has got blonde hair.","spanishTranslation":"La mujer es alta y tiene el cabello rubio.","phoneticTranscription":"[wʊmən] [tɔːl] [blɒnd]","audioModelURL":"URL_placeholder_4","pronunciationTips":"Enfoca en 'tall' y 'blonde', asegurando una pronunciación clara.","focusPoints":"Acentuar la primera sílaba de 'tall' y 'blonde'.","commonMistakes":"Evitar acentuar mal 'blonde'."},{"englishSentence":"She wears a long green dress.","spanishTranslation":"Ella lleva un vestido largo verde.","phoneticTranscription":"[lɒŋ] [ɡriːn] [drɛs]","audioModelURL":"URL_placeholder_5","pronunciationTips":"Acentúa 'green' y 'dress', con una pronunciación clara.","focusPoints":"Acentuar 'green' y 'dress'.","commonMistakes":"No mezclar el acento en 'dress'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex25',
  'a2-m1-l4',
  25,
  'multiple-choice',
  $_$Describing People: Appearance and Features$_$,
  $_${"title":"Describing People: Appearance and Features","instructions":"Choose the correct answer for each question about describing appearance using 'have got' and adjective order.","questions":[{"id":"q1","question":"She _______ long, curly hair.","options":["A. has got","B. have got","C. is got","D. are got"],"correctAnswer":"A","explanation":"La forma correcta del verbo 'have' en tercera persona singular es 'has got'."},{"id":"q2","question":"He has got _______ eyes.","options":["A. blue big","B. big blue","C. blue and big","D. big and blue"],"correctAnswer":"B","explanation":"El orden correcto en inglés es adjetivo de tamaño seguido de color, por lo que es 'big blue'."},{"id":"q3","question":"They _______ a small, round face.","options":["A. have got","B. has got","C. is got","D. are got"],"correctAnswer":"A","explanation":"Para el pronombre 'they', usamos 'have got' ya que es plural."},{"id":"q4","question":"She has got _______ shirt.","options":["A. red beautiful","B. beautiful red","C. red and beautiful","D. beautiful and red"],"correctAnswer":"B","explanation":"El orden correcto de los adjetivos en inglés es opinión seguido de color, por lo que es 'beautiful red'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex26',
  'a2-m1-l4',
  26,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"id":1,"sentence":"Samantha _____ (have got) a small dog with brown fur.","acceptableAnswers":["has got"],"explanation":"En inglés, 'have got' se usa para indicar posesión. Aquí, 'Samantha' es tercera persona singular, por lo que se usa 'has got'.","points":1},{"id":2,"sentence":"My brother _____ (have got) short, black hair.","acceptableAnswers":["has got"],"explanation":"Para describir posesiones o características físicas de alguien, como el cabello, usamos 'have got'. Para 'my brother', que es tercera persona singular, usamos 'has got'.","points":1},{"id":3,"sentence":"We _____ (not have got) green eyes, we have blue eyes.","acceptableAnswers":["haven't got","have not got"],"explanation":"'Haven't got' es la forma negativa de 'have got', usada aquí para negar la posesión de ojos verdes.","points":1},{"id":4,"sentence":"The tall woman _____ (have got) a red dress.","acceptableAnswers":["has got"],"explanation":"La estructura es 'adjetivo + sustantivo'; en este caso, 'tall woman'. Para indicar posesión de un vestido rojo, se usa 'has got'.","points":1},{"id":5,"sentence":"They _____ (have got) a big, white house near the park.","acceptableAnswers":["have got"],"explanation":"Para describir una posesión como una casa, usamos 'have got'. 'They' es plural, así que usamos 'have got'.","points":1},{"id":6,"sentence":"I _____ (have got) a new phone with a blue case.","acceptableAnswers":["have got"],"explanation":"Para describir una posesión personal como un teléfono, usamos 'have got'. 'I' es primera persona singular, así que usamos 'have got'.","points":1},{"id":7,"sentence":"Maria and Carlos _____ (have got) curly, brown hair.","acceptableAnswers":["have got"],"explanation":"Para describir características físicas como el cabello, usamos 'have got'. Como 'Maria and Carlos' es plural, usamos 'have got'.","points":1},{"id":8,"sentence":"The old man _____ (not have got) a beard.","acceptableAnswers":["hasn't got","has not got"],"explanation":"'Hasn't got' es la forma negativa de 'has got', usada para negar la posesión de una barba.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex27',
  'a2-m1-l4',
  27,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["have","He","blue","eyes","."],"correctOrder":["He","have","blue","eyes","."],"translation":"Él tiene ojos azules.","hint":"La estructura es sujeto + have + adjetivo + sustantivo.","points":2},{"id":2,"words":["got","She","long","has","hair","."],"correctOrder":["She","has","got","long","hair","."],"translation":"Ella tiene el pelo largo.","hint":"Usa 'has got' para describir posesiones o características.","points":2},{"id":3,"words":["a","He","brown","beard","has","."],"correctOrder":["He","has","a","brown","beard","."],"translation":"Él tiene una barba marrón.","hint":"La estructura es sujeto + has + a + adjetivo + sustantivo.","points":2},{"id":4,"words":["got","Her","eyes","are","brown","."],"correctOrder":["Her","eyes","are","brown","."],"translation":"Sus ojos son marrones.","hint":"Usa 'are' para describir características permanentes.","points":2},{"id":5,"words":["got","He","has","a","nose","big","."],"correctOrder":["He","has","got","a","big","nose","."],"translation":"Él tiene una nariz grande.","hint":"Usa 'has got' seguido de adjetivo + sustantivo.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex28',
  'a2-m1-l4',
  28,
  'speaking-analysis',
  $_$Speaking Analysis: Describing Appearance$_$,
  $_${"title":"Speaking Analysis: Describing Appearance","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are at a cafe with a friend, and someone you both know walks in. Your friend says, 'Look, there's Sarah. She has got...' What is the most appropriate way to complete the sentence?","question":"Choose the correct description.","options":["A beautiful long hair and blue eyes.","A long beautiful hair and blue eyes.","Beautiful long hair and blue eyes.","Long beautiful hair and blue eyes."],"correctAnswer":"C","explanation":"La opción C es correcta porque sigue el orden correcto de adjetivos en inglés: 'beautiful' (opinión) antes de 'long' (tamaño). La palabra 'hair' no lleva artículo 'a' porque es un sustantivo no contable.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are describing a person to a friend who hasn't met them. You say, 'He's got...'","question":"Choose the best description.","options":["Eyes brown and short curly hair.","Brown eyes and hair short curly.","Short curly hair and brown eyes.","Hair short curly and eyes brown."],"correctAnswer":"C","explanation":"La opción C es correcta porque sigue el orden del adjetivo + sustantivo. Los adjetivos 'short' y 'curly' describen el cabello y preceden al sustantivo 'hair'.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are talking about a mutual friend. Your friend asks about his appearance. You say, 'He has got...'","question":"Select the most natural description.","options":["A tall man and brown eyes.","Tall man and eyes brown.","A tall man with brown eyes.","Man tall with eyes brown."],"correctAnswer":"C","explanation":"La opción C es correcta porque usa 'with' para conectar dos descripciones: 'a tall man' y 'brown eyes'.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are at a party and someone asks you to describe your sister. You say, 'She has got...'","question":"Choose the right phrase.","options":["Dark long hair and eyes green.","Long dark hair and green eyes.","Hair long dark and green eyes.","Eyes green and long dark hair."],"correctAnswer":"B","explanation":"La opción B es correcta porque sigue el orden correcto de adjetivos: 'long' (tamaño) antes de 'dark' (color), y luego el sustantivo 'hair'.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You meet someone new and want to describe their outfit to a friend. You say, 'She is wearing...'","question":"Pick the most appropriate description.","options":["A blue beautiful dress.","A beautiful blue dress.","Beautiful blue a dress.","Blue a beautiful dress."],"correctAnswer":"B","explanation":"La opción B es correcta porque sigue el orden del adjetivo: 'beautiful' (opinión) antes de 'blue' (color) y después el sustantivo 'dress'.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex29',
