INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex38',
  'a2-m1-l4',
  38,
  'crossword',
  $_$Crossword: Describing Appearance$_$,
  $_${"title":"Crossword: Describing Appearance","instructions":"Complete the crossword puzzle","items":[{"word":"BLONDE","clue":"Color of hair that is light yellow.","direction":"across","row":0,"col":0},{"word":"TALL","clue":"A person who is not short.","direction":"down","row":0,"col":0},{"word":"BEARD","clue":"Hair that grows on a man's face.","direction":"across","row":1,"col":2},{"word":"GLASSES","clue":"You wear these on your eyes to see better.","direction":"down","row":1,"col":4},{"word":"BLUE","clue":"Color of the sky and sea.","direction":"across","row":3,"col":1},{"word":"SHORT","clue":"Opposite of tall.","direction":"down","row":3,"col":4},{"word":"EYES","clue":"You use these to see.","direction":"across","row":5,"col":3}]}$_$
