INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex39',
  'a2-m1-l4',
  39,
  'reading-comprehension',
  $_$Describing Appearance$_$,
  $_${"title":"Describing Appearance","text_passage":"Lisa has got long, curly brown hair and bright blue eyes. She loves wearing colorful clothes, like her favorite red sweater and yellow shoes. Her best friend, Tom, has got short, straight black hair and dark brown eyes. They both like going to the park on sunny days. Lisa usually wears a pink hat to protect her fair skin from the sun. Tom, on the other hand, prefers wearing sunglasses. They often sit on a green bench and watch people passing by. Lisa thinks Tom is very handsome because he has a nice smile and a kind face. Tom says Lisa is beautiful because she always looks happy and cheerful.","wordCount":118,"estimatedReadingTime":1,"questions":[{"question":"What color is Lisa's hair?","options":["Blonde","Black","Brown","Red"],"answer":"Brown","explanation":"El texto menciona que Lisa tiene el cabello castaño, lo que en inglés se dice 'brown'."},{"question":"What does Lisa wear to protect her skin?","options":["Sunglasses","A pink hat","A red sweater","Yellow shoes"],"answer":"A pink hat","explanation":"Lisa lleva un sombrero rosa para proteger su piel clara del sol, según el texto."},{"question":"Which of the following is TRUE about Tom?","options":["He has curly hair.","He has blue eyes.","He prefers wearing sunglasses.","He likes wearing a pink hat."],"answer":"He prefers wearing sunglasses.","explanation":"El texto indica que Tom prefiere usar gafas de sol."},{"question":"What does Lisa think about Tom?","options":["He is funny.","He is handsome.","He is shy.","He is tall."],"answer":"He is handsome.","explanation":"Lisa piensa que Tom es guapo porque tiene una sonrisa agradable y un rostro amable."},{"question":"What does Tom say about Lisa?","options":["She is beautiful.","She is shy.","She is serious.","She is tall."],"answer":"She is beautiful.","explanation":"Tom dice que Lisa es hermosa porque siempre se ve feliz y alegre."},{"question":"Lisa and Tom like going to the park on ____ days.","options":["cloudy","rainy","sunny","cold"],"answer":"sunny","explanation":"Ellos disfrutan ir al parque en días soleados, como menciona el texto."},{"question":"TRUE or FALSE: Lisa has got green eyes.","answer":"FALSE","explanation":"Lisa tiene los ojos azules, no verdes."},{"question":"What is NOT mentioned about Tom?","options":["He has dark brown eyes.","He wears a red sweater.","He has a nice smile.","He has short hair."],"answer":"He wears a red sweater.","explanation":"El texto no menciona que Tom lleve un suéter rojo. Se menciona que Lisa tiene un suéter rojo."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex40',
  'a2-m1-l4',
  40,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Énfasis en el orden de adjetivos y sustantivos$_$,
  $_${"title":"Práctica de pronunciación: Énfasis en el orden de adjetivos y sustantivos","instructions":"Practica la pronunciación de las siguientes frases en inglés, prestando atención al orden de los adjetivos y sustantivos. Lee cada oración en voz alta y utiliza las transcripciones fonéticas para ayudarte.","targetSentences":[{"englishSentence":"She has got blue eyes and long hair.","spanishTranslation":"Ella tiene ojos azules y pelo largo.","phoneticTranscription":"/bluː/ /aɪz/ /ænd/ /lɔːŋ/ /heə/","audioModelUrl":"https://example.com/audio1","pronunciationTips":"Asegúrate de enfatizar el adjetivo antes del sustantivo, como 'blue' antes de 'eyes'.","focusPoints":"Enfatizar el adjetivo antes del sustantivo.","commonMistakes":"Colocar el sustantivo antes del adjetivo."},{"englishSentence":"The tall man is wearing a red shirt.","spanishTranslation":"El hombre alto lleva una camisa roja.","phoneticTranscription":"/tɔːl/ /mæn/ /rɛd/ /ʃɜːt/","audioModelUrl":"https://example.com/audio2","pronunciationTips":"Pronuncia claramente 'tall' y 'red' antes de sus respectivos sustantivos.","focusPoints":"Pronunciación de 'tall' y 'red' antes de los nombres.","commonMistakes":"Olvidar enfatizar los adjetivos."},{"englishSentence":"They have got a small, cozy room.","spanishTranslation":"Ellos tienen una habitación pequeña y acogedora.","phoneticTranscription":"/smɔːl/ /ˈkəʊzi/ /ruːm/","audioModelUrl":"https://example.com/audio3","pronunciationTips":"El énfasis debe estar en 'small' y 'cozy'.","focusPoints":"Claridad en la pronunciación de múltiples adjetivos.","commonMistakes":"No dejar clara la pausa entre los adjetivos."},{"englishSentence":"He is wearing a black jacket and white shoes.","spanishTranslation":"Él lleva una chaqueta negra y zapatos blancos.","phoneticTranscription":"/blæk/ /ˈʤækɪt/ /waɪt/ /ʃuːz/","audioModelUrl":"https://example.com/audio4","pronunciationTips":"Asegúrate de que 'black' y 'white' sean claramente audibles antes de los sustantivos.","focusPoints":"Distinción clara entre colores y objetos.","commonMistakes":"Confusión entre adjetivo y sustantivo."},{"englishSentence":"The children have got bright smiles.","spanishTranslation":"Los niños tienen sonrisas brillantes.","phoneticTranscription":"/braɪt/ /smaɪlz/","audioModelUrl":"https://example.com/audio5","pronunciationTips":"Pronuncia 'bright' con claridad antes de 'smiles'.","focusPoints":"Enfatizar adjetivos descriptivos.","commonMistakes":"No enfatizar suficientemente el adjetivo."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex41',
  'a2-m1-l4',
  41,
  'multiple-choice',
  $_$Describing Appearance: People and Physical Description$_$,
  $_${"title":"Describing Appearance: People and Physical Description","instructions":"Choose the correct answer for each question. Focus on using have got, adjective + noun order, and vocabulary related to colors, hair, and eyes.","questions":[{"id":"q1","question":"What color eyes has she got?","options":["A. She has got blue eyes.","B. She has got eyes blue.","C. She has blue got eyes.","D. She eyes has blue got."],"correctAnswer":"A","explanation":"La estructura correcta en inglés es 'have got' seguido de 'adjective + noun'. Aquí, 'blue' es el adjetivo y 'eyes' es el sustantivo."},{"id":"q2","question":"He has got ___ hair.","options":["A. black short","B. short black","C. blackly short","D. shortly black"],"correctAnswer":"B","explanation":"La correcta secuencia es 'adjective + noun', por lo tanto 'short black hair' es la estructura correcta."},{"id":"q3","question":"They have got ___ eyes.","options":["A. brown big","B. big brown","C. bigly brown","D. brownly big"],"correctAnswer":"B","explanation":"El orden correcto es 'adjective + noun', así que 'big brown eyes' es la respuesta correcta."},{"id":"q4","question":"My sister has got ___ hair.","options":["A. curly long","B. long curly","C. long curl","D. curl long"],"correctAnswer":"B","explanation":"En inglés, usamos 'adjective + noun', así que 'long curly hair' es la forma correcta de describirlo."},{"id":"q5","question":"What kind of hair has he got?","options":["A. He has got blonde short hair.","B. He has got hair short blonde.","C. He short blonde has got hair.","D. He has blonde got short hair."],"correctAnswer":"A","explanation":"La estructura correcta es 'have got' seguido de 'adjective + noun'. Aquí, 'blonde' y 'short' son adjetivos que describen 'hair'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex42',
  'a2-m1-l4',
  42,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"She ____ (have got) a beautiful blue dress.","acceptableAnswers":["has got"],"explanation":"Usamos 'has got' para describir que alguien posee algo. En este caso, describimos la ropa que tiene.","points":1},{"sentence":"He ____ (have got) short black hair.","acceptableAnswers":["has got"],"explanation":"Usamos 'has got' para describir características físicas como el tipo y color del cabello.","points":1},{"sentence":"They ____ (have got) green eyes and a friendly smile.","acceptableAnswers":["have got"],"explanation":"Se usa 'have got' para describir características físicas y expresiones faciales.","points":1},{"sentence":"Maria ____ (have got) a tall brother with brown hair.","acceptableAnswers":["has got"],"explanation":"Utilizamos 'has got' para describir relaciones familiares y características físicas.","points":1},{"sentence":"The little boy ____ (have got) a red bicycle.","acceptableAnswers":["has got"],"explanation":"Empleamos 'has got' para hablar de posesiones, en este caso, el niño tiene una bicicleta.","points":1},{"sentence":"You ____ (have got) a new pair of blue shoes.","acceptableAnswers":["have got"],"explanation":"Se utiliza 'have got' para describir posesiones personales, como los zapatos nuevos.","points":1},{"sentence":"My grandparents ____ (have got) a big garden with many flowers.","acceptableAnswers":["have got"],"explanation":"Usamos 'have got' para describir posesiones que son grandes o notables, como un jardín.","points":1},{"sentence":"The girl ____ (have got) curly hair and a small nose.","acceptableAnswers":["has got"],"explanation":"Utilizamos 'has got' para describir características físicas detalladas, como el tipo de cabello y la forma de la nariz.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex43',
  'a2-m1-l4',
  43,
  'sentence-building',
  $_$Exercise$_$,
  $_${"exercises":[{"id":1,"words":["has","eyes","blue","Sarah","."],"correctOrder":["Sarah","has","blue","eyes","."],"translation":"Sarah tiene ojos azules.","hint":"Usa 'has' para describir lo que alguien tiene.","points":2},{"id":2,"words":["curly","got","hair","Tom","has","."],"correctOrder":["Tom","has","got","curly","hair","."],"translation":"Tom tiene el pelo rizado.","hint":"Recuerda el orden: 'has got' seguido del adjetivo y luego el sustantivo.","points":2},{"id":3,"words":["a","got","big","dog","They","have","."],"correctOrder":["They","have","got","a","big","dog","."],"translation":"Ellos tienen un perro grande.","hint":"Empieza con 'They have got' y sigue la estructura adjetivo + sustantivo.","points":2},{"id":4,"words":["wears","Emma","a","red","dress","."],"correctOrder":["Emma","wears","a","red","dress","."],"translation":"Emma lleva un vestido rojo.","hint":"Usa 'wears' para describir ropa, luego sigue con adjetivo + sustantivo.","points":2},{"id":5,"words":["has","John","black","a","car","."],"correctOrder":["John","has","a","black","car","."],"translation":"John tiene un coche negro.","hint":"Empieza con 'John has' y sigue con adjetivo + sustantivo.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex44',
  'a2-m1-l4',
  44,
  'speaking-analysis',
  $_$Speaking Analysis: Describing Appearance$_$,
  $_${"title":"Speaking Analysis: Describing Appearance","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking to a friend about someone you met at a party.","question":"How would you describe their hair?","options":["A. They have got brown curly hair.","B. They have got curly brown hair.","C. They have got hair brown curly.","D. They have brown and hair curly."],"correctAnswer":"B","explanation":"En inglés, el orden de los adjetivos antes del sustantivo es primero la calidad (curly) y luego el color (brown).","points":2},{"id":"q2","type":"multiple-choice","scenario":"A friend is describing a new classmate to you.","question":"Which sentence describes their eye color correctly?","options":["A. She has got blue eyes.","B. She have got blue eyes.","C. She has blue got eyes.","D. She has got eyes blue."],"correctAnswer":"A","explanation":"La estructura correcta es 'has got' seguido del adjetivo (color) y luego el sustantivo (eyes).","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are explaining to your teacher what your favorite actor looks like.","question":"What is the correct way to describe their height?","options":["A. He has got tall.","B. He is got tall.","C. He is tall.","D. He tall is."],"correctAnswer":"C","explanation":"Para describir altura, se usa 'is' con el adjetivo 'tall'.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You're talking about a famous person with a friend.","question":"How do you describe their hair color?","options":["A. They have got a blonde hair.","B. They have got blonde hair.","C. They got blonde a hair.","D. They have blonde got hair."],"correctAnswer":"B","explanation":"No se usa 'a' con el color del cabello porque 'hair' es incontable en este contexto.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You see an interesting person on the street and describe them to your sibling.","question":"What is the best way to describe their eye shape?","options":["A. They have round eyes.","B. They have eyes round.","C. They eyes have round.","D. They round have eyes."],"correctAnswer":"A","explanation":"El adjetivo viene antes del sustantivo, así que 'round' antes de 'eyes'.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex45',
  'a2-m1-l4',
  45,
  'word-search',
  $_$Word Search: Describing Appearance$_$,
  $_${"title":"Word Search: Describing Appearance","instructions":"Find the hidden words in the grid","words":["BLONDE","HAIR","EYES","BROWN","TALL","SHORT","GLASSES","BEARD","YOUNG"],"gridSize":10,"clues":[{"word":"BLONDE","clue":"Color de cabello claro"},{"word":"HAIR","clue":"Lo que crece en tu cabeza"},{"word":"EYES","clue":"Usados para ver"},{"word":"BROWN","clue":"Color de ojos común"},{"word":"TALL","clue":"Alguien que es alto"},{"word":"SHORT","clue":"Lo contrario de alto"},{"word":"GLASSES","clue":"Usados para ver mejor"},{"word":"BEARD","clue":"Pelo en la cara de un hombre"},{"word":"YOUNG","clue":"Lo opuesto de viejo"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex46',
  'a2-m1-l4',
  46,
  'crossword',
  $_$Crossword: Describing Appearance$_$,
  $_${"title":"Crossword: Describing Appearance","instructions":"Complete the crossword puzzle","items":[{"word":"BROWN","clue":"The color of chocolate. (Color del chocolate)","direction":"across","row":0,"col":0},{"word":"BLOND","clue":"Hair color that is light and yellow. (Color de pelo que es claro y amarillo)","direction":"down","row":0,"col":3},{"word":"LONG","clue":"Not short, usually describes hair. (No corto, generalmente describe el pelo)","direction":"across","row":1,"col":2},{"word":"BEARD","clue":"Hair on the chin and cheeks of a man. (Pelo en la barbilla y mejillas de un hombre)","direction":"down","row":1,"col":6},{"word":"BLUE","clue":"Color of the sky on a clear day. (Color del cielo en un día despejado)","direction":"across","row":3,"col":1},{"word":"TALL","clue":"Opposite of short, often used for height. (Opuesto de bajo, a menudo usado para altura)","direction":"down","row":3,"col":4},{"word":"GREEN","clue":"Color of grass. (Color del césped)","direction":"across","row":5,"col":0},{"word":"CURLY","clue":"Describes hair with lots of curves or waves. (Describe el pelo con muchas curvas u ondas)","direction":"across","row":6,"col":3}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex47',
  'a2-m1-l4',
  47,
  'reading-comprehension',
  $_$Describing Appearance$_$,
  $_${"title":"Describing Appearance","textPassage":"Anna is a young girl who has got long brown hair and big green eyes. She usually wears a blue jacket and black shoes. Her best friend, Jake, has got short blond hair and dark brown eyes. They like to play in the park every Saturday. Anna's brother, Tom, has got curly black hair and small blue eyes. He always wears a red cap and white sneakers. They all enjoy going to the beach, where Anna likes to collect shells, Jake plays soccer, and Tom builds sandcastles.","wordCount":84,"estimatedReadingTime":1,"questions":[{"question":"What color is Anna's hair?","options":["Blond","Black","Brown","Red"],"answer":"Brown","explanation":"Anna tiene el cabello largo y marrón, como se menciona en el texto."},{"question":"What does Jake like to do at the beach?","options":["Collect shells","Build sandcastles","Play soccer","Swim"],"answer":"Play soccer","explanation":"Jake juega al fútbol en la playa, según el pasaje."},{"question":"True or False: Tom always wears a red cap and white sneakers.","options":["True","False"],"answer":"True","explanation":"El texto dice que Tom siempre lleva una gorra roja y zapatillas blancas."},{"question":"Who has got small blue eyes?","options":["Anna","Jake","Tom","Anna's best friend"],"answer":"Tom","explanation":"Tom tiene ojos azules pequeños, como se describe en el texto."},{"question":"What is NOT mentioned about Anna?","options":["Her eye color","Her favorite activity at the beach","Her shoe color","Her height"],"answer":"Her height","explanation":"La altura de Anna no se menciona en el texto."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex48',
  'a2-m1-l4',
  48,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Enfoque en la Descripción de Apariencia$_$,
  $_${"title":"Práctica de Pronunciación: Enfoque en la Descripción de Apariencia","instructions":"Practica la pronunciación de las siguientes oraciones enfocándote en el orden de adjetivos y sustantivos. Asegúrate de prestar atención a la entonación y el énfasis correcto en las palabras clave.","targetSentences":[{"englishSentence":"She has got long brown hair.","spanishTranslation":"Ella tiene el pelo largo y castaño.","phoneticTranscription":"/ʃiː hæz ɡɒt lɒŋ braʊn heə/","audioModelUrl":"audio_placeholder_url_1","pronunciationTips":"Emphasize the beginning of each adjective and ensure a smooth flow between 'long' and 'brown'.","focusPoints":"Adjective order, clear 'h' sound in 'hair'.","commonMistakesToAvoid":"Avoid separating 'long' and 'brown' with pauses; they should be pronounced together smoothly."},{"englishSentence":"He has blue eyes.","spanishTranslation":"Él tiene ojos azules.","phoneticTranscription":"/hiː hæz bluː aɪz/","audioModelUrl":"audio_placeholder_url_2","pronunciationTips":"Make sure to pronounce the 's' at the end of 'eyes' clearly.","focusPoints":"Clear 's' sound, stress on 'blue'.","commonMistakesToAvoid":"Do not drop the 's' at the end of 'eyes'."},{"englishSentence":"My friend has got a small red notebook.","spanishTranslation":"Mi amigo tiene un cuaderno pequeño y rojo.","phoneticTranscription":"/maɪ frɛnd hæz ɡɒt ə smɔːl rɛd ˈnəʊtbʊk/","audioModelUrl":"audio_placeholder_url_3","pronunciationTips":"Focus on the 'r' sound in 'red' and ensure 'notebook' is pronounced as two distinct syllables.","focusPoints":"Adjective order, clear 'r' sound.","commonMistakesToAvoid":"Avoid merging 'red' and 'notebook' into one word; each should be distinct."},{"englishSentence":"They have got a tall green tree.","spanishTranslation":"Ellos tienen un árbol alto y verde.","phoneticTranscription":"/ðeɪ hæv ɡɒt ə tɔːl ɡriːn triː/","audioModelUrl":"audio_placeholder_url_4","pronunciationTips":"Ensure a clear 't' at the beginning of 'tall' and stress the 'g' sound in 'green'.","focusPoints":"Adjective order, clear 't' and 'g' sounds.","commonMistakesToAvoid":"Do not omit the 't' in 'tall'; it should be crisp and clear."},{"englishSentence":"The girl has bright blue eyes.","spanishTranslation":"La chica tiene ojos azul brillante.","phoneticTranscription":"/ðə ɡɜːl hæz braɪt bluː aɪz/","audioModelUrl":"audio_placeholder_url_5","pronunciationTips":"Pronounce 'bright' with emphasis on the initial 'br' blend and ensure 'eyes' is pronounced with a clear 's'.","focusPoints":"Adjective order, 'br' blend in 'bright'.","commonMistakesToAvoid":"Avoid dropping the 's' sound in 'eyes'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex49',
  'a2-m1-l4',
  49,
  'multiple-choice',
  $_$Describing People: Appearance and Physical Features$_$,
  $_${"title":"Describing People: Appearance and Physical Features","instructions":"Choose the correct option to complete each sentence about appearance.","questions":[{"id":"q1","question":"Paul _____ blue eyes and short brown hair.","options":["A has got","B have got","C is","D are"],"correctAnswer":"A","explanation":"La opción correcta es 'has got' porque estamos hablando de la tercera persona del singular (Paul), y usamos 'has got' para describir características físicas en inglés."},{"id":"q2","question":"She is wearing a _____ dress today.","options":["A red beautiful","B beautiful red","C red beautifully","D beautifully red"],"correctAnswer":"B","explanation":"En inglés, el orden correcto de los adjetivos es primero el adjetivo de opinión, luego el adjetivo de color. Por eso, 'beautiful red' es la opción correcta."},{"id":"q3","question":"Tommy _____ tall and slim.","options":["A have","B has","C is","D are"],"correctAnswer":"C","explanation":"La opción correcta es 'is' porque estamos utilizando el verbo 'to be' para describir el aspecto físico de Tommy."},{"id":"q4","question":"They _____ got curly hair.","options":["A is","B has","C have","D are"],"correctAnswer":"C","explanation":"La opción correcta es 'have' porque 'they' es plural, y usamos 'have got' cuando describimos características en plural."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l4-ex50',
  'a2-m1-l4',
  50,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"How ______ apples do you want from the store?","options":["much","many"],"answer":"many","explanation":"Usamos 'how many' con sustantivos contables en plural como 'apples'.","points":1},{"sentence":"You ______ go to the doctor if you feel sick.","options":["should","shouldn't"],"answer":"should","explanation":"Usamos 'should' para dar consejos sobre lo que es mejor hacer.","points":1},{"sentence":"There isn't ______ milk left in the fridge.","options":["many","much"],"answer":"much","explanation":"Usamos 'much' con sustantivos incontables como 'milk'.","points":1},{"sentence":"This soup is too hot, I can't eat it ______.","options":["enough","yet"],"answer":"yet","explanation":"Usamos 'yet' para indicar que algo no se ha completado, pero se espera que ocurra.","points":1},{"sentence":"We are going to visit our grandparents next weekend.","options":["should","going to"],"answer":"going to","explanation":"Usamos 'going to' para expresar planes o intenciones futuras.","points":1},{"sentence":"I have to finish my homework before dinner.","options":["have to","shouldn't"],"answer":"have to","explanation":"Usamos 'have to' para expresar necesidad u obligación.","points":1},{"sentence":"Do we have ______ time to finish the project?","options":["enough","much"],"answer":"enough","explanation":"Usamos 'enough' para indicar suficiencia en cantidad.","points":1},{"sentence":"He is ______ shy to speak in front of the class.","options":["too","enough"],"answer":"too","explanation":"Usamos 'too' para indicar un exceso que impide hacer algo.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;

