INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex1',
  'a2-m1-l6',
  1,
  'multiple-choice',
  $_$Review: My World - Grammar and Vocabulary Exercise$_$,
  $_${"title":"Review: My World - Grammar and Vocabulary Exercise","instructions":"Choose the correct answer for each question. All explanations are in Spanish.","questions":[{"id":"q1","question":"Which sentence uses the present continuous correctly for future plans?","options":["A) I travel to Spain next week.","B) I am traveling to Spain next week.","C) I traveled to Spain next week.","D) I will travel to Spain next week."],"correctAnswer":"B","explanation":"La opción B usa el presente continuo 'am traveling' para indicar un plan futuro. Es la forma correcta para expresar planes que ya están decididos."},{"id":"q2","question":"Choose the correct quantifier: There isn't _____ milk in the fridge.","options":["A) some","B) any","C) much","D) many"],"correctAnswer":"B","explanation":"Usamos 'any' en oraciones negativas con sustantivos incontables como 'milk'."},{"id":"q3","question":"Which sentence correctly uses a superlative adjective?","options":["A) This is the more interesting movie.","B) This is the interestingest movie.","C) This is the most interesting movie.","D) This is the interesting movie."],"correctAnswer":"C","explanation":"Se usa 'the most interesting' para formar el superlativo de adjetivos largos, como 'interesting'."},{"id":"q4","question":"Complete the sentence: I would like _____ a new book.","options":["A) to buy","B) buy","C) buying","D) bought"],"correctAnswer":"A","explanation":"Después de 'would like', usamos 'to' + infinitivo, como en 'to buy', para expresar deseos."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex2',
  'a2-m1-l6',
  2,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"There are ____ apples in the basket. We can make a pie.","options":["some","any","much"],"answer":"some","explanation":"Utilizamos 'some' en oraciones afirmativas cuando hablamos de cantidades no especificadas pero suficientes.","points":1},{"sentence":"You ____ eat too much candy. It's not good for you.","options":["must","mustn't","can"],"answer":"mustn't","explanation":"'Mustn't' se usa para indicar una prohibición, algo que no está permitido o que no es recomendable hacer.","points":1},{"sentence":"I am ____ going to the park this afternoon.","options":["going to","will","must"],"answer":"going to","explanation":"'Going to' se utiliza para hablar de planes o intenciones futuras.","points":1},{"sentence":"How ____ sugar do you need for the cake?","options":["much","many","a lot of"],"answer":"much","explanation":"Usamos 'much' con sustantivos incontables en preguntas y oraciones negativas.","points":1},{"sentence":"It is ____ cold to go swimming today. Let's stay inside.","options":["too","enough"],"answer":"too","explanation":"'Too' se usa para indicar un exceso, algo más de lo deseado o necesario.","points":1},{"sentence":"You ____ drink more water. It's good for your health.","options":["should","shouldn't","mustn't"],"answer":"should","explanation":"'Should' se usa para dar consejos o sugerencias.","points":1},{"sentence":"We don't have ____ milk left. Can you buy some?","options":["any","some","many"],"answer":"any","explanation":"'Any' se usa en oraciones negativas y preguntas para referirse a cantidades no especificadas.","points":1},{"sentence":"They have ____ friends in the city. They often visit them.","options":["a lot of","many","few"],"answer":"a lot of","explanation":"'A lot of' se usa para indicar una gran cantidad de algo, más informal que 'many'.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex3',
  'a2-m1-l6',
  3,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["must","You","your","homework","complete","."],"correctOrder":["You","must","complete","your","homework","."],"translation":"Debes completar tu tarea.","hint":"La estructura es sujeto + must + verbo en infinitivo + complemento.","points":2},{"id":2,"words":["How","many","apples","are","in","the","basket","?"],"correctOrder":["How","many","apples","are","in","the","basket","?"],"translation":"¿Cuántas manzanas hay en la cesta?","hint":"Utiliza 'How many' para preguntar por la cantidad de sustantivos contables.","points":2},{"id":3,"words":["We","are","going","to","watch","a","movie","tonight","."],"correctOrder":["We","are","going","to","watch","a","movie","tonight","."],"translation":"Vamos a ver una película esta noche.","hint":"La estructura es sujeto + be + going to + verbo en infinitivo para planes futuros.","points":2},{"id":4,"words":["There","is","some","milk","in","the","fridge","."],"correctOrder":["There","is","some","milk","in","the","fridge","."],"translation":"Hay algo de leche en la nevera.","hint":"Usa 'some' para cantidades no contables en afirmaciones.","points":2},{"id":5,"words":["Will","it","rain","tomorrow","?"],"correctOrder":["Will","it","rain","tomorrow","?"],"translation":"¿Lloverá mañana?","hint":"La estructura es will + sujeto + verbo en infinitivo para predicciones.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex4',
  'a2-m1-l6',
  4,
  'speaking-analysis',
  $_$Speaking Analysis: My World$_$,
  $_${"title":"Speaking Analysis: My World","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are at the bank and you need to know where to find the ATM.","question":"What should you ask?","options":["Where is the closest ATM, please?","Do you know where the restroom is?","I need to open a new account.","Can you tell me the time?"],"correctAnswer":"A","explanation":"La opción A es la pregunta correcta porque pregunta específicamente sobre la ubicación del cajero automático.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are talking to a friend about your vacation plans.","question":"Which sentence uses the present perfect correctly?","options":["I have been to Paris last year.","I have visited Rome since two years.","I have visited London many times.","I have go to New York tomorrow."],"correctAnswer":"C","explanation":"La opción C es correcta porque usa el present perfect para referirse a una acción que ha ocurrido varias veces en el pasado.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You want to suggest a movie to a friend.","question":"How would you recommend it?","options":["You should watch this movie. It's very exciting.","You must not watch this movie. It's very boring.","You have to watch this movie. It's very interesting.","You could watch this movie. It's very long."],"correctAnswer":"A","explanation":"La opción A es la más adecuada porque sugiere ver la película y utiliza un adjetivo positivo para describirla.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You meet someone for the first time at a party.","question":"What is a polite way to start a conversation?","options":["Hey, what's up?","Hello, nice to meet you.","Where have you been?","Do you know where I can get a drink?"],"correctAnswer":"B","explanation":"La opción B es la más educada para iniciar una conversación con alguien que acabas de conocer.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are at a restaurant and you want to order food.","question":"What would you say to the waiter?","options":["Can I have the menu, please?","Do you have coffee?","I need a table!","Is this place open?"],"correctAnswer":"A","explanation":"La opción A es la más adecuada porque estás pidiendo educadamente el menú para poder ordenar.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You want to describe your favorite hobby to a friend.","question":"Which sentence is correct?","options":["I likes playing guitar on weekends.","I like to play guitar on weekends.","I am like to play guitar on weekends.","I liked play guitar on weekends."],"correctAnswer":"B","explanation":"La opción B es correcta porque usa 'like to play' para describir un gusto o preferencia.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex5',
  'a2-m1-l6',
  5,
  'word-search',
  $_$Word Search: My World$_$,
  $_${"title":"Word Search: My World","instructions":"Find the hidden words in the grid","words":["RAIN","SUNNY","CLOUDY","COLD","HOT","SPRING","SUMMER","AUTUMN","WINTER","WINDY"],"gridSize":10,"clues":[{"word":"RAIN","clue":"Water falling from the sky"},{"word":"SUNNY","clue":"Bright and clear weather"},{"word":"CLOUDY","clue":"Covered with clouds"},{"word":"COLD","clue":"Low temperature"},{"word":"HOT","clue":"High temperature"},{"word":"SPRING","clue":"Season after winter"},{"word":"SUMMER","clue":"Warmest season"},{"word":"AUTUMN","clue":"Season of falling leaves"},{"word":"WINTER","clue":"Coldest season"},{"word":"WINDY","clue":"With a lot of air movement"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex6',
  'a2-m1-l6',
  6,
  'crossword',
  $_$Crossword: Review My World$_$,
  $_${"title":"Crossword: Review My World","instructions":"Complete the crossword puzzle","items":[{"word":"APPLE","clue":"A red or green fruit","direction":"across","row":0,"col":0},{"word":"DOCTOR","clue":"A person who helps you when you are sick","direction":"down","row":0,"col":1},{"word":"MARKET","clue":"A place where you can buy fresh fruits and vegetables","direction":"across","row":2,"col":0},{"word":"BANK","clue":"A place where you keep your money","direction":"down","row":1,"col":2},{"word":"HEALTHY","clue":"Feeling good and strong","direction":"across","row":4,"col":0},{"word":"CHEAP","clue":"Not expensive","direction":"down","row":1,"col":4},{"word":"MONEY","clue":"What you need to buy things","direction":"across","row":6,"col":0},{"word":"STORE","clue":"Another word for shop","direction":"down","row":2,"col":5}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex7',
  'a2-m1-l6',
  7,
  'reading-comprehension',
  $_$A Day at the Restaurant$_$,
  $_${"title":"A Day at the Restaurant","textPassage":"Last Saturday, Maria and her friends went to a new restaurant in town. It is called 'The Green Table'. They wanted to try something different. Maria ordered a big salad with tomatoes, cucumbers, and lettuce. Her friend Carlos ordered a cheese and tomato sandwich. It was very tasty but also a bit expensive. Their other friend Ana chose a bowl of soup. The food was delicious, and everyone enjoyed the meal. Maria thinks they should visit this restaurant again. After lunch, they walked around the park and talked about their plans for the summer.","wordCount":91,"estimatedReadingTime":1,"questions":[{"question":"What is the main idea of the passage?","options":["Maria and her friends had a picnic.","Maria and her friends went to a new restaurant.","Maria cooked a meal for her friends.","Maria and her friends went shopping."],"answer":"Maria and her friends went to a new restaurant.","explanation":"La idea principal es que María y sus amigos visitaron un nuevo restaurante para probar algo diferente."},{"question":"What did Maria order at the restaurant?","options":["A cheese and tomato sandwich.","A bowl of soup.","A big salad.","A pizza."],"answer":"A big salad.","explanation":"María pidió una ensalada grande con tomates, pepinos y lechuga."},{"question":"True or False: Carlos thought the sandwich was cheap.","options":["True","False"],"answer":"False","explanation":"Carlos pensó que el sándwich estaba sabroso pero un poco caro."},{"question":"What can be inferred about 'The Green Table'?","options":["It only serves vegetarian food.","It is a popular restaurant.","It has a variety of dishes.","It is located in the countryside."],"answer":"It has a variety of dishes.","explanation":"Podemos inferir que el restaurante tiene una variedad de platos porque los amigos pidieron diferentes tipos de comida."},{"question":"What did the friends do after their meal?","options":["They went to see a movie.","They walked around the park.","They visited another restaurant.","They went home."],"answer":"They walked around the park.","explanation":"Después de la comida, caminaron por el parque y hablaron sobre sus planes para el verano."},{"question":"Why does Maria think they should visit the restaurant again?","options":["The food was delicious.","It was very cheap.","It is close to her house.","The service was slow."],"answer":"The food was delicious.","explanation":"María piensa que deberían volver al restaurante porque la comida fue deliciosa."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex8',
  'a2-m1-l6',
  8,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Enfocarse en el estrés de las palabras$_$,
  $_${"title":"Práctica de pronunciación: Enfocarse en el estrés de las palabras","instructions":"Lee las siguientes oraciones en inglés y presta atención al estrés en las palabras resaltadas. Practica la pronunciación y usa los consejos proporcionados. Las transcripciones fonéticas te ayudarán a identificar la pronunciación correcta.","targetSentences":[{"englishSentence":"The young boy is very happy today.","spanishTranslation":"El niño joven está muy feliz hoy.","phoneticTranscription":"jʌŋ bɔɪ","audioModelURL":"http://example.com/audio1","pronunciationTips":"Recuerda que 'young' tiene un sonido suave al inicio, y el estrés principal está en la primera sílaba.","focusPoints":"Enfócate en la palabra 'young'.","commonMistakesToAvoid":"No pronuncies 'young' como 'yong'; la 'u' es importante."},{"englishSentence":"I have to finish my homework.","spanishTranslation":"Tengo que terminar mi tarea.","phoneticTranscription":"ˈfɪnɪʃ","audioModelURL":"http://example.com/audio2","pronunciationTips":"El estrés está en la primera sílaba de 'finish'. Asegúrate de no alargar la segunda sílaba.","focusPoints":"La palabra 'finish'.","commonMistakesToAvoid":"No alargues la segunda sílaba de 'finish'."},{"englishSentence":"She always eats breakfast at home.","spanishTranslation":"Ella siempre desayuna en casa.","phoneticTranscription":"ˈɔːlweɪz","audioModelURL":"http://example.com/audio3","pronunciationTips":"El estrés está en la primera sílaba de 'always'.","focusPoints":"La palabra 'always'.","commonMistakesToAvoid":"No pongas el estrés en la segunda sílaba."},{"englishSentence":"We should go to the new restaurant.","spanishTranslation":"Deberíamos ir al nuevo restaurante.","phoneticTranscription":"ˈrɛstərɒnt","audioModelURL":"http://example.com/audio4","pronunciationTips":"'Restaurant' tiene el estrés en la primera sílaba.","focusPoints":"La palabra 'restaurant'.","commonMistakesToAvoid":"Asegúrate de no pronunciar todas las sílabas con el mismo estrés."},{"englishSentence":"My sister likes to swim in the pool.","spanishTranslation":"A mi hermana le gusta nadar en la piscina.","phoneticTranscription":"ˈsɪstər","audioModelURL":"http://example.com/audio5","pronunciationTips":"El estrés de 'sister' está en la primera sílaba.","focusPoints":"La palabra 'sister'.","commonMistakesToAvoid":"No pongas el estrés en la segunda sílaba de 'sister'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex9',
  'a2-m1-l6',
  9,
  'multiple-choice',
  $_$A2 Level: Grammar and Vocabulary Review$_$,
  $_${"title":"A2 Level: Grammar and Vocabulary Review","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"Which sentence uses the superlative correctly?","options":["A. This is the most big apple in the basket.","B. This is the bigger apple in the basket.","C. This is the biggest apple in the basket.","D. This is the more big apple in the basket."],"correctAnswer":"C","explanation":"La opción C es correcta porque 'biggest' es la forma superlativa de 'big'. Se usa 'the biggest' para describir algo que es más grande que todos los demás."},{"id":"q2","question":"How many apples are there on the table?","options":["A. There is much apples.","B. There are many apples.","C. There is many apples.","D. There are much apples."],"correctAnswer":"B","explanation":"La opción B es correcta porque 'many' se utiliza con sustantivos contables en plural, como 'apples'."},{"id":"q3","question":"We are going to the park this afternoon. Which is a correct response?","options":["A. I will go with you!","B. I am go with you!","C. I going with you!","D. I am went with you!"],"correctAnswer":"A","explanation":"La opción A es correcta porque 'will' se usa para decisiones espontáneas, y la estructura correcta es 'I will go'."},{"id":"q4","question":"Which sentence uses 'going to' correctly?","options":["A. She going to visit her grandma.","B. She going visit her grandma.","C. She is going to visit her grandma.","D. She will going to visit her grandma."],"correctAnswer":"C","explanation":"La opción C es correcta porque 'is going to' se usa para hablar de planes futuros, y la estructura correcta es 'She is going to visit'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex10',
  'a2-m1-l6',
  10,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I ______ (have to) go to school every day. It's important.","acceptable_answers":["have to"],"explanation":"En inglés, 'have to' se usa para expresar obligación. Aquí se indica que es necesario ir a la escuela todos los días.","points":1},{"sentence":"She is ______ (really) happy with her new job.","acceptable_answers":["really"],"explanation":"Los adverbios de grado como 'really' se usan para enfatizar adjetivos. 'Really' significa 'realmente' o 'muy'.","points":1},{"sentence":"This bag is ______ (too) heavy for me to carry.","acceptable_answers":["too"],"explanation":"Usamos 'too' para indicar que algo es más de lo que se desea o necesita. Aquí, la bolsa es demasiado pesada.","points":1},{"sentence":"We are ______ (going to) visit our grandparents this weekend.","acceptable_answers":["going to"],"explanation":"'Going to' se utiliza para hablar de planes o intenciones futuras. Aquí indica que planean visitar a los abuelos.","points":1},{"sentence":"______ (How many) apples are there in the basket?","acceptable_answers":["How many"],"explanation":"Usamos 'How many' para preguntar por la cantidad de cosas contables. Aquí preguntamos cuántas manzanas hay.","points":1},{"sentence":"You ______ (don't have to) bring your own lunch. We will provide it.","acceptable_answers":["don't have to"],"explanation":"'Don't have to' se usa para indicar que algo no es necesario. Aquí no es necesario traer su almuerzo.","points":1},{"sentence":"He is not old ______ (enough) to drive a car.","acceptable_answers":["enough"],"explanation":"'Enough' se usa después de un adjetivo para indicar suficiencia. Aquí indica que no es suficientemente viejo para conducir.","points":1},{"sentence":"______ (How much) water do you drink every day?","acceptable_answers":["How much"],"explanation":"Usamos 'How much' para preguntar por la cantidad de cosas no contables. Aquí se pregunta cuánta agua bebes.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex11',
  'a2-m1-l6',
  11,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["is","very","this","movie","interesting","."],"correctOrder":["This","movie","is","very","interesting","."],"translation":"Esta película es muy interesante.","hint":"Usa 'very' para describir el grado de interés.","points":2},{"id":2,"words":["like","ride","to","would","I","a","bike","."],"correctOrder":["I","would","like","to","ride","a","bike","."],"translation":"Me gustaría montar en bicicleta.","hint":"Usa 'would like to' para expresar un deseo.","points":2},{"id":3,"words":["should","you","drink","more","water","."],"correctOrder":["You","should","drink","more","water","."],"translation":"Deberías beber más agua.","hint":"Usa 'should' para dar un consejo.","points":2},{"id":4,"words":["apples","how","many","there","are","?"],"correctOrder":["How","many","apples","are","there","?"],"translation":"¿Cuántas manzanas hay?","hint":"Usa 'How many' para preguntar sobre cantidad.","points":2},{"id":5,"words":["this","is","coffee","better","than","tea","."],"correctOrder":["This","coffee","is","better","than","tea","."],"translation":"Este café es mejor que el té.","hint":"Usa un adjetivo comparativo para comparar.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex12',
  'a2-m1-l6',
  12,
  'speaking-analysis',
  $_$Speaking Analysis: My World - A2 Level$_$,
  $_${"title":"Speaking Analysis: My World - A2 Level","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are at a café with a friend, and you want to ask if they like their coffee.","question":"What do you say?","options":["A: Do you enjoy your coffee?","B: Are you liking the coffee?","C: Is the coffee enjoying you?","D: Do you like the coffee?"],"correctAnswer":"D","explanation":"La pregunta correcta es 'Do you like the coffee?' porque en inglés usamos 'like' para preguntar si algo es del agrado de alguien.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are describing a past holiday to a friend.","question":"Which sentence is correct?","options":["A: I was go to the beach every day.","B: I went to the beach every day.","C: I gone to the beach every day.","D: I going to the beach every day."],"correctAnswer":"B","explanation":"Usamos 'went' para hablar de acciones pasadas en inglés, como en 'I went to the beach every day.'","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are talking to a friend about plans for the weekend.","question":"What do you ask?","options":["A: What you will do this weekend?","B: What are you doing this weekend?","C: What you doing this weekend?","D: What are you do this weekend?"],"correctAnswer":"B","explanation":"La pregunta correcta es 'What are you doing this weekend?' porque usamos el presente continuo para hablar de planes futuros.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are in a shop and want to ask the price of an item.","question":"What do you say?","options":["A: How much is this?","B: What is cost this?","C: How many is this?","D: What is the price this?"],"correctAnswer":"A","explanation":"La forma correcta de preguntar el precio es 'How much is this?' en inglés.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are in a restaurant and want to compliment the meal.","question":"What do you say to the waiter?","options":["A: The food was very delicious.","B: The food is much delicious.","C: The food are very delicious.","D: The food was much delicious."],"correctAnswer":"A","explanation":"La expresión correcta es 'The food was very delicious.' porque usamos 'was' para referirnos a algo en el pasado y 'very' para enfatizar.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are telling a friend about your new hobby.","question":"Which sentence is correct?","options":["A: I am start painting.","B: I start painting.","C: I have started painting.","D: I starting painting."],"correctAnswer":"C","explanation":"Usamos 'I have started painting.' para hablar de una acción que comenzó en el pasado y tiene relevancia en el presente.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex13',
  'a2-m1-l6',
  13,
  'word-search',
  $_$Word Search: My World$_$,
  $_${"title":"Word Search: My World","instructions":"Find the hidden words in the grid","words":["EXPENSIVE","CHEAP","HEALTHY","MENU","BREAD","DOCTOR","PRICE","STORE","SHOP","FITNESS"],"gridSize":10,"clues":[{"word":"EXPENSIVE","clue":"Costing a lot of money."},{"word":"CHEAP","clue":"Not costing much."},{"word":"HEALTHY","clue":"Good for your body."},{"word":"MENU","clue":"A list of dishes available in a restaurant."},{"word":"BREAD","clue":"A common baked food."},{"word":"DOCTOR","clue":"A person who helps sick people."},{"word":"PRICE","clue":"The amount of money something costs."},{"word":"STORE","clue":"A place to buy things, another word for shop."},{"word":"SHOP","clue":"A place where goods are sold."},{"word":"FITNESS","clue":"The condition of being physically fit and healthy."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex14',
  'a2-m1-l6',
  14,
  'crossword',
  $_$Crossword: My World Review$_$,
  $_${"title":"Crossword: My World Review","instructions":"Complete the crossword puzzle","items":[{"word":"APPLE","clue":"A red or green fruit","direction":"across","row":0,"col":0},{"word":"THEATRE","clue":"A place to watch plays","direction":"down","row":0,"col":1},{"word":"MUSIC","clue":"You can listen to this at a concert","direction":"across","row":1,"col":3},{"word":"MOVIE","clue":"Another word for film","direction":"down","row":0,"col":5},{"word":"CHEAP","clue":"Not expensive","direction":"across","row":4,"col":0},{"word":"EXCITED","clue":"How you feel before a fun event","direction":"across","row":5,"col":1},{"word":"TICKET","clue":"You need this to enter a concert","direction":"down","row":0,"col":7},{"word":"GUITAR","clue":"A musical instrument with strings","direction":"across","row":7,"col":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex15',
  'a2-m1-l6',
  15,
  'reading-comprehension',
  $_$Review: My World - Basics$_$,
  $_${"title":"Review: My World - Basics","text_passage":"Maria is planning a dinner for her friends this Saturday. She wants to cook spaghetti because it is her favorite dish and it is not difficult to prepare. Maria thinks she should buy the ingredients on Friday after work. She also needs to clean the house and set the table nicely. Her friend, John, suggested that she make a salad as well. Maria agreed because it is a healthy option. She hopes everyone will enjoy the meal and have a good time together. Maria is excited because she hasn't seen her friends for a long time. She is planning to organize more dinners in the future.","wordCount":111,"estimatedReadingTime":2,"questions":[{"question":"What is Maria planning for Saturday?","options":["A picnic","A dinner","A movie night","A shopping trip"],"answer":"A dinner","explanation":"Maria está planeando una cena para sus amigos el sábado, como se menciona al principio del texto."},{"question":"Why does Maria decide to make spaghetti?","options":["It is her favorite dish","It is her friends' favorite","It is easy to cook","Both A and C"],"answer":"Both A and C","explanation":"Maria elige hacer espaguetis porque es su plato favorito y no es difícil de preparar, como se menciona en el texto."},{"question":"What does John suggest Maria should also prepare?","options":["Soup","Salad","Dessert","Bread"],"answer":"Salad","explanation":"John sugiere que Maria también haga una ensalada, lo cual ella acepta, como se menciona en el texto."},{"question":"How does Maria feel about seeing her friends?","options":["Worried","Excited","Nervous","Bored"],"answer":"Excited","explanation":"Maria está emocionada por ver a sus amigos porque no los ha visto en mucho tiempo, según el texto."},{"question":"Maria wants to organize more dinners in the future. True or False?","options":["True","False"],"answer":"True","explanation":"Maria tiene la intención de organizar más cenas en el futuro, como se menciona al final del texto."},{"question":"Which of the following is NOT mentioned as part of Maria's preparation?","options":["Cleaning the house","Setting the table","Buying flowers","Buying ingredients"],"answer":"Buying flowers","explanation":"Comprar flores no se menciona en el texto, mientras que las otras opciones sí forman parte de la preparación de Maria."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex16',
  'a2-m1-l6',
  16,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Intonation$_$,
  $_${"title":"Práctica de pronunciación: Intonation","instructions":"Practica la entonación en las oraciones en inglés. Lee cada oración en voz alta y presta atención a la entonación. Usa las transcripciones fonéticas para ayudarte y sigue los consejos de pronunciación.","targetSentences":[{"englishSentence":"Do you want to go shopping this weekend?","spanishTranslation":"¿Quieres ir de compras este fin de semana?","phoneticTranscription":"/dʒuː wɒnt tə ɡəʊ ˈʃɒpɪŋ ðɪs ˈwiːkˌɛnd/","audioModelURL":"https://example.com/audio1","pronunciationTips":"Sube ligeramente el tono al final para indicar que es una pregunta.","focusPoints":"Entonación ascendente al final de la pregunta.","commonMistakes":"No dejes el tono plano al final, ya que podría sonar como una declaración."},{"englishSentence":"It's a sunny day, so let's have a picnic.","spanishTranslation":"Es un día soleado, así que hagamos un picnic.","phoneticTranscription":"/ɪts ə ˈsʌni deɪ səʊ lɛts hæv ə ˈpɪknɪk/","audioModelURL":"https://example.com/audio2","pronunciationTips":"Mantén el tono alegre y sube un poco al final para expresar entusiasmo.","focusPoints":"Entonación que refleja entusiasmo y positividad.","commonMistakes":"Evita un tono monótono; debe reflejar excitación."},{"englishSentence":"I should buy a gift for my friend.","spanishTranslation":"Debería comprar un regalo para mi amigo.","phoneticTranscription":"/aɪ ʃʊd baɪ ə ɡɪft fɔː maɪ frɛnd/","audioModelURL":"https://example.com/audio3","pronunciationTips":"El tono debe ser neutro con una ligera caída al final.","focusPoints":"Entonación descendente al final de la declaración.","commonMistakes":"No termines con un tono ascendente, ya que no es una pregunta."},{"englishSentence":"Can you tell me where the nearest bank is?","spanishTranslation":"¿Puedes decirme dónde está el banco más cercano?","phoneticTranscription":"/kæn juː tɛl miː wɛər ðə ˈnɪərəst bæŋk ɪz/","audioModelURL":"https://example.com/audio4","pronunciationTips":"Asegúrate de elevar el tono al final, ya que es una pregunta.","focusPoints":"Entonación ascendente en la última palabra.","commonMistakes":"No uses un tono plano, ya que podría confundirse con una afirmación."},{"englishSentence":"The movie starts at 7 o'clock.","spanishTranslation":"La película comienza a las 7 en punto.","phoneticTranscription":"/ðə ˈmuːvi stɑːts æt ˈsɛvən əˈklɒk/","audioModelURL":"https://example.com/audio5","pronunciationTips":"Termina la oración con un tono descendente para indicar certeza.","focusPoints":"Entonación descendente en el final de la declaración.","commonMistakes":"No uses un tono ascendente, ya que puede sonar como una pregunta."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex17',
  'a2-m1-l6',
  17,
  'multiple-choice',
  $_$Review: My World - A2 Grammar and Vocabulary$_$,
  $_${"title":"Review: My World - A2 Grammar and Vocabulary","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"You mustn't talk during the movie. It's ____ to disturb others.","options":["A. important","B. very","C. quite","D. enough"],"correctAnswer":"B","explanation":"La opción correcta es 'very' porque estamos utilizando un adverbio de grado para intensificar 'importante'. 'Important' es un adjetivo, y los adverbios de grado como 'very' se usan para modificar adjetivos."},{"id":"q2","question":"Sarah is taller ____ her brother.","options":["A. than","B. then","C. as","D. so"],"correctAnswer":"A","explanation":"La opción correcta es 'than' porque estamos haciendo una comparación entre dos personas. 'Than' se usa en comparativos para comparar características entre dos elementos."},{"id":"q3","question":"I'm really ____ to visit the new museum next week.","options":["A. exciting","B. excited","C. excite","D. excitement"],"correctAnswer":"B","explanation":"La opción correcta es 'excited' porque se refiere a cómo se siente una persona en particular. 'Excited' es un adjetivo que describe el estado emocional de una persona."},{"id":"q4","question":"It's too hot today. We can't go outside ____ it's cooler.","options":["A. until","B. before","C. since","D. for"],"correctAnswer":"A","explanation":"La opción correcta es 'until' porque indica el momento en el que algo cambiará. Estamos diciendo que no podemos salir hasta que el clima sea más fresco."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex18',
  'a2-m1-l6',
  18,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"How ______ apples are there in the basket?","options":["much","many"],"answer":"many","explanation":"Usamos 'how many' para preguntar sobre la cantidad de sustantivos contables en plural, como 'apples'.","points":1},{"sentence":"This soup is ______ delicious. I would like some more.","options":["very","quite"],"answer":"very","explanation":"Usamos 'very' para intensificar adjetivos de una manera más fuerte, especialmente cuando algo es realmente bueno o malo.","points":1},{"sentence":"It is ______ book I have ever read.","options":["the most interesting","most interesting"],"answer":"the most interesting","explanation":"Usamos 'the most' antes de un adjetivo para formar el superlativo de adjetivos largos (de más de dos sílabas).","points":1},{"sentence":"There isn't ______ water left in the bottle.","options":["many","much"],"answer":"much","explanation":"Usamos 'much' con sustantivos incontables para referirnos a una cantidad grande.","points":1},{"sentence":"I think it ______ rain tomorrow.","options":["will","is going to"],"answer":"will","explanation":"Usamos 'will' para hacer predicciones sobre el futuro basadas en nuestra intuición o creencias.","points":1},{"sentence":"She is ______ tired to go out tonight.","options":["too","enough"],"answer":"too","explanation":"Usamos 'too' para indicar un exceso, en este caso, que está demasiado cansada para salir.","points":1},{"sentence":"He has been working here ______ last year.","options":["since","for"],"answer":"since","explanation":"Usamos 'since' para referirnos al inicio de un período de tiempo.","points":1},{"sentence":"We ______ to leave at 8 o'clock to catch the train.","options":["have","must"],"answer":"have","explanation":"Usamos 'have to' para expresar obligación o necesidad, especialmente en situaciones planificadas.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex19',
  'a2-m1-l6',
  19,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["He","a","is","man","old","."],"correctOrder":["He","is","an","old","man","."],"translation":"Él es un hombre viejo.","hint":"Recuerda que los adjetivos van antes del sustantivo.","points":2},{"id":2,"words":["a","restaurant","cheap","is","This","."],"correctOrder":["This","is","a","cheap","restaurant","."],"translation":"Este es un restaurante barato.","hint":"Utiliza el adjetivo antes del sustantivo.","points":2},{"id":3,"words":["The","new","is","hotel","nice","."],"correctOrder":["The","new","hotel","is","nice","."],"translation":"El hotel nuevo es bonito.","hint":"El adjetivo 'new' describe al hotel.","points":2},{"id":4,"words":["would","visit","like","I","to","Paris","."],"correctOrder":["I","would","like","to","visit","Paris","."],"translation":"Me gustaría visitar París.","hint":"Usa 'would like to' para expresar deseos.","points":2},{"id":5,"words":["You","shouldn't","eat","so","much","sugar","."],"correctOrder":["You","shouldn't","eat","so","much","sugar","."],"translation":"No deberías comer tanto azúcar.","hint":"Usa 'should' para dar consejos.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex20',
  'a2-m1-l6',
  20,
  'speaking-analysis',
  $_$Speaking Analysis: My World - Basics$_$,
  $_${"title":"Speaking Analysis: My World - Basics","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are at a restaurant and the waiter asks, 'How is your meal?'","question":"What is the most appropriate response?","options":["A. It is very delicious.","B. I am very delicious.","C. It is very hunger.","D. I am very hunger."],"correctAnswer":"A","explanation":"La opción A es la correcta porque 'delicious' describe la comida y usamos 'it is' para hablar de algo no humano.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are planning to visit a new city. Your friend asks, 'What are your plans for the weekend?'","question":"Choose the most natural response.","options":["A. I am going to visit a new city.","B. I will going to visit a new city.","C. I are going to visit a new city.","D. I will to visit a new city."],"correctAnswer":"A","explanation":"La opción A es correcta porque usamos 'am going to' para planes futuros en inglés.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are at a café and your friend asks if you want another cup of coffee.","question":"What is the best way to politely refuse?","options":["A. No, I don't.","B. No, thank you.","C. No, I not want.","D. No, I want not."],"correctAnswer":"B","explanation":"La opción B es la más educada porque incluye 'thank you', que es una forma cortés de rechazar una oferta.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are telling a friend about a past holiday.","question":"What is the correct way to describe it?","options":["A. I go to the beach.","B. I went to the beach.","C. I going to the beach.","D. I goed to the beach."],"correctAnswer":"B","explanation":"La opción B es correcta porque 'went' es el pasado de 'go' y estamos hablando de un evento pasado.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You want to make a suggestion to your friend about visiting a museum.","question":"Which phrase is the most appropriate?","options":["A. We could visit the museum.","B. We should to visit the museum.","C. We must visit the museum.","D. We can to visit the museum."],"correctAnswer":"A","explanation":"La opción A es correcta porque 'could' se usa para sugerencias suaves en inglés.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex21',
  'a2-m1-l6',
  21,
  'word-search',
  $_$Word Search: My World$_$,
  $_${"title":"Word Search: My World","instructions":"Find the hidden words in the grid","words":["CHEAP","SMALL","TALL","INTERESTING","BORING","SURPRISED","TIRED","BEAUTIFUL"],"gridSize":10,"clues":[{"word":"CHEAP","clue":"Not expensive."},{"word":"SMALL","clue":"Opposite of big."},{"word":"TALL","clue":"Opposite of short."},{"word":"INTERESTING","clue":"Not boring."},{"word":"BORING","clue":"Not exciting or interesting."},{"word":"SURPRISED","clue":"Feeling when you did not expect something."},{"word":"TIRED","clue":"Needing rest or sleep."},{"word":"BEAUTIFUL","clue":"Very pleasing to look at."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex22',
  'a2-m1-l6',
  22,
  'crossword',
  $_$Crossword: My World Review$_$,
  $_${"title":"Crossword: My World Review","instructions":"Complete the crossword puzzle.","items":[{"word":"RAINY","clue":"Weather with a lot of water falling from the sky","direction":"across","row":0,"col":0},{"word":"COLD","clue":"When the temperature is low","direction":"down","row":0,"col":4},{"word":"HEALTHY","clue":"When someone is in good physical condition","direction":"across","row":2,"col":0},{"word":"SUPERMARKET","clue":"A place where you buy food and other products","direction":"down","row":0,"col":2},{"word":"SHOULD","clue":"A modal verb used to give advice","direction":"across","row":4,"col":0},{"word":"SUMMER","clue":"The warmest season of the year","direction":"down","row":2,"col":3},{"word":"DOCTOR","clue":"A person who helps you when you are sick","direction":"across","row":6,"col":0}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex23',
  'a2-m1-l6',
  23,
  'reading-comprehension',
  $_$A Day at the Market$_$,
  $_${"title":"A Day at the Market","textPassage":"Maria loves going to the market on Saturdays. She usually goes with her friend, Luis. They like to look at the colorful fruits and vegetables. Maria's favorite fruit is the strawberry because it is sweet and juicy. Luis prefers oranges because they are refreshing. They always buy some bread from the bakery. The bakery makes delicious bread every day. Sometimes, they also buy cheese to eat with the bread. After shopping, Maria and Luis like to sit in the park and enjoy their snacks. They watch people walking their dogs and children playing. It's a relaxing way to start the weekend.","wordCount":113,"estimatedReadingTime":2,"questions":[{"question":"What does Maria like about strawberries?","options":["They are colorful.","They are sweet and juicy.","They are cheap.","They are big."],"answer":"They are sweet and juicy.","explanation":"Maria prefiere las fresas porque son dulces y jugosas, como se menciona en el texto."},{"question":"True or False: Luis prefers apples over oranges.","options":["True","False"],"answer":"False","explanation":"Luis prefiere las naranjas, no las manzanas. Esto se menciona claramente en el texto."},{"question":"Where do Maria and Luis buy bread?","options":["From a supermarket","From a bakery","From a street vendor","From a café"],"answer":"From a bakery","explanation":"Ellos compran pan de una panadería, como se dice en el texto."},{"question":"What do Maria and Luis do after shopping?","options":["They go home.","They cook dinner.","They sit in the park.","They visit friends."],"answer":"They sit in the park.","explanation":"Después de comprar, Maria y Luis se sientan en el parque, según el texto."},{"question":"Why do Maria and Luis enjoy sitting in the park?","options":["It's a busy place.","It's relaxing.","It's a good place to shop.","It's full of cafes."],"answer":"It's relaxing.","explanation":"Sentarse en el parque es relajante para Maria y Luis, como se describe en el texto."},{"question":"What do they watch in the park?","options":["People shopping","Cars driving","Dogs walking and children playing","Birds flying"],"answer":"Dogs walking and children playing","explanation":"Ellos observan a las personas paseando con sus perros y a los niños jugando, como se menciona en el texto."},{"question":"Which of these is NOT mentioned in the text?","options":["Maria's favorite fruit","The price of bread","Luis's favorite fruit","What they do in the park"],"answer":"The price of bread","explanation":"El precio del pan no se menciona en el texto."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex24',
  'a2-m1-l6',
  24,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Enfocarse en la Entonación$_$,
  $_${"title":"Práctica de Pronunciación: Enfocarse en la Entonación","instructions":"Practica la entonación en las siguientes oraciones. Lee cada oración en voz alta, prestando atención a cómo cambia la entonación dependiendo de si la oración es una pregunta, una afirmación o una exclamación. Escucha los ejemplos de audio para mejorar tu pronunciación.","targetSentences":[{"englishSentence":"Are you going to the park today?","spanishTranslation":"¿Vas al parque hoy?","phoneticTranscription":"/ɑːr juː ˈɡəʊɪŋ tuː ðə pɑːrk təˈdeɪ/","audioModelURL":"https://example.com/audio1.mp3","pronunciationTips":"Sube la entonación al final de la oración para indicar una pregunta.","focusPoints":"Intonation rise at the end","commonMistakes":"Evitar un tono plano que no indique pregunta."},{"englishSentence":"I had a great time at the concert!","spanishTranslation":"¡Lo pasé genial en el concierto!","phoneticTranscription":"/aɪ hæd ə ɡreɪt taɪm æt ðə ˈkɒnsərt/","audioModelURL":"https://example.com/audio2.mp3","pronunciationTips":"Usa una entonación más alta y entusiasta para transmitir emoción.","focusPoints":"Enthusiastic intonation","commonMistakes":"Evitar una entonación monótona que no exprese emoción."},{"englishSentence":"She must be very tired after the trip.","spanishTranslation":"Debe estar muy cansada después del viaje.","phoneticTranscription":"/ʃiː mʌst biː ˈveri ˈtaɪərd ˈɑːftər ðə trɪp/","audioModelURL":"https://example.com/audio3.mp3","pronunciationTips":"Manten una entonación suave al hacer una afirmación.","focusPoints":"Smooth intonation for statements","commonMistakes":"No terminar la oración con un tono elevado que sugiera una pregunta."},{"englishSentence":"What a beautiful day it is!","spanishTranslation":"¡Qué día tan hermoso!","phoneticTranscription":"/wɒt ə ˈbjuːtəfəl deɪ ɪt ɪz/","audioModelURL":"https://example.com/audio4.mp3","pronunciationTips":"Usa una entonación descendente para mostrar admiración.","focusPoints":"Descending intonation for exclamations","commonMistakes":"Evitar una entonación plana que no transmita admiración."},{"englishSentence":"Will you help me with my homework?","spanishTranslation":"¿Me ayudarás con mi tarea?","phoneticTranscription":"/wɪl juː help miː wɪð maɪ ˈhoʊmwɜːrk/","audioModelURL":"https://example.com/audio5.mp3","pronunciationTips":"Asegúrate de subir la entonación al final para hacer una pregunta.","focusPoints":"Rising intonation for questions","commonMistakes":"No usar el mismo tono que en una afirmación."},{"englishSentence":"I can't believe it's already Friday!","spanishTranslation":"¡No puedo creer que ya sea viernes!","phoneticTranscription":"/aɪ kɑːnt bɪˈliːv ɪts ɔːlˈredi ˈfraɪdeɪ/","audioModelURL":"https://example.com/audio6.mp3","pronunciationTips":"Usa una entonación ascendente para expresar sorpresa.","focusPoints":"Expressive intonation for surprise","commonMistakes":"Evitar una entonación que no refleje sorpresa."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex25',
  'a2-m1-l6',
  25,
  'multiple-choice',
  $_$Review: My World - A2 Level Grammar Practice$_$,
  $_${"title":"Review: My World - A2 Level Grammar Practice","instructions":"Choose the correct answer for each question. Read the explanations carefully to understand your mistakes.","questions":[{"id":"q1","question":"Which sentence is correct using 'should'?","options":["A. You should to eat more vegetables.","B. You should eat more vegetables.","C. You should eating more vegetables.","D. You should eats more vegetables."],"correctAnswer":"B","explanation":"La opción correcta es B porque 'should' va seguido de un verbo en la forma base. No se usa 'to' ni se cambia la forma del verbo después de 'should'."},{"id":"q2","question":"Choose the correct form of the adjective: 'This book is ____ than the other one.'","options":["A. more interesting","B. interesting","C. interestinger","D. most interesting"],"correctAnswer":"A","explanation":"La opción correcta es A porque 'more interesting' es la forma comparativa correcta para adjetivos largos (más de dos sílabas)."},{"id":"q3","question":"Which sentence uses 'going to' correctly?","options":["A. I going to visit my grandma tomorrow.","B. I am going visit my grandma tomorrow.","C. I am going to visit my grandma tomorrow.","D. I will going to visit my grandma tomorrow."],"correctAnswer":"C","explanation":"La opción correcta es C porque 'am going to' seguido de un verbo en la forma base se utiliza para expresar planes futuros."},{"id":"q4","question":"Which sentence uses a quantifier correctly?","options":["A. There is much apples in the basket.","B. There are many apples in the basket.","C. There is any apples in the basket.","D. There are some apple in the basket."],"correctAnswer":"B","explanation":"La opción correcta es B porque 'many' se usa con sustantivos contables en plural. 'Apples' es contable y plural, por lo tanto, 'many' es el cuantificador correcto."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex26',
  'a2-m1-l6',
  26,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I am _______ (go) to visit my grandparents this weekend.","correctAnswer":"going","explanation":"La estructura 'going to' se utiliza para expresar planes e intenciones futuras. Aquí, 'I am going to visit' significa que tengo la intención de visitar a mis abuelos.","points":1},{"sentence":"This book is _______ (interesting) than the one I read last month.","correctAnswer":"more interesting","explanation":"Se usa 'more interesting' como un adjetivo comparativo para expresar que este libro es más interesante que el otro.","points":1},{"sentence":"You _______ (should) eat more vegetables to stay healthy.","correctAnswer":"should","explanation":"'Should' se usa para dar consejos. Aquí, se aconseja comer más verduras para mantenerse saludable.","points":1},{"sentence":"There are _______ (much) apples in the basket.","correctAnswer":"many","explanation":"'Many' se utiliza con sustantivos contables en plural, como 'apples'.","points":1},{"sentence":"There is _______ (much) time to finish the game.","correctAnswer":"enough","explanation":"'Enough' indica que hay suficiente tiempo para completar el juego.","points":1},{"sentence":"She is _______ (tall) enough to reach the top shelf.","correctAnswer":"tall","explanation":"La estructura 'adjetivo + enough' se utiliza para indicar que alguien tiene la altura necesaria para alcanzar algo.","points":1},{"sentence":"We are _______ (plan) to go on a holiday in December.","correctAnswer":"going","explanation":"Se usa 'going to' para hablar de un plan futuro. Aquí, 'We are going to go' indica un plan de vacaciones.","points":1},{"sentence":"This dress is _______ (cheap) than the one I saw yesterday.","correctAnswer":"cheaper","explanation":"Se usa 'cheaper' como adjetivo comparativo para indicar que este vestido cuesta menos que el de ayer.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex27',
  'a2-m1-l6',
  27,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["we","going","to","are","visit","museum","the","Saturday","."],"correctOrder":["We","are","going","to","visit","the","museum","Saturday","."],"translation":"Vamos a visitar el museo el sábado.","hint":"La estructura básica para el futuro con 'going to'.","points":2},{"id":2,"words":["how","many","apples","do","you","need","?"],"correctOrder":["How","many","apples","do","you","need","?"],"translation":"¿Cuántas manzanas necesitas?","hint":"Pregunta sobre cantidad con 'how many'.","points":2},{"id":3,"words":["is","the","fastest","Cheetah","animal","land","the","on","."],"correctOrder":["The","cheetah","is","the","fastest","animal","on","the","land","."],"translation":"El guepardo es el animal más rápido en tierra.","hint":"Uso de adjetivos superlativos.","points":2},{"id":4,"words":["do","have","we","to","homework","our","today","finish","."],"correctOrder":["We","have","to","finish","our","homework","today","."],"translation":"Tenemos que terminar nuestra tarea hoy.","hint":"Uso de 'have to' para expresar obligación.","points":2},{"id":5,"words":["is","she","the","most","intelligent","student","in","class","the","."],"correctOrder":["She","is","the","most","intelligent","student","in","the","class","."],"translation":"Ella es la estudiante más inteligente de la clase.","hint":"Uso de adjetivos superlativos con 'the most'.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex28',
  'a2-m1-l6',
  28,
  'speaking-analysis',
  $_$Speaking Analysis: My World$_$,
  $_${"title":"Speaking Analysis: My World","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking to a friend about your last vacation.","question":"What is the most natural way to start your story?","options":["A. Last summer, I went to the beach.","B. The summer last, to the beach I go.","C. Beach, last summer I went.","D. I last summer went to the beach."],"correctAnswer":"A","explanation":"Opción A es la respuesta correcta porque sigue el orden correcto de sujeto + verbo + complemento. Las otras opciones no siguen el orden correcto del inglés.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You want to suggest a movie to watch with friends.","question":"Which is the most polite way to suggest a film?","options":["A. You must watch this movie.","B. We could watch this film, if you like.","C. Watch this film now.","D. You have to see this film."],"correctAnswer":"B","explanation":"La opción B es la más adecuada porque utiliza un modal de sugerencia ('could') y es educada al incluir 'if you like'. Las otras opciones son más imperativas y menos diplomáticas.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are describing a recent experience at a new restaurant.","question":"Which adjective is correctly placed?","options":["A. I visited a new restaurant very.","B. I visited very a new restaurant.","C. I visited a very new restaurant.","D. I visited new very a restaurant."],"correctAnswer":"C","explanation":"La opción C es correcta porque mantiene el orden 'a + very + adjetivo + sustantivo'. Las otras opciones no siguen el orden correcto de adjetivos y adverbios en inglés.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are talking about your weekend plans with a colleague.","question":"Which informal expression fits best?","options":["A. I will relax and take it easy.","B. I shall rest and recuperate.","C. I am to relax and take it easy.","D. I will be relaxing and recuperating."],"correctAnswer":"A","explanation":"La opción A es la más adecuada porque es una expresión común y informal que se usa para describir un fin de semana relajante. Las otras opciones son más formales o no usan la expresión idiomática correcta.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are explaining why you are late to a meeting.","question":"Which discourse marker is most appropriate to start your explanation?","options":["A. Actually, I was stuck in traffic.","B. By the way, I was stuck in traffic.","C. Also, I was stuck in traffic.","D. Despite, I was stuck in traffic."],"correctAnswer":"A","explanation":"La opción A es la correcta porque 'actually' se utiliza para introducir una explicación o aclaración. Las otras opciones no son adecuadas en este contexto.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You need to end a call politely.","question":"Which phrase is the most tactful?","options":["A. Bye, gotta go.","B. I have to leave now.","C. I should go, it was nice talking to you.","D. I can't talk now."],"correctAnswer":"C","explanation":"La opción C es la más diplomática porque usa 'should' para suavizar la despedida y agrega un comentario amable. Las otras opciones son demasiado abruptas o directas.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex29',
  'a2-m1-l6',
  29,
  'word-search',
  $_$Word Search: My World - Review Module 1$_$,
  $_${"title":"Word Search: My World - Review Module 1","instructions":"Find the hidden words in the grid","words":["COMPUTER","HEAD","MONEY","EXPENSIVE","HEALTHY","STRONG","CHEAP","SMART","PHONE","ARM"],"gridSize":10,"clues":[{"word":"COMPUTER","clue":"A machine for working and playing"},{"word":"HEAD","clue":"Part of the body where you wear a hat"},{"word":"MONEY","clue":"What you use to buy things"},{"word":"EXPENSIVE","clue":"Costs a lot of money"},{"word":"HEALTHY","clue":"Good for your body"},{"word":"STRONG","clue":"Having power and energy"},{"word":"CHEAP","clue":"Does not cost a lot"},{"word":"SMART","clue":"Clever or intelligent"},{"word":"PHONE","clue":"Device for talking to others"},{"word":"ARM","clue":"You use this to lift things"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex30',
  'a2-m1-l6',
  30,
  'crossword',
  $_$Crossword: My World$_$,
  $_${"title":"Crossword: My World","instructions":"Complete the crossword puzzle","items":[{"word":"APPLE","clue":"A red or green fruit","direction":"across","row":0,"col":0},{"word":"RAIN","clue":"Water falling from clouds","direction":"down","row":0,"col":2},{"word":"THEATRE","clue":"A place to watch plays","direction":"across","row":2,"col":1},{"word":"SUNNY","clue":"Weather with a lot of sunshine","direction":"down","row":0,"col":5},{"word":"MUSIC","clue":"Sound that people sing or play","direction":"across","row":4,"col":0},{"word":"COLD","clue":"Low temperature","direction":"down","row":2,"col":4},{"word":"SHOP","clue":"A place to buy things","direction":"across","row":6,"col":3},{"word":"MOVIE","clue":"A film you watch at the cinema","direction":"down","row":2,"col":6}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex31',
  'a2-m1-l6',
  31,
  'reading-comprehension',
  $_$My World: Review Module 1$_$,
  $_${"title":"My World: Review Module 1","textPassage":"My name is Carlos and I live in a small town near the mountains. Every day, I walk to the local bakery to buy fresh bread. The bakery is famous for its delicious pastries. On weekends, my family and I often go to the park to have a picnic. We bring sandwiches, fruit, and juice. It's a nice way to spend time together. I am learning English because I want to travel to England next year. I think it will be an exciting adventure. I have been practicing English for two years now, and I feel more confident speaking with others. My teacher says I should practice listening to English songs and watching English movies. I hope to improve my skills soon.","wordCount":121,"estimatedReadingTime":2,"questions":[{"question":"What is the main idea of the passage?","options":["Carlos's daily routine","Carlos's plans to travel","Carlos's family picnic","Carlos's favorite bakery"],"answer":"Carlos's daily routine","explanation":"La idea principal del pasaje es la rutina diaria de Carlos, incluyendo sus actividades y sus planes futuros."},{"question":"Where does Carlos buy bread?","options":["In a supermarket","At a bakery","In a restaurant","At a cafe"],"answer":"At a bakery","explanation":"Carlos compra pan en una panadería local famosa por sus pasteles deliciosos."},{"question":"Why is Carlos learning English?","options":["To teach English","To write books","To travel to England","To understand music"],"answer":"To travel to England","explanation":"Carlos está aprendiendo inglés porque quiere viajar a Inglaterra el próximo año."},{"question":"How long has Carlos been practicing English?","options":["One year","Two years","Three years","Six months"],"answer":"Two years","explanation":"Carlos ha estado practicando inglés durante dos años."},{"question":"TRUE or FALSE: Carlos feels nervous speaking English.","options":["TRUE","FALSE"],"answer":"FALSE","explanation":"Carlos se siente más seguro hablando inglés ahora."},{"question":"What does Carlos's teacher suggest he should do?","options":["Read English books","Practice grammar exercises","Listen to English songs and watch movies","Travel to an English-speaking country"],"answer":"Listen to English songs and watch movies","explanation":"El maestro de Carlos le sugiere que escuche canciones en inglés y vea películas para mejorar sus habilidades."},{"question":"What does Carlos think about his future trip?","options":["It will be boring","It will be difficult","It will be exciting","It will be long"],"answer":"It will be exciting","explanation":"Carlos piensa que su viaje a Inglaterra será una aventura emocionante."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex32',
  'a2-m1-l6',
  32,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Estrés de Palabras$_$,
  $_${"title":"Práctica de Pronunciación: Estrés de Palabras","instructions":"Practica la pronunciación de las siguientes oraciones en inglés, poniendo atención al estrés de las palabras. Lee la oración en inglés, observa la transcripción fonética de las palabras clave y escucha el modelo de audio para mejorar tu pronunciación.","targetSentences":[{"englishSentence":"My friend has a big garden.","spanishTranslation":"Mi amigo tiene un jardín grande.","phoneticTranscription":"[ˈfrɛnd] [ˈgɑːrdən]","audioModelURL":"https://example.com/audio1.mp3","pronunciationTips":"Enfoca el estrés en 'friend' y 'garden'. Asegúrate de pronunciar bien la 'r' en ambas palabras.","focusPoints":"Word stress on 'friend' and 'garden'.","commonMistakes":"Evitar pronunciar la 'e' final en 'garden'."},{"englishSentence":"The movie starts at seven.","spanishTranslation":"La película empieza a las siete.","phoneticTranscription":"[ˈmuːvi] [ˈstɑːrts]","audioModelURL":"https://example.com/audio2.mp3","pronunciationTips":"Pon énfasis en 'movie' y 'starts'. La 's' de 'starts' es fuerte.","focusPoints":"Word stress on 'movie' and 'starts'.","commonMistakes":"No mezclar 'starts' con 'starts'."},{"englishSentence":"I will travel to Spain next month.","spanishTranslation":"Viajaré a España el próximo mes.","phoneticTranscription":"[ˈtrævl] [ˈspɛɪn]","audioModelURL":"https://example.com/audio3.mp3","pronunciationTips":"Asegúrate de que 'travel' y 'Spain' tengan el estrés apropiado.","focusPoints":"Word stress on 'travel' and 'Spain'.","commonMistakes":"No pronunciar 'Spain' como 'spin'."},{"englishSentence":"We should visit the museum today.","spanishTranslation":"Deberíamos visitar el museo hoy.","phoneticTranscription":"[ˈvɪzɪt] [mjuːˈziːəm]","audioModelURL":"https://example.com/audio4.mp3","pronunciationTips":"El estrés está en 'visit' y 'museum'. Pronuncia claramente la 'm' y 'z'.","focusPoints":"Word stress on 'visit' and 'museum'.","commonMistakes":"No cambiar 'museum' por 'muzem'."},{"englishSentence":"They enjoy playing soccer.","spanishTranslation":"Ellos disfrutan jugar al fútbol.","phoneticTranscription":"[ɪnˈʤɔɪ] [ˈpleɪɪŋ]","audioModelURL":"https://example.com/audio5.mp3","pronunciationTips":"Céntrate en 'enjoy' y 'playing'. La 'j' en 'enjoy' es suave.","focusPoints":"Word stress on 'enjoy' and 'playing'.","commonMistakes":"No pronunciar 'enjoy' como 'en-joy'."},{"englishSentence":"The bakery opens early.","spanishTranslation":"La panadería abre temprano.","phoneticTranscription":"[ˈbeɪkəri] [ˈɜːrli]","audioModelURL":"https://example.com/audio6.mp3","pronunciationTips":"Pon atención al estrés en 'bakery' y 'early'. La 'r' en 'early' es suave.","focusPoints":"Word stress on 'bakery' and 'early'.","commonMistakes":"No decir 'bakery' como 'bak-ery'."},{"englishSentence":"He usually wakes up late.","spanishTranslation":"Él normalmente se despierta tarde.","phoneticTranscription":"[ˈjuːʒuəli] [ˈleɪt]","audioModelURL":"https://example.com/audio7.mp3","pronunciationTips":"Asegúrate de que 'usually' y 'late' tengan el estrés correcto.","focusPoints":"Word stress on 'usually' and 'late'.","commonMistakes":"No decir 'usually' como 'usual-ly'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex33',
  'a2-m1-l6',
  33,
  'multiple-choice',
  $_$My World: A2 Grammar and Vocabulary Review$_$,
  $_${"title":"My World: A2 Grammar and Vocabulary Review","instructions":"Choose the correct answer for each question. Las explicaciones están en español.","questions":[{"id":"q1","question":"What are you going to do this weekend?","options":["A. I am going to visit my grandmother.","B. I visit my grandmother.","C. I visited my grandmother.","D. I will visit my grandmother."],"correctAnswer":"A","explanation":"La opción A utiliza 'going to' para expresar un plan futuro, que es lo adecuado para indicar lo que se hará el fin de semana."},{"id":"q2","question":"You don't have to bring your own food.","options":["A. It's necessary to bring food.","B. You must bring food.","C. It's not necessary to bring food.","D. You should bring food."],"correctAnswer":"C","explanation":"La frase 'don't have to' indica que no es necesario hacer algo, por lo tanto, la opción C es correcta."},{"id":"q3","question":"Should we take an umbrella today?","options":["A. No, it is sunny.","B. Yes, we have to.","C. No, it is raining.","D. Yes, it is not necessary."],"correctAnswer":"A","explanation":"La opción A responde adecuadamente a la pregunta con un consejo basado en el clima, ya que 'should' se utiliza para dar consejos."},{"id":"q4","question":"There is some milk in the fridge.","options":["A. Milk is a countable noun.","B. Milk is an uncountable noun.","C. Milk is a plural noun.","D. Milk is a singular noun."],"correctAnswer":"B","explanation":"La leche se considera un sustantivo incontable, lo que significa que no se puede contar directamente utilizando números."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex34',
  'a2-m1-l6',
  34,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"She would like to visit the ____ mountain in the world one day.","answer":["highest"],"explanation":"Utilizamos el superlativo 'the highest' porque estamos hablando de la montaña que es más alta que todas las demás.","points":1},{"sentence":"It is ___ cold to go swimming today.","answer":["too"],"explanation":"Usamos 'too' para indicar que hace demasiado frío para nadar.","points":1},{"sentence":"He has lived in this city ___ five years.","answer":["for"],"explanation":"Usamos 'for' para indicar un periodo de tiempo específico.","points":1},{"sentence":"I think it will ___ rain tomorrow.","answer":["probably"],"explanation":"Usamos 'probably' como un adverbio de grado para indicar que es probable que llueva.","points":1},{"sentence":"This restaurant is ___ the cheapest in town.","answer":["not"],"explanation":"Usamos 'not' para indicar una negación, diciendo que el restaurante no es el más barato.","points":1},{"sentence":"She is ___ tired to continue working.","answer":["too"],"explanation":"Usamos 'too' para indicar que está demasiado cansada para seguir trabajando.","points":1},{"sentence":"You must wear a coat because it is ___ cold outside.","answer":["very"],"explanation":"Usamos 'very' para enfatizar que hace mucho frío.","points":1},{"sentence":"This is the ___ interesting book I have ever read.","answer":["most"],"explanation":"Usamos 'the most' para formar el superlativo de adjetivos largos como 'interesting'.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex35',
  'a2-m1-l6',
  35,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["have","I","to","do","homework","my","today","."],"correctOrder":["I","have","to","do","my","homework","today","."],"translation":"Tengo que hacer mi tarea hoy.","hint":"La estructura es 'have to' + verbo para expresar obligación.","points":2},{"id":2,"words":["will","be","It","sunny","tomorrow","."],"correctOrder":["It","will","be","sunny","tomorrow","."],"translation":"Estará soleado mañana.","hint":"Usa 'will' para hacer predicciones sobre el futuro.","points":2},{"id":3,"words":["enough","tall","isn't","He","to","reach","the","shelf","."],"correctOrder":["He","isn't","tall","enough","to","reach","the","shelf","."],"translation":"Él no es lo suficientemente alto para alcanzar el estante.","hint":"Coloca 'enough' después del adjetivo para indicar suficiencia.","points":2},{"id":4,"words":["very","The","movie","was","interesting","."],"correctOrder":["The","movie","was","very","interesting","."],"translation":"La película fue muy interesante.","hint":"Usa adverbios de grado como 'very' para intensificar adjetivos.","points":2},{"id":5,"words":["There","students","in","are","a","lot","of","the","classroom","."],"correctOrder":["There","are","a","lot","of","students","in","the","classroom","."],"translation":"Hay muchos estudiantes en el aula.","hint":"Usa 'a lot of' para indicar una gran cantidad.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex36',
  'a2-m1-l6',
  36,
  'speaking-analysis',
  $_$Speaking Analysis: My World$_$,
  $_${"title":"Speaking Analysis: My World","instructions":"Choose the most appropriate option for each speaking situation.","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking to a friend about your weekend plans.","question":"What will you probably say if your friend asks, 'Do you have any plans for the weekend?'","options":["I will go to the park since it is sunny.","I go to the park since it is sunny.","I went to the park since it is sunny.","I have gone to the park since it is sunny."],"correctAnswer":"A","explanation":"La opción A usa 'will' para expresar un plan futuro, que es correcto en este contexto.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You want to invite a classmate to a study session.","question":"How would you invite them politely?","options":["Come study with me.","Do you like to study with me?","Would you like to study with me?","Study with me, please."],"correctAnswer":"C","explanation":"La opción C es la más educada y adecuada para invitar a alguien.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are describing a past holiday to a friend.","question":"Which sentence correctly describes your holiday?","options":["I have been to the beach last summer.","I was at the beach last summer.","I will be at the beach last summer.","I am at the beach last summer."],"correctAnswer":"B","explanation":"La opción B usa el pasado simple 'was' para describir un evento pasado.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You're talking about a recent movie you saw.","question":"What would you say if you found the movie exciting?","options":["The movie was very excited.","The movie was very excite.","The movie was very exciting.","The movie was very excitedly."],"correctAnswer":"C","explanation":"La opción C usa 'exciting' para describir la película correctamente.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are telling your friend about your new job.","question":"How do you describe your responsibilities?","options":["I must to work every day.","I must work every day.","I have to work every day.","I should to work every day."],"correctAnswer":"C","explanation":"La opción C usa 'have to' correctamente para expresar una obligación.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex37',
  'a2-m1-l6',
  37,
  'word-search',
  $_$Word Search: My World$_$,
  $_${"title":"Word Search: My World","instructions":"Find the hidden words in the grid","words":["CHEESE","TOMATO","SANDWICH","EXPENSIVE","YOUNG","BEAUTIFUL","HOTEL","RESTAURANT"],"gridSize":10,"clues":[{"word":"CHEESE","clue":"A common ingredient in sandwiches"},{"word":"TOMATO","clue":"A red fruit often used in salads"},{"word":"SANDWICH","clue":"A food item made with two slices of bread and fillings"},{"word":"EXPENSIVE","clue":"The opposite of cheap"},{"word":"YOUNG","clue":"The opposite of old"},{"word":"BEAUTIFUL","clue":"A synonym for pretty"},{"word":"HOTEL","clue":"A place where you stay when traveling"},{"word":"RESTAURANT","clue":"A place where you go to eat"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex38',
  'a2-m1-l6',
  38,
  'crossword',
  $_$Crossword: Review: My World$_$,
  $_${"title":"Crossword: Review: My World","instructions":"Complete the crossword puzzle","items":[{"word":"RAIN","clue":"Water falling from clouds","direction":"across","row":0,"col":2},{"word":"SUNNY","clue":"Weather with a lot of sunshine","direction":"down","row":0,"col":5},{"word":"CHEESE","clue":"A dairy product often used in sandwiches","direction":"across","row":1,"col":0},{"word":"COLD","clue":"Low temperature weather","direction":"down","row":1,"col":3},{"word":"SHOP","clue":"A place where you can buy things","direction":"across","row":3,"col":1},{"word":"SPRING","clue":"The season after winter","direction":"down","row":2,"col":6},{"word":"SUMMER","clue":"The warmest season of the year","direction":"across","row":5,"col":0},{"word":"WINDY","clue":"Weather with a lot of wind","direction":"down","row":4,"col":4}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex39',
  'a2-m1-l6',
  39,
  'reading-comprehension',
  $_$Reading Comprehension: A Visit to the Doctor$_$,
  $_${"title":"Reading Comprehension: A Visit to the Doctor","textPassage":"Anna was not feeling well for a few days. She had a sore throat and a headache. Her mother decided that it was time to see a doctor. They went to the local clinic. The doctor was very kind and asked Anna about her symptoms. She told him she felt tired and had a fever. After checking her, the doctor said Anna had a cold. He gave her some medicine and told her to rest at home for a few days. Anna was happy because she liked the doctor's advice. She thanked the doctor and promised to follow his suggestions. Anna and her mother then went to the pharmacy to get the medicine. On the way home, Anna felt better knowing she would soon recover.","wordCount":132,"estimatedReadingTime":2,"questions":[{"question":"What is the main idea of the passage?","options":["Anna went to a restaurant.","Anna visited a doctor because she was not feeling well.","Anna played with her friends.","Anna went to school."],"answer":"Anna visited a doctor because she was not feeling well.","explanation":"La idea principal es que Anna fue a ver a un médico porque no se sentía bien."},{"question":"What symptoms did Anna have?","options":["She had a stomachache.","She had a sore throat and a headache.","She had a broken arm.","She had a toothache."],"answer":"She had a sore throat and a headache.","explanation":"Anna tenía dolor de garganta y dolor de cabeza, como se menciona en el texto."},{"question":"Why did Anna visit the doctor?","options":["Because she wanted new glasses.","Because she wanted to buy medicine.","Because she was not feeling well.","Because she needed a vaccination."],"answer":"Because she was not feeling well.","explanation":"Anna visitó al médico porque no se sentía bien, lo cual es evidente en el texto."},{"question":"What did the doctor advise Anna to do?","options":["To play outside.","To rest at home for a few days.","To go to school.","To eat ice cream."],"answer":"To rest at home for a few days.","explanation":"El médico aconsejó a Anna descansar en casa por unos días, como se indica en el texto."},{"question":"How did Anna feel after visiting the doctor?","options":["She felt worse.","She felt scared.","She felt happy because she liked the doctor's advice.","She felt angry."],"answer":"She felt happy because she liked the doctor's advice.","explanation":"Anna se sintió feliz porque le gustaron los consejos del médico, según el texto."},{"question":"TRUE or FALSE: Anna went to the pharmacy before seeing the doctor.","answer":"FALSE","explanation":"FALSO: Anna fue a la farmacia después de ver al médico, no antes."},{"question":"What did Anna do after visiting the doctor?","options":["She went to play with friends.","She went directly home.","She went to the pharmacy to get medicine.","She went to the dentist."],"answer":"She went to the pharmacy to get medicine.","explanation":"Después de visitar al médico, Anna fue a la farmacia a comprar la medicina, como se describe en el texto."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex40',
  'a2-m1-l6',
  40,
  'pronunciation-practice',
  $_$Práctica de pronunciación en inglés: Enfoque en la entonación$_$,
  $_${"title":"Práctica de pronunciación en inglés: Enfoque en la entonación","instructions":"Lee las siguientes oraciones en inglés y presta atención a la entonación. Usa las transcripciones fonéticas para practicar. Escucha el modelo de audio y repite, enfocándote en la entonación correcta.","targetSentences":[{"englishSentence":"We must leave at eight o'clock.","spanishTranslation":"Debemos salir a las ocho en punto.","phoneticTranscription":"/wiː mʌst liːv æt eɪt əˈklɒk/","audioModelURL":"http://example.com/audio1","pronunciationTips":"Emphasize the word 'must' to convey the necessity.","focusPoints":"Emphasize 'must' and the time 'eight o'clock'.","commonMistakes":"Avoid stressing the preposition 'at'."},{"englishSentence":"Should I bring a jacket?","spanishTranslation":"¿Debería llevar una chaqueta?","phoneticTranscription":"/ʃʊd aɪ brɪŋ ə ˈʤækɪt/","audioModelURL":"http://example.com/audio2","pronunciationTips":"Rise your intonation at the end to indicate it's a question.","focusPoints":"Rising intonation on 'jacket'.","commonMistakes":"Do not use falling intonation; it should rise at the end."},{"englishSentence":"It's been raining for hours.","spanishTranslation":"Ha estado lloviendo durante horas.","phoneticTranscription":"/ɪts bɪn ˈreɪnɪŋ fɔːr ˈaʊərz/","audioModelURL":"http://example.com/audio3","pronunciationTips":"Stress the duration 'for hours' to emphasize how long.","focusPoints":"Stress on 'for hours'.","commonMistakes":"Avoid equal stress throughout; emphasize the duration instead."},{"englishSentence":"We have to hurry.","spanishTranslation":"Tenemos que darnos prisa.","phoneticTranscription":"/wiː hæv tə ˈhʌri/","audioModelURL":"http://example.com/audio4","pronunciationTips":"Stress 'hurry' to show urgency.","focusPoints":"Stress on 'hurry'.","commonMistakes":"Don't pronounce 'to' with stress; it's a weak form."},{"englishSentence":"Can you help me with this?","spanishTranslation":"¿Puedes ayudarme con esto?","phoneticTranscription":"/kæn juː hɛlp miː wɪð ðɪs/","audioModelURL":"http://example.com/audio5","pronunciationTips":"Use rising intonation to indicate a question.","focusPoints":"Rising intonation on 'this'.","commonMistakes":"Avoid a flat intonation; it should rise at the end."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex41',
  'a2-m1-l6',
  41,
  'multiple-choice',
  $_$Review: My World - Grammar Basics$_$,
  $_${"title":"Review: My World - Grammar Basics","instructions":"Elige la respuesta correcta para cada pregunta.","questions":[{"id":"q1","question":"Would you like to come to the party tonight?","options":["A. Yes, I would like.","B. Yes, I would.","C. Yes, I like.","D. Yes, I do."],"correctAnswer":"B","explanation":"La estructura correcta para responder con 'would like' es 'Yes, I would'. La opción A es incorrecta porque 'would like' ya está implícito en 'would'."},{"id":"q2","question":"How much milk do we need for the recipe?","options":["A. We need many milk.","B. We need much milk.","C. We need a lot of milk.","D. We need many milks."],"correctAnswer":"C","explanation":"'Milk' es un sustantivo incontable, por eso usamos 'a lot of' para expresar cantidad. 'Much' se usa en oraciones negativas o interrogativas, y 'many' es para sustantivos contables."},{"id":"q3","question":"You mustn't forget to bring your passport.","options":["A. You have to forget it.","B. You must forget it.","C. You don't have to bring it.","D. You must not forget it."],"correctAnswer":"D","explanation":"'Mustn't' indica prohibición, lo que significa que no debes olvidar traer el pasaporte. Las otras opciones no reflejan esta prohibición."},{"id":"q4","question":"How many books are there on the table?","options":["A. There isn't any books.","B. There are three books.","C. There is much books.","D. There are a lot books."],"correctAnswer":"B","explanation":"'Books' es un sustantivo contable, por lo que usamos 'how many'. La respuesta correcta es 'There are three books'. Las otras opciones no usan la gramática correcta."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex42',
  'a2-m1-l6',
  42,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"She is _______ interested in art and loves visiting galleries.","gap":"really","acceptableAnswers":["really"],"explanation":"Usamos 'really' para enfatizar el interés en algo.","points":1},{"sentence":"You _______ drink more water every day.","gap":"should","acceptableAnswers":["should"],"explanation":"Usamos 'should' para dar consejos.","points":1},{"sentence":"I think it _______ rain tomorrow.","gap":"will","acceptableAnswers":["will"],"explanation":"Usamos 'will' para hacer predicciones sobre el futuro.","points":1},{"sentence":"There are _______ apples in the basket.","gap":"some","acceptableAnswers":["some"],"explanation":"Usamos 'some' con sustantivos contables en plural cuando no especificamos la cantidad exacta.","points":1},{"sentence":"I am _______ to the cinema with my friends later.","gap":"going","acceptableAnswers":["going"],"explanation":"Usamos el presente continuo para hablar de planes futuros.","points":1},{"sentence":"The soup is _______ hot. Be careful!","gap":"very","acceptableAnswers":["very"],"explanation":"Usamos 'very' para intensificar la cantidad o grado de algo.","points":1},{"sentence":"You _______ eat so many sweets; it's not healthy.","gap":"shouldn't","acceptableAnswers":["shouldn't"],"explanation":"Usamos 'shouldn't' para dar consejos negativos.","points":1},{"sentence":"I _______ finish my homework before dinner tonight.","gap":"will","acceptableAnswers":["will"],"explanation":"Usamos 'will' para decisiones espontáneas.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex43',
  'a2-m1-l6',
  43,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["the","bigger","is","park","than","school","."],"correctOrder":["the","park","is","bigger","than","school","."],"translation":"El parque es más grande que la escuela.","hint":"Usa un adjetivo comparativo para comparar dos cosas.","points":2},{"id":2,"words":["a","will","be","sunny","day","tomorrow","."],"correctOrder":["tomorrow","will","be","a","sunny","day","."],"translation":"Mañana será un día soleado.","hint":"Usa 'will' para hacer una predicción sobre el clima.","points":2},{"id":3,"words":["like","would","a","I","coffee","please","."],"correctOrder":["I","would","like","a","coffee","please","."],"translation":"Me gustaría un café, por favor.","hint":"Usa 'would like' para expresar un deseo o preferencia.","points":2},{"id":4,"words":["many","how","are","apples","there","?"],"correctOrder":["how","many","apples","are","there","?"],"translation":"¿Cuántas manzanas hay?","hint":"Usa 'how many' para preguntar sobre la cantidad de cosas contables.","points":2},{"id":5,"words":["is","really","the","cat","cute","."],"correctOrder":["the","cat","is","really","cute","."],"translation":"El gato es realmente lindo.","hint":"Usa un adverbio de grado para modificar un adjetivo.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex44',
  'a2-m1-l6',
  44,
  'speaking-analysis',
  $_$Speaking Analysis: My World (A2 Level)$_$,
  $_${"title":"Speaking Analysis: My World (A2 Level)","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are at a café and your friend asks if you want to try the new sandwich.","question":"How do you reply?","options":["Sure, I'd love to try it!","No, I haven't seen it.","Yes, I went there yesterday.","I don't know him."],"correctAnswer":"A","explanation":"La respuesta más apropiada es A, ya que indica interés en probar el nuevo sándwich, que es relevante para la situación.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You meet a friend at the bank who asks, 'How was your weekend?'","question":"What's a polite way to respond?","options":["It was nice, thank you. How about yours?","I don't remember.","I was busy.","I didn't like it."],"correctAnswer":"A","explanation":"La opción A es la más educada porque no solo responde a la pregunta, sino que también sigue la conversación preguntando sobre el fin de semana de la otra persona.","points":2},{"id":"q3","type":"multiple-choice","scenario":"Your teacher says, 'You should practice speaking English every day.'","question":"How do you express agreement?","options":["I think yes.","I don't think so.","I agree with you.","I disagree."],"correctAnswer":"C","explanation":"La respuesta C es la más adecuada para expresar acuerdo, utilizando una estructura simple y clara.","points":2},{"id":"q4","type":"multiple-choice","scenario":"Your friend tells you about a surprising event and you want to express your surprise.","question":"What do you say?","options":["That's very interesting!","That's boring.","Really? That's surprising!","I'm not surprised."],"correctAnswer":"C","explanation":"La opción C es la más adecuada porque expresa sorpresa genuina de manera directa.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are invited to a party but you can't attend.","question":"What is a polite way to decline?","options":["I can't come, sorry.","No, I don't want to go.","I hate parties.","Thank you, but I have other plans."],"correctAnswer":"D","explanation":"La opción D es la más educada porque agradece la invitación y da una razón de forma cortés.","points":2},{"id":"q6","type":"multiple-choice","scenario":"Your friend is telling a story and pauses.","question":"How do you encourage them to continue?","options":["Why?","And then?","Stop.","I don't care."],"correctAnswer":"B","explanation":"La respuesta B es la más apropiada porque muestra interés y anima al amigo a continuar con su historia.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex45',
  'a2-m1-l6',
  45,
  'word-search',
  $_$Word Search: My World$_$,
  $_${"title":"Word Search: My World","instructions":"Find the hidden words in the grid","words":["CHEESE","SHIRT","HEALTH","WINTER","PHONE","EMAIL","FRUIT","JACKET"],"gridSize":10,"clues":[{"word":"CHEESE","clue":"A dairy product"},{"word":"SHIRT","clue":"A piece of clothing you wear on top"},{"word":"HEALTH","clue":"The state of being free from illness"},{"word":"WINTER","clue":"The coldest season of the year"},{"word":"PHONE","clue":"A device used to make calls"},{"word":"EMAIL","clue":"A method of sending messages electronically"},{"word":"FRUIT","clue":"A sweet product of a tree or plant"},{"word":"JACKET","clue":"A piece of clothing worn outdoors"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex46',
  'a2-m1-l6',
  46,
  'crossword',
  $_$Crossword: My World$_$,
  $_${"title":"Crossword: My World","instructions":"Complete the crossword puzzle","items":[{"word":"PHONE","clue":"A device used to call people","direction":"across","row":0,"col":0},{"word":"MONEY","clue":"You use this to buy things","direction":"down","row":0,"col":2},{"word":"CLOUD","clue":"White and fluffy in the sky","direction":"across","row":1,"col":1},{"word":"SHOP","clue":"A place to buy items","direction":"down","row":1,"col":4},{"word":"CHEAP","clue":"Not expensive","direction":"across","row":3,"col":0},{"word":"EMAIL","clue":"A way to send messages online","direction":"down","row":2,"col":3},{"word":"FRIEND","clue":"Someone you like and spend time with","direction":"across","row":5,"col":0}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex47',
  'a2-m1-l6',
  47,
  'reading-comprehension',
  $_$A2 Reading Comprehension: My World Basics$_$,
  $_${"title":"A2 Reading Comprehension: My World Basics","textPassage":"Anna is planning a trip to the coast. She wants to go next weekend. She loves the sea and enjoys swimming. Anna has been saving money for this trip since January because she wants to stay in a nice hotel. She usually travels with her friend, but this time she will go alone. Anna thinks it will be relaxing to have some time by herself. She has already booked a room in a hotel with a beautiful view of the ocean. On Friday, she will pack her suitcase. She needs to remember her swimsuit and sunscreen. Anna is very excited about her trip and hopes the weather will be sunny.","wordCount":110,"estimatedReadingTime":1,"questions":[{"question":"What is Anna planning?","options":["A trip to the mountains","A trip to the coast","A visit to her parents","A party at home"],"answer":"A trip to the coast","explanation":"Anna está planeando un viaje a la costa para el próximo fin de semana. Le encanta el mar y disfruta nadar."},{"question":"How long has Anna been saving money for her trip?","options":["Since January","For one week","Since last year","For two months"],"answer":"Since January","explanation":"Anna ha estado ahorrando dinero para este viaje desde enero, porque quiere quedarse en un buen hotel."},{"question":"Why is Anna traveling alone this time?","options":["Her friend is busy","She wants to relax by herself","The hotel is expensive","She lost her friend's contact"],"answer":"She wants to relax by herself","explanation":"Anna usualmente viaja con su amiga, pero esta vez irá sola porque piensa que será relajante tener algo de tiempo para ella misma."},{"question":"What has Anna already done for her trip?","options":["Packed her suitcase","Booked a hotel room","Bought a new swimsuit","Called her friend"],"answer":"Booked a hotel room","explanation":"Anna ya ha reservado una habitación en un hotel con una bonita vista al océano."},{"question":"True or False: Anna hopes the weather will be rainy.","options":["True","False"],"answer":"False","explanation":"Anna espera que el clima sea soleado. Ella está muy emocionada por su viaje."},{"question":"What does Anna need to remember to pack?","options":["Her passport","Her book","Her swimsuit and sunscreen","Her camera"],"answer":"Her swimsuit and sunscreen","explanation":"Anna necesita recordar empacar su traje de baño y protector solar. Ella se irá el viernes."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex48',
  'a2-m1-l6',
  48,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Énfasis en la oración$_$,
  $_${"title":"Práctica de pronunciación: Énfasis en la oración","instructions":"Practica la pronunciación de las siguientes oraciones en inglés, enfocándote en el énfasis de la oración. Lee cada oración, revisa la transcripción fonética de las palabras clave y sigue los consejos de pronunciación.","targetSentences":[{"englishSentence":"I have been waiting for the bus since morning.","spanishTranslation":"He estado esperando el autobús desde la mañana.","phoneticTranscription":"/ˈweɪ.tɪŋ/ /ˈmɔː.nɪŋ/","audioModelUrl":"https://example.com/audio1","pronunciationTips":"Emphasize the words 'waiting' and 'morning' to convey the duration and time of the action.","focusPoints":"Emphasize the continuous action and the time reference.","commonMistakes":"Avoid placing stress on 'for' and 'the' as they are not the key words."},{"englishSentence":"We must visit the doctor today.","spanishTranslation":"Debemos visitar al médico hoy.","phoneticTranscription":"/ˈvɪ.zɪt/ /ˈdɒk.tər/","audioModelUrl":"https://example.com/audio2","pronunciationTips":"Focus on 'visit' and 'doctor' to highlight the action and the person you need to see.","focusPoints":"Highlight the urgency of the visit.","commonMistakes":"Don't stress 'must' too heavily; it should be subtle."},{"englishSentence":"They should bring their own snacks.","spanishTranslation":"Deberían traer sus propios bocadillos.","phoneticTranscription":"/ʃʊd/ /brɪŋ/","audioModelUrl":"https://example.com/audio3","pronunciationTips":"Stress 'bring' to emphasize the action expected from them.","focusPoints":"The responsibility of bringing snacks.","commonMistakes":"Avoid stressing 'their' as it is not the main point of focus."},{"englishSentence":"She enjoys reading during her free time.","spanishTranslation":"Ella disfruta leer durante su tiempo libre.","phoneticTranscription":"/ɪnˈdʒɔɪz/ /ˈriː.dɪŋ/","audioModelUrl":"https://example.com/audio4","pronunciationTips":"Highlight 'enjoys' and 'reading' to focus on her hobby.","focusPoints":"The enjoyment and activity.","commonMistakes":"Do not place emphasis on 'during' as it is not the key part of the sentence."},{"englishSentence":"We plan to travel in December.","spanishTranslation":"Planeamos viajar en diciembre.","phoneticTranscription":"/plæn/ /ˈtræv.əl/","audioModelUrl":"https://example.com/audio5","pronunciationTips":"Stress 'plan' and 'travel' to indicate the intention and action.","focusPoints":"The future intention and activity.","commonMistakes":"Avoid emphasizing prepositions like 'in'."},{"englishSentence":"He has lived here for ten years.","spanishTranslation":"Ha vivido aquí durante diez años.","phoneticTranscription":"/lɪvd/ /jɪəz/","audioModelUrl":"https://example.com/audio6","pronunciationTips":"Emphasize 'lived' and 'ten years' to show the duration.","focusPoints":"The duration of time living at the location.","commonMistakes":"Don't stress 'here' too much, as the focus is on the duration."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex49',
  'a2-m1-l6',
  49,
  'multiple-choice',
  $_$My World: A2 Grammar Review$_$,
  $_${"title":"My World: A2 Grammar Review","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"Maria is going to visit her grandmother this weekend. What does she have to bring?","options":["A warm coat","A big umbrella","Her favorite book","A map"],"correctAnswer":"A warm coat","explanation":"Maria va a visitar a su abuela y posiblemente haga frío, por lo que tiene que llevar un abrigo cálido. 'Have to' se usa para indicar obligación."},{"id":"q2","question":"Tom is taller than his brother, but his sister is the ____ in the family.","options":["tallest","taller","more tall","most tall"],"correctAnswer":"tallest","explanation":"En esta oración, 'tallest' es el adjetivo superlativo que se usa para comparar más de dos cosas o personas, indicando que la hermana es la más alta de la familia."},{"id":"q3","question":"You shouldn't eat too much candy because it is not good for your teeth.","options":["should","shouldn't","must","have to"],"correctAnswer":"shouldn't","explanation":"'Shouldn't' se utiliza para dar consejos sobre lo que no es recomendable hacer. En este caso, no es aconsejable comer demasiado dulce."},{"id":"q4","question":"We are going to the park tomorrow if it doesn't rain. What are you going to do?","options":["I am going to the beach","I go to the beach","I went to the beach","I will go to the beach"],"correctAnswer":"I am going to the beach","explanation":"'Going to' se usa para hablar de planes futuros. En este contexto, la persona está hablando sobre lo que planea hacer mañana."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m1-l6-ex50',
  'a2-m1-l6',
  50,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I am going to _______ my friends at the park tomorrow.","options":["meet","meets","meeting"],"correct_answer":"meet","explanation":"Usamos 'going to' para expresar intenciones o planes futuros. Aquí, 'meet' es el verbo en infinitivo que sigue a 'going to'.","points":1},{"sentence":"You _______ to do your homework before dinner.","options":["have","don't have","must"],"correct_answer":"have","explanation":"'Have to' expresa obligación. En este caso, es necesario hacer los deberes antes de la cena.","points":1},{"sentence":"This is the _______ movie I have ever seen.","options":["most exciting","more exciting","excitingest"],"correct_answer":"most exciting","explanation":"Usamos 'the most' para formar el superlativo de adjetivos largos, como 'exciting'.","points":1},{"sentence":"It is _______ cold outside, so wear a jacket.","options":["very","much","too"],"correct_answer":"very","explanation":"Los adverbios de grado como 'very' se usan para intensificar adjetivos. Aquí, 'very' intensifica 'cold'.","points":1},{"sentence":"They are _______ a picnic this weekend.","options":["having","have","has"],"correct_answer":"having","explanation":"El presente continuo se usa para hablar de planes futuros. 'Having a picnic' es un plan para el fin de semana.","points":1},{"sentence":"You _______ to bring your own lunch to the trip.","options":["don't have","mustn't","shouldn't"],"correct_answer":"don't have","explanation":"'Don't have to' indica que algo no es obligatorio. No es necesario traer tu propio almuerzo.","points":1},{"sentence":"This book is _______ interesting than the last one.","options":["more","most","much"],"correct_answer":"more","explanation":"Usamos 'more' para comparar dos cosas con adjetivos largos como 'interesting'.","points":1},{"sentence":"We are _______ to visit our grandparents next month.","options":["going","go","gone"],"correct_answer":"going","explanation":"El presente continuo con 'going to' se usa para planes futuros. Aquí, el plan es visitar a los abuelos.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;

