INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex1',
  'a2-m2-l7',
  1,
  'multiple-choice',
  $_$Daily Routines Practice: Present Simple and Prepositions of Time$_$,
  $_${"title":"Daily Routines Practice: Present Simple and Prepositions of Time","instructions":"Elige la opción correcta para cada pregunta sobre rutinas diarias. Usa el presente simple y preposiciones de tiempo.","questions":[{"id":"q1","question":"Maria ___ breakfast at 7 o'clock every morning.","options":["A. eats","B. eat","C. is eating","D. ate"],"correctAnswer":"A","explanation":"En el presente simple, para la tercera persona (he, she, it), se añade una 's' al verbo. Por lo tanto, 'eats' es correcto."},{"id":"q2","question":"We usually go to the park ___ Saturday afternoons.","options":["A. in","B. on","C. at","D. from"],"correctAnswer":"B","explanation":"Usamos 'on' para hablar de días específicos, como 'on Saturday'."},{"id":"q3","question":"Tom ___ to school by bus.","options":["A. go","B. goes","C. going","D. gone"],"correctAnswer":"B","explanation":"En presente simple, 'go' se convierte en 'goes' para la tercera persona singular (he, she, it)."},{"id":"q4","question":"My parents watch TV ___ the evening.","options":["A. in","B. on","C. at","D. by"],"correctAnswer":"A","explanation":"Usamos 'in' para partes del día, como 'in the evening'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex2',
  'a2-m2-l7',
  2,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I always ___ (get up) early in the morning.","options":["get up","gets up"],"answer":"get up","explanation":"Usamos la forma base del verbo con 'I' en presente simple.","points":1},{"sentence":"She ___ (brush) her teeth after breakfast.","options":["brush","brushes"],"answer":"brushes","explanation":"Para la tercera persona singular (she), añadimos 'es' a los verbos que terminan en 'sh'.","points":1},{"sentence":"They ___ (go) to work by train every day.","options":["go","goes"],"answer":"go","explanation":"Con el pronombre 'They' usamos la forma base del verbo 'go'.","points":1},{"sentence":"He sometimes ___ (watch) TV in the evening.","options":["watch","watches"],"answer":"watches","explanation":"Añadimos 'es' al verbo 'watch' para la tercera persona singular 'he'.","points":1},{"sentence":"We usually ___ (have) lunch at 1 o'clock.","options":["have","has"],"answer":"have","explanation":"Usamos 'have' con 'we' en presente simple.","points":1},{"sentence":"My father ___ (finish) work at 5 p.m.","options":["finish","finishes"],"answer":"finishes","explanation":"Añadimos 'es' a 'finish' para la tercera persona (my father = he).","points":1},{"sentence":"Do you ___ (take) a shower in the morning?","options":["take","takes"],"answer":"take","explanation":"En preguntas con 'do', el verbo siempre va en su forma base.","points":1},{"sentence":"Sarah ___ (not / study) on Sundays.","options":["doesn't study","don't study"],"answer":"doesn't study","explanation":"Usamos 'doesn't' para la negación en tercera persona singular.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex3',
  'a2-m2-l7',
  3,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["She","at","gets","up","6:30","."],"correctOrder":["She","gets","up","at","6:30","."],"translation":"Ella se levanta a las 6:30.","hint":"Sujeto + verbo + preposición de tiempo + hora.","points":2},{"id":2,"words":["breakfast","in","He","the","morning","eats","."],"correctOrder":["He","eats","breakfast","in","the","morning","."],"translation":"Él desayuna por la mañana.","hint":"Sujeto + verbo + objeto + preposición de tiempo + parte del día.","points":2},{"id":3,"words":["at","They","watch","TV","night","."],"correctOrder":["They","watch","TV","at","night","."],"translation":"Ellos ven la televisión por la noche.","hint":"Sujeto + verbo + objeto + preposición de tiempo + parte del día.","points":2},{"id":4,"words":["on","the","She","weekends","visits","her","grandparents","."],"correctOrder":["She","visits","her","grandparents","on","the","weekends","."],"translation":"Ella visita a sus abuelos los fines de semana.","hint":"Sujeto + verbo + objeto + preposición de tiempo + periodo de tiempo.","points":2},{"id":5,"words":["in","go","the","We","to","gym","afternoon","the","."],"correctOrder":["We","go","to","the","gym","in","the","afternoon","."],"translation":"Vamos al gimnasio por la tarde.","hint":"Sujeto + verbo + preposición + lugar + preposición de tiempo + parte del día.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex4',
  'a2-m2-l7',
  4,
  'speaking-analysis',
  $_$Speaking Analysis: Daily Routines$_$,
  $_${"title":"Speaking Analysis: Daily Routines","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking to a friend about your morning routine.","question":"Friend: What time do you usually wake up? You:","options":["A. I wake up at 7 o'clock.","B. I am waking up at 7 o'clock.","C. I woke up at 7 o'clock.","D. I will wake up at 7 o'clock."],"correctAnswer":"A","explanation":"La respuesta correcta es 'A. I wake up at 7 o'clock.' porque usamos el presente simple para hablar de acciones habituales o rutinas diarias.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are explaining your daily schedule to your teacher.","question":"Teacher: When do you have breakfast? You:","options":["A. I have breakfast in the morning.","B. I have breakfast on the morning.","C. I have breakfast at the morning.","D. I have breakfast in morning."],"correctAnswer":"A","explanation":"La respuesta correcta es 'A. I have breakfast in the morning.' porque usamos 'in' para hablar de partes del día como la mañana, tarde y noche.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are telling a friend about your weekend routine.","question":"Friend: Do you do anything special on Sundays? You:","options":["A. Yes, I go to the park in the afternoon.","B. Yes, I go to the park at afternoon.","C. Yes, I go to the park on afternoon.","D. Yes, I go to the park at the afternoon."],"correctAnswer":"A","explanation":"La respuesta correcta es 'A. Yes, I go to the park in the afternoon.' porque usamos 'in' para hablar de partes del día.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are describing your evening routine to a new friend.","question":"Friend: What do you do after dinner? You:","options":["A. I watch TV and read a book.","B. I watching TV and read a book.","C. I am watching TV and read a book.","D. I watched TV and read a book."],"correctAnswer":"A","explanation":"La respuesta correcta es 'A. I watch TV and read a book.' porque usamos el presente simple para actividades habituales.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are practicing English and talking about your daily activities.","question":"Instructor: What time do you go to bed? You:","options":["A. I go to bed at 10 o'clock.","B. I go to bed on 10 o'clock.","C. I go to bed in 10 o'clock.","D. I go to bed during 10 o'clock."],"correctAnswer":"A","explanation":"La respuesta correcta es 'A. I go to bed at 10 o'clock.' porque usamos 'at' para hablar de horas específicas.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are sharing your daily routine with a classmate.","question":"Classmate: Do you exercise every day? You:","options":["A. Yes, I exercise in the morning.","B. Yes, I exercise on the morning.","C. Yes, I exercise at morning.","D. Yes, I exercise during morning."],"correctAnswer":"A","explanation":"La respuesta correcta es 'A. Yes, I exercise in the morning.' porque usamos 'in' para hablar de partes del día.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex5',
  'a2-m2-l7',
  5,
  'word-search',
  $_$Word Search: Daily Routines$_$,
  $_${"title":"Word Search: Daily Routines","instructions":"Find the hidden words in the grid","words":["BREAKFAST","DINNER","EVENING","MORNING","LUNCH","NIGHT","WEEKEND","SCHOOL","WORK","HOME"],"gridSize":10,"clues":[{"word":"BREAKFAST","clue":"The first meal of the day."},{"word":"DINNER","clue":"The main meal of the evening."},{"word":"EVENING","clue":"The time of day when it starts to get dark."},{"word":"MORNING","clue":"The first part of the day before noon."},{"word":"LUNCH","clue":"The meal you eat in the middle of the day."},{"word":"NIGHT","clue":"The time when it is dark and people usually sleep."},{"word":"WEEKEND","clue":"Saturday and Sunday."},{"word":"SCHOOL","clue":"A place where children go to learn."},{"word":"WORK","clue":"The place you go to do your job."},{"word":"HOME","clue":"The place where you live."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex6',
  'a2-m2-l7',
  6,
  'crossword',
  $_$Crossword: Daily Routines$_$,
  $_${"title":"Crossword: Daily Routines","instructions":"Complete the crossword puzzle","items":[{"word":"BREAKFAST","clue":"The first meal of the day","direction":"across","row":0,"col":0},{"word":"MORNING","clue":"The time of day when you usually wake up","direction":"down","row":0,"col":5},{"word":"DINNER","clue":"The meal you eat in the evening","direction":"across","row":2,"col":2},{"word":"SCHOOL","clue":"A place where children go to learn","direction":"down","row":1,"col":7},{"word":"AFTERNOON","clue":"The time of day between lunch and dinner","direction":"across","row":4,"col":0},{"word":"NIGHT","clue":"The time of day when you sleep","direction":"down","row":3,"col":9},{"word":"EXERCISE","clue":"An activity you do to keep fit","direction":"across","row":6,"col":2},{"word":"AT","clue":"A preposition used with times of day, like '___ 7 o'clock'","direction":"down","row":5,"col":4}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex7',
  'a2-m2-l7',
  7,
  'reading-comprehension',
  $_$A Day in the Life of Emma$_$,
  $_${"title":"A Day in the Life of Emma","textPassage":"Emma is a student. She lives in a small town. Every day, she wakes up at 7 o'clock in the morning. She has breakfast with her family at 7:30. On weekdays, she goes to school at 8 o'clock. She usually studies math and English in the morning. In the afternoon, she has lunch at 12 o'clock with her friends. After lunch, she plays in the school playground. Emma goes home at 3 o'clock in the afternoon. She does her homework in the evening at 5 o'clock. Emma has dinner with her family at 7 o'clock. She likes to read books before bed. Emma goes to sleep at 9 o'clock at night.","wordCount":115,"estimatedReadingTime":1,"questions":[{"question":"What time does Emma wake up?","options":["7 o'clock","7:30","8 o'clock","12 o'clock"],"answer":"7 o'clock","explanation":"Emma se despierta a las 7 en punto de la mañana, según el texto."},{"question":"What does Emma do in the afternoon?","options":["She goes to school.","She plays in the playground.","She has breakfast.","She wakes up."],"answer":"She plays in the playground.","explanation":"El texto menciona que Emma juega en el patio de la escuela por la tarde."},{"question":"Emma has dinner at 7 o'clock. True or False?","options":["True","False"],"answer":"True","explanation":"El texto indica que Emma cena con su familia a las 7 en punto."},{"question":"Which subject does Emma study in the morning?","options":["Science","Math","History","Art"],"answer":"Math","explanation":"Emma estudia matemáticas por la mañana, como se menciona en el texto."},{"question":"When does Emma do her homework?","options":["In the morning","In the afternoon","In the evening","At night"],"answer":"In the evening","explanation":"El texto dice que Emma hace su tarea por la tarde a las 5 en punto."},{"question":"What does Emma like to do before bed?","options":["Watch TV","Read books","Play games","Talk with friends"],"answer":"Read books","explanation":"Emma le gusta leer libros antes de dormir, como se menciona en el texto."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex8',
  'a2-m2-l7',
  8,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Rutinas Diarias$_$,
  $_${"title":"Práctica de Pronunciación: Rutinas Diarias","instructions":"Practica la pronunciación de las siguientes frases. Enfócate en el uso correcto del presente simple y las preposiciones de tiempo. Escucha cada frase y repite, prestando atención a la pronunciación de las palabras clave.","targetSentences":[{"englishSentence":"I have breakfast at 7 o'clock in the morning.","spanishTranslation":"Desayuno a las 7 en la mañana.","phoneticTranscription":"[ˈbrɛkfəst]","audioModelURL":"https://example.com/audio1","pronunciationTips":"Enfoca en la pronunciación clara de 'breakfast' y el sonido de la 'd' en 'and'.","focusPoints":"Pronunciación de 'breakfast' y preposición de tiempo 'at'.","commonMistakes":"Evitar agregar un sonido 'uh' al final de 'breakfast'."},{"englishSentence":"She goes to work at 9 o'clock.","spanishTranslation":"Ella va al trabajo a las 9.","phoneticTranscription":"[ɡoʊz]","audioModelURL":"https://example.com/audio2","pronunciationTips":"Asegúrate de pronunciar la 's' final en 'goes'.","focusPoints":"Pronunciación del presente simple en tercera persona 'goes'.","commonMistakes":"No olvidar la 's' en 'goes' para tercera persona singular."},{"englishSentence":"We have lunch in the afternoon.","spanishTranslation":"Almorzamos por la tarde.","phoneticTranscription":"[lʌntʃ]","audioModelURL":"https://example.com/audio3","pronunciationTips":"Pronuncia claramente el sonido 'ch' en 'lunch'.","focusPoints":"Sonido 'ch' en 'lunch' y uso de 'in' para periodos del día.","commonMistakes":"Evitar pronunciar 'lunch' como 'lanch'."},{"englishSentence":"He watches TV at night.","spanishTranslation":"Él ve televisión en la noche.","phoneticTranscription":"[ˈwɔtʃɪz]","audioModelURL":"https://example.com/audio4","pronunciationTips":"Presta atención al sonido 'ch' en 'watches' y la 's' final.","focusPoints":"Pronunciación de 'watches' en presente simple.","commonMistakes":"No omitir la 's' en 'watches'."},{"englishSentence":"They go to bed at 10 o'clock.","spanishTranslation":"Ellos se acuestan a las 10.","phoneticTranscription":"[ɡoʊ]","audioModelURL":"https://example.com/audio5","pronunciationTips":"Asegúrate de pronunciar bien 'go' con el sonido 'oʊ'.","focusPoints":"Pronunciación de 'go' y uso de preposición 'at'.","commonMistakes":"No pronunciar 'go' como 'goo'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex9',
  'a2-m2-l7',
  9,
  'multiple-choice',
  $_$Daily Routines Exercise$_$,
  $_${"title":"Daily Routines Exercise","instructions":"Selecciona la respuesta correcta para cada pregunta.","questions":[{"id":"q1","question":"What time do you usually have breakfast?","options":["A) at 7:00 in the morning","B) in 7:00 in the morning","C) on 7:00 in the morning","D) at 7:00 on the morning"],"correctAnswer":"A","explanation":"La preposición correcta para hablar de horas específicas es 'at'."},{"id":"q2","question":"She goes to the gym ____ the afternoon.","options":["A) on","B) in","C) at","D) of"],"correctAnswer":"B","explanation":"Usamos 'in' para hablar de partes del día como 'in the afternoon'."},{"id":"q3","question":"I take my dog for a walk ____ every morning.","options":["A) in 8 o'clock","B) at 8 o'clock","C) on 8 o'clock","D) by 8 o'clock"],"correctAnswer":"B","explanation":"La preposición correcta para una hora exacta es 'at', así que es 'at 8 o'clock'."},{"id":"q4","question":"We have a family dinner ____ Sundays.","options":["A) in","B) at","C) on","D) by"],"correctAnswer":"C","explanation":"Usamos 'on' para días específicos como 'on Sundays'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex10',
  'a2-m2-l7',
  10,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I usually ___ (have) breakfast at 7:30.","answer":"have","explanation":"Con 'I' usamos la forma base del verbo 'have'.","points":1},{"sentence":"She ___ (not / drink) coffee in the evening.","answer":"doesn't drink","explanation":"Usamos 'doesn't' para negar en tercera persona singular (she).","points":1},{"sentence":"Do you ___ (exercise) every day?","answer":"exercise","explanation":"En preguntas con 'do', el verbo se mantiene en su forma base.","points":1},{"sentence":"My brother ___ (watch) TV after dinner.","answer":"watches","explanation":"Añadimos 'es' al verbo 'watch' para la tercera persona singular (he).","points":1},{"sentence":"They ___ (go) to bed at 11 p.m.","answer":"go","explanation":"Con 'They' usamos la forma base del verbo.","points":1},{"sentence":"We ___ (clean) the house on Saturdays.","answer":"clean","explanation":"Con 'We' usamos la forma base del verbo.","points":1},{"sentence":"He ___ (finish) his homework before dinner.","answer":"finishes","explanation":"Añadimos 'es' al verbo 'finish' para la tercera persona singular (he).","points":1},{"sentence":"Does she ___ (cook) lunch for her family?","answer":"cook","explanation":"En preguntas con 'does', el verbo vuelve a su forma base.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex11',
  'a2-m2-l7',
  11,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["at","night","I","sleep","."],"correctOrder":["I","sleep","at","night","."],"translation":"Duermo por la noche.","hint":"La estructura es Sujeto + Verbo + Preposición de tiempo + Complemento.","points":2},{"id":2,"words":["play","Saturdays","on","football","we","."],"correctOrder":["We","play","football","on","Saturdays","."],"translation":"Jugamos al fútbol los sábados.","hint":"Utiliza el presente simple afirmativo y preposición para días de la semana.","points":2},{"id":3,"words":["at","breakfast","o'clock","have","7","I","."],"correctOrder":["I","have","breakfast","at","7","o'clock","."],"translation":"Desayuno a las 7 en punto.","hint":"Empieza con el sujeto y sigue con la rutina diaria usando preposición de tiempo.","points":2},{"id":4,"words":["work","usually","I","in","the","garden","."],"correctOrder":["I","usually","work","in","the","garden","."],"translation":"Usualmente trabajo en el jardín.","hint":"Recuerda el adverbio de frecuencia y la preposición adecuada de lugar.","points":2},{"id":5,"words":["at","dinner","we","8","have","o'clock","."],"correctOrder":["We","have","dinner","at","8","o'clock","."],"translation":"Cenamos a las 8 en punto.","hint":"Utiliza el presente simple afirmativo y preposición para horas específicas.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex12',
  'a2-m2-l7',
  12,
  'speaking-analysis',
  $_$Speaking Analysis: Daily Routines$_$,
  $_${"title":"Speaking Analysis: Daily Routines","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You greet your friend at 8:00 AM on a weekday.","question":"What do you say?","options":["A. Good evening! What are you doing tonight?","B. Good morning! Are you ready for work?","C. Good night! See you tomorrow.","D. Hello! Where is everyone?"],"correctAnswer":"B","explanation":"La mañana es una parte del día donde se saluda con 'Good morning'. La opción B es la más adecuada porque relaciona el saludo con la preparación para el trabajo, típico de las rutinas diarias.","points":2},{"id":"q2","type":"multiple-choice","scenario":"Your friend asks about your daily routine.","question":"You want to say you have breakfast at 7:30 AM. How do you say it?","options":["A. I have breakfast in 7:30 AM.","B. I have breakfast at 7:30 AM.","C. I have breakfast on 7:30 AM.","D. I have breakfast by 7:30 AM."],"correctAnswer":"B","explanation":"Usamos 'at' para indicar horas específicas del día, por eso 'at 7:30 AM' es correcto.","points":2},{"id":"q3","type":"multiple-choice","scenario":"Your teacher asks when you study English.","question":"How do you respond?","options":["A. I study English at night.","B. I study English in night.","C. I study English on night.","D. I study English by night."],"correctAnswer":"A","explanation":"Para indicar partes del día, como 'night', usamos 'at'. Por lo tanto, 'at night' es correcto.","points":2},{"id":"q4","type":"multiple-choice","scenario":"Your friend is curious about your weekend activities.","question":"How do you say you go jogging on Saturdays?","options":["A. I go jogging in Saturdays.","B. I go jogging on Saturdays.","C. I go jogging at Saturdays.","D. I go jogging by Saturdays."],"correctAnswer":"B","explanation":"Utilizamos 'on' para los días de la semana, así que 'on Saturdays' es correcto.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are explaining your evening routine to a new friend.","question":"How do you say you watch TV at 8 PM?","options":["A. I watch TV on 8 PM.","B. I watch TV by 8 PM.","C. I watch TV in 8 PM.","D. I watch TV at 8 PM."],"correctAnswer":"D","explanation":"Para indicar una hora específica, usamos 'at', por lo que 'at 8 PM' es la opción correcta.","points":2},{"id":"q6","type":"multiple-choice","scenario":"Your friend asks about your activities in the afternoon.","question":"What do you say if you study in the library?","options":["A. I study at the library in the afternoon.","B. I study in the library at the afternoon.","C. I study on the library at the afternoon.","D. I study at the library by the afternoon."],"correctAnswer":"A","explanation":"Usamos 'in the afternoon' para indicar partes del día. 'At the library' es correcto para lugares específicos.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex13',
  'a2-m2-l7',
  13,
  'word-search',
  $_$Word Search: Daily Routines$_$,
  $_${"title":"Word Search: Daily Routines","instructions":"Find the hidden words in the grid","words":["WAKE","BREAKFAST","SCHOOL","HOMEWORK","DINNER","BED","MORNING","EVENING","NIGHT","WALK"],"gridSize":10,"clues":[{"word":"WAKE","clue":"To open your eyes in the morning"},{"word":"BREAKFAST","clue":"The first meal of the day"},{"word":"SCHOOL","clue":"Place where children learn"},{"word":"HOMEWORK","clue":"Tasks you do after school"},{"word":"DINNER","clue":"Meal in the evening"},{"word":"BED","clue":"Furniture for sleeping"},{"word":"MORNING","clue":"Time of day from sunrise"},{"word":"EVENING","clue":"Time of day before night"},{"word":"NIGHT","clue":"Time of day when it is dark"},{"word":"WALK","clue":"To move on foot"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex14',
  'a2-m2-l7',
  14,
  'crossword',
  $_$Crossword: Daily Routines$_$,
  $_${"title":"Crossword: Daily Routines","instructions":"Complete the crossword puzzle","items":[{"word":"BREAKFAST","clue":"The first meal of the day, usually in the morning.","direction":"across","row":0,"col":0},{"word":"READ","clue":"To look at and understand words in a book or newspaper.","direction":"down","row":1,"col":7},{"word":"EVENING","clue":"The time of day after the afternoon and before the night.","direction":"across","row":2,"col":2},{"word":"DINNER","clue":"The main meal of the day, usually eaten in the evening.","direction":"down","row":0,"col":5},{"word":"SLEEP","clue":"To rest with your eyes closed at night.","direction":"across","row":4,"col":3},{"word":"MORNING","clue":"The early part of the day, before noon.","direction":"down","row":0,"col":9}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex15',
  'a2-m2-l7',
  15,
  'reading-comprehension',
  $_$Daily Routines: A Day in the Life of Emma$_$,
  $_${"title":"Daily Routines: A Day in the Life of Emma","textPassage":"Emma starts her day early. She wakes up at 6:30 in the morning. After getting up, she takes a shower and gets dressed. At 7:00, she has breakfast with her family. Emma usually eats cereal and drinks a glass of orange juice. At 8:00, she leaves the house and catches the bus to work. She works in an office from 9:00 to 5:00. During her lunch break at 12:30, Emma eats a sandwich and chats with her colleagues. After work, she likes to go to the gym. She exercises for about an hour. Emma gets home at 6:30 in the evening. She has dinner at 7:00 with her family. In the evening, Emma watches TV or reads a book. She usually goes to bed at 10:00. Emma loves her daily routine because it is simple and organized.","wordCount":150,"estimatedReadingTime":2,"questions":[{"question":"What does Emma do at 7:00?","options":["She wakes up","She has breakfast","She leaves for work","She exercises"],"answer":"She has breakfast","explanation":"Emma toma desayuno a las 7:00 con su familia."},{"question":"True or False: Emma takes the train to work.","options":["True","False"],"answer":"False","explanation":"Emma toma el autobús al trabajo, no el tren."},{"question":"When does Emma eat her lunch?","options":["At 12:00","At 12:30","At 1:00","At 1:30"],"answer":"At 12:30","explanation":"Emma almuerza durante su descanso a las 12:30."},{"question":"What activity does Emma do after work?","options":["Goes shopping","Goes to the gym","Cooks dinner","Watches TV"],"answer":"Goes to the gym","explanation":"Después del trabajo, Emma va al gimnasio."},{"question":"True or False: Emma goes to bed at 11:00.","options":["True","False"],"answer":"False","explanation":"Emma se acuesta a las 10:00, no a las 11:00."},{"question":"What does Emma do in the evening?","options":["Reads a book","Goes to the gym","Has breakfast","Takes a shower"],"answer":"Reads a book","explanation":"Por la noche, Emma ve televisión o lee un libro."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex16',
  'a2-m2-l7',
  16,
  'pronunciation-practice',
  $_$Practica de Pronunciación: Rutinas Diarias$_$,
  $_${"title":"Practica de Pronunciación: Rutinas Diarias","instructions":"Practica la pronunciación de las siguientes oraciones enfocándote en el estrés de las palabras y la entonación. Escucha el audio y repite cada oración varias veces.","targetSentences":[{"englishSentence":"I wake up at seven in the morning.","spanishTranslation":"Me despierto a las siete de la mañana.","phoneticTranscription":"/aɪ weɪk ʌp æt ˈsɛvən ɪn ðə ˈmɔːrnɪŋ/","audioModelURL":"https://example.com/audio1","pronunciationTips":"Enfatiza la palabra 'wake' y la hora 'seven'. Mantén un ritmo constante.","focusPoints":"Acento en 'wake' y 'seven'.","commonMistakes":"Evita acentuar 'at' o 'in'."},{"englishSentence":"She goes to work at nine.","spanishTranslation":"Ella va al trabajo a las nueve.","phoneticTranscription":"/ʃi ɡoʊz tu wɜrk æt naɪn/","audioModelURL":"https://example.com/audio2","pronunciationTips":"Fíjate en el acento en 'goes' y 'nine'. Asegúrate de que 'to' no esté demasiado acentuado.","focusPoints":"Acento en 'goes' y 'nine'.","commonMistakes":"Evita acentuar 'at'."},{"englishSentence":"We have lunch at noon.","spanishTranslation":"Almorzamos al mediodía.","phoneticTranscription":"/wi hæv lʌntʃ æt nun/","audioModelURL":"https://example.com/audio3","pronunciationTips":"Acentúa la palabra 'lunch' y 'noon'. Mantén una entonación clara.","focusPoints":"Acento en 'lunch' y 'noon'.","commonMistakes":"No acentúes 'at'."},{"englishSentence":"They finish work at five.","spanishTranslation":"Ellos terminan de trabajar a las cinco.","phoneticTranscription":"/ðeɪ ˈfɪnɪʃ wɜrk æt faɪv/","audioModelURL":"https://example.com/audio4","pronunciationTips":"Enfatiza 'finish' y 'five'. Asegura una transición suave entre las palabras.","focusPoints":"Acento en 'finish' y 'five'.","commonMistakes":"No alargues el sonido de 'at'."},{"englishSentence":"He watches TV in the evening.","spanishTranslation":"Él ve la televisión por la tarde.","phoneticTranscription":"/hi ˈwɑtʃɪz ˈtiˈvi ɪn ði ˈivnɪŋ/","audioModelURL":"https://example.com/audio5","pronunciationTips":"Céntrate en 'watches' y 'evening'. Mantén la parte de 'in the' rápida y ligera.","focusPoints":"Acento en 'watches' y 'evening'.","commonMistakes":"Evita acentuar 'in' o 'the'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex17',
  'a2-m2-l7',
  17,
  'multiple-choice',
  $_$Daily Routines and Times of Day$_$,
  $_${"title":"Daily Routines and Times of Day","instructions":"Selecciona la opción correcta para completar cada oración sobre las rutinas diarias.","questions":[{"id":"q1","question":"I usually have breakfast ___ 8 o'clock in the morning.","options":["A. in","B. on","C. at","D. by"],"correctAnswer":"C","explanation":"Usamos 'at' para hablar de horas específicas del día. Por ejemplo, 'at 8 o'clock'."},{"id":"q2","question":"She goes to bed ___ night after reading a book.","options":["A. in","B. on","C. at","D. for"],"correctAnswer":"C","explanation":"Usamos 'at' con momentos específicos del día, como 'at night'."},{"id":"q3","question":"They play football ___ Saturdays.","options":["A. in","B. on","C. at","D. by"],"correctAnswer":"B","explanation":"Usamos 'on' para los días de la semana. Por ejemplo, 'on Saturdays'."},{"id":"q4","question":"We have a family lunch ___ the afternoon.","options":["A. in","B. on","C. at","D. during"],"correctAnswer":"A","explanation":"Usamos 'in' para hablar de partes del día, como 'in the afternoon'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex18',
  'a2-m2-l7',
  18,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I _____ (have to) wake up early every day for work.","answer":"have to","explanation":"Usamos 'have to' para indicar obligación en la rutina diaria.","points":1},{"sentence":"She _____ (have to) go to school on Saturdays.","answer":"doesn't have to","explanation":"Usamos 'doesn't have to' para indicar que algo no es obligatorio.","points":1},{"sentence":"I _____ (always) drink coffee in the morning.","answer":"always","explanation":"Los adverbios de frecuencia como 'always' indican con qué frecuencia ocurre la rutina.","points":1},{"sentence":"He _____ (usually) finishes work at 5 PM.","answer":"usually","explanation":"'Usually' se coloca antes del verbo principal para hablar de hábitos.","points":1},{"sentence":"We _____ (sometimes) have lunch at a restaurant.","answer":"sometimes","explanation":"'Sometimes' indica una frecuencia ocasional en nuestra rutina.","points":1},{"sentence":"They _____ (never) go to bed late on weekdays.","answer":"never","explanation":"'Never' indica que una acción no ocurre nunca.","points":1},{"sentence":"Do you _____ (have to) wear a uniform for work?","answer":"have to","explanation":"En preguntas, usamos 'have to' para preguntar por obligaciones.","points":1},{"sentence":"My brother _____ (has to) study every evening.","answer":"has to","explanation":"Usamos 'has to' para la tercera persona singular (he/she/it).","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex19',
  'a2-m2-l7',
  19,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["at","I","8","am","wake","up","."],"correctOrder":["I","wake","up","at","8","am","."],"translation":"Me despierto a las 8 am.","hint":"La estructura es Sujeto + Verbo + Preposición + Hora.","points":2},{"id":2,"words":["in","usually","the","I","evening",".","read"],"correctOrder":["I","usually","read","in","the","evening","."],"translation":"Usualmente leo por la tarde.","hint":"La estructura es Sujeto + Adverbio + Verbo + Preposición + Tiempo del día.","points":2},{"id":3,"words":["have","breakfast","on","I","Sundays","late","."],"correctOrder":["I","have","breakfast","late","on","Sundays","."],"translation":"Desayuno tarde los domingos.","hint":"La estructura es Sujeto + Verbo + Objeto + Adverbio + Preposición + Día.","points":2},{"id":4,"words":["on","go","I","to","bed","10","at","pm","."],"correctOrder":["I","go","to","bed","at","10","pm","."],"translation":"Me acuesto a las 10 pm.","hint":"La estructura es Sujeto + Verbo + Preposición + Hora.","points":2},{"id":5,"words":["at","I","work","start","9","am","."],"correctOrder":["I","start","work","at","9","am","."],"translation":"Comienzo a trabajar a las 9 am.","hint":"La estructura es Sujeto + Verbo + Objeto + Preposición + Hora.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex20',
  'a2-m2-l7',
  20,
  'speaking-analysis',
  $_$Speaking Analysis: Daily Routines$_$,
  $_${"title":"Speaking Analysis: Daily Routines","instructions":"Choose the most appropriate option for each speaking situation.","questions":[{"id":"q1","type":"multiple-choice","scenario":"Your friend asks when you usually have breakfast.","question":"What is the most natural reply?","options":["A. I eat breakfast in the morning.","B. I am eating breakfast on the morning.","C. I have breakfast at the morning.","D. I having breakfast in the morning."],"correctAnswer":"A","explanation":"La respuesta correcta es 'I eat breakfast in the morning.' porque usamos 'in the morning' para referirnos a un tiempo del día.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are telling your class about your daily activities.","question":"Which sentence uses the correct preposition of time?","options":["A. I play soccer at evenings.","B. I play soccer in the evenings.","C. I play soccer on evenings.","D. I play soccer by evenings."],"correctAnswer":"B","explanation":"La opción correcta es 'I play soccer in the evenings.' porque 'in the evenings' es la forma correcta de hablar sobre actividades habituales.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are explaining when you go to bed.","question":"Which sentence is correct?","options":["A. I go to bed in night.","B. I go to bed on night.","C. I go to bed at night.","D. I go to bed by night."],"correctAnswer":"C","explanation":"La respuesta correcta es 'I go to bed at night.' porque usamos 'at night' para hablar sobre actividades nocturnas.","points":2},{"id":"q4","type":"multiple-choice","scenario":"Your friend wants to know when you usually do your homework.","question":"What is the most natural way to respond?","options":["A. I do my homework at the afternoon.","B. I do my homework on the afternoon.","C. I do my homework in the afternoon.","D. I doing my homework in the afternoon."],"correctAnswer":"C","explanation":"La opción correcta es 'I do my homework in the afternoon.' porque 'in the afternoon' es la preposición correcta para esta expresión temporal.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are telling someone when you leave for work.","question":"Which sentence is correct?","options":["A. I leave for work on 8 o'clock.","B. I leave for work at 8 o'clock.","C. I leave for work in 8 o'clock.","D. I leave for work by 8 o'clock."],"correctAnswer":"B","explanation":"La respuesta correcta es 'I leave for work at 8 o'clock.' porque 'at' se usa para tiempos específicos.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are describing your weekend activities to a friend.","question":"Which sentence is correct?","options":["A. I relax in the weekends.","B. I relax on the weekends.","C. I relax at the weekends.","D. I relax by the weekends."],"correctAnswer":"B","explanation":"La opción correcta es 'I relax on the weekends.' porque 'on the weekends' es la preposición correcta para referirse a los fines de semana.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex21',
  'a2-m2-l7',
