  $_${"title":"Word Search: Daily Routines","instructions":"Find the hidden words in the grid","words":["BREAKFAST","SCHOOL","MORNING","EVENING","DINNER","WALK","TV","BED"],"gridSize":10,"clues":[{"word":"BREAKFAST","clue":"The first meal of the day"},{"word":"SCHOOL","clue":"Place where you learn"},{"word":"MORNING","clue":"Time of day before noon"},{"word":"EVENING","clue":"Time of day after the sun sets"},{"word":"DINNER","clue":"Meal at the end of the day"},{"word":"WALK","clue":"Go on foot"},{"word":"TV","clue":"Watch this for shows and news"},{"word":"BED","clue":"You sleep here at night"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex22',
  'a2-m2-l7',
  22,
  'crossword',
  $_$Crossword: Daily Routines$_$,
  $_${"title":"Crossword: Daily Routines","instructions":"Complete the crossword puzzle","items":[{"word":"BREAKFAST","clue":"The meal you have in the morning","direction":"across","row":0,"col":0},{"word":"EVENING","clue":"The time of day after the afternoon","direction":"down","row":0,"col":3},{"word":"SCHOOL","clue":"A place where children learn","direction":"across","row":2,"col":0},{"word":"AT","clue":"We use this preposition with clock times","direction":"down","row":0,"col":6},{"word":"WATCH","clue":"A verb meaning to look at something like TV","direction":"across","row":4,"col":2},{"word":"SLEEP","clue":"What you do at night in bed","direction":"down","row":2,"col":8},{"word":"MORNING","clue":"The time of day when you wake up","direction":"across","row":6,"col":1}]}$_$
