INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex23',
  'a2-m2-l7',
  23,
  'reading-comprehension',
  $_$Daily Routines$_$,
  $_${"title":"Daily Routines","textPassage":"Every morning, Maria wakes up at 7 o'clock. She has breakfast with her family in the kitchen. At 8:30, she takes the bus to school. Maria's classes start at 9 o'clock and finish at 2 o'clock in the afternoon. After school, Maria usually does her homework in the living room. In the evening, she has dinner at 7 o'clock with her family. She watches TV or reads a book before going to bed at 10 o'clock.","wordCount":84,"estimatedReadingTime":1,"questions":[{"question":"What time does Maria wake up in the morning?","options":["At 6 o'clock","At 7 o'clock","At 8 o'clock","At 9 o'clock"],"answer":"At 7 o'clock","explanation":"Maria se despierta a las 7 en punto, como se menciona en la primera oración."},{"question":"Where does Maria have breakfast?","options":["In the living room","In the garden","In the kitchen","In her bedroom"],"answer":"In the kitchen","explanation":"Maria desayuna en la cocina con su familia, como se indica en el texto."},{"question":"Maria's classes finish at...","options":["1 o'clock","2 o'clock","3 o'clock","4 o'clock"],"answer":"2 o'clock","explanation":"Las clases de Maria terminan a las 2 en punto de la tarde, según el texto."},{"question":"True or False: Maria goes to bed at 9 o'clock.","answer":"False","explanation":"Falso. Maria se acuesta a las 10 en punto, no a las 9."},{"question":"What does Maria do after school?","options":["She goes shopping","She visits friends","She does her homework","She plays sports"],"answer":"She does her homework","explanation":"Después de la escuela, Maria generalmente hace su tarea en la sala de estar."},{"question":"True or False: Maria takes the bus to school in the evening.","answer":"False","explanation":"Falso. Maria toma el autobús a la escuela por la mañana, a las 8:30."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex24',
  'a2-m2-l7',
  24,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Rutinas Diarias$_$,
  $_${"title":"Práctica de Pronunciación: Rutinas Diarias","instructions":"Practica la pronunciación de estas oraciones en inglés enfocándote en el estrés de las palabras y la entonación. Lee cada oración en voz alta y presta atención a los consejos de pronunciación.","targetSentences":[{"englishSentence":"I wake up at seven in the morning.","spanishTranslation":"Me despierto a las siete de la mañana.","phoneticTranscription":"/aɪ weɪk ʌp æt ˈsɛvən ɪn ðə ˈmɔːrnɪŋ/","audioModelURL":"audio-model-url-placeholder-1","pronunciationTips":"Asegúrate de enfatizar 'wake' y 'seven'. La 'th' en 'the' es suave.","focusPoints":"Enfatiza 'wake' y 'seven'.","commonMistakes":"Evita pronunciar 'the' como 'ze', y no olvides la 'k' en 'wake'."},{"englishSentence":"I have breakfast at eight o'clock.","spanishTranslation":"Desayuno a las ocho en punto.","phoneticTranscription":"/aɪ hæv ˈbrɛkfəst æt eɪt əˈklɒk/","audioModelURL":"audio-model-url-placeholder-2","pronunciationTips":"La palabra 'breakfast' se pronuncia como /ˈbrɛkfəst/, no olvides la 'st'.","focusPoints":"Enfatiza 'breakfast' y 'eight'.","commonMistakes":"No pronuncies 'breakfast' como si tuviera una pausa entre 'break' y 'fast'."},{"englishSentence":"I go to work at nine.","spanishTranslation":"Voy al trabajo a las nueve.","phoneticTranscription":"/aɪ ɡoʊ tə wɜrk æt naɪn/","audioModelURL":"audio-model-url-placeholder-3","pronunciationTips":"Enfatiza 'work' y 'nine'. La 'r' en 'work' es suave.","focusPoints":"Enfatiza 'work' y 'nine'.","commonMistakes":"No digas 'wark'; la 'o' en 'go' debe ser clara."},{"englishSentence":"I have lunch at twelve.","spanishTranslation":"Almuerzo a las doce.","phoneticTranscription":"/aɪ hæv lʌnʧ æt twɛlv/","audioModelURL":"audio-model-url-placeholder-4","pronunciationTips":"Asegúrate de que 'lunch' suene clara, sin omitir la 'n'.","focusPoints":"Enfatiza 'lunch' y 'twelve'.","commonMistakes":"Evita decir 'twelv'; la 'e' al final se pronuncia."},{"englishSentence":"I watch TV in the evening.","spanishTranslation":"Veo la televisión por la tarde.","phoneticTranscription":"/aɪ wɒʧ ˌtiːˈviː ɪn ði ˈiːvnɪŋ/","audioModelURL":"audio-model-url-placeholder-5","pronunciationTips":"La 'ch' en 'watch' suena como /ʧ/. Enfoca en 'TV' y 'evening'.","focusPoints":"Enfatiza 'TV' y 'evening'.","commonMistakes":"No pronuncies 'watch' como 'wach'; la 'o' es corta."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex25',
  'a2-m2-l7',
  25,
  'multiple-choice',
  $_$Daily Routines and Times of Day$_$,
  $_${"title":"Daily Routines and Times of Day","instructions":"Selecciona la respuesta correcta. Todas las preguntas están relacionadas con las rutinas diarias y los tiempos del día. Lee cuidadosamente cada opción antes de elegir.","questions":[{"id":"q1","question":"Maria usually goes to the gym ______ the morning.","options":["A. in","B. on","C. at","D. from"],"correctAnswer":"A","explanation":"Usamos 'in' para hablar de partes del día, como 'in the morning'."},{"id":"q2","question":"They have dinner ______ 7 PM every day.","options":["A. in","B. on","C. at","D. over"],"correctAnswer":"C","explanation":"Usamos 'at' para hablar de horas exactas, como 'at 7 PM'."},{"id":"q3","question":"Tom works ______ Monday to Friday.","options":["A. at","B. from","C. on","D. in"],"correctAnswer":"B","explanation":"Usamos 'from' para indicar el inicio de un periodo de tiempo, como 'from Monday to Friday'."},{"id":"q4","question":"She reads a book ______ the afternoon.","options":["A. on","B. in","C. at","D. by"],"correctAnswer":"B","explanation":"Usamos 'in' para hablar de partes del día, como 'in the afternoon'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex26',
  'a2-m2-l7',
  26,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I have to wake up early _______ the morning to get ready for school.","acceptableAnswers":["in"],"explanation":"Usamos 'in' para hablar de partes del día. Aquí nos referimos a 'la mañana'.","points":1},{"sentence":"My sister likes to read a book _______ night before she sleeps.","acceptableAnswers":["at"],"explanation":"Usamos 'at' para hablar de tiempos específicos del día, como 'at night'.","points":1},{"sentence":"We usually go to the park _______ Sundays.","acceptableAnswers":["on"],"explanation":"Usamos 'on' para hablar de días específicos de la semana.","points":1},{"sentence":"I would like to have a lot of friends to play with _______ school.","acceptableAnswers":["after"],"explanation":"Usamos 'after' para hablar de secuencias de tiempo, en este caso, después de la escuela.","points":1},{"sentence":"My dad has to work _______ the weekend, so we can't take a trip.","acceptableAnswers":["on"],"explanation":"Usamos 'on' para hablar de días específicos, incluso 'on the weekend'.","points":1},{"sentence":"How many books do you have _______ your school bag?","acceptableAnswers":["in"],"explanation":"Usamos 'in' para referirnos a lo que contiene un objeto, como dentro de una mochila.","points":1},{"sentence":"We have to finish our homework _______ 5 PM.","acceptableAnswers":["by"],"explanation":"Usamos 'by' para indicar un límite de tiempo para completar una acción.","points":1},{"sentence":"I don't have to go to school _______ Saturday.","acceptableAnswers":["on"],"explanation":"Usamos 'on' para hablar de días de la semana, como en 'on Saturday'.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex27',
  'a2-m2-l7',
  27,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["I","at","breakfast","o'clock","have","seven","."],"correctOrder":["I","have","breakfast","at","seven","o'clock","."],"translation":"Desayuno a las siete en punto.","hint":"Sujeto + Verbo + Objeto + Preposición + Hora.","points":2},{"id":2,"words":["play","in","the","I","park","football","."],"correctOrder":["I","play","football","in","the","park","."],"translation":"Juego al fútbol en el parque.","hint":"Sujeto + Verbo + Objeto + Preposición + Lugar.","points":2},{"id":3,"words":["work","at","starts","eight","o'clock","my","."],"correctOrder":["My","work","starts","at","eight","o'clock","."],"translation":"Mi trabajo empieza a las ocho en punto.","hint":"Sujeto (My work) + Verbo + Preposición + Hora.","points":2},{"id":4,"words":["in","I","homework","do","evening","my","the","."],"correctOrder":["I","do","my","homework","in","the","evening","."],"translation":"Hago mis deberes por la tarde.","hint":"Sujeto + Verbo + Objeto + Preposición + Tiempo del día.","points":2},{"id":5,"words":["go","bed","at","to","I","ten","o'clock","."],"correctOrder":["I","go","to","bed","at","ten","o'clock","."],"translation":"Me acuesto a las diez en punto.","hint":"Sujeto + Verbo + Preposición + Lugar + Preposición + Hora.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex28',
  'a2-m2-l7',
  28,
  'speaking-analysis',
  $_$Speaking Analysis: Daily Routines$_$,
  $_${"title":"Speaking Analysis: Daily Routines","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking to a friend about your daily routine.","question":"What do you say to explain what you do in the morning?","options":["I wake up at 7 o'clock and have breakfast.","I am waking up at 7 o'clock and having breakfast.","I was waking up at 7 o'clock and having breakfast.","I will wake up at 7 o'clock and have breakfast."],"correctAnswer":"A","explanation":"La opción A utiliza el presente simple correctamente para describir una rutina diaria.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are telling a friend when you have lunch.","question":"Which sentence is correct?","options":["I have lunch in the afternoon.","I have lunch on the afternoon.","I have lunch at the afternoon.","I have lunch by the afternoon."],"correctAnswer":"A","explanation":"La opción A es correcta porque 'in the afternoon' es la preposición adecuada para hablar de periodos del día.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are explaining your evening activities to a classmate.","question":"How do you describe your activities?","options":["I study at night and go to bed at 10.","I study on night and go to bed on 10.","I study in night and go to bed in 10.","I study by night and go to bed by 10."],"correctAnswer":"A","explanation":"La opción A es correcta porque 'at night' y 'at 10' son las preposiciones adecuadas para hablar de tiempos específicos.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You want to ask a friend what time they start their day.","question":"What is the best way to ask?","options":["At what time do you wake up?","On what time do you wake up?","In what time do you wake up?","By what time do you wake up?"],"correctAnswer":"A","explanation":"La opción A es la correcta porque 'at what time' es la estructura adecuada para preguntar sobre la hora.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are discussing when you like to read.","question":"Which sentence is correct?","options":["I like to read at night.","I like to read in night.","I like to read on night.","I like to read by night."],"correctAnswer":"A","explanation":"La opción A es correcta porque 'at night' es la preposición adecuada para hablar de la noche en general.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are telling someone about your favorite TV show time.","question":"How do you say it?","options":["My favorite show is on at 8 PM.","My favorite show is in 8 PM.","My favorite show is by 8 PM.","My favorite show is on 8 PM."],"correctAnswer":"A","explanation":"La opción A es correcta porque 'at' se usa para hablar de horas específicas en inglés.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex29',
  'a2-m2-l7',
  29,
  'word-search',
  $_$Word Search: Daily Routines$_$,
  $_${"title":"Word Search: Daily Routines","instructions":"Find the hidden words in the grid","words":["WAKE","BREAKFAST","WORK","LUNCH","DINNER","SLEEP","MORNING","EVENING","NIGHT","AFTERNOON"],"gridSize":10,"clues":[{"word":"WAKE","clue":"To stop sleeping and become conscious in the morning."},{"word":"BREAKFAST","clue":"The first meal of the day, usually in the morning."},{"word":"WORK","clue":"An activity you do for a job or career."},{"word":"LUNCH","clue":"A meal you have in the middle of the day."},{"word":"DINNER","clue":"The main meal of the day, usually in the evening."},{"word":"SLEEP","clue":"To rest with your eyes closed, usually at night."},{"word":"MORNING","clue":"The early part of the day, before noon."},{"word":"EVENING","clue":"The time of day between afternoon and night."},{"word":"NIGHT","clue":"The time of day when it is dark and you usually sleep."},{"word":"AFTERNOON","clue":"The time of day from noon until evening."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex30',
  'a2-m2-l7',
  30,
  'crossword',
  $_$Crossword: Daily Routines$_$,
  $_${"title":"Crossword: Daily Routines","instructions":"Complete the crossword puzzle","items":[{"word":"MORNING","clue":"The time of day when you have breakfast.","direction":"across","row":0,"col":0},{"word":"EVENING","clue":"The time of day when you usually have dinner.","direction":"down","row":0,"col":2},{"word":"WORK","clue":"An activity you do to earn money.","direction":"across","row":2,"col":0},{"word":"SCHOOL","clue":"A place where you learn new things.","direction":"down","row":0,"col":4},{"word":"LUNCH","clue":"A meal you have in the middle of the day.","direction":"across","row":4,"col":0},{"word":"AT","clue":"Preposition used with specific times, e.g., ___ 7 o'clock.","direction":"across","row":6,"col":1},{"word":"READ","clue":"What you do with a book.","direction":"down","row":6,"col":4}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex31',
  'a2-m2-l7',
  31,
  'reading-comprehension',
  $_$Daily Routines$_$,
  $_${"title":"Daily Routines","textPassage":"Every day, Maria wakes up at 7:00 in the morning. She gets out of bed and has breakfast in the kitchen at 7:30. Maria likes to eat toast and drink orange juice. At 8:00, she leaves home and catches the bus to work. She arrives at her office at 8:45 and starts working at 9:00. Maria works in an office in the city. She has lunch at 12:30 in the afternoon at a small cafe near her office. After lunch, she continues working until 5:00 in the evening. Maria usually gets home at 6:00. In the evening, she likes to cook dinner and watch TV. She goes to bed at 10:30 at night. On weekends, Maria wakes up later and enjoys spending time with her friends.","wordCount":130,"estimatedReadingTime":2,"questions":[{"question":"What time does Maria wake up?","options":["6:30","7:00","7:30","8:00"],"answer":"7:00","explanation":"El texto menciona que Maria se despierta a las 7:00 de la mañana."},{"question":"Where does Maria have breakfast?","options":["In her bedroom","At her office","In the kitchen","At a cafe"],"answer":"In the kitchen","explanation":"Maria desayuna en la cocina, según el texto."},{"question":"What does Maria do at 8:00?","options":["She starts working","She catches the bus","She has lunch","She goes to bed"],"answer":"She catches the bus","explanation":"A las 8:00, Maria toma el autobús para ir al trabajo."},{"question":"Maria has lunch at a small cafe.","options":["True","False"],"answer":"True","explanation":"El texto dice que Maria almuerza en un pequeño café cerca de su oficina."},{"question":"On weekends, Maria:","options":["Wakes up earlier","Works at the office","Spends time with friends","Cooks dinner"],"answer":"Spends time with friends","explanation":"Durante los fines de semana, Maria disfruta pasar tiempo con sus amigos."},{"question":"Which of the following is NOT mentioned about Maria's routine?","options":["She works in the city","She reads books","She watches TV","She cooks dinner"],"answer":"She reads books","explanation":"El texto no menciona que Maria lea libros, pero sí menciona que trabaja en la ciudad, ve televisión y cocina la cena."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex32',
  'a2-m2-l7',
  32,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Enfatizar Palabras en Oraciones$_$,
  $_${"title":"Práctica de Pronunciación: Enfatizar Palabras en Oraciones","instructions":"Practica la pronunciación de las siguientes oraciones enfocándote en el uso correcto del presente simple y las preposiciones de tiempo. Escucha los modelos de audio y repite las oraciones en voz alta. Presta atención a la pronunciación de las palabras clave y al ritmo de la frase.","targetSentences":[{"englishSentence":"I wake up at six o'clock every morning.","spanishTranslation":"Me despierto a las seis en punto todas las mañanas.","phoneticTranscription":"/aɪ weɪk ʌp æt sɪks əˈklɒk ˈɛvri ˈmɔːrnɪŋ/","audioModelURL":"https://example.com/audio1","pronunciationTips":"Enfatiza 'wake' y 'six o'clock' para indicar la acción principal y el tiempo.","focusPoints":"Enfatizar 'wake' y 'six o'clock'","commonMistakes":"No omitas el sonido final en 'wake' y asegúrate de pronunciar el sonido /s/ en 'six'."},{"englishSentence":"She has breakfast in the kitchen.","spanishTranslation":"Ella desayuna en la cocina.","phoneticTranscription":"/ʃiː hæz ˈbrɛkfəst ɪn ðə ˈkɪtʃɪn/","audioModelURL":"https://example.com/audio2","pronunciationTips":"Resalta 'breakfast' para subrayar la actividad principal.","focusPoints":"Enfatizar 'breakfast'","commonMistakes":"No pronuncies 'breakfast' como dos palabras separadas."},{"englishSentence":"We go to work at eight in the morning.","spanishTranslation":"Vamos al trabajo a las ocho de la mañana.","phoneticTranscription":"/wiː ɡoʊ tuː wɜːrk æt eɪt ɪn ðə ˈmɔːrnɪŋ/","audioModelURL":"https://example.com/audio3","pronunciationTips":"Asegúrate de enfatizar 'work' y 'eight' para claridad.","focusPoints":"Enfatizar 'work' y 'eight'","commonMistakes":"Evita decir 'at eight' como una sola palabra; debe sonar separado."},{"englishSentence":"They have lunch at noon.","spanishTranslation":"Ellos almuerzan al mediodía.","phoneticTranscription":"/ðeɪ hæv lʌntʃ æt nuːn/","audioModelURL":"https://example.com/audio4","pronunciationTips":"Resalta 'lunch' y 'noon' para indicar claramente el evento y el tiempo.","focusPoints":"Enfatizar 'lunch' y 'noon'","commonMistakes":"No olvides el sonido /n/ final en 'noon'."},{"englishSentence":"He goes to bed at ten o'clock.","spanishTranslation":"Él se acuesta a las diez en punto.","phoneticTranscription":"/hiː ɡoʊz tuː bɛd æt tɛn əˈklɒk/","audioModelURL":"https://example.com/audio5","pronunciationTips":"Enfatiza 'bed' y 'ten o'clock' para claridad en la rutina.","focusPoints":"Enfatizar 'bed' y 'ten o'clock'","commonMistakes":"Asegúrate de no unir 'ten' y 'o'clock' como una sola palabra."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex33',
  'a2-m2-l7',
  33,
  'multiple-choice',
  $_$Daily Routines: Present Simple and Prepositions of Time$_$,
  $_${"title":"Daily Routines: Present Simple and Prepositions of Time","instructions":"Selecciona la respuesta correcta para cada pregunta sobre las rutinas diarias. Usa el presente simple y las preposiciones de tiempo.","questions":[{"id":"q1","question":"Maria wakes up _____ 7 o'clock every morning.","options":["A. at","B. in","C. on","D. by"],"correctAnswer":"A","explanation":"La preposición correcta para las horas es 'at', por ejemplo, 'at 7 o'clock'."},{"id":"q2","question":"I usually go to the gym _____ the evening.","options":["A. on","B. at","C. in","D. to"],"correctAnswer":"C","explanation":"Usamos 'in' para indicar momentos del día, como 'in the evening'."},{"id":"q3","question":"They have lunch _____ noon every day.","options":["A. in","B. at","C. on","D. over"],"correctAnswer":"B","explanation":"Para tiempos específicos como 'noon', utilizamos 'at'."},{"id":"q4","question":"We visit our grandparents _____ Sundays.","options":["A. in","B. at","C. on","D. by"],"correctAnswer":"C","explanation":"La preposición correcta para los días de la semana es 'on', como en 'on Sundays'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex34',
  'a2-m2-l7',
  34,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I __________ (go) to school at eight o'clock every morning.","acceptableAnswers":["go"],"explanation":"En presente simple afirmativo, usamos la forma base del verbo para sujetos como 'I', 'you', 'we', 'they'.","points":1},{"sentence":"We have lunch __________ (at/on/in) noon.","acceptableAnswers":["at"],"explanation":"Usamos 'at' para referirnos a horas específicas del día.","points":1},{"sentence":"She __________ (watch) TV in the evening.","acceptableAnswers":["watches"],"explanation":"Para tercera persona del singular, añadimos '-es' al verbo en presente simple.","points":1},{"sentence":"My brother usually __________ (play) football on Saturdays.","acceptableAnswers":["plays"],"explanation":"En presente simple para tercera persona, añadimos '-s' al verbo.","points":1},{"sentence":"They go to the park __________ (at/on/in) the weekend.","acceptableAnswers":["on"],"explanation":"Usamos 'on' para referirnos a días específicos y fines de semana.","points":1},{"sentence":"We __________ (have) dinner at 7 p.m.","acceptableAnswers":["have"],"explanation":"En presente simple afirmativo, usamos la forma base del verbo para sujetos como 'I', 'you', 'we', 'they'.","points":1},{"sentence":"She reads a book __________ (at/on/in) the afternoon.","acceptableAnswers":["in"],"explanation":"Usamos 'in' para referirnos a partes del día como la tarde, mañana o noche.","points":1},{"sentence":"Tom __________ (study) English every day.","acceptableAnswers":["studies"],"explanation":"Para tercera persona del singular, cambiamos 'y' por 'ies' en verbos terminados en consonante + 'y'.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex35',
  'a2-m2-l7',
  35,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["Saturday","clean","I","every","my","have","to","room","."],"correctOrder":["I","have","to","clean","my","room","every","Saturday","."],"translation":"Tengo que limpiar mi habitación todos los sábados.","hint":"Sujeto + have to + verbo + complemento.","points":2},{"id":2,"words":["have","doesn't","go","school","on","to","to","She","Sunday","."],"correctOrder":["She","doesn't","have","to","go","to","school","on","Sunday","."],"translation":"Ella no tiene que ir a la escuela el domingo.","hint":"Sujeto + doesn't have to + verbo + complemento.","points":2},{"id":3,"words":["visit","this","We","will","grandparents","weekend","our","."],"correctOrder":["We","will","visit","our","grandparents","this","weekend","."],"translation":"Visitaremos a nuestros abuelos este fin de semana.","hint":"Sujeto + will + verbo + complemento.","points":2},{"id":4,"words":["are","a","party","to","They","next","have","going","week","."],"correctOrder":["They","are","going","to","have","a","party","next","week","."],"translation":"Ellos van a tener una fiesta la próxima semana.","hint":"Sujeto + are going to + verbo + complemento.","points":2},{"id":5,"words":["Spain","travel","to","He","next","would","like","to","year","."],"correctOrder":["He","would","like","to","travel","to","Spain","next","year","."],"translation":"Le gustaría viajar a España el próximo año.","hint":"Sujeto + would like to + verbo + complemento.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex36',
  'a2-m2-l7',
  36,
  'speaking-analysis',
  $_$Speaking Analysis: Daily Routines$_$,
  $_${"title":"Speaking Analysis: Daily Routines","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking to a friend about your daily routine and they ask, 'What time do you usually wake up?'","question":"What is the most natural response?","options":["A. I wake up at 7:00 in the morning.","B. I wake up on 7:00 in the morning.","C. I wake up in 7:00 in the morning.","D. I wake up by 7:00 in the morning."],"correctAnswer":"A","explanation":"La opción A es la correcta porque usamos 'at' para referirnos a horas específicas del día.","points":2},{"id":"q2","type":"multiple-choice","scenario":"Your friend asks, 'When do you usually have lunch?'","question":"Choose the most appropriate preposition.","options":["A. I have lunch on 12:30.","B. I have lunch in 12:30.","C. I have lunch at 12:30.","D. I have lunch by 12:30."],"correctAnswer":"C","explanation":"La opción C es correcta porque 'at' se utiliza para indicar un momento específico.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You're describing your weekend routine to a colleague.","question":"Which sentence is in the correct form?","options":["A. I usually going to the park in the afternoon.","B. I usually go to the park in the afternoon.","C. I usually goes to the park in the afternoon.","D. I usually gone to the park in the afternoon."],"correctAnswer":"B","explanation":"La opción B es correcta porque el verbo en presente simple para 'I' es 'go', no 'going', 'goes' ni 'gone'.","points":2},{"id":"q4","type":"multiple-choice","scenario":"A friend asks, 'What do you do in the evenings?'","question":"Which response is most appropriate?","options":["A. I going for a walk.","B. I go for a walk.","C. I gone for a walk.","D. I goes for a walk."],"correctAnswer":"B","explanation":"La opción B es correcta porque el presente simple para 'I' se forma con 'go'.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are telling a friend about your morning routine.","question":"What is the correct sentence?","options":["A. I check my emails in the morning.","B. I check my emails on the morning.","C. I check my emails at the morning.","D. I check my emails by the morning."],"correctAnswer":"A","explanation":"La opción A es correcta porque usamos 'in' para referirnos a partes del día como 'in the morning'.","points":2},{"id":"q6","type":"multiple-choice","scenario":"Someone asks about your afternoon activities.","question":"What is the correct response?","options":["A. I take a nap in the afternoon.","B. I take a nap on the afternoon.","C. I take a nap at the afternoon.","D. I take a nap by the afternoon."],"correctAnswer":"A","explanation":"La opción A es la correcta porque 'in' se utiliza para partes del día como 'in the afternoon'.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
