INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex39',
  'a2-m2-l7',
  39,
  'reading-comprehension',
  $_$A Day in the Life of Maria$_$,
  $_${"title":"A Day in the Life of Maria","text":"Maria is a student. She goes to school from Monday to Friday. Every morning, she wakes up at 7 o'clock. After she wakes up, she brushes her teeth and has breakfast at 7:30. She usually eats toast and drinks orange juice. At 8:15, she leaves the house and walks to school. School starts at 9 o'clock. She finishes school at 3 o'clock in the afternoon. After school, Maria goes home and does her homework. She likes to read books in the evening. Maria has dinner with her family at 7 o'clock. They often eat pasta or chicken. Maria goes to bed at 10 o'clock. She always reads a little before sleeping.","wordCount":120,"estimatedReadingTime":1,"questions":[{"question":"What time does Maria wake up?","options":["6 o'clock","7 o'clock","8 o'clock","9 o'clock"],"answer":"7 o'clock","explanation":"Maria se despierta a las 7 en punto, como se menciona al principio del texto."},{"question":"Maria has breakfast at:","options":["7:00","7:30","8:00","8:30"],"answer":"7:30","explanation":"El texto dice que Maria desayuna a las 7:30."},{"question":"TRUE or FALSE: Maria walks to school at 8:15.","options":["TRUE","FALSE"],"answer":"TRUE","explanation":"Es cierto que Maria sale de casa y camina a la escuela a las 8:15, según el texto."},{"question":"What does Maria do after school?","options":["She reads books.","She does her homework.","She goes to bed.","She plays video games."],"answer":"She does her homework.","explanation":"Después de la escuela, Maria va a casa y hace su tarea, como se indica en el texto."},{"question":"Maria has dinner with her family at:","options":["6:00","6:30","7:00","7:30"],"answer":"7:00","explanation":"Maria cena con su familia a las 7:00, según el texto."},{"question":"What does Maria do before sleeping?","options":["She watches TV.","She reads a little.","She plays games.","She listens to music."],"answer":"She reads a little.","explanation":"Maria siempre lee un poco antes de dormir, como se menciona al final del texto."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex40',
  'a2-m2-l7',
  40,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Tiempos y Rutinas Diarias$_$,
  $_${"title":"Práctica de Pronunciación: Tiempos y Rutinas Diarias","instructions":"Practica la pronunciación de las siguientes oraciones enfocándote en la entonación correcta y la pronunciación clara de las palabras clave. Escucha cada modelo de audio y repite en voz alta.","targetSentences":[{"english":"I have breakfast at seven in the morning.","spanish":"Desayuno a las siete de la mañana.","phoneticTranscription":"/aɪ hæv ˈbrɛkfəst æt ˈsɛvən ɪn ðə ˈmɔrnɪŋ/","audioModelURL":"https://example.com/audio1","pronunciationTips":"Asegúrate de pronunciar claramente 'breakfast' y prestar atención a la entonación al mencionar 'seven in the morning'.","focusPoints":"Entonación al mencionar la hora.","commonMistakes":"No omitir el sonido de 't' en 'at'."},{"english":"She goes to the gym in the evening.","spanish":"Ella va al gimnasio por la tarde.","phoneticTranscription":"/ʃi ɡoʊz tu ðə dʒɪm ɪn ðə ˈivnɪŋ/","audioModelURL":"https://example.com/audio2","pronunciationTips":"Enfatiza el sonido /ʒ/ en 'gym' y asegúrate de una entonación suave al decir 'in the evening'.","focusPoints":"Pronunciación de 'gym'.","commonMistakes":"Evita pronunciar 'gym' como 'gim'."},{"english":"He reads a book before bed.","spanish":"Él lee un libro antes de dormir.","phoneticTranscription":"/hi ridz ə bʊk bɪˈfɔr bɛd/","audioModelURL":"https://example.com/audio3","pronunciationTips":"Asegúrate de alargar el sonido /i/ en 'reads' y utiliza una entonación descendente al final de la oración.","focusPoints":"Pronunciación de 'reads' y entonación.","commonMistakes":"No confundir 'reads' con 'reed'."},{"english":"We have lunch at noon.","spanish":"Almorzamos al mediodía.","phoneticTranscription":"/wi hæv lʌntʃ æt nun/","audioModelURL":"https://example.com/audio4","pronunciationTips":"Cuida la pronunciación del sonido /ʌ/ en 'lunch'.","focusPoints":"Claridad en 'lunch' y 'noon'.","commonMistakes":"No pronunciar 'lunch' como 'launch'."},{"english":"They watch TV in the afternoon.","spanish":"Ellos ven televisión por la tarde.","phoneticTranscription":"/ðeɪ wɑtʃ ˈtiˈvi ɪn ði ˌæftərˈnun/","audioModelURL":"https://example.com/audio5","pronunciationTips":"Enfatiza la 'ch' en 'watch' y utiliza una entonación clara al mencionar 'afternoon'.","focusPoints":"Pronunciación de 'watch'.","commonMistakes":"No cambiar 'watch' por 'wotch'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex41',
  'a2-m2-l7',
  41,
  'multiple-choice',
  $_$Daily Routines and Time Prepositions$_$,
  $_${"title":"Daily Routines and Time Prepositions","instructions":"Elige la respuesta correcta para completar las oraciones sobre rutinas diarias y preposiciones de tiempo.","questions":[{"id":"q1","question":"I usually wake up ___ 7 o'clock in the morning.","options":["A. in","B. on","C. at","D. by"],"correctAnswer":"C","explanation":"Usamos 'at' para referirnos a horas específicas del día, como 'at 7 o'clock'."},{"id":"q2","question":"She goes to the gym ___ Mondays and Wednesdays.","options":["A. on","B. in","C. at","D. by"],"correctAnswer":"A","explanation":"Usamos 'on' para referirnos a días específicos de la semana, como 'on Mondays'."},{"id":"q3","question":"We have dinner ___ the evening.","options":["A. in","B. at","C. on","D. by"],"correctAnswer":"A","explanation":"Usamos 'in' para referirnos a partes del día, como 'in the evening'."},{"id":"q4","question":"He reads a book ___ night before sleeping.","options":["A. on","B. at","C. in","D. by"],"correctAnswer":"B","explanation":"Usamos 'at' para referirnos a 'at night'. Es una excepción al uso común de 'in' para partes del día."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex42',
  'a2-m2-l7',
  42,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"Tom ______ (have to) wake up early every day because he works at a bakery.","acceptableAnswers":["has to"],"explanation":"Usamos 'have to' para indicar obligación. En tercera persona singular del presente simple, se convierte en 'has to'.","points":1},{"sentence":"Sarah ______ (would like to) visit her grandmother this weekend.","acceptableAnswers":["would like to"],"explanation":"'Would like to' se utiliza para expresar deseos o preferencias. No cambia con el sujeto.","points":1},{"sentence":"You ______ (mustn't) eat too much candy before dinner.","acceptableAnswers":["mustn't"],"explanation":"Se utiliza 'mustn't' para indicar prohibición, es decir, algo que no está permitido.","points":1},{"sentence":"It is too hot ______ (in/on/at) summer, so we usually go to the beach.","acceptableAnswers":["in"],"explanation":"Usamos 'in' con estaciones del año como 'in summer'.","points":1},{"sentence":"Lisa ______ (have to) finish her homework before she can watch TV.","acceptableAnswers":["has to"],"explanation":"Usamos 'have to' para obligación. En tercera persona singular del presente simple, se convierte en 'has to'.","points":1},{"sentence":"We ______ (would like to) try the new Italian restaurant in town.","acceptableAnswers":["would like to"],"explanation":"'Would like to' se utiliza para expresar deseos o preferencias. No cambia con el sujeto.","points":1},{"sentence":"You ______ (don't have to) bring any food, we have plenty for everyone.","acceptableAnswers":["don't have to"],"explanation":"Usamos 'don't have to' para indicar que algo no es necesario o obligatorio.","points":1},{"sentence":"We ______ (must) wear a uniform at school every day.","acceptableAnswers":["must"],"explanation":"Se utiliza 'must' para indicar obligación fuerte o reglas.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex43',
  'a2-m2-l7',
  43,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["in","She","evening","book","a","the","reads","."],"correctOrder":["She","reads","a","book","in","the","evening","."],"translation":"Ella lee un libro por la tarde.","hint":"Sujeto + Verbo + Objeto + Preposición + Tiempo del día.","points":2},{"id":2,"words":["have","o'clock","in","They","the","breakfast","at","8","morning","."],"correctOrder":["They","have","breakfast","at","8","o'clock","in","the","morning","."],"translation":"Ellos desayunan a las 8 de la mañana.","hint":"Sujeto + Verbo + Objeto + Preposición + Hora + Preposición + Momento del día.","points":2},{"id":3,"words":["p.m.","to","at","I","go","10","bed","."],"correctOrder":["I","go","to","bed","at","10","p.m.","."],"translation":"Me voy a la cama a las 10 p.m.","hint":"Sujeto + Verbo + Preposición + Lugar + Preposición + Hora.","points":2},{"id":4,"words":["the","in","watch","afternoon","TV","We","."],"correctOrder":["We","watch","TV","in","the","afternoon","."],"translation":"Vemos la televisión por la tarde.","hint":"Sujeto + Verbo + Objeto + Preposición + Tiempo del día.","points":2},{"id":5,"words":["at","9","goes","He","to","work","a.m.","."],"correctOrder":["He","goes","to","work","at","9","a.m.","."],"translation":"Él va al trabajo a las 9 a.m.","hint":"Sujeto + Verbo + Preposición + Lugar + Preposición + Hora.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex44',
  'a2-m2-l7',
  44,
  'speaking-analysis',
  $_$Speaking Analysis: Daily Routines$_$,
  $_${"title":"Speaking Analysis: Daily Routines","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are telling a friend about your daily routine.","question":"Which sentence correctly describes an everyday activity?","options":["I eat breakfast in the 8 o'clock.","I have breakfast at 8 o'clock.","I am breakfast on 8 o'clock.","I breakfast in at 8 o'clock."],"correctAnswer":"B","explanation":"Se usa 'at' para especificar la hora exacta en que ocurre una actividad. 'Have breakfast' es la expresión correcta.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You want to describe when you usually go to bed.","question":"Which sentence uses the correct preposition of time?","options":["I go to bed in night.","I go to bed at night.","I go to bed on night.","I go to bed into night."],"correctAnswer":"B","explanation":"Se usa 'at' para momentos del día como 'night'. Las otras opciones son incorrectas.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are explaining your morning routine to a classmate.","question":"Which sentence is correct?","options":["I wake up in 7 o'clock.","I wake up on 7 o'clock.","I wake up at 7 o'clock.","I wake up to 7 o'clock."],"correctAnswer":"C","explanation":"'At' se utiliza para hablar de una hora específica del día. 'Wake up' es la forma correcta de describir la acción de levantarse por la mañana.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are talking about when you usually have dinner.","question":"Which preposition of time is most suitable?","options":["I have dinner in the evening.","I have dinner on the evening.","I have dinner at the evening.","I have dinner into the evening."],"correctAnswer":"A","explanation":"Se usa 'in' para partes del día como 'morning', 'afternoon' y 'evening'.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are telling someone when you do your homework.","question":"Which sentence correctly uses a preposition of time?","options":["I do my homework in the afternoon.","I do my homework on the afternoon.","I do my homework at afternoon.","I do my homework to the afternoon."],"correctAnswer":"A","explanation":"'In' se usa para hablar de las partes del día, como 'afternoon'. Las otras opciones utilizan preposiciones incorrectas.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You want to say when you usually go jogging.","question":"Which sentence is correct?","options":["I go jogging on the morning.","I go jogging in the morning.","I go jogging at the morning.","I go jogging to the morning."],"correctAnswer":"B","explanation":"'In' es la preposición correcta para indicar la parte del día. 'Morning' se usa con 'in'.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
