INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex47',
  'a2-m2-l7',
  47,
  'reading-comprehension',
  $_$Daily Routines$_$,
  $_${"title":"Daily Routines","textPassage":"Maria wakes up early in the morning. She usually gets up at 6:30 and has breakfast at 7:00. On weekdays, she goes to work at 8:00. She works in an office and starts her work at 9:00. In the afternoon, she has lunch at 1:00 with her colleagues. After work, she often goes for a walk in the park at 6:00. In the evening, she cooks dinner at 7:30. Maria likes to read books before going to bed. She usually goes to bed at 10:00 and falls asleep quickly at night.","wordCount":100,"estimatedReadingTime":1,"questions":[{"question":"What time does Maria get up?","options":["6:00","6:30","7:00","7:30"],"answer":"6:30","explanation":"Maria se levanta a las 6:30 de la mañana."},{"question":"At what time does Maria have breakfast?","options":["6:30","7:00","7:30","8:00"],"answer":"7:00","explanation":"Maria desayuna a las 7:00 de la mañana."},{"question":"TRUE or FALSE: Maria works in a hospital.","options":["TRUE","FALSE"],"answer":"FALSE","explanation":"Maria trabaja en una oficina, no en un hospital."},{"question":"What does Maria do after work?","options":["She goes shopping","She goes for a walk in the park","She watches TV","She goes to the gym"],"answer":"She goes for a walk in the park","explanation":"Después del trabajo, Maria suele ir a caminar al parque."},{"question":"When does Maria have dinner?","options":["6:00","7:00","7:30","8:00"],"answer":"7:30","explanation":"Maria cocina la cena a las 7:30 de la noche."},{"question":"TRUE or FALSE: Maria reads books before going to bed.","options":["TRUE","FALSE"],"answer":"TRUE","explanation":"Maria le gusta leer libros antes de irse a dormir."},{"question":"What time does Maria go to bed?","options":["9:00","9:30","10:00","10:30"],"answer":"10:00","explanation":"Maria suele irse a la cama a las 10:00 de la noche."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex48',
  'a2-m2-l7',
  48,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Estrés de Frases$_$,
  $_${"title":"Práctica de Pronunciación: Estrés de Frases","instructions":"Escucha las frases en inglés y presta atención al estrés de las palabras. Practica la pronunciación asegurándote de enfatizar correctamente las palabras clave en cada frase.","targetSentences":[{"englishSentence":"I have breakfast at eight in the morning.","spanishTranslation":"Desayuno a las ocho de la mañana.","phoneticTranscription":"/aɪ hæv ˈbrɛkfəst æt eɪt ɪn ðə ˈmɔrnɪŋ/","audioModelURL":"audio_url_placeholder_1","pronunciationTips":"Enfatiza 'breakfast' y 'eight' para dar claridad a la rutina diaria.","focusPoints":"Enfatiza las palabras clave relacionadas con el tiempo.","commonMistakes":"No te olvides de pronunciar claramente 'eight' y no omitir el sonido /t/."},{"englishSentence":"He goes to work at nine o'clock.","spanishTranslation":"Él va al trabajo a las nueve en punto.","phoneticTranscription":"/hi ɡoʊz tə wɜrk æt naɪn əˈklɒk/","audioModelURL":"audio_url_placeholder_2","pronunciationTips":"Asegúrate de enfatizar 'work' y 'nine o'clock' para destacar la hora.","focusPoints":"Las palabras de acción y las horas deben ser claras.","commonMistakes":"Evita decir 'o'clock' sin el sonido /k/ final."},{"englishSentence":"She watches TV in the evening.","spanishTranslation":"Ella mira televisión por la tarde.","phoneticTranscription":"/ʃi ˈwɑtʃəz ˈtiˈvi ɪn ði ˈivnɪŋ/","audioModelURL":"audio_url_placeholder_3","pronunciationTips":"Enfatiza 'watches' y 'evening' para hacer la frase más comprensible.","focusPoints":"Asegúrate de que 'evening' se destaque claramente.","commonMistakes":"No te olvides de pronunciar el sonido /ŋ/ en 'evening'."},{"englishSentence":"They have dinner at seven.","spanishTranslation":"Ellos cenan a las siete.","phoneticTranscription":"/ðeɪ hæv ˈdɪnər æt ˈsɛvən/","audioModelURL":"audio_url_placeholder_4","pronunciationTips":"Haz hincapié en 'dinner' y 'seven' para claridad en la rutina.","focusPoints":"El tiempo y la acción principal deben ser claros.","commonMistakes":"Asegúrate de no omitir el sonido /n/ en 'seven'."},{"englishSentence":"We go to bed at ten at night.","spanishTranslation":"Nos vamos a la cama a las diez de la noche.","phoneticTranscription":"/wi ɡoʊ tə bɛd æt tɛn æt naɪt/","audioModelURL":"audio_url_placeholder_5","pronunciationTips":"Resalta 'bed' y 'ten' para enfatizar la rutina nocturna.","focusPoints":"Las palabras relacionadas con el tiempo deben ser claras.","commonMistakes":"No te olvides de pronunciar correctamente 'ten' con un sonido claro /t/."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex49',
  'a2-m2-l7',
  49,
  'multiple-choice',
  $_$Daily Routines Exercise$_$,
  $_${"title":"Daily Routines Exercise","instructions":"Elige la respuesta correcta para completar las oraciones sobre rutinas diarias.","questions":[{"id":"q1","question":"I usually wake up ___ 7 o'clock in the morning.","options":["A. in","B. on","C. at","D. by"],"correctAnswer":"C","explanation":"Usamos 'at' para hablar de horas específicas del día. Por ejemplo, 'at 7 o'clock'."},{"id":"q2","question":"She has breakfast ___ the kitchen every day.","options":["A. in","B. on","C. at","D. by"],"correctAnswer":"A","explanation":"Usamos 'in' para indicar que algo está dentro de un lugar, como 'in the kitchen'."},{"id":"q3","question":"They go to bed ___ night after reading a book.","options":["A. on","B. at","C. in","D. during"],"correctAnswer":"C","explanation":"Usamos 'in' para hablar de partes del día, como 'in the morning', 'in the afternoon', y 'in the evening'. Sin embargo, para 'night' se usa 'at night'."},{"id":"q4","question":"We have a meeting ___ Wednesday mornings.","options":["A. in","B. on","C. at","D. by"],"correctAnswer":"B","explanation":"Usamos 'on' para indicar días específicos de la semana, como 'on Wednesday'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l7-ex50',
  'a2-m2-l7',
  50,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"text":"Maria must _____ finish her homework before she can watch TV.","options":["not","to","have"],"answer":"not","explanation":"En inglés, 'must' se utiliza para expresar una obligación fuerte. En este caso, 'must not' indica que no se debe hacer algo antes de completar la tarea.","points":1},{"text":"This book is _____ than the one I read last month.","options":["more interesting","interesting","most interesting"],"answer":"more interesting","explanation":"En inglés, 'more interesting' se usa para comparar dos cosas, en este caso, dos libros.","points":1},{"text":"How _____ apples are there in the basket?","options":["many","much","some"],"answer":"many","explanation":"'How many' se usa para preguntar sobre la cantidad de objetos contables como 'apples'.","points":1},{"text":"I would like to _____ a new bicycle for my birthday.","options":["buy","buys","buying"],"answer":"buy","explanation":"'Would like to' se usa para expresar un deseo o preferencia seguido de un verbo en infinitivo.","points":1},{"text":"You don't have to _____ the dishes now. You can do it later.","options":["wash","washing","washes"],"answer":"wash","explanation":"'Don't have to' se usa para expresar que no es necesario hacer algo en este momento.","points":1},{"text":"We have to _____ early tomorrow for the meeting.","options":["leave","leaves","leaving"],"answer":"leave","explanation":"'Have to' se utiliza para indicar una obligación y va seguido de un verbo en infinitivo.","points":1},{"text":"She mustn't _____ her phone during the exam.","options":["use","uses","using"],"answer":"use","explanation":"'Mustn't' se usa para prohibir una acción, seguido de un verbo en infinitivo.","points":1},{"text":"Is this test _____ than the previous one?","options":["easier","easy","easiest"],"answer":"easier","explanation":"'Easier' es el comparativo de 'easy', usado para comparar dos pruebas.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;

