INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex1',
  'a2-m2-l8',
  1,
  'multiple-choice',
  $_$Weekly Habits and Routines Exercise$_$,
  $_${"title":"Weekly Habits and Routines Exercise","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"Which day of the week do you usually go to the gym?","options":["A) I never go to the gym on Mondays.","B) I always go to the gym on Mondays.","C) I often go to the gym on Wednesdays.","D) I never go to the gym on Sundays."],"correctAnswer":"C","explanation":"La respuesta correcta es 'C) I often go to the gym on Wednesdays.' porque la pregunta pide el día en que 'usualmente' vas al gimnasio, y 'often' significa frecuentemente."},{"id":"q2","question":"What do you never do on Saturday mornings?","options":["A) I always watch TV on Saturday mornings.","B) I never work on Saturday mornings.","C) I often go shopping on Saturday mornings.","D) I sometimes cook on Saturday mornings."],"correctAnswer":"B","explanation":"La respuesta correcta es 'B) I never work on Saturday mornings.' porque 'never' indica una acción que no realizas en absoluto los sábados por la mañana."},{"id":"q3","question":"How often do you visit your grandparents?","options":["A) I visit my grandparents every week.","B) I never visit my grandparents.","C) I visit my grandparents once a year.","D) I visit my grandparents on Sundays."],"correctAnswer":"A","explanation":"La respuesta correcta es 'A) I visit my grandparents every week.' porque la pregunta pregunta por la frecuencia, y 'every week' indica una acción regular."},{"id":"q4","question":"Do you play soccer on Wednesdays?","options":["A) No, I don't play soccer on Wednesdays.","B) Yes, I always play soccer on Wednesdays.","C) No, I never play soccer on Sundays.","D) Yes, I sometimes play soccer on Saturdays."],"correctAnswer":"A","explanation":"La respuesta correcta es 'A) No, I don't play soccer on Wednesdays.' porque la pregunta es específica para los miércoles y la respuesta negativa correcta es con 'don't'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex2',
  'a2-m2-l8',
  2,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"context":"John goes to the gym ______________ (siempre) on Mondays.","acceptableAnswers":["always"],"explanation":"'Always' significa siempre. Se coloca antes del verbo principal.","points":1},{"context":"Sarah ______________ (nunca) eats breakfast in the morning.","acceptableAnswers":["never"],"explanation":"'Never' significa nunca. Se coloca antes del verbo principal.","points":1},{"context":"We ______________ (no ir) to the cinema on Sundays.","acceptableAnswers":["don't go","do not go"],"explanation":"Usamos 'don't go' para la negativa en presente simple.","points":1},{"context":"Tom ______________ (a menudo) visits his grandmother on Fridays.","acceptableAnswers":["often"],"explanation":"'Often' significa a menudo. Se coloca antes del verbo principal.","points":1},{"context":"They ______________ (no jugar) football every weekend.","acceptableAnswers":["don't play","do not play"],"explanation":"Usamos 'don't play' para la negativa plural.","points":1},{"context":"Lucy ______________ (a veces) reads a book in the evening.","acceptableAnswers":["sometimes"],"explanation":"'Sometimes' significa a veces. Se coloca antes del verbo principal.","points":1},{"context":"My brother ______________ (no gustar) vegetables.","acceptableAnswers":["doesn't like","does not like"],"explanation":"Usamos 'doesn't' para la tercera persona singular negativa.","points":1},{"context":"We ______________ (normalmente) have dinner at 7:00 pm.","acceptableAnswers":["usually"],"explanation":"'Usually' significa normalmente. Se coloca antes del verbo principal.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex3',
  'a2-m2-l8',
  3,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["I","have","to","do","not","my","homework","on","Sundays","."],"correctOrder":["I","do","not","have","to","do","my","homework","on","Sundays","."],"translation":"No tengo que hacer mis deberes los domingos.","hint":"Estructura: Sujeto + do not have to + verbo + complemento","points":2},{"id":2,"words":["On","Mondays",",","we","have","to","go","to","the","library","."],"correctOrder":["On","Mondays",",","we","have","to","go","to","the","library","."],"translation":"Los lunes, tenemos que ir a la biblioteca.","hint":"Estructura: Tiempo + have to + verbo + complemento","points":2},{"id":3,"words":["How","many","books","do","you","have","to","read","this","week","?"],"correctOrder":["How","many","books","do","you","have","to","read","this","week","?"],"translation":"¿Cuántos libros tienes que leer esta semana?","hint":"Estructura: How many + sustantivo + do + sujeto + have to + verbo + complemento","points":2},{"id":4,"words":["You","should","not","eat","too","much","candy","on","Fridays","."],"correctOrder":["You","should","not","eat","too","much","candy","on","Fridays","."],"translation":"No deberías comer demasiados dulces los viernes.","hint":"Estructura: Sujeto + should not + verbo + complemento","points":2},{"id":5,"words":["I","would","like","to","visit","my","grandparents","on","Saturday","."],"correctOrder":["I","would","like","to","visit","my","grandparents","on","Saturday","."],"translation":"Me gustaría visitar a mis abuelos el sábado.","hint":"Estructura: Sujeto + would like to + verbo + complemento","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex4',
  'a2-m2-l8',
  4,
  'speaking-analysis',
  $_$Speaking Analysis: Weekly Habits$_$,
  $_${"title":"Speaking Analysis: Weekly Habits","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"Ana is talking to her friend about her weekly routine.","question":"Ana: \"I never go to the gym on Mondays.\" What is the best response?","options":["A: Why don't you go on Mondays?","B: You should go every Sunday.","C: Let's go together tomorrow.","D: Do you like swimming?"],"correctAnswer":"A","explanation":"La opción A es la respuesta más natural porque continúa el tema del gimnasio y el día específico mencionado por Ana.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are meeting a new colleague at work.","question":"What's the most polite way to ask about their weekend plans?","options":["A: What are you doing this weekend?","B: Got any plans for the weekend?","C: Are you busy this weekend?","D: Do you want to hang out this weekend?"],"correctAnswer":"A","explanation":"La opción A es la más adecuada porque es directa y cortés, adecuada para un contexto formal.","points":2},{"id":"q3","type":"multiple-choice","scenario":"Tom is talking about his habits.","question":"Tom: \"I often read books in the evening.\" What's a good follow-up question?","options":["A: What kind of books do you read?","B: Do you like watching TV?","C: Are you reading now?","D: Do you have a favorite movie?"],"correctAnswer":"A","explanation":"La opción A es la mejor porque sigue la conversación sobre leer libros.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You're at a casual dinner with friends.","question":"Which informal expression fits the situation when asking about their week?","options":["A: How has your week been?","B: How was your agenda?","C: How's everything at your end?","D: How are your weekly activities?"],"correctAnswer":"A","explanation":"La opción A es la más informal y adecuada para una conversación casual.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are discussing a weekly meeting schedule.","question":"Which sentence uses the adverb of frequency correctly?","options":["A: We meet always on Thursdays.","B: We always meet on Thursdays.","C: We meet on Thursdays always.","D: Always we meet on Thursdays."],"correctAnswer":"B","explanation":"La opción B es correcta porque el adverbio 'always' va antes del verbo principal.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are having a conversation about daily routines.","question":"Which phrase shows the correct use of the present simple negative?","options":["A: I don't never drink coffee in the morning.","B: I not drink coffee in the morning.","C: I don't drink coffee in the morning.","D: I no drink coffee in the morning."],"correctAnswer":"C","explanation":"La opción C es correcta porque usa correctamente 'don't' para formar la negativa en presente simple.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex5',
  'a2-m2-l8',
  5,
  'word-search',
  $_$Word Search: Weekly Habits$_$,
  $_${"title":"Word Search: Weekly Habits","instructions":"Find the hidden words in the grid","words":["ALWAYS","OFTEN","NEVER","MONDAY","TUESDAY","WEDNESDAY","THURSDAY","FRIDAY","WEEKEND","SOMETIMES"],"gridSize":10,"clues":[{"word":"ALWAYS","clue":"Siempre"},{"word":"OFTEN","clue":"A menudo"},{"word":"NEVER","clue":"Nunca"},{"word":"MONDAY","clue":"Lunes"},{"word":"TUESDAY","clue":"Martes"},{"word":"WEDNESDAY","clue":"Miércoles"},{"word":"THURSDAY","clue":"Jueves"},{"word":"FRIDAY","clue":"Viernes"},{"word":"WEEKEND","clue":"Fin de semana"},{"word":"SOMETIMES","clue":"A veces"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex6',
  'a2-m2-l8',
  6,
  'crossword',
  $_$Crossword: Weekly Habits$_$,
  $_${"title":"Crossword: Weekly Habits","instructions":"Complete the crossword puzzle","items":[{"word":"ALWAYS","clue":"Every single time","direction":"across","row":0,"col":0},{"word":"NEVER","clue":"Not at any time","direction":"down","row":0,"col":2},{"word":"SUNDAY","clue":"The day before Monday","direction":"across","row":2,"col":0},{"word":"OFTEN","clue":"Many times, but not always","direction":"down","row":1,"col":4},{"word":"SOMETIMES","clue":"Occasionally, not always","direction":"across","row":4,"col":0},{"word":"FRIDAY","clue":"The day after Thursday","direction":"across","row":6,"col":0}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex7',
  'a2-m2-l8',
  7,
  'reading-comprehension',
  $_$Weekly Habits$_$,
  $_${"title":"Weekly Habits","textPassage":"Laura has a busy week. On Mondays, she always goes to the gym because she likes to start the week with energy. She often meets her friends for coffee on Tuesdays. She never misses this weekly meeting. On Wednesdays, Laura usually helps her brother with his homework. She enjoys spending time with him. On Thursdays, she has dance classes in the evening. Laura says she is not a great dancer, but she likes to learn new things. Fridays are her favorite day because she often goes to the movies with her cousins. On Saturdays, she sometimes spends the day at the park if the weather is nice. Sundays are for relaxing; she never works on Sundays. Instead, she reads books or watches her favorite TV shows. Laura believes that having a routine helps her stay organized and happy.","wordCount":137,"estimatedReadingTime":2,"questions":[{"question":"What is the main idea of the passage?","options":["Laura likes to travel every week.","Laura has a busy weekly routine.","Laura dislikes meeting her friends.","Laura never goes to the gym."],"answer":"Laura has a busy weekly routine.","explanation":"La idea principal del texto es que Laura tiene una rutina semanal ocupada, participando en varias actividades durante la semana."},{"question":"How often does Laura go to the gym?","options":["On Saturdays","Twice a week","Always on Mondays","Never"],"answer":"Always on Mondays","explanation":"Laura siempre va al gimnasio los lunes, como se menciona en el texto."},{"question":"What does Laura do on Thursdays?","options":["She helps her brother with homework.","She goes to dance classes.","She meets her friends for coffee.","She goes to the movies."],"answer":"She goes to dance classes.","explanation":"Los jueves, Laura tiene clases de baile, lo que se menciona específicamente en el pasaje."},{"question":"True or False: Laura never works on Sundays.","options":["True","False"],"answer":"True","explanation":"Es cierto que Laura nunca trabaja los domingos, ya que dedica este día a relajarse."},{"question":"What does Laura sometimes do on Saturdays?","options":["She goes shopping.","She spends the day at the park.","She visits her grandparents.","She goes to the beach."],"answer":"She spends the day at the park.","explanation":"Laura a veces pasa el día en el parque los sábados si hace buen tiempo."},{"question":"What activity is NOT mentioned in Laura's weekly routine?","options":["Reading books","Swimming","Watching TV shows","Going to the movies"],"answer":"Swimming","explanation":"Nadar no se menciona en la rutina semanal de Laura."},{"question":"What is Laura's favorite day of the week?","options":["Monday","Friday","Wednesday","Sunday"],"answer":"Friday","explanation":"El viernes es el día favorito de Laura porque a menudo va al cine con sus primos."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex8',
  'a2-m2-l8',
  8,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Estrés de Palabras$_$,
  $_${"title":"Práctica de Pronunciación: Estrés de Palabras","instructions":"Escucha las siguientes oraciones y presta atención a la pronunciación del estrés en ciertas palabras. Practica repitiendo cada oración después de escuchar el modelo.","targetSentences":[{"englishSentence":"I often go to the gym on Mondays.","spanishTranslation":"A menudo voy al gimnasio los lunes.","phoneticTranscription":"ˈɔːfən","audioModelURL":"https://example.com/audio1","pronunciationTips":"Enfatiza la primera sílaba de 'often'.","focusPoints":"Palabra 'often'","commonMistakes":"No pronunciar la 't' en 'often'."},{"englishSentence":"She always reads before bed.","spanishTranslation":"Ella siempre lee antes de dormir.","phoneticTranscription":"ˈɔːlweɪz","audioModelURL":"https://example.com/audio2","pronunciationTips":"Asegúrate de alargar la primera sílaba de 'always'.","focusPoints":"Palabra 'always'","commonMistakes":"Decir 'al-ways' en lugar de 'ol-ways'."},{"englishSentence":"We never eat fish on Sundays.","spanishTranslation":"Nunca comemos pescado los domingos.","phoneticTranscription":"ˈnevər","audioModelURL":"https://example.com/audio3","pronunciationTips":"Mantén la primera sílaba de 'never' corta y clara.","focusPoints":"Palabra 'never'","commonMistakes":"Pronunciar como 'neev-er'."},{"englishSentence":"They sometimes watch movies on Fridays.","spanishTranslation":"A veces ven películas los viernes.","phoneticTranscription":"ˈsʌmtaɪmz","audioModelURL":"https://example.com/audio4","pronunciationTips":"Recuerda enfatizar la primera sílaba de 'sometimes'.","focusPoints":"Palabra 'sometimes'","commonMistakes":"Olvidar la 's' final en 'sometimes'."},{"englishSentence":"I usually drink tea in the morning.","spanishTranslation":"Usualmente bebo té por la mañana.","phoneticTranscription":"ˈjuːʒuəli","audioModelURL":"https://example.com/audio5","pronunciationTips":"La primera sílaba de 'usually' lleva el estrés.","focusPoints":"Palabra 'usually'","commonMistakes":"Decir 'u-sually' en lugar de 'you-sually'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex9',
  'a2-m2-l8',
  9,
  'multiple-choice',
  $_$Weekly Habits and Routines$_$,
  $_${"title":"Weekly Habits and Routines","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"How often do you go to the gym?","options":["A) I never go to the gym.","B) I go to the gym yesterday.","C) I go to the gym last week.","D) I going to the gym tomorrow."],"correctAnswer":"A","explanation":"La opción A utiliza correctamente el adverbio de frecuencia 'never' en presente simple. Las otras opciones usan tiempos incorrectos para esta estructura."},{"id":"q2","question":"Maria _______ (not watch) TV on Mondays.","options":["A) doesn't watch","B) don't watch","C) didn't watch","D) not watches"],"correctAnswer":"A","explanation":"La opción A es correcta porque 'doesn't watch' es la forma negativa correcta del presente simple para 'Maria'."},{"id":"q3","question":"On which day of the week do you always have English class?","options":["A) On weekends","B) On Wednesday","C) On tomorrow","D) On last week"],"correctAnswer":"B","explanation":"La opción B es correcta porque 'On Wednesday' es un día específico de la semana, mientras que las otras opciones no son días específicos o no están en el tiempo presente."},{"id":"q4","question":"I _______ (always/go) to the park on Sundays.","options":["A) always go","B) go always","C) always going","D) go to always"],"correctAnswer":"A","explanation":"La opción A es correcta porque el adverbio de frecuencia 'always' va antes del verbo en presente simple para indicar una rutina."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex10',
  'a2-m2-l8',
  10,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"On Mondays, I _______ (not go) to the gym because it's closed.","acceptableAnswers":["don't go"],"explanation":"En presente simple, para la forma negativa, usamos 'don't' con 'I'.","points":1},{"sentence":"She _______ (always / visit) her grandmother on Sundays.","acceptableAnswers":["always visits"],"explanation":"Usamos 'always' antes del verbo principal en presente simple.","points":1},{"sentence":"They _______ (not have) breakfast on Saturdays.","acceptableAnswers":["don't have"],"explanation":"Para negaciones en tercera persona plural, usamos 'don't'.","points":1},{"sentence":"We _______ (often / watch) movies on Friday nights.","acceptableAnswers":["often watch"],"explanation":"El adverbio de frecuencia 'often' se coloca antes del verbo.","points":1},{"sentence":"She _______ (never / be) late for her yoga class on Wednesdays.","acceptableAnswers":["is never"],"explanation":"Con 'be', el adverbio de frecuencia 'never' va después del verbo 'is'.","points":1},{"sentence":"He _______ (not play) football when it rains.","acceptableAnswers":["doesn't play"],"explanation":"Para negaciones en tercera persona singular, usamos 'doesn't'.","points":1},{"sentence":"I _______ (sometimes / go) to the library on Thursdays.","acceptableAnswers":["sometimes go"],"explanation":"El adverbio 'sometimes' se coloca antes del verbo principal en presente simple.","points":1},{"sentence":"We _______ (not eat) lunch at home on school days.","acceptableAnswers":["don't eat"],"explanation":"En presente simple, 'don't' se usa con 'we' para hacer la negación.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex11',
  'a2-m2-l8',
  11,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["I","often","go","to","the","gym","on","Mondays","."],"correctOrder":["I","often","go","to","the","gym","on","Mondays","."],"translation":"A menudo voy al gimnasio los lunes.","hint":"Sujeto + adverbio de frecuencia + verbo + complemento + día.","points":2},{"id":2,"words":["She","never","watches","TV","on","weekdays","."],"correctOrder":["She","never","watches","TV","on","weekdays","."],"translation":"Ella nunca ve la televisión entre semana.","hint":"Pronombre + adverbio + verbo (en tercera persona) + complemento.","points":2},{"id":3,"words":["We","always","have","breakfast","at","8:00","."],"correctOrder":["We","always","have","breakfast","at","8:00","."],"translation":"Siempre desayunamos a las 8:00.","hint":"Sujeto + adverbio + verbo + objeto + hora.","points":2},{"id":4,"words":["Do","you","usually","play","soccer","on","Sundays","?"],"correctOrder":["Do","you","usually","play","soccer","on","Sundays","?"],"translation":"¿Sueles jugar al fútbol los domingos?","hint":"Auxiliar + sujeto + adverbio + verbo + complemento + día.","points":2},{"id":5,"words":["He","rarely","eats","out","during","the","week","."],"correctOrder":["He","rarely","eats","out","during","the","week","."],"translation":"Él rara vez come fuera durante la semana.","hint":"Sujeto + adverbio + verbo + partícula + complemento.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex12',
  'a2-m2-l8',
  12,
  'speaking-analysis',
  $_$Speaking Analysis: Weekly Habits$_$,
  $_${"title":"Speaking Analysis: Weekly Habits","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"Your friend asks you about your weekend plans. You have no plans.","question":"What is the most natural response?","options":["A) I always have something planned.","B) I never have plans on weekends.","C) I have plans every weekend.","D) I often have nothing to do."],"correctAnswer":"B","explanation":"La opción B es la respuesta más adecuada cuando se quiere expresar que no se tienen planes en los fines de semana de forma general.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are meeting a new colleague. They ask how often you exercise.","question":"Which response matches a formal speaking situation?","options":["A) I work out a lot.","B) I usually exercise three times a week.","C) I hit the gym all the time.","D) I never miss a day of fitness."],"correctAnswer":"B","explanation":"La opción B es más formal y apropiada para una situación en la que se busca ser educado y claro sobre la frecuencia del ejercicio.","points":2},{"id":"q3","type":"multiple-choice","scenario":"A friend asks you about your study habits.","question":"Which idiomatic expression is used correctly?","options":["A) I hit the books every now and then.","B) I hit the sack every day.","C) I hit the road for my studies.","D) I hit the nail on the head weekly."],"correctAnswer":"A","explanation":"La expresión 'hit the books' significa estudiar, y se usa correctamente en la opción A.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are discussing your daily schedule with a classmate.","question":"Which connector is most appropriate?","options":["A) First of all, I go to class.","B) However, I eat breakfast.","C) Meanwhile, I sleep.","D) Nevertheless, I study."],"correctAnswer":"A","explanation":"La opción A utiliza el conector 'First of all' que es adecuado para enumerar una secuencia de actividades diarias.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are explaining your work habits to a potential employer.","question":"Which version is most diplomatic?","options":["A) I sometimes don't finish tasks.","B) I rarely miss deadlines.","C) I never do enough.","D) I often skip work."],"correctAnswer":"B","explanation":"La opción B es diplomática y positiva, resaltando la fiabilidad y cumplimiento de plazos.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are practicing English pronunciation.","question":"Which word stress pattern is correct for 'always'?","options":["A) AL-ways","B) al-WAYS","C) a-LWAYS","D) all-WAYS"],"correctAnswer":"A","explanation":"La sílaba tónica en 'always' es la primera, por lo que la opción correcta es AL-ways.","points":2},{"id":"q7","type":"multiple-choice","scenario":"You are chatting casually with a friend about your weekend activities.","question":"Which informal expression fits the context?","options":["A) I hang out with friends a lot.","B) I socialize frequently.","C) I always engage with peers.","D) I consistently meet acquaintances."],"correctAnswer":"A","explanation":"La opción A usa una expresión informal y común 'hang out', adecuada para una conversación casual.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex13',
  'a2-m2-l8',
  13,
  'word-search',
  $_$Word Search: Weekly Habits$_$,
  $_${"title":"Word Search: Weekly Habits","instructions":"Find the hidden words in the grid","words":["ALWAYS","OFTEN","NEVER","MONDAY","FRIDAY","SOMETIMES","USUALLY","RARELY","SATURDAY","HARDLY"],"gridSize":10,"clues":[{"word":"ALWAYS","clue":"Siempre"},{"word":"OFTEN","clue":"A menudo"},{"word":"NEVER","clue":"Nunca"},{"word":"MONDAY","clue":"Lunes"},{"word":"FRIDAY","clue":"Viernes"},{"word":"SOMETIMES","clue":"A veces"},{"word":"USUALLY","clue":"Normalmente"},{"word":"RARELY","clue":"Raramente"},{"word":"SATURDAY","clue":"Sábado"},{"word":"HARDLY","clue":"Casi nunca"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex14',
  'a2-m2-l8',
  14,
  'crossword',
  $_$Crossword: Weekly Habits$_$,
  $_${"title":"Crossword: Weekly Habits","instructions":"Complete the crossword puzzle","items":[{"word":"MONDAY","clue":"The first day of the week","direction":"across","row":0,"col":0},{"word":"NEVER","clue":"The opposite of 'always'","direction":"down","row":0,"col":6},{"word":"OFTEN","clue":"Similar to 'frequently'","direction":"across","row":2,"col":3},{"word":"SUNDAY","clue":"The last day of the week","direction":"down","row":0,"col":8},{"word":"ALWAYS","clue":"Occurs every time without exception","direction":"across","row":4,"col":0},{"word":"FRIDAY","clue":"The day before the weekend starts","direction":"down","row":1,"col":4},{"word":"SOMETIMES","clue":"Not often, but occasionally","direction":"across","row":6,"col":0}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex15',
  'a2-m2-l8',
  15,
  'reading-comprehension',
  $_$Weekly Habits$_$,
  $_${"title":"Weekly Habits","textPassage":"Every week, my routine is quite simple but it helps me stay organized. On Mondays, I always start my day with a morning jog. It's a great way to begin the week. I usually have breakfast at 8:00 a.m. and then I go to work. I never skip breakfast because it's important for my energy. On Tuesdays and Thursdays, I often go to the gym in the evening. Exercise helps me relax after a busy day. Wednesdays are different because I rarely have any plans, so I use this time to read a book or watch a movie at home. Fridays are my favorite because I sometimes meet my friends for dinner. We often try new restaurants, but we don't always find good ones. The weekends are for rest, and I generally stay at home on Saturdays. I never work on Sundays, as it's a day for family and relaxation. This routine keeps me balanced and happy.","wordCount":148,"estimatedReadingTime":2,"questions":[{"question":"What does the person always do on Mondays?","options":["Go to the gym","Start with a morning jog","Meet friends for dinner","Read a book"],"answer":"Start with a morning jog","explanation":"El texto dice que siempre comienza con un trote matutino los lunes."},{"question":"What does the person never do on Sundays?","options":["Go to work","Read a book","Watch a movie","Meet friends"],"answer":"Go to work","explanation":"El texto menciona que nunca trabaja los domingos."},{"question":"True or False: The person often tries new restaurants on Fridays.","options":["True","False"],"answer":"True","explanation":"El texto indica que a menudo prueban nuevos restaurantes los viernes."},{"question":"Which day does the person rarely have plans?","options":["Monday","Wednesday","Friday","Saturday"],"answer":"Wednesday","explanation":"El texto menciona que rara vez tiene planes los miércoles."},{"question":"What is the main purpose of the person's weekly routine?","options":["To stay balanced and happy","To meet new people","To learn new skills","To travel to different places"],"answer":"To stay balanced and happy","explanation":"El texto dice que esta rutina lo mantiene equilibrado y feliz."},{"question":"What does the person usually do on Saturdays?","options":["Go to the gym","Stay at home","Work all day","Meet friends"],"answer":"Stay at home","explanation":"El texto indica que generalmente se queda en casa los sábados."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex16',
  'a2-m2-l8',
  16,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Estrés de Palabras$_$,
  $_${"title":"Práctica de Pronunciación: Estrés de Palabras","instructions":"Escucha y repite las siguientes oraciones para practicar el estrés de palabras. Presta atención al acento en las palabras clave para mejorar tu pronunciación.","targetSentences":[{"englishSentence":"She always goes shopping on Saturdays.","spanishTranslation":"Ella siempre va de compras los sábados.","phoneticTranscription":"ˈɔːl.weɪz","audioModelUrl":"https://example.com/audio1","pronunciationTips":"Enfócate en el estrés de 'always'. La primera sílaba es más fuerte.","focusPoints":"Estrés en 'always'","commonMistakes":"No pronunciar 'always' como una sola sílaba."},{"englishSentence":"I never watch TV on weekdays.","spanishTranslation":"Nunca veo televisión entre semana.","phoneticTranscription":"ˈnɛ.vər","audioModelUrl":"https://example.com/audio2","pronunciationTips":"Asegúrate de que 'never' tenga énfasis en la primera sílaba.","focusPoints":"Estrés en 'never'","commonMistakes":"No confundir el estrés en 'never'."},{"englishSentence":"We often visit our grandparents.","spanishTranslation":"A menudo visitamos a nuestros abuelos.","phoneticTranscription":"ˈɒf.ən","audioModelUrl":"https://example.com/audio3","pronunciationTips":"La primera sílaba de 'often' debe ser más fuerte.","focusPoints":"Estrés en 'often'","commonMistakes":"Pronunciar la 't' en 'often'."},{"englishSentence":"They usually eat dinner at eight.","spanishTranslation":"Usualmente cenan a las ocho.","phoneticTranscription":"ˈjuː.ʒu.ə.li","audioModelUrl":"https://example.com/audio4","pronunciationTips":"El estrés principal está en la primera sílaba de 'usually'.","focusPoints":"Estrés en 'usually'","commonMistakes":"No dividir 'usually' en demasiadas sílabas."},{"englishSentence":"My brother rarely plays sports.","spanishTranslation":"Mi hermano rara vez practica deportes.","phoneticTranscription":"ˈrɛər.li","audioModelUrl":"https://example.com/audio5","pronunciationTips":"El estrés en 'rarely' está en la primera sílaba.","focusPoints":"Estrés en 'rarely'","commonMistakes":"Olvidar el estrés en la primera sílaba de 'rarely'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex17',
  'a2-m2-l8',
  17,
  'multiple-choice',
  $_$Weekly Routines Practice$_$,
  $_${"title":"Weekly Routines Practice","instructions":"Choose the correct answer for each question. Focus on adverbs of frequency and present simple negative. All explanations are in Spanish.","questions":[{"id":"q1","question":"Maria ___ goes to the gym on Mondays. She prefers to rest on that day.","options":["always","often","never","sometimes"],"correctAnswer":"never","explanation":"La palabra 'never' significa 'nunca'. La oración indica que Maria no va al gimnasio los lunes porque prefiere descansar, por lo tanto, 'never' es la opción correcta."},{"id":"q2","question":"John and Peter ___ eat breakfast before 8 a.m. on weekdays.","options":["doesn't","don't","isn't","aren't"],"correctAnswer":"don't","explanation":"La forma negativa del presente simple para 'John and Peter' (ellos) es 'don't'. Se utiliza para indicar que no desayunan antes de las 8 a.m. durante la semana."},{"id":"q3","question":"We ___ visit our grandparents every Saturday. We never miss a week!","options":["hardly ever","rarely","never","always"],"correctAnswer":"always","explanation":"La palabra 'always' significa 'siempre'. Indica que la acción de visitar a los abuelos se realiza todos los sábados, sin excepción."},{"id":"q4","question":"On Fridays, Lisa ___ watch TV in the evening. She prefers to read a book.","options":["doesn't","isn't","don't","not"],"correctAnswer":"doesn't","explanation":"'Doesn't' es la forma negativa del presente simple para 'Lisa' (ella). Se usa para mostrar que Lisa no ve la televisión los viernes por la noche."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex18',
  'a2-m2-l8',
  18,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"question":"On Mondays, I ______ (always) go to the gym.","acceptableAnswers":["always"],"explanation":"La palabra 'always' se usa para describir algo que haces todo el tiempo. En este caso, es una rutina que se repite cada lunes.","points":1},{"question":"I ______ (not watch) TV on Tuesdays.","acceptableAnswers":["do not watch","don't watch"],"explanation":"Usamos la forma negativa 'do not' o su contracción 'don't' para indicar que no ves televisión los martes. Es una acción que no ocurre.","points":1},{"question":"My sister ______ (often) reads books on Wednesdays.","acceptableAnswers":["often"],"explanation":"La palabra 'often' significa 'a menudo' y se usa para indicar que la acción de leer libros es frecuente los miércoles.","points":1},{"question":"We ______ (never) eat fast food on Thursdays.","acceptableAnswers":["never"],"explanation":"'Never' se utiliza para describir algo que nunca ocurre. En este caso, nunca comemos comida rápida los jueves.","points":1},{"question":"He ______ (not go) to school on Fridays.","acceptableAnswers":["does not go","doesn't go"],"explanation":"Para la tercera persona del singular, usamos 'does not' o 'doesn't' para indicar que él no va a la escuela los viernes.","points":1},{"question":"On Saturdays, they ______ (usually) visit their grandparents.","acceptableAnswers":["usually"],"explanation":"'Usually' indica una acción que se realiza con regularidad. Aquí, describe que normalmente visitan a sus abuelos los sábados.","points":1},{"question":"I ______ (not work) on Sundays.","acceptableAnswers":["do not work","don't work"],"explanation":"La forma negativa 'do not' o 'don't' se utiliza para indicar que no trabajas los domingos. Es una negación de la acción.","points":1},{"question":"He ______ (sometimes) plays soccer on weekends.","acceptableAnswers":["sometimes"],"explanation":"'Sometimes' se usa para describir acciones que ocurren de vez en cuando. Indica que a veces juega al fútbol los fines de semana.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex19',
  'a2-m2-l8',
  19,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["not","I","often","on","Sundays","work","."],"correctOrder":["I","do","not","often","work","on","Sundays","."],"translation":"No trabajo a menudo los domingos.","hint":"La oración es una negación en presente simple con un adverbio de frecuencia.","points":2},{"id":2,"words":["never","He","plays","football","on","Mondays","."],"correctOrder":["He","never","plays","football","on","Mondays","."],"translation":"Él nunca juega al fútbol los lunes.","hint":"La oración usa 'never' para indicar la frecuencia de una acción.","points":2},{"id":3,"words":["usually","They","go","to","the","gym","on","Wednesdays","."],"correctOrder":["They","usually","go","to","the","gym","on","Wednesdays","."],"translation":"Ellos usualmente van al gimnasio los miércoles.","hint":"La oración es una afirmación con un adverbio de frecuencia.","points":2},{"id":4,"words":["She","always","in","the","morning","coffee","drinks","."],"correctOrder":["She","always","drinks","coffee","in","the","morning","."],"translation":"Ella siempre toma café por la mañana.","hint":"La oración describe un hábito diario usando 'always'.","points":2},{"id":5,"words":["We","not","read","often","do","in","the","evening","."],"correctOrder":["We","do","not","often","read","in","the","evening","."],"translation":"No leemos a menudo por la tarde.","hint":"La oración es una negación en presente simple con un adverbio de frecuencia.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex20',
  'a2-m2-l8',
  20,
  'speaking-analysis',
  $_$Speaking Analysis: Weekly Habits$_$,
  $_${"title":"Speaking Analysis: Weekly Habits","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are telling a friend about your usual Saturday routine.","question":"Which sentence correctly uses an adverb of frequency?","options":["I go to the gym always on Saturdays.","I always go to the gym on Saturdays.","I go to always the gym on Saturdays.","I go always to the gym on Saturdays."],"correctAnswer":"B","explanation":"La opción B usa correctamente el adverbio de frecuencia 'always' antes del verbo principal 'go'.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are discussing your weekly habits with a new classmate.","question":"Which sentence is the correct negative form in present simple?","options":["I not go to work on Sundays.","I do not goes to work on Sundays.","I do not go to work on Sundays.","I do not going to work on Sundays."],"correctAnswer":"C","explanation":"La opción C es correcta porque 'do not' se usa para formar la negativa en el presente simple, seguido del verbo base 'go'.","points":2},{"id":"q3","type":"multiple-choice","scenario":"In a conversation about daily activities, your friend asks what you never do.","question":"What is the correct response?","options":["I never watch TV in the morning.","I don't never watch TV in the morning.","I never not watch TV in the morning.","I don't watch TV never in the morning."],"correctAnswer":"A","explanation":"La opción A es correcta porque 'never' ya implica negación y no se necesita 'don't'.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are describing your weekly schedule to a friend.","question":"Which sentence correctly uses a day of the week?","options":["I go to the market in Monday.","I go to the market at Monday.","I go to the market on Monday.","I go to the market by Monday."],"correctAnswer":"C","explanation":"La opción C es correcta porque 'on' se usa para hablar de días específicos de la semana.","points":2},{"id":"q5","type":"multiple-choice","scenario":"Your friend is curious about how often you play soccer.","question":"Which sentence best describes a frequency expression?","options":["I play soccer every week.","I play soccer every day.","I play soccer every hour.","I play soccer every month."],"correctAnswer":"A","explanation":"La opción A es correcta porque 'every week' es una expresión de frecuencia común para actividades regulares.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are talking about your new hobby with a friend.","question":"Which adverb of frequency would you use to say you do it very rarely?","options":["Seldom","Often","Always","Usually"],"correctAnswer":"A","explanation":"La opción A es correcta porque 'seldom' significa que se hace algo muy raramente.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
