INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex22',
  'a2-m2-l8',
  22,
  'crossword',
  $_$Crossword: Weekly Habits$_$,
  $_${"title":"Crossword: Weekly Habits","instructions":"Complete the crossword puzzle","items":[{"word":"ALWAYS","clue":"With 100% frequency","direction":"across","row":0,"col":0},{"word":"NEVER","clue":"With 0% frequency","direction":"down","row":0,"col":0},{"word":"SUNDAY","clue":"The last day of the weekend","direction":"across","row":1,"col":1},{"word":"OFTEN","clue":"Frequently, but not always","direction":"down","row":0,"col":3},{"word":"NEVER","clue":"Not at any time","direction":"across","row":3,"col":1},{"word":"FRIDAY","clue":"The day before the weekend","direction":"across","row":5,"col":0},{"word":"RARELY","clue":"Almost never","direction":"down","row":1,"col":5},{"word":"MONDAY","clue":"The first day of the workweek","direction":"down","row":1,"col":6}]}$_$
