INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex31',
  'a2-m2-l8',
  31,
  'reading-comprehension',
  $_$My Weekly Habits$_$,
  $_${"title":"My Weekly Habits","text_passage":"Hello! I'm Maria and I want to tell you about my weekly habits. I always start my week with a morning jog on Monday. It's a great way to begin the week. On Tuesdays and Thursdays, I often go to the library after work to read. I love reading mystery novels. Wednesdays are a bit different because I usually have dinner with my parents. I never miss it because family time is important to me. On Fridays, I sometimes meet my friends for a movie night. We don't always watch new movies, but it's fun to relax together. Saturdays are my cleaning days. I clean my house and do laundry. I don't like cleaning, but I have to do it. Finally, Sundays are for rest. I often relax at home and prepare for the new week. I rarely go out on Sundays. That's my week! What are your weekly habits?","wordCount":149,"estimatedReadingTime":2,"questions":[{"question":"What does Maria always do on Monday?","options":["Go to the library","Have dinner with parents","Go for a jog","Meet friends for a movie"],"answer":"Go for a jog","explanation":"Maria siempre comienza la semana con una carrera por la mañana los lunes."},{"question":"How often does Maria go to the library?","options":["Always","Often","Never","Rarely"],"answer":"Often","explanation":"Maria va a la biblioteca a menudo los martes y jueves después del trabajo."},{"question":"Maria never misses dinner with her parents on which day?","options":["Monday","Wednesday","Friday","Sunday"],"answer":"Wednesday","explanation":"Maria nunca falta a la cena con sus padres los miércoles."},{"question":"What is Maria's activity on Fridays?","options":["Cleaning the house","Jogging","Meeting friends for a movie","Reading novels"],"answer":"Meeting friends for a movie","explanation":"Maria a veces se encuentra con sus amigos para una noche de cine los viernes."},{"question":"True or False: Maria likes cleaning her house on Saturdays.","options":["True","False"],"answer":"False","explanation":"Maria no le gusta limpiar, pero lo hace los sábados porque tiene que hacerlo."},{"question":"What does Maria rarely do on Sundays?","options":["Relax at home","Go out","Prepare for the week","Jog"],"answer":"Go out","explanation":"Maria rara vez sale los domingos, prefiere descansar en casa."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex32',
  'a2-m2-l8',
  32,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Rutinas Semanales$_$,
  $_${"title":"Práctica de pronunciación: Rutinas Semanales","instructions":"Escucha y repite las oraciones en inglés. Presta atención a la entonación y el estrés de las palabras. Usa las transcripciones fonéticas y los consejos de pronunciación para mejorar tu habilidad.","targetSentences":[{"english":"I always go to the gym on Monday.","spanish":"Siempre voy al gimnasio el lunes.","phonetic":"aɪ ˈɔːlweɪz ɡoʊ tuː ðə dʒɪm ɒn ˈmʌndeɪ","audioModelURL":"https://example.com/audio1","pronunciationTips":"Asegúrate de enfatizar 'always' al principio de la oración.","focusPoints":"Enfatizar la palabra 'always'.","commonMistakes":"No olvidar el sonido /s/ al final de 'always'."},{"english":"She never eats meat on Fridays.","spanish":"Ella nunca come carne los viernes.","phonetic":"ʃiː ˈnɛvər iːts miːt ɒn ˈfraɪdeɪz","audioModelURL":"https://example.com/audio2","pronunciationTips":"Pronuncia claramente la /s/ en 'eats'.","focusPoints":"Claridad en la pronunciación de 'never' y 'eats'.","commonMistakes":"No omitir el sonido /t/ en 'meat'."},{"english":"They often visit their grandparents on Sunday.","spanish":"Ellos a menudo visitan a sus abuelos el domingo.","phonetic":"ðeɪ ˈɒftən ˈvɪzɪt ðɛər ˈɡrænˌpɛrənts ɒn ˈsʌndeɪ","audioModelURL":"https://example.com/audio3","pronunciationTips":"Enfatiza 'often' para mostrar frecuencia.","focusPoints":"Correcta pronunciación de 'often' y 'visit'.","commonMistakes":"Confundir /v/ en 'visit' con /b/."},{"english":"We sometimes watch movies on Saturday night.","spanish":"A veces vemos películas el sábado por la noche.","phonetic":"wiː ˈsʌmtaɪmz wɒtʃ ˈmuːviz ɒn ˈsætədeɪ naɪt","audioModelURL":"https://example.com/audio4","pronunciationTips":"Asegúrate de que 'sometimes' suene claro.","focusPoints":"Estrés en 'sometimes' y 'movies'.","commonMistakes":"No pronunciar correctamente la /tʃ/ en 'watch'."},{"english":"He rarely drinks coffee in the morning.","spanish":"Él raramente bebe café por la mañana.","phonetic":"hiː ˈrɛərli drɪŋks ˈkɒfi ɪn ðə ˈmɔːrnɪŋ","audioModelURL":"https://example.com/audio5","pronunciationTips":"Enfatiza 'rarely' para indicar poca frecuencia.","focusPoints":"Claridad en 'rarely' y 'coffee'.","commonMistakes":"No confundir /r/ y /l/ en 'rarely'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex33',
  'a2-m2-l8',
  33,
  'multiple-choice',
  $_$Weekly Habits and Routines$_$,
  $_${"title":"Weekly Habits and Routines","instructions":"Choose the correct answer for each question. Use the context of weekly habits and routines.","questions":[{"id":"q1","question":"Sarah ___ goes to the gym on Mondays. She prefers to rest.","options":["always","never","often","sometimes"],"correctAnswer":"never","explanation":"Sarah nunca va al gimnasio los lunes. 'Never' significa que no lo hace ningún lunes."},{"id":"q2","question":"We ___ have a meeting on Wednesday mornings at 9 AM.","options":["never","always","rarely","hardly"],"correctAnswer":"always","explanation":"Siempre tenemos una reunión los miércoles por la mañana a las 9 AM. 'Always' indica que es una rutina fija."},{"id":"q3","question":"Mark ___ drinks coffee in the evening because it keeps him awake.","options":["often","always","never","usually"],"correctAnswer":"never","explanation":"Mark nunca bebe café por la noche porque lo mantiene despierto. 'Never' expresa que evita hacerlo por completo."},{"id":"q4","question":"They ___ visit their grandparents every Sunday.","options":["sometimes","never","often","hardly ever"],"correctAnswer":"often","explanation":"Ellos visitan a sus abuelos a menudo los domingos. 'Often' sugiere que es una actividad regular pero no siempre."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex34',
  'a2-m2-l8',
  34,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"On ________, I don't have to work, so I often go to the park.","gap":"Sundays","options":["Sundays","Mondays","Fridays"],"explanation":"Los días de la semana se usan para hablar de rutinas y actividades regulares. Aquí, 'Sundays' es correcto porque se menciona que no se trabaja, comúnmente el domingo.","points":1},{"sentence":"I ________ eat breakfast at home. I prefer to have it on the way to work.","gap":"never","options":["always","often","never"],"explanation":"Los adverbios de frecuencia como 'never' indican la regularidad de una acción. Aquí, 'never' es correcto porque indica que la persona no desayuna en casa.","points":1},{"sentence":"Sara ________ goes swimming on Thursday evenings.","gap":"always","options":["sometimes","always","never"],"explanation":"El adverbio de frecuencia 'always' indica que una acción se realiza todo el tiempo, que es lo que se describe aquí.","points":1},{"sentence":"We ________ watch movies on Saturday nights. We prefer to go out.","gap":"don't","options":["do","doesn't","don't"],"explanation":"En presente simple negativo usamos 'don't' para sujetos en plural, como 'we', para indicar que no realizamos la acción.","points":1},{"sentence":"Tom ________ goes to the gym on Wednesdays. It's his rest day.","gap":"never","options":["always","never","often"],"explanation":"Usamos 'never' para indicar que una acción no sucede en absoluto, como en este caso.","points":1},{"sentence":"I ________ do my homework after dinner every day.","gap":"usually","options":["never","always","usually"],"explanation":"'Usually' se usa para indicar que una acción ocurre con frecuencia, pero no siempre.","points":1},{"sentence":"She ________ goes to the market on Fridays because she buys groceries then.","gap":"often","options":["often","never","rarely"],"explanation":"'Often' indica que la acción sucede frecuentemente, lo cual es adecuado aquí ya que ella va al mercado regularmente los viernes.","points":1},{"sentence":"They ________ play football on Tuesdays because they have practice on other days.","gap":"don't","options":["do","don't","doesn't"],"explanation":"Para negar una acción en presente simple con sujetos en plural, usamos 'don't'.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex35',
  'a2-m2-l8',
  35,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["never","Sarah","Sunday","on","works","."],"correctOrder":["Sarah","never","works","on","Sunday","."],"translation":"Sarah nunca trabaja los domingos.","hint":"La estructura típica es Sujeto + Adverbio de frecuencia + Verbo + Complemento.","points":2},{"id":2,"words":["usually","homework","do","Fridays","on","they","not","."],"correctOrder":["They","do","not","usually","do","homework","on","Fridays","."],"translation":"Ellos no suelen hacer tareas los viernes.","hint":"Usa el presente simple en negativo con un adverbio de frecuencia.","points":2},{"id":3,"words":["Peter","dinner","sometimes","cooks","Saturday","on","."],"correctOrder":["Peter","sometimes","cooks","dinner","on","Saturday","."],"translation":"Peter a veces cocina la cena los sábados.","hint":"Recuerda colocar el adverbio de frecuencia antes del verbo principal.","points":2},{"id":4,"words":["to","never","John","goes","museums","."],"correctOrder":["John","never","goes","to","museums","."],"translation":"John nunca va a museos.","hint":"El adverbio de frecuencia se coloca entre el sujeto y el verbo.","points":2},{"id":5,"words":["she","doesn't","always","coffee","Monday","drink","on","."],"correctOrder":["She","doesn't","always","drink","coffee","on","Monday","."],"translation":"Ella no siempre toma café los lunes.","hint":"Usa el presente simple negativo con adverbios de frecuencia.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex36',
  'a2-m2-l8',
  36,
  'speaking-analysis',
  $_$Speaking Analysis: Weekly Habits$_$,
  $_${"title":"Speaking Analysis: Weekly Habits","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking to a friend about your weekly routine.","question":"How often do you exercise during the week?","options":["A: I always exercise on Mondays and Thursdays.","B: I exercise never on the weekends.","C: I exercise often on Monday and Thursday.","D: I not exercise on the weekends."],"correctAnswer":"A","explanation":"La opción A es correcta porque usa el adverbio de frecuencia 'always' que es adecuado para describir una rutina regular. La opción B tiene un error gramatical en la posición del adverbio 'never'. La opción C usa mal 'often' sin especificar la regularidad. La opción D es incorrecta por el uso negativo y falta de estructura adecuada.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are asked about your typical activities.","question":"What do you usually do on Sundays?","options":["A: I not do anything on Sunday.","B: I usually relax and watch TV.","C: On Sunday, I never do relax.","D: Relaxing I am usually."],"correctAnswer":"B","explanation":"La opción B es correcta porque usa correctamente el adverbio de frecuencia 'usually' para describir una actividad típica. La opción A tiene un error de estructura en la oración negativa. La opción C usa incorrectamente 'never' en una oración positiva. La opción D tiene un error en el orden de las palabras.","points":2},{"id":"q3","type":"multiple-choice","scenario":"Your friend asks if you go shopping often.","question":"How often do you go shopping?","options":["A: I go often shopping on Fridays.","B: Fridays shopping I often go.","C: I often go shopping on Fridays.","D: Shopping on Fridays I do often."],"correctAnswer":"C","explanation":"La opción C es correcta porque sigue el orden correcto de las palabras: sujeto + adverbio de frecuencia + verbo + complemento. Las otras opciones tienen errores en el orden de las palabras o en la estructura.","points":2},{"id":"q4","type":"multiple-choice","scenario":"Discussing how often you visit your grandparents.","question":"When do you visit your grandparents?","options":["A: I never visit them on Wednesday.","B: I visit them never on Wednesdays.","C: I visit them on Wednesday never.","D: I visit never them on Wednesdays."],"correctAnswer":"A","explanation":"La opción A es correcta porque el adverbio de frecuencia 'never' está colocado correctamente antes del verbo principal. Las otras opciones colocan 'never' incorrectamente en la oración.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are explaining your study habits.","question":"Do you study every day?","options":["A: No, I don't study every days.","B: No, I study not every day.","C: No, I don't study every day.","D: No, I do not study every day."],"correctAnswer":"C","explanation":"La opción C es correcta porque usa la forma negativa del presente simple correctamente: 'I don't study'. La opción D es correcta gramaticalmente, pero es menos común usar 'do not' en conversación diaria informal.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex37',
  'a2-m2-l8',
  37,
  'word-search',
  $_$Word Search: Weekly Habits$_$,
  $_${"title":"Word Search: Weekly Habits","instructions":"Find the hidden words in the grid","words":["ALWAYS","NEVER","OFTEN","MONDAY","WEDNESDAY","FRIDAY","USUALLY","RARELY","SOMETIMES","HARDLY","EVER","WEEKEND"],"gridSize":10,"clues":[{"word":"ALWAYS","clue":"Siempre"},{"word":"NEVER","clue":"Nunca"},{"word":"OFTEN","clue":"A menudo"},{"word":"MONDAY","clue":"Lunes"},{"word":"WEDNESDAY","clue":"Miércoles"},{"word":"FRIDAY","clue":"Viernes"},{"word":"USUALLY","clue":"Usualmente"},{"word":"RARELY","clue":"Rara vez"},{"word":"SOMETIMES","clue":"A veces"},{"word":"HARDLY","clue":"Apenas"},{"word":"EVER","clue":"Nunca"},{"word":"WEEKEND","clue":"Fin de semana"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex38',
  'a2-m2-l8',
  38,
  'crossword',
  $_$Crossword: Weekly Habits$_$,
  $_${"title":"Crossword: Weekly Habits","instructions":"Complete the crossword puzzle","items":[{"word":"ALWAYS","clue":"I _____ go to bed at 10 PM.","direction":"across","row":0,"col":0},{"word":"NEVER","clue":"I _____ drink coffee in the evening.","direction":"down","row":0,"col":6},{"word":"OFTEN","clue":"She _____ visits her grandmother on Sundays.","direction":"across","row":1,"col":3},{"word":"MONDAY","clue":"The first day of the work week.","direction":"down","row":0,"col":2},{"word":"RARELY","clue":"He _____ eats fast food.","direction":"across","row":3,"col":1},{"word":"SOMETIMES","clue":"We _____ go to the park on Saturdays.","direction":"down","row":2,"col":5},{"word":"TUESDAY","clue":"The day after Monday.","direction":"across","row":5,"col":0}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex39',
  'a2-m2-l8',
  39,
  'reading-comprehension',
  $_$Weekly Habits$_$,
  $_${"title":"Weekly Habits","textPassage":"Tom is a very busy person. He always wakes up early, at 6 AM, from Monday to Friday. On weekends, he never wakes up before 8 AM. He often goes for a run in the morning, especially on Tuesdays and Thursdays. Tom does not like to run on weekends because he prefers to relax. Every Monday, he goes to the supermarket because he never shops during the week. On Wednesdays and Fridays, Tom usually visits his grandmother. She is always happy to see him. Tom does not play video games every day, but he often plays them on Saturday evenings. Sundays are special for Tom because he always has lunch with his family. He never misses this family time.","wordCount":120,"estimatedReadingTime":2,"questions":[{"question":"What is the main idea of the text?","options":["Tom's running habits.","Tom's weekly routine.","Tom's favorite hobbies.","Tom's family life."],"answer":"Tom's weekly routine.","explanation":"La idea principal del texto describe la rutina semanal de Tom, incluyendo sus actividades diarias y hábitos."},{"question":"How often does Tom go for a run?","options":["Every day.","Only on weekends.","On Tuesdays and Thursdays.","Never."],"answer":"On Tuesdays and Thursdays.","explanation":"Tom corre especialmente los martes y jueves, según el texto."},{"question":"Is it true that Tom always wakes up at 8 AM on weekends?","options":["True","False"],"answer":"True","explanation":"Tom nunca se levanta antes de las 8 AM en los fines de semana, como se menciona en el texto."},{"question":"What does Tom usually do on Wednesdays?","options":["He goes running.","He goes to the supermarket.","He visits his grandmother.","He plays video games."],"answer":"He visits his grandmother.","explanation":"Tom visita a su abuela los miércoles y viernes, como se indica en el texto."},{"question":"When does Tom play video games?","options":["Every day.","Saturday evenings.","Monday mornings.","Friday nights."],"answer":"Saturday evenings.","explanation":"Tom juega videojuegos a menudo los sábados por la tarde, según el texto."},{"question":"What is special about Sundays for Tom?","options":["He runs a marathon.","He visits friends.","He has lunch with his family.","He goes shopping."],"answer":"He has lunch with his family.","explanation":"Los domingos son especiales para Tom porque siempre almuerza con su familia, como se menciona en el texto."},{"question":"Which of the following activities does Tom NEVER do during the week?","options":["Shopping.","Visiting his grandmother.","Playing video games.","Running."],"answer":"Shopping.","explanation":"Tom nunca hace compras durante la semana; siempre va al supermercado los lunes."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex40',
  'a2-m2-l8',
  40,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Rutinas Semanales$_$,
$_${"title": "Práctica de Pronunciación: Rutinas Semanales", "instructions": "Lee las siguientes oraciones enfocándote en la pronunciación correcta de las palabras clave y la entonación de las frases. Después, practica repitiendo cada oración hasta que te sientas cómodo con su pronunciación.", "targetSentences": [{"english": "I always go to the gym on Mondays.", "spanish": "Siempre voy al gimnasio los lunes.", "phonetic": "/ˈɔːlweɪz/ /ɡoʊ/ /tuː/ /ðə/ /dʒɪm/ /ɒn/ /ˈmʌndeɪz/", "audioModelUrl": "https://placeholder.audio/always_gym_mondays.mp3", "pronunciationTips": "Enfatiza la 'a' inicial en 'always' y asegúrate de que el sonido de 'gym' (/dʒ/) se pronuncie correctamente.", "focusPoints": "Acento en 'always' y el día 'Mondays'.", "commonMistakes": "Evita pronunciar la 'g' de 'gym' como una 'g' fuerte (como en 'gato')."}, {"english": "She never drinks coffee in the morning.", "spanish": "Ella nunca bebe café por la mañana.", "phonetic": "/ˈnɛvər/ /drɪŋks/ /ˈkɒfi/ /ɪn/ /ðə/ /ˈmɔːrnɪŋ/", "audioModelUrl": "https://placeholder.audio/never_coffee_morning.mp3", "pronunciationTips": "Enfócate en la 'r' final de 'never' y la 'o' en 'coffee'.", "focusPoints": "Acento en 'never' y 'morning'.", "commonMistakes": "No enfatices demasiado la 'r' en 'morning'."}, {"english": "We often visit our grandparents on Sunday.", "spanish": "A menudo visitamos a nuestros abuelos los domingos.", "phonetic": "/ˈɒfən/ /ˈvɪzɪt/ /aʊər/ /ˈɡrændˌpɛərənts/ /ɒn/ /ˈsʌndeɪ/", "audioModelUrl": "https://placeholder.audio/often_visit_grandparents_sunday.mp3", "pronunciationTips": "Asegúrate de que la 't' en 'often' sea muda y acentúa la 'S' en 'Sunday'.", "focusPoints": "Acento de la oración en 'visit our grandparents'.", "commonMistakes": "Evita pronunciar la 't' en 'often'."}, {"english": "They sometimes cook dinner on Fridays.", "spanish": "A veces cocinan la cena los viernes.", "phonetic": "/ˈsʌmtaɪmz/ /kʊk/ /ˈdɪnər/ /ɒn/ /ˈfraɪdeɪz/", "audioModelUrl": "https://placeholder.audio/sometimes_cook_dinner_fridays.mp3", "pronunciationTips": "Focus on the 's' in 'sometimes' and ensure 'Fridays' is clear.", "focusPoints": "Acento en 'sometimes' y 'Fridays'.", "commonMistakes": "No olvides la 's' final en 'sometimes'."}, {"english": "He rarely watches TV in the evening.", "spanish": "Rara vez ve la televisión por la noche.", "phonetic": "/ˈrerli/ /ˈwɒtʃɪz/ /ˈtiːˈviː/ /ɪn/ /ðə/ /ˈiːvnɪŋ/", "audioModelUrl": "https://placeholder.audio/rarely_watches_TV_evening.mp3", "pronunciationTips": "Enfatiza el sonido de la 'r' en 'rarely' y la 'v' en 'evening'.", "focusPoints": "Acento de la oración en 'watches TV'.", "commonMistakes": "Evita pronunciar 'rarely' como 'rearly'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex41',
  'a2-m2-l8',
  41,
  'multiple-choice',
  $_$Weekly Habits: Understanding Routines$_$,
  $_${"title":"Weekly Habits: Understanding Routines","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"Which day of the week do you never go to school?","options":["A) Monday","B) Saturday","C) Wednesday","D) Thursday"],"correctAnswer":"B","explanation":"Los estudiantes generalmente no tienen clases los sábados. Este día suele ser parte del fin de semana."},{"id":"q2","question":"I _____ watch TV in the morning because I have to go to work.","options":["A) never","B) sometimes","C) always","D) often"],"correctAnswer":"A","explanation":"La palabra 'never' se usa para indicar que algo no ocurre en ningún momento. Aquí, la persona no ve televisión por las mañanas debido al trabajo."},{"id":"q3","question":"We _____ go to the gym on Tuesdays. It's our rest day.","options":["A) never","B) always","C) often","D) sometimes"],"correctAnswer":"A","explanation":"El uso de 'never' indica que no van al gimnasio los martes porque es su día de descanso."},{"id":"q4","question":"On which day do you usually have English class?","options":["A) Friday","B) Monday","C) Sunday","D) Saturday"],"correctAnswer":"B","explanation":"Las clases de inglés comúnmente se programan durante los días de semana, siendo el lunes un día típico para empezar."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex42',
  'a2-m2-l8',
  42,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"On Mondays, I ______ (not go) to the gym because I'm too tired.","acceptableAnswers":["do not go","don't go"],"explanation":"Usamos el presente simple negativo 'do not' o 'don't' para hablar de lo que no hacemos regularmente. Aquí, el sujeto es 'I', por lo tanto, usamos 'do not go'.","points":1},{"sentence":"She ______ (never / eat) breakfast before school.","acceptableAnswers":["never eats"],"explanation":"Usamos 'never' para indicar que algo no ocurre en absoluto. En presente simple, añadimos la 's' al verbo 'eat' porque el sujeto es 'she'.","points":1},{"sentence":"They ______ (always / play) football on Saturdays.","acceptableAnswers":["always play"],"explanation":"Con el presente simple y un sujeto plural como 'they', el verbo no cambia. 'Always' indica una frecuencia del 100%.","points":1},{"sentence":"We ______ (not watch) TV on Thursdays because we have dance class.","acceptableAnswers":["do not watch","don't watch"],"explanation":"Para hacer una negación en presente simple, usamos 'do not' o 'don't' con sujetos plurales como 'we'.","points":1},{"sentence":"He ______ (often / visit) his grandparents on Sundays.","acceptableAnswers":["often visits"],"explanation":"Con sujetos en tercera persona singular como 'he', añadimos 's' al verbo en presente simple. 'Often' indica una frecuencia regular.","points":1},{"sentence":"I ______ (not like) to wake up early on weekends.","acceptableAnswers":["do not like","don't like"],"explanation":"En presente simple negativo usamos 'do not' o 'don't' con 'I'.","points":1},{"sentence":"My brother ______ (never / clean) his room on Fridays.","acceptableAnswers":["never cleans"],"explanation":"Para sujetos en tercera persona singular como 'my brother', añadimos 's' al verbo. 'Never' indica que algo no sucede.","points":1},{"sentence":"You ______ (not usually / cook) dinner on weekdays.","acceptableAnswers":["do not usually cook","don't usually cook"],"explanation":"Para la negación en presente simple con 'you', usamos 'do not' o 'don't', seguido del adverbio de frecuencia 'usually'.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex43',
  'a2-m2-l8',
  43,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["I","always","breakfast","eat","at","7","o'clock","."],"correctOrder":["I","always","eat","breakfast","at","7","o'clock","."],"translation":"Siempre desayuno a las 7 en punto.","hint":"La estructura es: sujeto + adverbio de frecuencia + verbo.","points":2},{"id":2,"words":["Sarah","to","goes","never","work","on","Sundays","."],"correctOrder":["Sarah","never","goes","to","work","on","Sundays","."],"translation":"Sarah nunca va al trabajo los domingos.","hint":"Coloca el adverbio de frecuencia antes del verbo.","points":2},{"id":3,"words":["We","often","the","visit","library","on","Wednesdays","."],"correctOrder":["We","often","visit","the","library","on","Wednesdays","."],"translation":"A menudo visitamos la biblioteca los miércoles.","hint":"Sujeto + adverbio de frecuencia + verbo + complemento.","points":2},{"id":4,"words":["He","doesn't","play","soccer","on","Fridays","usually","."],"correctOrder":["He","doesn't","usually","play","soccer","on","Fridays","."],"translation":"Él no suele jugar al fútbol los viernes.","hint":"La estructura es: sujeto + no + adverbio + verbo.","points":2},{"id":5,"words":["My","parents","sometimes","dinner","prepare","at","6","pm","."],"correctOrder":["My","parents","sometimes","prepare","dinner","at","6","pm","."],"translation":"Mis padres a veces preparan la cena a las 6 p.m.","hint":"Sujeto + adverbio de frecuencia + verbo + complemento.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex44',
  'a2-m2-l8',
  44,
  'speaking-analysis',
  $_$Speaking Analysis: Weekly Habits$_$,
  $_${"title":"Speaking Analysis: Weekly Habits","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"It's Monday morning and your friend asks about your weekend activities.","question":"Friend: 'Did you enjoy your weekend?' You reply:","options":["A: I always enjoy my weekends.","B: I am going to enjoy my weekends.","C: I never enjoyed my weekends.","D: I often enjoy my weekends."],"correctAnswer":"D","explanation":"La respuesta 'I often enjoy my weekends' es la más apropiada ya que indica que disfrutas tus fines de semana con frecuencia, usando un adverbio de frecuencia común en el presente simple.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You meet a colleague in an informal setting and discuss your daily routine.","question":"Colleague: 'Do you always have breakfast before work?' You reply:","options":["A: I never have breakfast before work.","B: I don't always have breakfast before work.","C: I am not having breakfast before work.","D: I will not have breakfast before work."],"correctAnswer":"B","explanation":"La respuesta 'I don't always have breakfast before work' es correcta porque usa el presente simple negativo y un adverbio de frecuencia para expresar que no siempre desayunas antes del trabajo.","points":2},{"id":"q3","type":"multiple-choice","scenario":"Talking to a friend about your weekly habits.","question":"You say: 'On Thursdays, I usually ...'","options":["A: go to the gym.","B: going to the gym.","C: went to the gym.","D: going to the cinema."],"correctAnswer":"A","explanation":"La opción 'go to the gym' es la correcta porque usa la forma base del verbo después de 'usually', que es un adverbio de frecuencia en presente simple.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are asked about how often you practice a hobby.","question":"Friend: 'How often do you play the guitar?' You reply:","options":["A: I am playing the guitar every day.","B: I play the guitar every day.","C: I played the guitar every day.","D: I will play the guitar every day."],"correctAnswer":"B","explanation":"La respuesta 'I play the guitar every day' es correcta porque utiliza el presente simple para hablar de un hábito regular con una expresión de frecuencia.","points":2},{"id":"q5","type":"multiple-choice","scenario":"Discussing your usual morning routine with a classmate.","question":"You say: 'I ... have coffee in the morning.'","options":["A: never","B: seldom","C: always","D: sometimes"],"correctAnswer":"C","explanation":"Elegir 'always' es correcto si deseas indicar que tomar café es parte de tu rutina diaria en las mañanas.","points":2},{"id":"q6","type":"multiple-choice","scenario":"Talking to a friend about how often you watch movies.","question":"Friend: 'Do you watch movies on weekends?' You reply:","options":["A: I never watch movies on weekends.","B: I don't ever watch movies on weekends.","C: I always watch movies on weekends.","D: I am not watching movies on weekends."],"correctAnswer":"A","explanation":"La respuesta 'I never watch movies on weekends' es la más directa para expresar que no ves películas los fines de semana, usando el presente simple negativo.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l8-ex45',
