INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex1',
  'a2-m2-l9',
  1,
  'multiple-choice',
  $_$Jobs and Workplaces: Present Simple Questions$_$,
  $_${"title":"Jobs and Workplaces: Present Simple Questions","instructions":"Selecciona la respuesta correcta para cada pregunta.","questions":[{"id":"q1","question":"Where does a teacher work?","options":["A. In a hospital","B. In a school","C. In a restaurant","D. In a bank"],"correctAnswer":"B","explanation":"Los maestros trabajan en escuelas, ya que es el lugar donde enseñan a los estudiantes."},{"id":"q2","question":"Do doctors work in a bank?","options":["A. Yes, they do","B. No, they don't","C. Yes, she does","D. No, she doesn't"],"correctAnswer":"B","explanation":"Los doctores trabajan en hospitales, no en bancos. Por lo tanto, la respuesta correcta es 'No, they don't'."},{"id":"q3","question":"Does a chef work in a school?","options":["A. Yes, he does","B. No, he doesn't","C. Yes, she does","D. No, she doesn't"],"correctAnswer":"B","explanation":"Un chef trabaja en un restaurante o cocina, no en una escuela. Por eso, la respuesta correcta es 'No, he doesn't'."},{"id":"q4","question":"Where do nurses usually work?","options":["A. In a factory","B. In a hospital","C. In a library","D. In a shop"],"correctAnswer":"B","explanation":"Las enfermeras generalmente trabajan en hospitales, donde cuidan a los pacientes."},{"id":"q5","question":"Does a waiter work in an office?","options":["A. Yes, he does","B. No, he doesn't","C. Yes, she does","D. No, she doesn't"],"correctAnswer":"B","explanation":"Un camarero trabaja en un restaurante, no en una oficina. Por eso, la respuesta correcta es 'No, he doesn't'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex2',
  'a2-m2-l9',
  2,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"___ you work as a doctor or a nurse?","options":["Do","Does"],"answer":"Do","explanation":"Usamos 'Do' para formar preguntas en presente simple para sujetos 'you', 'I', 'we', y 'they'.","points":1},{"sentence":"Where ___ your sister work?","options":["do","does"],"answer":"does","explanation":"Usamos 'Does' para formar preguntas en presente simple para sujetos 'he', 'she', 'it'.","points":1},{"sentence":"___ a teacher work in a hospital?","options":["Do","Does"],"answer":"Does","explanation":"Usamos 'Does' porque 'a teacher' es singular y se refiere a 'he' o 'she'.","points":1},{"sentence":"What time ___ the bank open?","options":["do","does"],"answer":"does","explanation":"Usamos 'Does' para preguntas en presente simple con sujetos singulares.","points":1},{"sentence":"___ engineers work with computers?","options":["Do","Does"],"answer":"Do","explanation":"Usamos 'Do' porque 'engineers' es plural y se refiere a 'they'.","points":1},{"sentence":"___ your boss have a meeting every Monday?","options":["Do","Does"],"answer":"Does","explanation":"Usamos 'Does' porque 'your boss' se refiere a 'he' o 'she'.","points":1},{"sentence":"Where ___ the chefs prepare the food?","options":["do","does"],"answer":"do","explanation":"Usamos 'Do' para preguntas en presente simple con sujetos plurales como 'chefs'.","points":1},{"sentence":"Why ___ you want to become a firefighter?","options":["do","does"],"answer":"do","explanation":"Usamos 'Do' para preguntas en presente simple con el sujeto 'you'.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex3',
  'a2-m2-l9',
  3,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["her","Do","work","at","a","hospital","?"],"correctOrder":["Do","her","work","at","a","hospital","?"],"translation":"¿Trabaja ella en un hospital?","hint":"Usa 'Do' para preguntas en presente simple.","points":2},{"id":2,"words":["your","Does","brother","as","an","engineer","work","?"],"correctOrder":["Does","your","brother","work","as","an","engineer","?"],"translation":"¿Trabaja tu hermano como ingeniero?","hint":"Usa 'Does' para preguntas en tercera persona singular.","points":2},{"id":3,"words":["Do","at","they","office","an","work","?"],"correctOrder":["Do","they","work","at","an","office","?"],"translation":"¿Trabajan ellos en una oficina?","hint":"Usa 'Do' para preguntas en presente simple con 'they'.","points":2},{"id":4,"words":["Does","she","as","a","teacher","work","?"],"correctOrder":["Does","she","work","as","a","teacher","?"],"translation":"¿Trabaja ella como profesora?","hint":"Recuerda usar 'Does' para 'she' en preguntas.","points":2},{"id":5,"words":["your","Do","parents","at","a","factory","work","?"],"correctOrder":["Do","your","parents","work","at","a","factory","?"],"translation":"¿Trabajan tus padres en una fábrica?","hint":"Usa 'Do' para preguntar sobre más de una persona.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex4',
  'a2-m2-l9',
  4,
  'speaking-analysis',
  $_$Speaking Analysis: Work and Jobs$_$,
  $_${"title":"Speaking Analysis: Work and Jobs","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are at a coffee shop and meet someone new. They ask about your job.","question":"What do you say?","options":["A) I am working in a school as a teacher.","B) I works in a school as a teacher.","C) I work in a school as a teacher.","D) I working in a school as a teacher."],"correctAnswer":"C","explanation":"La respuesta correcta es 'I work in a school as a teacher' porque usa la forma correcta del presente simple para 'I'.","points":2},{"id":"q2","type":"multiple-choice","scenario":"Your friend is asking about your brother's job.","question":"What question do you ask?","options":["A) Do your brother work at the office?","B) Does your brother works at the office?","C) Does your brother work at the office?","D) Do your brother works at the office?"],"correctAnswer":"C","explanation":"La pregunta correcta es 'Does your brother work at the office?' porque 'does' se usa con 'he/she/it' y el verbo base.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are discussing your weekend plans with a colleague.","question":"What do you say?","options":["A) I will go to the cinema.","B) I going to the cinema.","C) I go to the cinema.","D) I goes to the cinema."],"correctAnswer":"A","explanation":"La respuesta correcta es 'I will go to the cinema' porque estamos hablando de intenciones futuras.","points":2},{"id":"q4","type":"multiple-choice","scenario":"Your friend asks if you like your new job.","question":"What do you say?","options":["A) Yes, I like it very much.","B) Yes, I likes it very much.","C) Yes, I liked it very much.","D) Yes, I liking it very much."],"correctAnswer":"A","explanation":"La respuesta correcta es 'Yes, I like it very much' porque usamos 'like' con 'I' en presente simple.","points":2},{"id":"q5","type":"multiple-choice","scenario":"Someone asks about your sister's workplace.","question":"How do you respond?","options":["A) She work in a hospital.","B) She works in a hospital.","C) She working in a hospital.","D) She is work in a hospital."],"correctAnswer":"B","explanation":"La respuesta correcta es 'She works in a hospital' porque usamos 'works' con 'she' en presente simple.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex5',
  'a2-m2-l9',
  5,
  'word-search',
  $_$Word Search: Work and Jobs$_$,
  $_${"title":"Word Search: Work and Jobs","instructions":"Find the hidden words in the grid","words":["DOCTOR","NURSE","TEACHER","ENGINEER","POLICE","OFFICE","HOSPITAL","SCHOOL","ARCHITECT","LAWYER"],"gridSize":10,"clues":[{"word":"DOCTOR","clue":"A person who treats sick people."},{"word":"NURSE","clue":"A person who helps doctors and cares for patients."},{"word":"TEACHER","clue":"A person who helps students learn."},{"word":"ENGINEER","clue":"A person who designs and builds machines or buildings."},{"word":"POLICE","clue":"A person who keeps law and order."},{"word":"OFFICE","clue":"A place where people work at desks."},{"word":"HOSPITAL","clue":"A place where sick people are treated."},{"word":"SCHOOL","clue":"A place where students go to learn."},{"word":"ARCHITECT","clue":"A person who designs buildings."},{"word":"LAWYER","clue":"A person who practices law and gives legal advice."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex6',
  'a2-m2-l9',
  6,
  'crossword',
  $_$Crossword: Work and Jobs$_$,
  $_${"title":"Crossword: Work and Jobs","instructions":"Complete the crossword puzzle","items":[{"word":"TEACHER","clue":"Who works in a school?","direction":"across","row":0,"col":0},{"word":"NURSE","clue":"Who helps doctors in a hospital?","direction":"down","row":0,"col":2},{"word":"POLICE","clue":"Who works to maintain public safety?","direction":"across","row":2,"col":0},{"word":"OFFICE","clue":"Where do people work at desks?","direction":"down","row":1,"col":3},{"word":"CHEF","clue":"Who cooks in a restaurant?","direction":"across","row":4,"col":1},{"word":"FARMER","clue":"Who works in fields and grows crops?","direction":"across","row":5,"col":0},{"word":"BANK","clue":"Where do people go to save and borrow money?","direction":"down","row":3,"col":5}]}$_$
