INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex7',
  'a2-m2-l9',
  7,
  'reading-comprehension',
  $_$A Day at Work$_$,
  $_${"title":"A Day at Work","textPassage":"Maria works in a busy office. She is a secretary. Every day, she answers phones, writes emails, and helps her boss organize meetings. Her office is on the third floor of a big building in the city center. Maria likes her job because she meets many people and learns new things. She starts work at 9 a.m. and finishes at 5 p.m. Sometimes, she has lunch with her colleagues in a small café nearby. On Fridays, they finish work early and go to the park to relax. Maria always says that working in the office keeps her busy, but she enjoys it very much.","wordCount":100,"estimatedReadingTime":3,"questions":[{"type":"multiple-choice","question":"What is Maria's job?","options":["Teacher","Secretary","Doctor","Chef"],"answer":"Secretary","explanation":"La lectura menciona que Maria trabaja como secretaria en una oficina ocupada."},{"type":"multiple-choice","question":"Where is Maria's office located?","options":["In a small town","In a big building in the city center","Next to a park","On the first floor"],"answer":"In a big building in the city center","explanation":"El texto dice que la oficina de Maria está en un edificio grande en el centro de la ciudad."},{"type":"true-false","question":"Maria starts work at 8 a.m.","answer":false,"explanation":"Maria comienza a trabajar a las 9 a.m., no a las 8 a.m."},{"type":"multiple-choice","question":"What does Maria do during her working day?","options":["Cooks meals","Answers phones and writes emails","Teaches students","Drives a bus"],"answer":"Answers phones and writes emails","explanation":"Durante el día laboral, Maria contesta teléfonos y escribe correos electrónicos."},{"type":"multiple-choice","question":"Why does Maria like her job?","options":["Because she earns a lot of money","Because she meets many people","Because she works with her family","Because she travels a lot"],"answer":"Because she meets many people","explanation":"Maria disfruta de su trabajo porque conoce a muchas personas y aprende cosas nuevas."},{"type":"true-false","question":"On Fridays, Maria finishes work early and goes to the park.","answer":true,"explanation":"El texto menciona que los viernes, Maria termina el trabajo temprano y va al parque."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex8',
  'a2-m2-l9',
  8,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Estrés de Palabras$_$,
  $_${"title":"Práctica de Pronunciación: Estrés de Palabras","instructions":"Practica la pronunciación y el estrés de palabras en las siguientes oraciones. Escucha el ejemplo de pronunciación y presta atención a las sílabas acentuadas. Lee cada oración en voz alta y concéntrate en el estrés correcto de las palabras clave.","targetSentences":[{"englishSentence":"The doctor works in a busy hospital.","spanishTranslation":"El médico trabaja en un hospital concurrido.","phoneticTranscription":"/ˈdɒktər/ /ˈbɪzi/ /ˈhɒspɪtəl/","audioModelURL":"https://example.com/audio1","pronunciationTips":"Asegúrate de acentuar correctamente la primera sílaba de 'doctor' y 'busy'.","focusPoints":"Palabras: doctor, busy, hospital","commonMistakes":"No acentuar la primera sílaba de 'doctor'."},{"englishSentence":"My sister is a talented artist.","spanishTranslation":"Mi hermana es una artista talentosa.","phoneticTranscription":"/ˈsɪstər/ /ˈtæləntɪd/ /ˈɑːrtɪst/","audioModelURL":"https://example.com/audio2","pronunciationTips":"Enfócate en la primera sílaba de 'sister' y la segunda de 'talented'.","focusPoints":"Palabras: sister, talented, artist","commonMistakes":"Confundir el estrés en 'talented'."},{"englishSentence":"The engineer designs new buildings.","spanishTranslation":"El ingeniero diseña nuevos edificios.","phoneticTranscription":"/ˌɛnʤɪˈnɪr/ /dɪˈzaɪnz/ /ˈbɪldɪŋz/","audioModelURL":"https://example.com/audio3","pronunciationTips":"Recuerda que 'engineer' tiene el estrés en la última sílaba.","focusPoints":"Palabras: engineer, designs, buildings","commonMistakes":"Olvidar el estrés final en 'engineer'."},{"englishSentence":"She is a nurse at a local clinic.","spanishTranslation":"Ella es enfermera en una clínica local.","phoneticTranscription":"/nɜːrs/ /ˈloʊkəl/ /ˈklɪnɪk/","audioModelURL":"https://example.com/audio4","pronunciationTips":"Acentúa la primera sílaba de 'local' y 'clinic'.","focusPoints":"Palabras: nurse, local, clinic","commonMistakes":"Pronunciar 'local' con el estrés incorrecto."},{"englishSentence":"The chef prepares delicious meals.","spanishTranslation":"El chef prepara comidas deliciosas.","phoneticTranscription":"/ʃɛf/ /prɪˈpɛrz/ /dɪˈlɪʃəs/","audioModelURL":"https://example.com/audio5","pronunciationTips":"Enfócate en el estrés de la segunda sílaba de 'delicious'.","focusPoints":"Palabras: chef, prepares, delicious","commonMistakes":"No enfatizar correctamente 'delicious'."},{"englishSentence":"He is an accountant in a small firm.","spanishTranslation":"Él es contador en una pequeña empresa.","phoneticTranscription":"/əˈkaʊntənt/ /smɔːl/ /fɜːrm/","audioModelURL":"https://example.com/audio6","pronunciationTips":"Recuerda que 'accountant' tiene el estrés en la segunda sílaba.","focusPoints":"Palabras: accountant, small, firm","commonMistakes":"Decir 'accountant' sin acentuar la sílaba correcta."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex9',
  'a2-m2-l9',
  9,
  'multiple-choice',
  $_$Work and Jobs: Present Simple Questions$_$,
  $_${"title":"Work and Jobs: Present Simple Questions","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"Does Maria work in a hospital?","options":["A) Yes, she does.","B) No, she don't.","C) Yes, she do.","D) No, she doesn't."],"correctAnswer":"A","explanation":"En inglés, para responder afirmativamente a una pregunta en presente simple que comienza con 'Does', se usa 'Yes, she does.' para confirmar."},{"id":"q2","question":"Do the teachers finish work at 3 PM?","options":["A) Yes, they do.","B) No, they doesn't.","C) Yes, they doesn't.","D) No, they do."],"correctAnswer":"A","explanation":"Para preguntas en presente simple que comienzan con 'Do', la respuesta afirmativa correcta es 'Yes, they do.'"},{"id":"q3","question":"Does your brother have a job in the bank?","options":["A) Yes, he have.","B) No, he doesn't.","C) Yes, he does.","D) No, he don't."],"correctAnswer":"C","explanation":"La forma correcta de responder afirmativamente a 'Does your brother have...' es 'Yes, he does.' porque 'does' es el auxiliar usado con 'he'."},{"id":"q4","question":"Do engineers work in an office?","options":["A) Yes, they doesn't.","B) No, they do.","C) Yes, they do.","D) No, they don't."],"correctAnswer":"C","explanation":"Para preguntas en presente simple que comienzan con 'Do', la respuesta afirmativa correcta es 'Yes, they do.'"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex10',
  'a2-m2-l9',
  10,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"________ your sister work as a teacher at the local school?","options":["Do","Does"],"answer":"Does","explanation":"Usamos 'Does' para preguntas en presente simple con sujetos en tercera persona singular.","points":1},{"sentence":"Where ________ you usually have lunch during your work break?","options":["do","does"],"answer":"do","explanation":"Usamos 'do' para preguntas en presente simple con sujetos en primera y segunda persona, y plural.","points":1},{"sentence":"________ your parents own a business in town?","options":["Do","Does"],"answer":"Do","explanation":"Usamos 'Do' para preguntas en presente simple con sujetos en plural.","points":1},{"sentence":"How ________ your brother travel to his job every day?","options":["do","does"],"answer":"does","explanation":"Usamos 'Does' para preguntas en presente simple con sujetos en tercera persona singular.","points":1},{"sentence":"What ________ you do when you finish work?","options":["do","does"],"answer":"do","explanation":"Usamos 'do' para preguntas en presente simple con sujetos en primera y segunda persona, y plural.","points":1},{"sentence":"Where ________ your friend work as a nurse?","options":["do","does"],"answer":"does","explanation":"Usamos 'Does' para preguntas en presente simple con sujetos en tercera persona singular.","points":1},{"sentence":"What time ________ they start their work at the factory?","options":["do","does"],"answer":"do","explanation":"Usamos 'do' para preguntas en presente simple con sujetos en plural.","points":1},{"sentence":"Why ________ he enjoy working at the restaurant?","options":["do","does"],"answer":"does","explanation":"Usamos 'Does' para preguntas en presente simple con sujetos en tercera persona singular.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex11',
  'a2-m2-l9',
  11,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["does","a","doctor","work","Where","?"],"correctOrder":["Where","does","a","doctor","work","?"],"translation":"¿Dónde trabaja un doctor?","hint":"Pregunta sobre el lugar de trabajo de una persona. Usa 'Where' al inicio.","points":2},{"id":2,"words":["you","Do","in","work","an","office","?"],"correctOrder":["Do","you","work","in","an","office","?"],"translation":"¿Trabajas en una oficina?","hint":"Pregunta si alguien trabaja en un lugar específico. Comienza con 'Do'.","points":2},{"id":3,"words":["Does","teacher","a","school","in","work","?"],"correctOrder":["Does","a","teacher","work","in","a","school","?"],"translation":"¿Trabaja un maestro en una escuela?","hint":"Pregunta sobre el lugar de trabajo usando 'Does'.","points":2},{"id":4,"words":["driver","bus","a","work","Where","does","?"],"correctOrder":["Where","does","a","bus","driver","work","?"],"translation":"¿Dónde trabaja un conductor de autobús?","hint":"Usa 'Where' para preguntar sobre el lugar de trabajo.","points":2},{"id":5,"words":["work","in","Do","a","you","hospital","?"],"correctOrder":["Do","you","work","in","a","hospital","?"],"translation":"¿Trabajas en un hospital?","hint":"Pregunta sobre el lugar de trabajo usando 'Do' al inicio.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex12',
  'a2-m2-l9',
  12,
  'speaking-analysis',
  $_$Speaking Analysis: Work and Jobs$_$,
  $_${"title":"Speaking Analysis: Work and Jobs","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are asking a friend about their brother's job.","question":"What does your brother do?","options":["A: He is a doctor.","B: He does a doctor.","C: He do a doctor.","D: He doctors."],"correctAnswer":"A","explanation":"La opción A utiliza correctamente el presente simple para describir la ocupación de alguien: 'He is a doctor'.","points":2},{"id":"q2","type":"multiple-choice","scenario":"A friend mentions they work at a café and you want to know more.","question":"Do you like working there?","options":["A: Yes, I does.","B: Yes, I do like.","C: Yes, I do.","D: Yes, do I."],"correctAnswer":"C","explanation":"La respuesta correcta es 'Yes, I do.', que es la forma correcta de responder afirmativamente a una pregunta con 'do'.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are at a job interview and they ask about your skills.","question":"What skills do you have?","options":["A: I am good at computer.","B: I have good computer skills.","C: I does computer good.","D: Computer skills I do."],"correctAnswer":"B","explanation":"La opción B es correcta porque usa correctamente el adjetivo 'good' para describir 'computer skills'.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You want to know if your colleague is busy at work.","question":"Are you busy today?","options":["A: No, I am not.","B: No, I not.","C: No, am not.","D: No, not I am."],"correctAnswer":"A","explanation":"La respuesta correcta es 'No, I am not.', que es la forma correcta de negar una pregunta con 'are you'.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You meet someone new and want to ask about their job.","question":"What do you do?","options":["A: I am teacher.","B: I am a teacher.","C: I teacher.","D: I do a teacher."],"correctAnswer":"B","explanation":"La opción B es correcta porque se necesita el artículo 'a' antes de la profesión: 'I am a teacher'.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are curious about where your friend works.","question":"Where do you work?","options":["A: I work in a hospital.","B: I do work hospital.","C: Work in the hospital.","D: In hospital I work."],"correctAnswer":"A","explanation":"La opción A es correcta porque sigue la estructura correcta de sujeto + verbo + lugar: 'I work in a hospital'.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
