  $_$Word Search: Work and Jobs$_$,
  $_${"title":"Word Search: Work and Jobs","instructions":"Find the hidden words in the grid","words":["DOCTOR","NURSE","TEACHER","OFFICE","SCHOOL","FARMER","ENGINEER","BANK","SHOP","WAITRESS"],"gridSize":10,"clues":[{"word":"DOCTOR","clue":"A person who helps sick people."},{"word":"NURSE","clue":"A person who helps doctors."},{"word":"TEACHER","clue":"Works in a school."},{"word":"OFFICE","clue":"A place where people work at desks."},{"word":"SCHOOL","clue":"A place where teachers work."},{"word":"FARMER","clue":"Works in the fields."},{"word":"ENGINEER","clue":"Designs and builds things."},{"word":"BANK","clue":"A place where money is kept."},{"word":"SHOP","clue":"A place where things are sold."},{"word":"WAITRESS","clue":"Serves food in a restaurant."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex14',
  'a2-m2-l9',
  14,
  'crossword',
  $_$Crossword: Work and Jobs$_$,
  $_${"title":"Crossword: Work and Jobs","instructions":"Complete the crossword puzzle","items":[{"word":"TEACHER","clue":"Who teaches students in a school?","direction":"across","row":0,"col":0},{"word":"NURSE","clue":"Who helps doctors in a hospital?","direction":"down","row":0,"col":1},{"word":"OFFICE","clue":"Where do people work at desks?","direction":"across","row":2,"col":0},{"word":"CHEF","clue":"Who cooks food in a restaurant?","direction":"down","row":2,"col":3},{"word":"BANK","clue":"Where do people keep their money?","direction":"across","row":4,"col":1},{"word":"DOES","clue":"Use this word to ask questions with 'he', 'she', or 'it'.","direction":"down","row":0,"col":5}]}$_$
