INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex15',
  'a2-m2-l9',
  15,
  'reading-comprehension',
  $_$Understanding Work and Jobs$_$,
  $_${"title":"Understanding Work and Jobs","textPassage":"Anna works in a small bakery in her town. She loves her job because she enjoys making bread and cakes. Every morning, she wakes up at 5 a.m. to start baking fresh products for the day. Does Anna work alone? No, she has a team of three people who help her. They prepare the dough and decorate cakes together. The bakery opens at 7 a.m. and many customers come to buy breakfast. Do they sell only bread? No, they also sell coffee and sandwiches. Anna's favorite part of her job is talking to the customers. She likes to ask them about their day and sometimes she gives them small samples to try. Anna works from Monday to Saturday, and she rests on Sundays. Does she have any plans for the future? Yes, she wants to open her own bakery one day.","wordCount":141,"estimatedReadingTime":2,"questions":[{"question":"What is the main idea of the passage?","options":["Anna has a boring job.","Anna works in a bakery and enjoys her job.","Anna works alone in the bakery.","Anna's bakery only sells bread."],"answer":"Anna works in a bakery and enjoys her job.","explanation":"La idea principal del texto es sobre el trabajo de Anna en una panadería y cuánto disfruta de su trabajo."},{"question":"What time does the bakery open?","options":["5 a.m.","6 a.m.","7 a.m.","8 a.m."],"answer":"7 a.m.","explanation":"El texto menciona que la panadería abre a las 7 a.m."},{"question":"Does Anna work alone?","options":["True","False"],"answer":"False","explanation":"Anna no trabaja sola; tiene un equipo de tres personas que la ayudan."},{"question":"What is Anna's favorite part of her job?","options":["Baking bread","Decorating cakes","Talking to customers","Preparing dough"],"answer":"Talking to customers","explanation":"A Anna le gusta hablar con los clientes, y es su parte favorita del trabajo."},{"question":"Which of the following is NOT mentioned as a product sold in the bakery?","options":["Bread","Sandwiches","Cakes","Pasta"],"answer":"Pasta","explanation":"El texto menciona que venden pan, café y sándwiches, pero no menciona pasta."},{"question":"What are Anna's future plans?","options":["To become a chef","To open her own bakery","To travel around the world","To work in a restaurant"],"answer":"To open her own bakery","explanation":"Anna planea abrir su propia panadería en el futuro, como se menciona en el texto."},{"question":"How many people work with Anna?","options":["One","Two","Three","Four"],"answer":"Three","explanation":"Anna trabaja con un equipo de tres personas, como se indica en el texto."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex16',
  'a2-m2-l9',
  16,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Estrés en las Preguntas$_$,
  $_${"title":"Práctica de Pronunciación: Estrés en las Preguntas","instructions":"En este ejercicio, practica la pronunciación de preguntas en inglés enfocándote en el estrés correcto de las palabras. Escucha el modelo de audio y repite cada oración. Presta atención al estrés en las palabras clave y asegúrate de practicar varias veces.","targetSentences":[{"englishSentence":"Does the manager work here?","spanishTranslation":"¿Trabaja aquí el gerente?","phoneticTranscription":"dʌz ðə ˈmænɪdʒər wɜrk hɪr?","audioModelURL":"https://example.com/audio1","pronunciationTips":"Enfoca el estrés en 'manager' y 'here'.","focusPoints":"estrés de palabras en preguntas","commonMistakes":"Evitar poner estrés en 'does'."},{"englishSentence":"Do they have a meeting today?","spanishTranslation":"¿Tienen una reunión hoy?","phoneticTranscription":"du ðeɪ hæv ə ˈmitɪŋ təˈdeɪ?","audioModelURL":"https://example.com/audio2","pronunciationTips":"Pon el estrés en 'meeting' y 'today'.","focusPoints":"estrés de palabras en preguntas","commonMistakes":"No poner estrés en 'do'."},{"englishSentence":"Does she enjoy her job?","spanishTranslation":"¿Disfruta ella de su trabajo?","phoneticTranscription":"dʌz ʃi ɪnˈdʒɔɪ hɜr ʤɑb?","audioModelURL":"https://example.com/audio3","pronunciationTips":"Enfoca el estrés en 'enjoy' y 'job'.","focusPoints":"estrés de palabras en preguntas","commonMistakes":"Evitar acentuar 'does'."},{"englishSentence":"Do you like your new office?","spanishTranslation":"¿Te gusta tu nueva oficina?","phoneticTranscription":"du ju laɪk jʊr nu ˈɔfəs?","audioModelURL":"https://example.com/audio4","pronunciationTips":"Pon el estrés en 'like' y 'office'.","focusPoints":"estrés de palabras en preguntas","commonMistakes":"No poner estrés en 'do'."},{"englishSentence":"Does he need to call the client?","spanishTranslation":"¿Necesita él llamar al cliente?","phoneticTranscription":"dʌz hi nid tə kɔl ðə ˈklaɪənt?","audioModelURL":"https://example.com/audio5","pronunciationTips":"Enfoca el estrés en 'need' y 'client'.","focusPoints":"estrés de palabras en preguntas","commonMistakes":"Evitar acentuar 'does'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex17',
  'a2-m2-l9',
  17,
  'multiple-choice',
  $_$Work and Jobs: Present Simple Questions$_$,
  $_${"title":"Work and Jobs: Present Simple Questions","instructions":"Elige la opción correcta para completar las preguntas en presente simple sobre trabajos comunes y lugares de trabajo.","questions":[{"id":"q1","question":"___ your sister work as a nurse in the hospital?","options":["A) Does","B) Do","C) Is","D) Are"],"correctAnswer":"A","explanation":"Usamos 'Does' para formar preguntas en presente simple con la tercera persona del singular (he, she, it)."},{"id":"q2","question":"___ they have a meeting at the office every Monday?","options":["A) Does","B) Do","C) Is","D) Are"],"correctAnswer":"B","explanation":"Usamos 'Do' para formar preguntas en presente simple con la primera persona del singular y plural (I, we, you, they)."},{"id":"q3","question":"___ your brother like his job as a teacher?","options":["A) Does","B) Do","C) Has","D) Is"],"correctAnswer":"A","explanation":"Usamos 'Does' para preguntas en presente simple con 'he', 'she', y 'it'."},{"id":"q4","question":"___ you usually finish work at 5 p.m.?","options":["A) Does","B) Do","C) Have","D) Is"],"correctAnswer":"B","explanation":"Usamos 'Do' para preguntas en presente simple con 'I', 'you', 'we', y 'they'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex18',
  'a2-m2-l9',
  18,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"___ your father work in a hospital or a school?","acceptableAnswers":["Does"],"explanation":"Usamos 'Does' para preguntar sobre el trabajo de una tercera persona singular (he, she, it).","points":1},{"sentence":"___ you like to work as a teacher or a doctor?","acceptableAnswers":["Do"],"explanation":"Usamos 'Do' para formular preguntas sobre los gustos o deseos de la segunda persona (you).","points":1},{"sentence":"Where ___ your brother work?","acceptableAnswers":["does"],"explanation":"Para preguntar sobre el lugar de trabajo de una tercera persona singular, usamos 'does'.","points":1},{"sentence":"What ___ your friends do on weekends?","acceptableAnswers":["do"],"explanation":"Usamos 'do' para preguntar sobre las actividades de personas en plural (they).","points":1},{"sentence":"___ your mother enjoy her job?","acceptableAnswers":["Does"],"explanation":"Para preguntar si alguien disfruta su trabajo, usamos 'Does' con tercera persona singular.","points":1},{"sentence":"How often ___ you work late?","acceptableAnswers":["do"],"explanation":"Usamos 'do' para preguntar sobre la frecuencia de acciones en segunda persona.","points":1},{"sentence":"___ they work in an office or at home?","acceptableAnswers":["Do"],"explanation":"Usamos 'Do' para preguntar sobre el lugar de trabajo de un grupo de personas (they).","points":1},{"sentence":"Why ___ the manager not like the new schedule?","acceptableAnswers":["does"],"explanation":"Usamos 'does' para preguntar sobre los sentimientos o preferencias de una tercera persona singular.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex19',
  'a2-m2-l9',
  19,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["a","is","very","man","My","boss","busy","."],"correctOrder":["My","boss","is","a","very","busy","man","."],"translation":"Mi jefe es un hombre muy ocupado.","hint":"Sujeto + verbo 'to be' + artículo + adverbio + adjetivo + sustantivo","points":2},{"id":2,"words":["should","We","hire","a","receptionist","new","."],"correctOrder":["We","should","hire","a","new","receptionist","."],"translation":"Deberíamos contratar a un nuevo recepcionista.","hint":"Pronombre + should + verbo + complemento","points":2},{"id":3,"words":["a","There","is","computer","big","on","the","desk","."],"correctOrder":["There","is","a","big","computer","on","the","desk","."],"translation":"Hay una computadora grande en el escritorio.","hint":"There is + artículo + adjetivo + sustantivo + preposición + lugar","points":2},{"id":4,"words":["the","You","call","should","manager","new","."],"correctOrder":["You","should","call","the","new","manager","."],"translation":"Deberías llamar al nuevo gerente.","hint":"Pronombre + should + verbo + complemento","points":2},{"id":5,"words":["is","kind","My","doctor","very","."],"correctOrder":["My","doctor","is","very","kind","."],"translation":"Mi doctor es muy amable.","hint":"Sujeto + verbo 'to be' + adverbio de grado + adjetivo","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex20',
  'a2-m2-l9',
  20,
  'speaking-analysis',
  $_$Speaking Analysis: Work and Jobs$_$,
  $_${"title":"Speaking Analysis: Work and Jobs","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are at a job interview, and the interviewer asks you about your daily tasks.","question":"Interviewer: Can you tell me what you do every day at your current job?","options":["I am working hard every day.","I work on reports and attend meetings.","I will work on reports tomorrow.","I worked on reports last week."],"correctAnswer":"B","explanation":"La opción B es la correcta porque usa el presente simple para describir tareas diarias, que es lo que se espera en la situación dada.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are speaking with a colleague about a new project.","question":"Colleague: How do you usually approach new projects?","options":["I usually start with research.","I am starting with research.","I started with research.","I will start with research."],"correctAnswer":"A","explanation":"La opción A es la correcta porque describe un hábito o acción regular utilizando el presente simple.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You are at a networking event, introducing yourself to potential clients.","question":"You say: I am a marketing specialist. Do you work with any marketing firms?","options":["Yes, I do work with one.","Yes, I am working with one.","Yes, I will work with one.","Yes, I worked with one."],"correctAnswer":"A","explanation":"La opción A es la correcta porque responde a la pregunta en presente simple, indicando un hecho actual.","points":2},{"id":"q4","type":"multiple-choice","scenario":"Your boss is asking about your availability for a meeting.","question":"Boss: Do you have time for a meeting this afternoon?","options":["Yes, I do.","Yes, I am.","Yes, I have.","Yes, I will."],"correctAnswer":"A","explanation":"La opción A es la correcta ya que es una respuesta directa y adecuada a una pregunta de sí/no en presente simple.","points":2},{"id":"q5","type":"multiple-choice","scenario":"A colleague asks about your opinion on a new workplace policy.","question":"Colleague: Do you think the new policy is effective?","options":["Yes, I do.","Yes, I am.","Yes, I did.","Yes, I will."],"correctAnswer":"A","explanation":"La opción A es la correcta porque es la forma adecuada de responder a una pregunta de opinión en presente simple.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex21',
  'a2-m2-l9',
  21,
  'word-search',
  $_$Word Search: Work and Jobs$_$,
  $_${"title":"Word Search: Work and Jobs","instructions":"Find the hidden words in the grid","words":["TEACHER","NURSE","ENGINEER","DOCTOR","OFFICE","FARM","FACTORY","SHOP","SCHOOL","HOSPITAL"],"gridSize":10,"clues":[{"word":"TEACHER","clue":"A person who teaches students."},{"word":"NURSE","clue":"A person who cares for sick people."},{"word":"ENGINEER","clue":"A person who designs and builds things."},{"word":"DOCTOR","clue":"A person who helps sick people feel better."},{"word":"OFFICE","clue":"A place where people work at desks."},{"word":"FARM","clue":"A place where animals and crops are raised."},{"word":"FACTORY","clue":"A place where things are made."},{"word":"SHOP","clue":"A place where you buy things."},{"word":"SCHOOL","clue":"A place where students learn."},{"word":"HOSPITAL","clue":"A place where sick people are treated."}]}$_$
