INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex22',
  'a2-m2-l9',
  22,
  'crossword',
  $_$Crossword: Work and Jobs$_$,
  $_${"title":"Crossword: Work and Jobs","instructions":"Complete the crossword puzzle","items":[{"word":"DOCTOR","clue":"This person helps sick people. (Across)","direction":"across","row":0,"col":0},{"word":"TEACHER","clue":"This person works in a school and helps students learn. (Down)","direction":"down","row":0,"col":4},{"word":"NURSE","clue":"This person works in a hospital and helps doctors. (Across)","direction":"across","row":2,"col":0},{"word":"POLICE","clue":"This person helps to keep people safe and enforce the law. (Down)","direction":"down","row":0,"col":8},{"word":"FARMER","clue":"This person works with animals or grows food on a farm. (Across)","direction":"across","row":4,"col":2},{"word":"OFFICE","clue":"A place where people work at desks with computers and phones. (Down)","direction":"down","row":1,"col":6},{"word":"ENGINEER","clue":"This person designs and builds machines or structures. (Across)","direction":"across","row":6,"col":0},{"word":"CHEF","clue":"This person cooks food in a restaurant. (Down)","direction":"down","row":3,"col":3}]}$_$
