INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex23',
  'a2-m2-l9',
  23,
  'reading-comprehension',
  $_$A Day at the Office$_$,
  $_${"title":"A Day at the Office","textPassage":"Anna works in a small office in the city. She is a receptionist. Every day, she answers the phone and helps visitors. Anna starts work at 9:00 AM and finishes at 5:00 PM. She does not work on weekends. Her office is on the second floor. There is a big window near her desk, and she can see the park. Anna likes her job because she meets many people. Sometimes, she talks to her colleagues during lunch. Anna's favorite colleague is Tom. He is very funny, and he works in the same office. They often have lunch together in a small café nearby.","wordCount":100,"estimatedReadingTime":2,"questions":[{"question":"What is Anna's job?","options":["A teacher","A receptionist","A manager","A nurse"],"answer":"A receptionist","explanation":"Anna trabaja como recepcionista en una oficina pequeña. El pasaje menciona que ella contesta el teléfono y ayuda a los visitantes, lo que es típico del trabajo de una recepcionista."},{"question":"Where is Anna's office located?","options":["On the first floor","In the city","Next to a café","In a big building"],"answer":"In the city","explanation":"La oficina de Anna está en la ciudad, como se menciona en la primera línea del pasaje."},{"question":"Does Anna work on weekends?","options":["Yes, she does","No, she doesn't","Only on Saturdays","Only on Sundays"],"answer":"No, she doesn't","explanation":"El texto claramente indica que Anna no trabaja los fines de semana."},{"question":"Who is Anna's favorite colleague?","options":["John","Tom","Mary","Sue"],"answer":"Tom","explanation":"Tom es el colega favorito de Anna, como se menciona que es muy divertido y trabajan en la misma oficina."},{"question":"What does Anna do during lunch?","options":["She works","She takes a walk","She talks to her colleagues","She reads a book"],"answer":"She talks to her colleagues","explanation":"Durante el almuerzo, Anna a veces habla con sus colegas, como se menciona en el texto."},{"question":"Does Anna like her job?","options":["Yes, she does","No, she doesn't","She is not sure","Sometimes"],"answer":"Yes, she does","explanation":"Anna le gusta su trabajo porque conoce a muchas personas, lo cual se menciona directamente en el texto."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex24',
  'a2-m2-l9',
  24,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Énfasis en el trabajo$_$,
  $_${"title":"Práctica de pronunciación: Énfasis en el trabajo","instructions":"Escucha y repite las siguientes oraciones en inglés, enfocándote en el uso correcto del presente simple en preguntas y el vocabulario relacionado con trabajos y lugares de trabajo.","targetSentences":[{"english":"Does the teacher work at this school?","spanish":"¿Trabaja el maestro en esta escuela?","phonetic":"/dʌz ðə ˈtiːʧər wɜrk æt ðɪs skuːl/","audioModel":"URL_placeholder_1","pronunciationTips":"Asegúrate de enfatizar 'does' al inicio de la pregunta para indicar que es una pregunta en presente simple.","focusPoints":"Enfoca en 'does' y la pronunciación clara de 'teacher'.","commonMistakes":"Evitar omitir 'does' al inicio de la pregunta."},{"english":"Do they work in the hospital?","spanish":"¿Trabajan ellos en el hospital?","phonetic":"/duː ðeɪ wɜrk ɪn ðə ˈhɒspɪtəl/","audioModel":"URL_placeholder_2","pronunciationTips":"Enfatiza 'do' al inicio para clarificar que es una pregunta.","focusPoints":"Enfoca en 'do' y la correcta pronunciación de 'hospital'.","commonMistakes":"Asegúrate de no confundir la pronunciación de 'do' y 'they'."},{"english":"Does your brother like his job as a chef?","spanish":"¿A tu hermano le gusta su trabajo como chef?","phonetic":"/dʌz jɔr ˈbrʌðər laɪk hɪz ʤɒb æz ə ʃɛf/","audioModel":"URL_placeholder_3","pronunciationTips":"Presta atención a la pronunciación de 'chef' y enfatiza 'does' al inicio.","focusPoints":"Enfoca en 'does' y la pronunciación de 'brother'.","commonMistakes":"Evitar decir 'chef' como /ʃeɪf/."},{"english":"Does she work in a big office?","spanish":"¿Trabaja ella en una oficina grande?","phonetic":"/dʌz ʃi wɜrk ɪn ə bɪg ˈɔfɪs/","audioModel":"URL_placeholder_4","pronunciationTips":"Asegúrate de enfatizar 'does' y pronunciar claramente 'office'.","focusPoints":"Enfoca en 'does' y la pronunciación de 'office'.","commonMistakes":"No omitir el sonido /s/ al final de 'does'."},{"english":"Do you enjoy your work as a nurse?","spanish":"¿Disfrutas tu trabajo como enfermero?","phonetic":"/duː juː ɪnˈʤɔɪ jɔr wɜrk æz ə nɜrs/","audioModel":"URL_placeholder_5","pronunciationTips":"Enfatiza 'do' y presta atención a la pronunciación de 'nurse'.","focusPoints":"Enfoca en 'do' y la pronunciación de 'enjoy'.","commonMistakes":"Evitar confundir 'enjoy' con 'enjo'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex25',
  'a2-m2-l9',
  25,
  'multiple-choice',
  $_$Work and Jobs: Present Simple Questions$_$,
  $_${"title":"Work and Jobs: Present Simple Questions","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"Where does a doctor work?","options":["A hospital","A school","A supermarket","A factory"],"correctAnswer":"A hospital","explanation":"Los médicos trabajan en hospitales, donde tratan a los pacientes."},{"id":"q2","question":"Do teachers work with children?","options":["Yes, they do","No, they don't","Yes, she does","No, he doesn't"],"correctAnswer":"Yes, they do","explanation":"Los maestros trabajan con niños en escuelas, impartiendo clases y ayudándoles a aprender."},{"id":"q3","question":"Does a chef cook food?","options":["Yes, he does","No, he doesn't","Yes, they do","No, they don't"],"correctAnswer":"Yes, he does","explanation":"Un chef cocina comida en restaurantes u otros establecimientos gastronómicos."},{"id":"q4","question":"Where do cashiers usually work?","options":["In a supermarket","In a hospital","In a library","In a school"],"correctAnswer":"In a supermarket","explanation":"Los cajeros suelen trabajar en supermercados, donde ayudan a los clientes a pagar por sus compras."},{"id":"q5","question":"Does an engineer design buildings?","options":["Yes, he does","No, he doesn't","Yes, they do","No, they don't"],"correctAnswer":"Yes, he does","explanation":"Un ingeniero puede diseñar edificios, aunque más comúnmente lo hace un arquitecto. Sin embargo, los ingenieros a menudo están involucrados en el diseño estructural."},{"id":"q6","question":"Do nurses assist doctors?","options":["Yes, they do","No, they don't","Yes, she does","No, he doesn't"],"correctAnswer":"Yes, they do","explanation":"Las enfermeras asisten a los médicos en el cuidado de los pacientes en hospitales y clínicas."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex26',
  'a2-m2-l9',
  26,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"________ (Does/Do) a teacher work in a school?","correct_answer":"Does","explanation":"Usamos 'Does' para formar preguntas en presente simple con sujetos en tercera persona singular (he, she, it). Aquí, 'a teacher' se refiere a 'he' o 'she'.","points":1},{"sentence":"________ (Do/Does) doctors help sick people?","correct_answer":"Do","explanation":"Usamos 'Do' para formar preguntas en presente simple con sujetos en primera y segunda persona del plural o singular (I, you, we, they). Aquí, 'doctors' es plural (they).","points":1},{"sentence":"Where ________ (do/does) a nurse work?","correct_answer":"does","explanation":"Usamos 'does' para preguntas con sujetos en tercera persona singular. 'A nurse' es 'he' o 'she'.","points":1},{"sentence":"________ (Does/Do) engineers design buildings?","correct_answer":"Do","explanation":"Usamos 'Do' para preguntas con sujetos en plural. 'Engineers' es plural (they).","points":1},{"sentence":"What ________ (do/does) a firefighter do?","correct_answer":"does","explanation":"Usamos 'does' para preguntas con sujetos en tercera persona singular. 'A firefighter' es 'he' o 'she'.","points":1},{"sentence":"________ (Do/Does) chefs work in a restaurant?","correct_answer":"Do","explanation":"Usamos 'Do' para preguntas con sujetos en plural. 'Chefs' es plural (they).","points":1},{"sentence":"Where ________ (does/do) an artist work?","correct_answer":"does","explanation":"Usamos 'does' para preguntas con sujetos en tercera persona singular. 'An artist' es 'he' o 'she'.","points":1},{"sentence":"________ (Do/Does) secretaries use computers?","correct_answer":"Do","explanation":"Usamos 'Do' para preguntas con sujetos en plural. 'Secretaries' es plural (they).","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex27',
  'a2-m2-l9',
  27,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["you","do","work","?","where"],"correctOrder":["Where","do","you","work","?"],"translation":"¿Dónde trabajas?","hint":"Pregunta sobre el lugar de trabajo usando 'do'.","points":2},{"id":2,"words":["he","does","a","doctor","is","?","work","as"],"correctOrder":["Does","he","work","as","a","doctor","?"],"translation":"¿Trabaja él como doctor?","hint":"Pregunta si trabaja en una profesión usando 'does'.","points":2},{"id":3,"words":["do","they","a","bank","at","work","?"],"correctOrder":["Do","they","work","at","a","bank","?"],"translation":"¿Trabajan ellos en un banco?","hint":"Pregunta sobre el lugar de trabajo usando 'do'.","points":2},{"id":4,"words":["your","does","sister","a","teacher","is","work","as","?"],"correctOrder":["Does","your","sister","work","as","a","teacher","?"],"translation":"¿Trabaja tu hermana como profesora?","hint":"Pregunta sobre la ocupación usando 'does'.","points":2},{"id":5,"words":["do","you","in","an","office","work","?"],"correctOrder":["Do","you","work","in","an","office","?"],"translation":"¿Trabajas en una oficina?","hint":"Pregunta sobre el lugar de trabajo usando 'do'.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex28',
  'a2-m2-l9',
  28,
  'speaking-analysis',
  $_$Speaking Analysis: Work and Jobs$_$,
  $_${"title":"Speaking Analysis: Work and Jobs","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"Your friend asks you about your job.","question":"What do you do?","options":["A: I am a teacher.","B: I was a teacher.","C: I will be a teacher.","D: I have been a teacher."],"correctAnswer":"A","explanation":"La pregunta está en presente simple, por lo que la respuesta debe coincidir con el tiempo presente para describir tu trabajo actual.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are at a job interview. The interviewer asks:","question":"Do you work well in a team?","options":["A: Yes, I do.","B: Yes, I am.","C: Yes, I can.","D: Yes, I should."],"correctAnswer":"A","explanation":"La respuesta correcta a una pregunta en presente simple con 'do' es 'Yes, I do'. Las otras opciones no concuerdan.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You meet someone new at a party who asks about your job.","question":"Where do you work?","options":["A: I work in a bank.","B: I am working in a bank.","C: I worked in a bank.","D: I have worked in a bank."],"correctAnswer":"A","explanation":"La pregunta usa el presente simple, así que la respuesta debe describir el lugar de trabajo actual usando el presente simple.","points":2},{"id":"q4","type":"multiple-choice","scenario":"Your colleague asks if you enjoy your job.","question":"Do you like your job?","options":["A: Yes, I do.","B: Yes, I am.","C: Yes, I like.","D: Yes, I did."],"correctAnswer":"A","explanation":"La respuesta correcta a una pregunta en presente simple con 'do' es 'Yes, I do'. Las otras opciones no son correctas en este contexto.","points":2},{"id":"q5","type":"multiple-choice","scenario":"Your boss asks if you can finish a report today.","question":"Do you have time to finish the report today?","options":["A: Yes, I do.","B: Yes, I have.","C: Yes, I can.","D: Yes, I must."],"correctAnswer":"A","explanation":"La pregunta está en presente simple utilizando 'do', por lo que la respuesta debe ser 'Yes, I do' para indicar que tienes tiempo.","points":2},{"id":"q6","type":"multiple-choice","scenario":"Your friend is curious about your work schedule.","question":"Do you work on weekends?","options":["A: No, I don't.","B: No, I am not.","C: No, I can't.","D: No, I won't."],"correctAnswer":"A","explanation":"La respuesta correcta a una pregunta en presente simple con 'do' es 'No, I don't'. Las otras opciones no concuerdan con la estructura de la pregunta.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex29',
  'a2-m2-l9',
