INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex46',
  'a2-m2-l9',
  46,
  'crossword',
  $_$Crossword: Work and Jobs$_$,
  $_${"title":"Crossword: Work and Jobs","instructions":"Complete the crossword puzzle","items":[{"word":"TEACHER","clue":"Who works in a school? (Hint: They help students learn)","direction":"across","row":0,"col":0},{"word":"DOCTOR","clue":"Who helps sick people in a hospital?","direction":"down","row":0,"col":2},{"word":"OFFICE","clue":"Where do many people work with computers and papers?","direction":"across","row":2,"col":0},{"word":"NURSE","clue":"Who assists doctors and takes care of patients?","direction":"down","row":2,"col":3},{"word":"BANK","clue":"Where do people go to keep their money safe?","direction":"across","row":4,"col":1},{"word":"CHEF","clue":"Who cooks food in a restaurant?","direction":"down","row":3,"col":5},{"word":"FARMER","clue":"Who works on a farm and grows crops?","direction":"across","row":6,"col":0},{"word":"RECEPTIONIST","clue":"Who answers the phone and greets visitors in an office?","direction":"down","row":0,"col":7}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex47',
  'a2-m2-l9',
  47,
  'reading-comprehension',
  $_$Daily Routines at Work$_$,
  $_${"title":"Daily Routines at Work","textPassage":"Maria works at a small bakery in her town. She loves her job because she enjoys baking and talking to customers. Every morning, Maria opens the bakery at 7 AM. Does she make bread first? No, she starts by making cakes and cookies. Many people come to the bakery for breakfast and enjoy fresh pastries. Does Maria work alone? No, she works with her friend, John. John makes coffee and serves drinks. They both clean the bakery before closing at 3 PM. Maria always asks her customers, 'Do you like the pastries?' Most people say they are delicious! In the afternoon, Maria sometimes goes to the market to buy fresh ingredients. Does she have weekends off? Yes, the bakery is closed on Saturdays and Sundays. On those days, Maria spends time with her family. She often asks, 'Do you want to go to the park?' They enjoy picnics and playing games together.","wordCount":150,"estimatedReadingTime":2,"questions":[{"question":"What is the main idea of the passage?","options":["Maria's daily routine at the bakery.","John's job at the coffee shop.","How Maria spends her weekends.","The types of bread Maria makes."],"answer":"Maria's daily routine at the bakery.","explanation":"La idea principal se centra en las actividades diarias de Maria en la panadería."},{"question":"What does Maria make first in the morning?","options":["Bread","Cakes and cookies","Coffee","Sandwiches"],"answer":"Cakes and cookies","explanation":"Maria empieza su día horneando pasteles y galletas, no pan."},{"question":"Who works with Maria at the bakery?","options":["Her brother","Her friend John","Her sister","Her boss"],"answer":"Her friend John","explanation":"John es el amigo de Maria que trabaja con ella en la panadería."},{"question":"Does Maria work on weekends?","options":["Yes, she works every day.","No, she has weekends off.","Only on Saturdays.","Only on Sundays."],"answer":"No, she has weekends off.","explanation":"La panadería está cerrada los sábados y domingos, así que Maria no trabaja esos días."},{"question":"What does John do at the bakery?","options":["He bakes bread.","He makes coffee and serves drinks.","He buys ingredients.","He decorates cakes."],"answer":"He makes coffee and serves drinks.","explanation":"John se encarga de preparar café y servir bebidas en la panadería."},{"question":"Is the bakery open in the afternoon?","options":["Yes, until 5 PM.","No, it closes at 3 PM.","Yes, all day.","No, it only opens in the morning."],"answer":"No, it closes at 3 PM.","explanation":"La panadería cierra a las 3 PM, por lo que no está abierta por la tarde."},{"question":"True or False: Maria usually buys fresh ingredients in the morning.","options":["True","False"],"answer":"False","explanation":"Maria compra ingredientes frescos por la tarde, no por la mañana."},{"question":"What does Maria often ask her family on weekends?","options":["Do you like the pastries?","Do you want to go to the park?","Do you want to help at the bakery?","Do you want to go shopping?"],"answer":"Do you want to go to the park?","explanation":"Maria a menudo pregunta a su familia si quieren ir al parque los fines de semana."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex48',
  'a2-m2-l9',
  48,
  'pronunciation-practice',
  $_$Práctica de pronunciación para el trabajo$_$,
  $_${"title":"Práctica de pronunciación para el trabajo","instructions":"Practica la pronunciación de las siguientes oraciones enfocándote en el uso correcto del presente simple con preguntas. Escucha atentamente la grabación y repite cada oración. Después, intenta decirlas en voz alta sin leer.","targetSentences":[{"english":"Does she work at the hospital?","spanish":"¿Trabaja ella en el hospital?","phoneticTranscription":"/dʌz ʃi wɜːrk æt ðə ˈhɒspɪtəl/","audioModelURL":"https://example.com/audio/does_she_work_at_the_hospital.mp3","pronunciationTips":"Enfatiza ligeramente el 'Does' al inicio de la pregunta para distinguirla como pregunta.","focusPoints":"Enfócate en la entonación ascendente al final de la pregunta.","commonMistakes":"Evita pronunciar 'does' como 'dos'; el sonido es /dʌz/."},{"english":"Do you know the new manager?","spanish":"¿Conoces al nuevo gerente?","phoneticTranscription":"/duː juː noʊ ðə nuː ˈmænɪdʒər/","audioModelURL":"https://example.com/audio/do_you_know_the_new_manager.mp3","pronunciationTips":"Asegúrate de que 'new' suene como /nuː/ y no como /njuː/.","focusPoints":"Mantén una entonación ascendente al final para marcar la pregunta.","commonMistakes":"No omitas el sonido /j/ en 'you'."},{"english":"Does he have to wear a uniform?","spanish":"¿Tiene que llevar uniforme?","phoneticTranscription":"/dʌz hi hæv tə wɛər ə ˈjuːnɪfɔːrm/","audioModelURL":"https://example.com/audio/does_he_have_to_wear_a_uniform.mp3","pronunciationTips":"La palabra 'uniform' comienza con el sonido /juː/, no /uː/.","focusPoints":"Focalízate en la conexión entre 'have' y 'to'.","commonMistakes":"Evita decir 'haves' en lugar de 'have'."},{"english":"Do they like their office?","spanish":"¿Les gusta su oficina?","phoneticTranscription":"/duː ðeɪ laɪk ðeər ˈɒfɪs/","audioModelURL":"https://example.com/audio/do_they_like_their_office.mp3","pronunciationTips":"Presta atención a la diferencia entre 'their' /ðeər/ y 'there' /ðeər/.","focusPoints":"Asegúrate de que 'office' suene correctamente como /ˈɒfɪs/.","commonMistakes":"No confundir 'like' con 'likes'; aquí no lleva 's'."},{"english":"Does your brother enjoy his job?","spanish":"¿Disfruta tu hermano de su trabajo?","phoneticTranscription":"/dʌz jɔːr ˈbrʌðər ɪnˈdʒɔɪ hɪz dʒɒb/","audioModelURL":"https://example.com/audio/does_your_brother_enjoy_his_job.mp3","pronunciationTips":"Asegúrate de pronunciar 'enjoy' con el sonido /ɪnˈdʒɔɪ/.","focusPoints":"La entonación debe subir al final de la pregunta.","commonMistakes":"Evita omitir el sonido /dʒ/ en 'enjoy'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex49',
  'a2-m2-l9',
  49,
  'multiple-choice',
  $_$Work and Jobs: Present Simple Questions$_$,
  $_${"title":"Work and Jobs: Present Simple Questions","instructions":"Selecciona la respuesta correcta para cada pregunta utilizando el presente simple con 'do/does'.","questions":[{"id":"q1","question":"Where does the teacher work?","options":["A) In a hospital","B) In a school","C) In a restaurant","D) In a bank"],"correctAnswer":"B","explanation":"La respuesta correcta es 'In a school' porque un profesor trabaja en una escuela. Las otras opciones son lugares de trabajo para diferentes profesiones."},{"id":"q2","question":"Do nurses work at night?","options":["A) Yes, they do","B) No, they doesn't","C) Yes, they does","D) No, they do"],"correctAnswer":"A","explanation":"La respuesta correcta es 'Yes, they do' porque la pregunta está hecha en presente simple, y la forma correcta de responder es usando 'do'."},{"id":"q3","question":"Does a chef cook food?","options":["A) No, he doesn't","B) Yes, he does","C) Yes, he cook","D) No, he do"],"correctAnswer":"B","explanation":"La respuesta correcta es 'Yes, he does' porque 'does' es la forma correcta en presente simple para he/she/it cuando se responde afirmativamente."},{"id":"q4","question":"Where do bank tellers work?","options":["A) In a bank","B) In a library","C) In a supermarket","D) In a factory"],"correctAnswer":"A","explanation":"La respuesta correcta es 'In a bank' porque los cajeros de banco trabajan en un banco. Las otras opciones no son lugares adecuados para su trabajo."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l9-ex50',
  'a2-m2-l9',
  50,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"___ you work in a hospital or an office?","options":["Do","Does"],"answer":"Do","explanation":"Usamos 'Do' para formar preguntas en present simple con 'you', 'we', 'they'.","points":1},{"sentence":"Where ___ your brother work?","options":["do","does"],"answer":"does","explanation":"Usamos 'Does' para formar preguntas en present simple con 'he', 'she', 'it'.","points":1},{"sentence":"___ your parents have jobs in the city?","options":["Do","Does"],"answer":"Do","explanation":"Usamos 'Do' para preguntas con 'your parents' (ellos).","points":1},{"sentence":"What time ___ the bank open?","options":["do","does"],"answer":"does","explanation":"Usamos 'Does' porque 'the bank' es singular (it).","points":1},{"sentence":"___ your teacher give you homework every day?","options":["Do","Does"],"answer":"Does","explanation":"Usamos 'Does' para preguntas en present simple con 'your teacher' (él/ella).","points":1},{"sentence":"Where ___ you usually buy your groceries?","options":["do","does"],"answer":"do","explanation":"Usamos 'Do' para preguntas con 'you'.","points":1},{"sentence":"___ it take long to get to your workplace?","options":["Do","Does"],"answer":"Does","explanation":"Usamos 'Does' con 'it' para formar preguntas en present simple.","points":1},{"sentence":"What ___ your sister do for a living?","options":["do","does"],"answer":"does","explanation":"Usamos 'Does' para preguntas con 'your sister' (ella).","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;

