INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex1',
  'a2-m2-l10',
  1,
  'multiple-choice',
  $_$Leisure Activities: Practice with Like/Love/Hate + -ing and Hardly Ever$_$,
  $_${"title":"Leisure Activities: Practice with Like/Love/Hate + -ing and Hardly Ever","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"Anna _____ going to the gym every week. She prefers reading a book at home.","options":["A) hardly ever","B) loves","C) likes","D) enjoys"],"correctAnswer":"A","explanation":"La expresión 'hardly ever' significa 'casi nunca', que encaja con el contexto de que Anna prefiere leer en casa en lugar de ir al gimnasio."},{"id":"q2","question":"Mark and Sarah _____ playing tennis on the weekends. It's their favorite sport.","options":["A) hate","B) dislike","C) love","D) hardly ever"],"correctAnswer":"C","explanation":"'Love' es el verbo correcto aquí porque indica que es su deporte favorito y disfrutan jugándolo los fines de semana."},{"id":"q3","question":"Peter _____ watching movies, but he always goes to the cinema with his friends.","options":["A) likes","B) hates","C) enjoys","D) loves"],"correctAnswer":"B","explanation":"El verbo 'hates' se usa aquí porque a pesar de que no le gusta ver películas, todavía va al cine con sus amigos, lo que sugiere una obligación o un compromiso."},{"id":"q4","question":"My brother _____ swimming in the sea. He goes every time we visit the beach.","options":["A) hardly ever","B) dislikes","C) loves","D) avoids"],"correctAnswer":"C","explanation":"'Loves' es correcto porque destaca que él siempre va a nadar cada vez que visita la playa, indicando que realmente le gusta hacerlo."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex2',
  'a2-m2-l10',
  2,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I ______ (love) swimming in the sea during the summer.","answer":"love","explanation":"Usamos 'love' con actividades que nos gustan mucho, seguido de un verbo en -ing.","points":1},{"sentence":"Sarah ______ (hardly ever) plays basketball because she prefers tennis.","answer":"hardly ever","explanation":"'Hardly ever' se usa para describir algo que ocurre muy raramente.","points":1},{"sentence":"They ______ (often) go to the cinema on weekends.","answer":"often","explanation":"'Often' se usa para describir actividades que hacemos frecuentemente.","points":1},{"sentence":"Mark ______ (hate) running in the park when it's cold.","answer":"hates","explanation":"Usamos 'hate' para actividades que no disfrutamos, seguido de un verbo en -ing.","points":1},{"sentence":"We ______ (never) go skiing because there is no snow nearby.","answer":"never","explanation":"'Never' se usa para hablar de algo que no hacemos nunca.","points":1},{"sentence":"Lucy ______ (enjoy) cooking for her family.","answer":"enjoys","explanation":"Usamos 'enjoy' para hablar de actividades que nos gustan, seguido de un verbo en -ing.","points":1},{"sentence":"He ______ (always) plays football with his friends every Saturday.","answer":"always","explanation":"'Always' se usa para actividades que ocurren el 100% de las veces.","points":1},{"sentence":"I ______ (sometimes) read books before going to sleep.","answer":"sometimes","explanation":"'Sometimes' se usa para actividades que hacemos algunas veces.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex3',
  'a2-m2-l10',
  3,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["playing","like","I","basketball","."],"correctOrder":["I","like","playing","basketball","."],"translation":"Me gusta jugar al baloncesto.","hint":"Empieza con el sujeto 'I' seguido de 'like' y un verbo en -ing.","points":2},{"id":2,"words":["tennis","hardly","play","ever","we","."],"correctOrder":["We","hardly","ever","play","tennis","."],"translation":"Casi nunca jugamos al tenis.","hint":"Usa 'hardly ever' después del sujeto 'we'.","points":2},{"id":3,"words":["love","watching","they","football","on","TV","."],"correctOrder":["They","love","watching","football","on","TV","."],"translation":"Les encanta ver fútbol en la televisión.","hint":"Comienza con 'They' seguido de 'love' y un verbo en -ing.","points":2},{"id":4,"words":["hates","she","swimming","in","the","sea","."],"correctOrder":["She","hates","swimming","in","the","sea","."],"translation":"Ella odia nadar en el mar.","hint":"Inicia con 'She' seguido de 'hates' y un verbo en -ing.","points":2},{"id":5,"words":["to","go","camping","we","love","in","summer","."],"correctOrder":["We","love","to","go","camping","in","summer","."],"translation":"Nos encanta ir de camping en verano.","hint":"Empieza con 'We' seguido de 'love to go' y luego la actividad.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex4',
  'a2-m2-l10',
  4,
  'speaking-analysis',
  $_$Speaking Analysis: Leisure Activities$_$,
  $_${"title":"Speaking Analysis: Leisure Activities","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking to a friend about hobbies. Your friend says, 'I like playing tennis, but I hardly ever have time for it.'","question":"What is the most natural response?","options":["A: I love playing tennis every weekend.","B: I hate playing tennis every weekend.","C: I think playing tennis is boring.","D: I like playing tennis once in a blue moon."],"correctAnswer":"A","explanation":"La opción A es la respuesta más natural porque muestra un acuerdo con el interés en el tenis, que es lo que dice el amigo.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are at a casual gathering and someone asks about your weekend plans.","question":"Which response is the most informal?","options":["A: I will engage in some leisure activities.","B: I'm going to chill out and maybe catch a movie.","C: I intend to participate in some recreational sports.","D: I shall partake in some leisurely pursuits."],"correctAnswer":"B","explanation":"La opción B es la más informal, usando expresiones coloquiales como 'chill out' y 'catch a movie'.","points":2},{"id":"q3","type":"multiple-choice","scenario":"A friend tells you, 'I tried rock climbing last weekend and it was a blast!'","question":"Which idiomatic expression fits in this context?","options":["A: It sounds like a piece of cake.","B: That must have been a ball.","C: It sounds like a walk in the park.","D: That must have been over the moon."],"correctAnswer":"B","explanation":"La opción B es la correcta porque 'have a ball' significa divertirse mucho, que es lo que implica la expresión del amigo.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are discussing your favorite leisure activities with a colleague.","question":"Which discourse marker is most appropriate?","options":["A: Well, I like swimming a lot.","B: However, I like swimming a lot.","C: Thus, I like swimming a lot.","D: Therefore, I like swimming a lot."],"correctAnswer":"A","explanation":"La opción A es la más adecuada porque 'well' se usa como marcador de discurso para introducir una opinión o cambio de tema.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You're politely declining an invitation to a sports event.","question":"Which version is most diplomatic?","options":["A: I can't come because I don't like sports.","B: I won't be able to attend, unfortunately.","C: I'm not coming, I have no interest.","D: I'll skip it, maybe next time."],"correctAnswer":"B","explanation":"La opción B es la más diplomática y cortés para rechazar una invitación, usando un tono formal y educado.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex5',
  'a2-m2-l10',
  5,
  'word-search',
  $_$Word Search: Leisure Activities$_$,
  $_${"title":"Word Search: Leisure Activities","instructions":"Find the hidden words in the grid","words":["SWIMMING","READING","CYCLING","FISHING","PAINTING","DANCING","GARDENING","COOKING"],"gridSize":10,"clues":[{"word":"SWIMMING","clue":"A popular water sport"},{"word":"READING","clue":"Looking at and understanding written words"},{"word":"CYCLING","clue":"Riding a bicycle"},{"word":"FISHING","clue":"Catching fish for sport or food"},{"word":"PAINTING","clue":"Creating art with colors"},{"word":"DANCING","clue":"Moving to music"},{"word":"GARDENING","clue":"Growing plants and flowers"},{"word":"COOKING","clue":"Preparing food"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex6',
  'a2-m2-l10',
  6,
  'crossword',
  $_$Crossword: Leisure Activities$_$,
  $_${"title":"Crossword: Leisure Activities","instructions":"Complete the crossword puzzle","items":[{"word":"SWIMMING","clue":"A water sport that many people love doing in the summer.","direction":"across","row":0,"col":0},{"word":"READING","clue":"A quiet activity you can do with a book.","direction":"down","row":0,"col":6},{"word":"CYCLING","clue":"Many people love this activity with a bike.","direction":"across","row":2,"col":4},{"word":"JOGGING","clue":"This is a running activity people do to stay fit.","direction":"down","row":1,"col":2},{"word":"PAINTING","clue":"An artistic activity involving colors and a brush.","direction":"across","row":4,"col":0},{"word":"GARDENING","clue":"A hobby that involves plants and flowers.","direction":"down","row":2,"col":5},{"word":"SKIING","clue":"A winter sport that involves sliding on snow.","direction":"across","row":6,"col":3}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex7',
  'a2-m2-l10',
  7,
  'reading-comprehension',
  $_$Leisure Activities$_$,
  $_${"title":"Leisure Activities","textPassage":"Emma loves spending her free time outdoors. She enjoys hiking in the mountains and often goes on long walks with her friends. On weekends, she likes playing tennis at the local sports club. Emma also loves painting and often paints landscapes of the places she visits. However, she hardly ever watches TV as she finds it boring. Instead, she prefers reading books or listening to music. Her brother, Tom, is different. He loves playing video games and hates going outside. He thinks hiking is tiring and prefers staying indoors. Despite their differences, they both enjoy cooking together every Sunday, trying new recipes and having fun in the kitchen.","wordCount":116,"estimatedReadingTime":2,"questions":[{"type":"multiple-choice","question":"What does Emma enjoy doing on the weekends?","options":["Watching TV","Playing tennis","Playing video games","Reading books"],"answer":"Playing tennis","explanation":"Emma enjoys playing tennis at the local sports club on weekends."},{"type":"multiple-choice","question":"How does Emma feel about watching TV?","options":["She loves it","She thinks it's boring","She finds it interesting","She doesn't mention it"],"answer":"She thinks it's boring","explanation":"Emma hardly ever watches TV because she finds it boring."},{"type":"multiple-choice","question":"What is something Emma and Tom both enjoy?","options":["Hiking","Playing tennis","Cooking","Watching TV"],"answer":"Cooking","explanation":"Despite their differences, Emma and Tom both enjoy cooking together every Sunday."},{"type":"true-false","question":"Tom loves hiking in the mountains.","answer":false,"explanation":"Tom hates going outside and thinks hiking is tiring."},{"type":"multiple-choice","question":"What activity does Emma hardly ever do?","options":["Hiking","Painting","Watching TV","Listening to music"],"answer":"Watching TV","explanation":"Emma hardly ever watches TV as she finds it boring."},{"type":"multiple-choice","question":"What does Tom prefer to do instead of hiking?","options":["Playing tennis","Playing video games","Painting","Reading books"],"answer":"Playing video games","explanation":"Tom loves playing video games and prefers staying indoors instead of hiking."},{"type":"true-false","question":"Emma and Tom enjoy hiking together.","answer":false,"explanation":"Emma enjoys hiking, but Tom hates going outside and prefers video games."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex8',
  'a2-m2-l10',
  8,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Estrés de palabras$_$,
  $_${"title":"Práctica de pronunciación: Estrés de palabras","instructions":"Practica la pronunciación del estrés de las palabras en inglés leyendo las siguientes oraciones. Presta atención a la sílaba acentuada en cada palabra destacada. Usa las transcripciones fonéticas como guía y escucha el modelo de audio proporcionado.","targetSentences":[{"englishSentence":"I love playing tennis on weekends.","spanishTranslation":"Me encanta jugar al tenis los fines de semana.","phoneticTranscription":"/ˈte.nɪs/","audioModelURL":"<audio-model-url-placeholder>","pronunciationTips":"Asegúrate de acentuar la primera sílaba en 'tennis'.","focusPoints":"Estrés en 'tennis'","commonMistakes":"No pronunciar 'tennis' como /təˈnɪs/."},{"englishSentence":"We hardly ever go hiking.","spanishTranslation":"Casi nunca vamos de excursión.","phoneticTranscription":"/ˈhaɪ.kɪŋ/","audioModelURL":"<audio-model-url-placeholder>","pronunciationTips":"La palabra 'hiking' lleva el estrés en la primera sílaba.","focusPoints":"Estrés en 'hiking'","commonMistakes":"No acentuar correctamente la primera sílaba de 'hiking'."},{"englishSentence":"She enjoys reading books about sports.","spanishTranslation":"Ella disfruta leer libros sobre deportes.","phoneticTranscription":"/ˈspɔːrts/","audioModelURL":"<audio-model-url-placeholder>","pronunciationTips":"Enfatiza la única sílaba en 'sports', que lleva el estrés.","focusPoints":"Estrés en 'sports'","commonMistakes":"No dar suficiente énfasis a la sílaba de 'sports'."},{"englishSentence":"My brother likes playing football.","spanishTranslation":"A mi hermano le gusta jugar al fútbol.","phoneticTranscription":"/ˈfʊt.bɔːl/","audioModelURL":"<audio-model-url-placeholder>","pronunciationTips":"Acentúa la primera sílaba en 'football'.","focusPoints":"Estrés en 'football'","commonMistakes":"Pronunciar 'football' como /fʊtˈbɔːl/."},{"englishSentence":"Her favorite hobby is painting.","spanishTranslation":"Su pasatiempo favorito es pintar.","phoneticTranscription":"/ˈpeɪn.tɪŋ/","audioModelURL":"<audio-model-url-placeholder>","pronunciationTips":"Recuerda acentuar la primera sílaba en 'painting'.","focusPoints":"Estrés en 'painting'","commonMistakes":"No acentuar la primera sílaba de 'painting'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex9',
  'a2-m2-l10',
  9,
  'multiple-choice',
  $_$Leisure Activities - A2 Level$_$,
  $_${"title":"Leisure Activities - A2 Level","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"Tom _______ playing soccer on the weekend.","options":["A. loves","B. hate","C. don't like","D. love"],"correctAnswer":"A","explanation":"La opción correcta es 'loves' porque es la forma correcta del verbo 'love' en tercera persona singular cuando se habla de una actividad que a alguien le gusta hacer regularmente."},{"id":"q2","question":"We _______ go to the cinema because it's expensive.","options":["A. hardly ever","B. always","C. often","D. every day"],"correctAnswer":"A","explanation":"La opción correcta es 'hardly ever' porque significa que casi nunca van al cine debido a los costos, lo cual encaja con la idea de que es caro."},{"id":"q3","question":"Anna _______ swimming at the local pool.","options":["A. enjoy","B. enjoys","C. enjoying","D. enjoyes"],"correctAnswer":"B","explanation":"La opción correcta es 'enjoys' porque es la forma correcta del verbo 'enjoy' en tercera persona singular presente simple."},{"id":"q4","question":"Peter _______ tennis matches on TV every weekend.","options":["A. loves watch","B. love watching","C. loves watching","D. love to watch"],"correctAnswer":"C","explanation":"La opción correcta es 'loves watching' porque después de 'love', cuando nos referimos a actividades, usamos el verbo con '-ing'. Además, 'loves' está en tercera persona singular."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex10',
  'a2-m2-l10',
  10,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I ___ (gustar / jugar) tenis con mis amigos los fines de semana.","gap":"like playing","explanation":"Usamos 'like' seguido de un verbo en -ing para hablar de actividades que disfrutamos.","points":1},{"sentence":"Ella ___ (no tener que) ir al gimnasio todos los días.","gap":"doesn't have to","explanation":"Usamos 'don't have to' para expresar que algo no es necesario.","points":1},{"sentence":"Nosotros ___ (ir a / planear) visitar el museo el sábado.","gap":"are going to","explanation":"Usamos 'going to' para hablar de planes futuros.","points":1},{"sentence":"Él ___ (decir / predicción) que el clima será soleado mañana.","gap":"will say","explanation":"Usamos 'will' para hacer predicciones sobre el futuro.","points":1},{"sentence":"Mi hermana ___ (amar / leer) libros en su tiempo libre.","gap":"loves reading","explanation":"Usamos 'love' seguido de un verbo en -ing para indicar actividades que nos encantan.","points":1},{"sentence":"Ellos ___ (casi nunca) juegan al fútbol.","gap":"hardly ever","explanation":"'Hardly ever' se usa para indicar que algo sucede muy raramente.","points":1},{"sentence":"Mi papá ___ (tener que) trabajar el sábado.","gap":"has to","explanation":"Usamos 'have to' para expresar obligación.","points":1},{"sentence":"Ella ___ (detestar / ver) películas de acción.","gap":"hates watching","explanation":"Usamos 'hate' seguido de un verbo en -ing para indicar actividades que no nos gustan.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex11',
  'a2-m2-l10',
  11,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["I","soccer","to","love","play","on","Saturdays","."],"correctOrder":["I","love","to","play","soccer","on","Saturdays","."],"translation":"Me encanta jugar al fútbol los sábados.","hint":"Usa 'love' seguido de 'to' para expresar gusto por una actividad habitual.","points":2},{"id":2,"words":["go","We","ever","to","hardly","the","cinema","."],"correctOrder":["We","hardly","ever","go","to","the","cinema","."],"translation":"Casi nunca vamos al cine.","hint":"Coloca 'hardly ever' para expresar poca frecuencia antes del verbo.","points":2},{"id":3,"words":["swimming","hate","in","the","I","cold","sea","."],"correctOrder":["I","hate","swimming","in","the","cold","sea","."],"translation":"Odio nadar en el mar frío.","hint":"Utiliza 'hate' seguido de un verbo en '-ing' para expresar disgusto.","points":2},{"id":4,"words":["volleyball","on","playing","Sundays","enjoy","We","."],"correctOrder":["We","enjoy","playing","volleyball","on","Sundays","."],"translation":"Disfrutamos jugando al voleibol los domingos.","hint":"Emplea 'enjoy' seguido de un verbo en '-ing' para actividades placenteras.","points":2},{"id":5,"words":["hiking","My","sister","loves","in","the","mountains","."],"correctOrder":["My","sister","loves","hiking","in","the","mountains","."],"translation":"A mi hermana le encanta hacer senderismo en las montañas.","hint":"Coloca 'loves' seguido de un verbo en '-ing' para expresar afición.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex12',
  'a2-m2-l10',
  12,
  'speaking-analysis',
  $_$Speaking Analysis: Leisure Activities$_$,
  $_${"title":"Speaking Analysis: Leisure Activities","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"Your friend asks if you like swimming.","question":"What is the most natural response?","options":["I like swim.","I like swimming.","I like to swim.","I am liking swimming."],"correctAnswer":"B","explanation":"En inglés, después de verbos como 'like', 'love', 'hate', se usa el gerundio (-ing) para expresar gustos o preferencias. Por eso, 'I like swimming' es la opción correcta.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You rarely play basketball.","question":"How would you express this using 'hardly ever'?","options":["I hardly play basketball.","I hardly ever play basketball.","I don't hardly ever play basketball.","I play basketball hardly."],"correctAnswer":"B","explanation":"'Hardly ever' se usa para indicar que algo ocurre muy raramente. La opción correcta es 'I hardly ever play basketball'.","points":2},{"id":"q3","type":"multiple-choice","scenario":"Talking to your friend about your weekend plans.","question":"Which is the most informal way to suggest playing football?","options":["Would you like to play football this weekend?","Do you fancy playing football this weekend?","Would you care to play football this weekend?","Shall we play football this weekend?"],"correctAnswer":"B","explanation":"'Do you fancy' es una forma informal y común en inglés británico para sugerir hacer algo.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You're describing your friend's hobby.","question":"Which sentence is correct?","options":["He loves to collect stamps.","He loves collecting stamps.","He love collecting stamps.","He loving collect stamps."],"correctAnswer":"B","explanation":"Después de 'love', se puede usar tanto 'to + infinitivo' como el gerundio (-ing), pero en este contexto, 'He loves collecting stamps' es más común.","points":2},{"id":"q5","type":"multiple-choice","scenario":"Your friend is describing their dislike for jogging.","question":"What is the most appropriate expression?","options":["I hate jogging.","I hate to jog.","I am hating jogging.","I hate jog."],"correctAnswer":"A","explanation":"El verbo 'hate' se combina con el gerundio para expresar aversión de forma general, por lo que 'I hate jogging' es correcto.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You want to politely suggest going to the cinema.","question":"Choose the most diplomatic way.","options":["Why not go to the cinema?","We could go to the cinema, maybe?","Let's go to the cinema!","Cinema is a good idea."],"correctAnswer":"B","explanation":"La opción 'We could go to the cinema, maybe?' es una sugerencia cortés y abierta, adecuada en una conversación diplomática.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex13',
  'a2-m2-l10',
  13,
  'word-search',
  $_$Word Search: Leisure Activities$_$,
  $_${"title":"Word Search: Leisure Activities","instructions":"Find the hidden words in the grid","words":["SWIMMING","CYCLING","READING","GARDENING","HIKING","DANCING","PAINTING","RUNNING","COOKING","FISHING"],"gridSize":10,"clues":[{"word":"SWIMMING","clue":"Nadar en una piscina o en el mar"},{"word":"CYCLING","clue":"Montar en bicicleta"},{"word":"READING","clue":"Actividad de pasar páginas de un libro"},{"word":"GARDENING","clue":"Cuidar plantas y flores en el jardín"},{"word":"HIKING","clue":"Caminar por senderos o montañas"},{"word":"DANCING","clue":"Mover el cuerpo al ritmo de la música"},{"word":"PAINTING","clue":"Usar pinceles y colores para crear arte"},{"word":"RUNNING","clue":"Correr rápidamente"},{"word":"COOKING","clue":"Preparar comida en la cocina"},{"word":"FISHING","clue":"Atrapar peces en el agua"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex14',
  'a2-m2-l10',
  14,
  'crossword',
  $_$Crossword: Leisure Activities$_$,
  $_${"title":"Crossword: Leisure Activities","instructions":"Complete the crossword puzzle","items":[{"word":"SWIMMING","clue":"An activity you do in the pool. I love _____ in summer.","direction":"across","row":0,"col":0},{"word":"READING","clue":"I like _____ books in my free time.","direction":"down","row":0,"col":2},{"word":"GUITAR","clue":"A musical instrument with strings. He loves playing the _____.","direction":"across","row":1,"col":0},{"word":"RUNNING","clue":"A sport you can do in the park. She hardly ever goes _____.","direction":"down","row":0,"col":4},{"word":"MOVIES","clue":"I enjoy watching _____ at the cinema.","direction":"across","row":3,"col":1},{"word":"CYCLING","clue":"An activity done on a bike. They like _____ on weekends.","direction":"down","row":1,"col":5},{"word":"GAMES","clue":"Children love playing video _____.","direction":"across","row":4,"col":3}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex15',
  'a2-m2-l10',
  15,
  'reading-comprehension',
  $_$John's Weekend Hobbies$_$,
  $_${"title":"John's Weekend Hobbies","textPassage":"John loves spending his weekends doing different activities. On Saturday mornings, he likes going for a run in the park. He feels happy when he is running because it gives him energy for the day. After his run, John often meets his friends to play basketball. He enjoys playing basketball because it's fun and keeps him fit. In the afternoon, John sometimes visits the local library. He likes reading books about sports and adventure. On Sunday, John hardly ever stays at home. He prefers going to the beach with his family. They love swimming and playing beach volleyball. At the end of the day, John usually watches a movie with his family. He doesn't like watching scary movies, so they choose comedies or action films. John's weekends are always full of activities, and he loves every moment of it.","wordCount":145,"estimatedReadingTime":3,"questions":[{"question":"What does John do on Saturday mornings?","options":["He goes for a run.","He plays football.","He visits the library.","He watches movies."],"answer":"He goes for a run.","explanation":"En el pasaje, se menciona que John gusta de correr en el parque los sábados por la mañana."},{"question":"Why does John enjoy playing basketball?","options":["Because it's relaxing.","Because it's fun and keeps him fit.","Because he plays alone.","Because he can do it at home."],"answer":"Because it's fun and keeps him fit.","explanation":"El texto dice que a John le gusta jugar al baloncesto porque es divertido y lo mantiene en forma."},{"question":"What does John prefer doing on Sundays?","options":["Staying at home.","Going to the beach.","Reading books.","Running in the park."],"answer":"Going to the beach.","explanation":"Se indica que John prefiere ir a la playa con su familia los domingos."},{"question":"What type of movies does John like to watch?","options":["Scary movies.","Comedies or action films.","Romantic movies.","Documentaries."],"answer":"Comedies or action films.","explanation":"A John no le gustan las películas de miedo, por lo que elige ver comedias o películas de acción."},{"question":"True or False: John hardly ever visits the library.","options":["True","False"],"answer":"False","explanation":"John a veces visita la biblioteca local los sábados por la tarde, lo cual sugiere que sí la frecuenta."},{"question":"What activity is NOT mentioned in the passage?","options":["Playing beach volleyball.","Reading sports books.","Cooking meals.","Running in the park."],"answer":"Cooking meals.","explanation":"La actividad de cocinar no se menciona en el texto."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex16',
  'a2-m2-l10',
  16,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Enfoque en el estrés de las palabras$_$,
  $_${"title":"Práctica de pronunciación: Enfoque en el estrés de las palabras","instructions":"Lee las siguientes oraciones en inglés enfocándote en el estrés de las palabras. Practica la pronunciación usando las transcripciones fonéticas y consejos proporcionados.","targetSentences":[{"sentence":"I love playing tennis on weekends.","translation":"Me encanta jugar al tenis los fines de semana.","phoneticTranscription":"/ˈpleɪ.ɪŋ/ /ˈten.ɪs/","audioModelURL":"audio-url-placeholder","pronunciationTips":"Asegúrate de enfatizar la primera sílaba de 'playing' y 'tennis'.","focusPoints":"Enfocar en el estrés de las palabras 'playing' y 'tennis'.","commonMistakes":"No coloques el estrés en la segunda sílaba de 'playing'."},{"sentence":"We hardly ever go swimming in winter.","translation":"Casi nunca vamos a nadar en invierno.","phoneticTranscription":"/ˈswɪm.ɪŋ/","audioModelURL":"audio-url-placeholder","pronunciationTips":"Enfatiza la primera sílaba de 'swimming'.","focusPoints":"Enfocar en la palabra 'swimming'.","commonMistakes":"No olvides el estrés en la primera sílaba de 'swimming'."},{"sentence":"She likes reading books about history.","translation":"A ella le gusta leer libros sobre historia.","phoneticTranscription":"/ˈriː.dɪŋ/ /ˈhɪs.tə.ri/","audioModelURL":"audio-url-placeholder","pronunciationTips":"La primera sílaba de 'reading' y 'history' debe ser fuerte.","focusPoints":"Enfocar en las palabras 'reading' y 'history'.","commonMistakes":"No coloques el estrés en la segunda sílaba de 'reading'."},{"sentence":"My dad enjoys cooking Italian food.","translation":"A mi papá le gusta cocinar comida italiana.","phoneticTranscription":"/ˈkʊk.ɪŋ/ /ɪˈtæl.jən/","audioModelURL":"audio-url-placeholder","pronunciationTips":"Enfatiza la primera sílaba de 'cooking' y la segunda de 'Italian'.","focusPoints":"Enfocar en 'cooking' y 'Italian'.","commonMistakes":"No coloques el estrés en la segunda sílaba de 'cooking'."},{"sentence":"They love hiking in the mountains.","translation":"Les encanta hacer senderismo en las montañas.","phoneticTranscription":"/ˈhaɪ.kɪŋ/ /ˈmaʊn.tɪnz/","audioModelURL":"audio-url-placeholder","pronunciationTips":"Asegúrate de enfatizar la primera sílaba de 'hiking' y 'mountains'.","focusPoints":"Enfocar en el estrés de 'hiking' y 'mountains'.","commonMistakes":"No coloques el estrés en la segunda sílaba de 'hiking'."},{"sentence":"She loves dancing to pop music.","translation":"A ella le encanta bailar música pop.","phoneticTranscription":"/ˈdæn.sɪŋ/ /ˈmjuː.zɪk/","audioModelURL":"audio-url-placeholder","pronunciationTips":"Enfatiza la primera sílaba de 'dancing' y 'music'.","focusPoints":"Enfocar en 'dancing' y 'music'.","commonMistakes":"No olvides el estrés en la primera sílaba de 'dancing'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex17',
  'a2-m2-l10',
  17,
  'multiple-choice',
  $_$Leisure Activities and Hobbies$_$,
  $_${"title":"Leisure Activities and Hobbies","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"What do you like doing in your free time?","options":["A. I like play football.","B. I like playing football.","C. I like plays football.","D. I like to playing football."],"correctAnswer":"B","explanation":"En inglés, después de 'like', usamos el verbo en la forma -ing para hablar de actividades que disfrutamos."},{"id":"q2","question":"How often do you go swimming?","options":["A. I am going swimming every weekend.","B. I go swimming hardly.","C. I hardly ever go swimming.","D. I am hardly ever going swimming."],"correctAnswer":"C","explanation":"'Hardly ever' se usa para indicar que algo casi nunca ocurre. La opción C es correcta porque sigue la estructura correcta de frecuencia."},{"id":"q3","question":"My sister loves ________ to music while jogging.","options":["A. listens","B. listen","C. listening","D. to listens"],"correctAnswer":"C","explanation":"Después de 'loves', se usa el verbo en -ing para describir acciones que le gustan a alguien."},{"id":"q4","question":"John doesn't enjoy ________ TV shows.","options":["A. watching","B. watch","C. watched","D. watches"],"correctAnswer":"A","explanation":"Después de 'enjoy', se utiliza el verbo en la forma -ing para hablar de actividades que no le gustan a alguien."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex18',
  'a2-m2-l10',
  18,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"question":"They like ______ (play) football every weekend.","acceptableAnswers":["playing"],"explanation":"En inglés, cuando usamos 'like', 'love' o 'hate' seguido de una acción, debemos añadir '-ing' al verbo. Aquí, 'play' se convierte en 'playing'.","points":1},{"question":"I hardly ever ______ (watch) TV during the week.","acceptableAnswers":["watch"],"explanation":"La expresión 'hardly ever' se utiliza para describir acciones que casi nunca se realizan. El verbo permanece en su forma base después de 'hardly ever'.","points":1},{"question":"She loves ______ (swim) in the sea during summer.","acceptableAnswers":["swimming"],"explanation":"Al igual que con 'like', cuando usamos 'love' seguido de un verbo, se añade '-ing'. Por eso, 'swim' se convierte en 'swimming'.","points":1},{"question":"Do you enjoy ______ (read) books in your free time?","acceptableAnswers":["reading"],"explanation":"Con 'enjoy', también usamos '-ing' después del verbo. Así, 'read' se convierte en 'reading'.","points":1},{"question":"He hates ______ (do) exercises every morning.","acceptableAnswers":["doing"],"explanation":"Después de 'hate', es necesario usar el verbo con '-ing'. Por eso, 'do' se transforma en 'doing'.","points":1},{"question":"We hardly ever ______ (go) to the cinema because we prefer watching movies at home.","acceptableAnswers":["go"],"explanation":"Después de 'hardly ever', el verbo permanece en su forma base, así que 'go' no cambia.","points":1},{"question":"They like ______ (have) picnics in the park.","acceptableAnswers":["having"],"explanation":"Con 'like', es necesario añadir '-ing' al verbo. Por eso, 'have' se convierte en 'having'.","points":1},{"question":"She hardly ever ______ (miss) her yoga class.","acceptableAnswers":["misses"],"explanation":"Con 'hardly ever', el verbo se conjuga en presente simple. Como el sujeto es 'she', se añade '-es' a 'miss'.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex19',
  'a2-m2-l10',
  19,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["shouldn't","eat","You","so","many","sweets","."],"correctOrder":["You","shouldn't","eat","so","many","sweets","."],"translation":"No deberías comer tantos dulces.","hint":"Usa 'shouldn't' para dar un consejo.","points":2},{"id":2,"words":["I","really","like","playing","football","on","Sundays","."],"correctOrder":["I","really","like","playing","football","on","Sundays","."],"translation":"Realmente me gusta jugar al fútbol los domingos.","hint":"Usa 'really' para expresar un grado alto de gusto.","points":2},{"id":3,"words":["We","have","to","wake","up","early","tomorrow","."],"correctOrder":["We","have","to","wake","up","early","tomorrow","."],"translation":"Tenemos que levantarnos temprano mañana.","hint":"Usa 'have to' para expresar obligación.","points":2},{"id":4,"words":["There","aren't","any","tickets","left","for","the","concert","."],"correctOrder":["There","aren't","any","tickets","left","for","the","concert","."],"translation":"No quedan entradas para el concierto.","hint":"Usa 'any' en oraciones negativas.","points":2},{"id":5,"words":["They","are","going","to","visit","the","museum","next","week","."],"correctOrder":["They","are","going","to","visit","the","museum","next","week","."],"translation":"Ellos van a visitar el museo la próxima semana.","hint":"Usa el presente continuo para planes futuros.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex20',
  'a2-m2-l10',
  20,
  'speaking-analysis',
  $_$Speaking Analysis: Leisure Activities$_$,
  $_${"title":"Speaking Analysis: Leisure Activities","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"Your friend asks if you enjoy playing sports.","question":"Which reply is most natural?","options":["I like play football every weekend.","I enjoys playing football every weekend.","I like playing football every weekend.","I like played football every weekend."],"correctAnswer":"C","explanation":"La opción correcta es 'I like playing football every weekend.' porque 'like' se sigue de un verbo en forma -ing para expresar gustos.","points":2},{"id":"q2","type":"multiple-choice","scenario":"A friend is describing their weekend plans. What is a polite way to ask if you can join?","question":"Which question is most diplomatic?","options":["Can I come?","May I join you?","I want to go with you.","You should take me."],"correctAnswer":"B","explanation":"La opción 'May I join you?' es la más educada y diplomática. 'May' se usa para hacer preguntas corteses.","points":2},{"id":"q3","type":"multiple-choice","scenario":"Your friend mentions they hardly ever watch TV. How do you express agreement using a similar structure?","question":"Which sentence is correct?","options":["I hardly ever watch TV too.","I hardly never watch TV either.","I don't hardly ever watch TV.","I hardly watch ever TV."],"correctAnswer":"A","explanation":"La opción 'I hardly ever watch TV too.' es correcta porque sigue la estructura 'hardly ever' para expresar baja frecuencia.","points":2},{"id":"q4","type":"multiple-choice","scenario":"A friend from England asks if you want to go hiking. How do you respond informally agreeing?","question":"Which phrase fits the casual context?","options":["Yes, I would like that.","Sure, sounds fun!","I am agreeing.","That would be agreeable."],"correctAnswer":"B","explanation":"La opción 'Sure, sounds fun!' es informal y adecuada para aceptar una invitación de manera amigable.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are telling a friend about a new hobby. How do you express enjoyment with a British expression?","question":"Which phrase is correct?","options":["I'm really into it.","I am keen on it.","I am fancy it.","I am liking it."],"correctAnswer":"B","explanation":"La opción 'I am keen on it.' es una expresión británica que significa que algo te gusta mucho.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are talking about a boring movie you watched. How do you use an idiomatic expression correctly?","question":"Which sentence is correct?","options":["The movie was as dull as dishwater.","The movie was exciting as a dishwater.","The movie was dull like dishwater.","The movie was so exciting it was dishwater."],"correctAnswer":"A","explanation":"La expresión 'as dull as dishwater' se usa para describir algo extremadamente aburrido, y es la correcta aquí.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex21',
  'a2-m2-l10',
  21,
  'word-search',
  $_$Word Search: Leisure Activities$_$,
  $_${"title":"Word Search: Leisure Activities","instructions":"Find the hidden words in the grid","words":["SWIMMING","CYCLING","READING","DANCING","RUNNING","JOGGING","FISHING","GARDENING","PAINTING","WATCHING","COOKING","PLAYING"],"gridSize":10,"clues":[{"word":"SWIMMING","clue":"Nadar"},{"word":"CYCLING","clue":"Montar en bicicleta"},{"word":"READING","clue":"Leer"},{"word":"DANCING","clue":"Bailar"},{"word":"RUNNING","clue":"Correr"},{"word":"JOGGING","clue":"Trotar"},{"word":"FISHING","clue":"Pescar"},{"word":"GARDENING","clue":"Hacer jardinería"},{"word":"PAINTING","clue":"Pintar"},{"word":"WATCHING","clue":"Ver (como en 'ver televisión')"},{"word":"COOKING","clue":"Cocinar"},{"word":"PLAYING","clue":"Jugar"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex22',
  'a2-m2-l10',
  22,
  'crossword',
  $_$Crossword: Leisure Activities$_$,
  $_${"title":"Crossword: Leisure Activities","instructions":"Complete the crossword puzzle","items":[{"word":"BASKETBALL","clue":"A sport played with a ball and a hoop.","direction":"across","row":0,"col":0},{"word":"READING","clue":"An activity you do with books.","direction":"down","row":0,"col":4},{"word":"SWIMMING","clue":"Moving through water for sport or fun.","direction":"across","row":3,"col":2},{"word":"GUITAR","clue":"A musical instrument with strings.","direction":"down","row":1,"col":7},{"word":"PAINTING","clue":"Creating art with colors on a canvas.","direction":"down","row":4,"col":0},{"word":"JOGGING","clue":"Running at a slow, steady pace.","direction":"across","row":6,"col":3}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex23',
  'a2-m2-l10',
  23,
  'reading-comprehension',
  $_$Exploring Hobbies$_$,
  $_${"title":"Exploring Hobbies","textPassage":"Many people have different hobbies that they enjoy in their free time. Some people love playing sports like football, basketball, or tennis. They enjoy being active and staying healthy. Others like more relaxing activities such as reading books or watching movies. These activities help them to relax and unwind after a long day. Some people hate cooking, but others love spending time in the kitchen trying new recipes. They find it fun and rewarding to create delicious meals. Some people hardly ever watch TV because they prefer to go for walks or spend time with friends. Everyone has different ways to enjoy their leisure time, and there is no right or wrong way. It's important to do what makes you happy and gives you joy.","wordCount":115,"estimatedReadingTime":2,"questions":[{"question":"What is the main idea of the passage?","options":["Different people enjoy different hobbies in their free time.","Everyone should play sports to stay healthy.","Cooking is the best way to relax.","Watching TV is a popular hobby for everyone."],"answer":"Different people enjoy different hobbies in their free time.","explanation":"La idea principal del pasaje es que las personas disfrutan de diferentes pasatiempos en su tiempo libre. No dice que todos deben hacer lo mismo."},{"question":"Which activity is mentioned as relaxing?","options":["Playing tennis","Reading books","Cooking","Going for walks"],"answer":"Reading books","explanation":"El texto menciona que leer libros es una actividad relajante que ayuda a las personas a relajarse después de un día largo."},{"question":"Some people hardly ever watch TV because they prefer:","options":["To play sports","To cook","To go for walks","To read books"],"answer":"To go for walks","explanation":"El texto indica que algunas personas prefieren salir a caminar en lugar de ver la televisión."},{"question":"What do some people find rewarding?","options":["Playing sports","Watching movies","Cooking","Reading books"],"answer":"Cooking","explanation":"El texto menciona que algunas personas encuentran gratificante cocinar, ya que disfrutan creando comidas deliciosas."},{"question":"True or False: Everyone loves playing sports.","options":["True","False"],"answer":"False","explanation":"El texto dice que algunas personas aman los deportes, pero no menciona que todos los aman. Cada persona tiene diferentes pasatiempos."},{"question":"What do people enjoy doing after a long day?","options":["Playing sports","Reading books","Cooking","Watching TV"],"answer":"Reading books","explanation":"Según el texto, leer libros o ver películas son actividades que las personas disfrutan para relajarse después de un día largo."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex24',
  'a2-m2-l10',
  24,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Estrés de Palabras en Actividades de Ocio$_$,
  $_${"title":"Práctica de Pronunciación: Estrés de Palabras en Actividades de Ocio","instructions":"Practica la pronunciación enfocándote en el estrés de las palabras. Escucha las oraciones y repite, prestando atención al acento en las sílabas destacadas.","targetSentences":[{"englishSentence":"I love swimming in the ocean.","spanishTranslation":"Me encanta nadar en el océano.","phoneticTranscription":"sˈwɪmɪŋ","audioModelURL":"https://example.com/audio1","pronunciationTips":"Enfócate en el estrés de la primera sílaba de 'swimming'.","focusPoints":"Acento en 'swim-'","commonMistakes":"No pronunciar el acento en la primera sílaba correctamente."},{"englishSentence":"She hardly ever goes hiking.","spanishTranslation":"Ella casi nunca va a hacer senderismo.","phoneticTranscription":"ˈhɑːrdli ˈevər","audioModelURL":"https://example.com/audio2","pronunciationTips":"Asegúrate de que 'hardly' tenga el acento en la primera sílaba.","focusPoints":"Acento en 'hard-'","commonMistakes":"Colocar el acento en la segunda sílaba de 'hardly'."},{"englishSentence":"They enjoy playing basketball.","spanishTranslation":"Disfrutan jugar al baloncesto.","phoneticTranscription":"ˈpleɪɪŋ","audioModelURL":"https://example.com/audio3","pronunciationTips":"Pon atención al acento en 'play-' al decir 'playing'.","focusPoints":"Acento en 'play-'","commonMistakes":"Reducir el énfasis en la primera sílaba de 'playing'."},{"englishSentence":"He likes reading books.","spanishTranslation":"Le gusta leer libros.","phoneticTranscription":"ˈriːdɪŋ","audioModelURL":"https://example.com/audio4","pronunciationTips":"Asegúrate de acentuar bien la primera sílaba en 'reading'.","focusPoints":"Acento en 'read-'","commonMistakes":"Pronunciar débilmente la primera sílaba de 'reading'."},{"englishSentence":"We often play tennis on weekends.","spanishTranslation":"A menudo jugamos al tenis los fines de semana.","phoneticTranscription":"ˈtenɪs","audioModelURL":"https://example.com/audio5","pronunciationTips":"El énfasis debe estar en la primera sílaba de 'tennis'.","focusPoints":"Acento en 'ten-'","commonMistakes":"Colocar el acento en la segunda sílaba de 'tennis'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex25',
  'a2-m2-l10',
  25,
  'multiple-choice',
  $_$Leisure Activities and Hobbies$_$,
  $_${"title":"Leisure Activities and Hobbies","instructions":"Choose the correct answer for each question.","questions":[{"id":"q1","question":"What does Sarah like to do in her free time?","options":["A. She likes playing basketball.","B. She like play basketball.","C. She likes play basketball.","D. She like playing basketball."],"correctAnswer":"A","explanation":"La respuesta correcta es 'She likes playing basketball.' porque después de 'likes' usamos el gerundio (-ing) para hablar de actividades que disfrutamos."},{"id":"q2","question":"How often does Tom go swimming?","options":["A. He always go swimming.","B. He hardly ever goes swimming.","C. He hardly ever go swimming.","D. He always goes swimming."],"correctAnswer":"B","explanation":"La respuesta correcta es 'He hardly ever goes swimming.' porque 'hardly ever' significa casi nunca y requiere la tercera persona del singular 'goes'."},{"id":"q3","question":"Do you like _____ books?","options":["A. read","B. reading","C. to reading","D. reads"],"correctAnswer":"B","explanation":"La respuesta correcta es 'Do you like reading books?' porque después de 'like' se utiliza el gerundio (-ing) para hablar de actividades que disfrutamos."},{"id":"q4","question":"My brother _____ play tennis on the weekends.","options":["A. don't","B. doesn't","C. isn't","D. didn't"],"correctAnswer":"B","explanation":"La respuesta correcta es 'My brother doesn't play tennis on the weekends.' porque 'doesn't' es la forma negativa correcta para la tercera persona del singular en presente simple."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex26',
  'a2-m2-l10',
  26,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"My brother _______________ (like) playing basketball every weekend.","gap":"likes","explanation":"En inglés, usamos 'like' seguido de un verbo en forma -ing cuando hablamos de actividades que disfrutamos.","points":1},{"sentence":"Claire _______________ (hardly ever) goes swimming in the winter.","gap":"hardly ever","explanation":"La expresión 'hardly ever' indica que algo sucede con muy poca frecuencia, casi nunca.","points":1},{"sentence":"They _______________ (love) going to the park to play soccer.","gap":"love","explanation":"Usamos 'love' seguido de un verbo en -ing para expresar actividades que nos encantan.","points":1},{"sentence":"I _______________ (not like) watching TV because I prefer reading.","gap":"don't like","explanation":"Para expresar que no disfrutamos de una actividad, usamos 'don't like' seguido de un verbo en -ing.","points":1},{"sentence":"Sarah _______________ (hate) running in the rain.","gap":"hates","explanation":"Usamos 'hate' seguido de un verbo en -ing para actividades que no nos gustan en absoluto.","points":1},{"sentence":"We _______________ (hardly ever) play board games because we prefer outdoor activities.","gap":"hardly ever","explanation":"La expresión 'hardly ever' se utiliza para decir que algo casi nunca sucede.","points":1},{"sentence":"Tom _______________ (enjoy) cycling in the countryside.","gap":"enjoys","explanation":"El verbo 'enjoy' se sigue de un verbo en -ing para indicar actividades que nos gustan.","points":1},{"sentence":"Do you _______________ (like) hiking in the mountains?","gap":"like","explanation":"Usamos 'like' seguido de un verbo en -ing para preguntar si a alguien le gusta una actividad.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex27',
  'a2-m2-l10',
  27,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["playing","He","football","loves","."],"correctOrder":["He","loves","playing","football","."],"translation":"Él ama jugar al fútbol.","hint":"Utiliza 'love' seguido de un verbo en '-ing'.","points":2},{"id":2,"words":["Sarah","like","doesn't","swimming","at","the","pool","."],"correctOrder":["Sarah","doesn't","like","swimming","at","the","pool","."],"translation":"A Sarah no le gusta nadar en la piscina.","hint":"Usa 'doesn't like' para expresar que no disfruta de una actividad.","points":2},{"id":3,"words":["often","goes","They","the","to","gym","."],"correctOrder":["They","often","go","to","the","gym","."],"translation":"Ellos van a menudo al gimnasio.","hint":"Coloca el adverbio de frecuencia antes del verbo principal.","points":2},{"id":4,"words":["hates","She","doing","yoga","in","the","morning","."],"correctOrder":["She","hates","doing","yoga","in","the","morning","."],"translation":"Ella odia hacer yoga por la mañana.","hint":"Usa 'hate' seguido de un verbo en '-ing' para actividades no deseadas.","points":2},{"id":5,"words":["hardly","ever","goes","to","the","cinema","He","."],"correctOrder":["He","hardly","ever","goes","to","the","cinema","."],"translation":"Él casi nunca va al cine.","hint":"Usa 'hardly ever' para expresar una frecuencia muy baja.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex28',
  'a2-m2-l10',
  28,
  'speaking-analysis',
  $_$Speaking Analysis: Leisure Activities$_$,
  $_${"title":"Speaking Analysis: Leisure Activities","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are at a friend's house, and they ask if you enjoy the weekend football matches.","question":"Which response is most natural?","options":["A: I hardly ever watch football, but I like playing.","B: I am hardly ever watching football, but I like to play it.","C: I hardly watch football ever, but I like to play it.","D: I am hardly watching football, but I like to play it."],"correctAnswer":"A","explanation":"La opción A es la respuesta más natural porque 'hardly ever' se utiliza correctamente para indicar que rara vez ves fútbol, pero te gusta jugarlo.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are in a small group discussing your hobbies.","question":"Which phrase is used correctly in this context?","options":["A: I love to reading books in my free time.","B: I love reading books in my free time.","C: I love to read books in my free time.","D: I love read books in my free time."],"correctAnswer":"B","explanation":"La opción B es correcta porque 'love' puede ir seguido de '-ing' para hablar de actividades que disfrutas.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You meet someone at a park who asks if you like outdoor activities.","question":"Which is the most appropriate response?","options":["A: Yes, I like very much hiking.","B: Yes, I like hiking very much.","C: Yes, I very much like to hike.","D: Yes, I hiking like very much."],"correctAnswer":"B","explanation":"La opción B es la respuesta más apropiada porque 'like' va seguido de un gerundio en inglés.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are talking to a friend about your weekend plans.","question":"Which discourse marker best fits the sentence: '___, I think going to the beach would be fun.'","options":["A: Well","B: However","C: Therefore","D: Meanwhile"],"correctAnswer":"A","explanation":"La opción A, 'Well', es un marcador discursivo apropiado para introducir una opinión personal en una conversación informal.","points":2},{"id":"q5","type":"multiple-choice","scenario":"You are asked if you enjoy painting during a casual conversation.","question":"Which response is informal and fits the casual context?","options":["A: I am not a fan of painting.","B: I don't really fancy painting.","C: Painting is not my cup of tea.","D: I am not interested in painting."],"correctAnswer":"C","explanation":"La opción C, 'not my cup of tea', es una expresión informal que significa que no te gusta algo.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You are chatting with a neighbor about favorite sports.","question":"Which phrase shows the correct use of 'hardly ever'?","options":["A: I hardly ever play tennis, but I like watching it.","B: I hardly play ever tennis, but I like to watch it.","C: I ever hardly play tennis, but I like watching it.","D: I play hardly ever tennis, but I like to watch it."],"correctAnswer":"A","explanation":"La opción A es correcta porque 'hardly ever' se usa para indicar que rara vez haces algo.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex29',
  'a2-m2-l10',
  29,
  'word-search',
  $_$Word Search: Leisure Activities$_$,
  $_${"title":"Word Search: Leisure Activities","instructions":"Find the hidden words in the grid","words":["SWIMMING","READING","CYCLING","JOGGING","PAINTING","DANCING","FISHING","CAMPING"],"gridSize":10,"clues":[{"word":"SWIMMING","clue":"Nadar"},{"word":"READING","clue":"Leer"},{"word":"CYCLING","clue":"Montar en bicicleta"},{"word":"JOGGING","clue":"Correr"},{"word":"PAINTING","clue":"Pintar"},{"word":"DANCING","clue":"Bailar"},{"word":"FISHING","clue":"Pescar"},{"word":"CAMPING","clue":"Acampar"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex30',
  'a2-m2-l10',
  30,
  'crossword',
  $_$Crossword: Leisure Activities$_$,
  $_${"title":"Crossword: Leisure Activities","instructions":"Complete the crossword puzzle","items":[{"word":"SWIMMING","clue":"An activity you do in a pool. (like/love/hate + -ing)","direction":"across","row":0,"col":0},{"word":"JOGGING","clue":"Running at a slow pace. (like/love/hate + -ing)","direction":"down","row":0,"col":6},{"word":"TENNIS","clue":"A sport played with a racket and a ball.","direction":"across","row":2,"col":2},{"word":"READING","clue":"Looking at and understanding written words. (like/love/hate + -ing)","direction":"down","row":1,"col":4},{"word":"CYCLING","clue":"Riding a bicycle. (like/love/hate + -ing)","direction":"across","row":4,"col":1},{"word":"CHESS","clue":"A board game for two players.","direction":"down","row":3,"col":5},{"word":"GAMES","clue":"Activities for fun and entertainment.","direction":"across","row":5,"col":3}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex31',
  'a2-m2-l10',
  31,
  'reading-comprehension',
  $_$Leisure Activities$_$,
  $_${"title":"Leisure Activities","textPassage":"Maria loves painting in her free time. Every weekend, she goes to a small park near her house and paints the beautiful flowers and trees she sees. She likes using bright colors because they make her feel happy. Maria hardly ever misses a weekend without painting. Her friend, Lucas, likes playing football. He often invites Maria to play, but she usually says no because she prefers quieter activities. Sometimes, Maria goes to watch Lucas play with his team. She cheers for him and takes photos. Lucas loves playing football because it keeps him active and he enjoys being with his friends. After the game, they often go to a cafe and talk about their day. Maria and Lucas both love spending time outdoors, but they enjoy different activities.","wordCount":133,"estimatedReadingTime":2,"questions":[{"question":"What does Maria love doing in her free time?","options":["Playing football","Painting","Reading books","Going to the gym"],"answer":"Painting","explanation":"Maria ama pintar en su tiempo libre. El pasaje menciona que ella pinta cada fin de semana en un parque cerca de su casa."},{"question":"How often does Maria paint?","options":["Every day","Every weekend","Once a month","Rarely"],"answer":"Every weekend","explanation":"Maria pinta cada fin de semana, ya que el texto dice que casi nunca falta un fin de semana sin pintar."},{"question":"Why does Maria like using bright colors?","options":["Because they are her favorite","Because they make her happy","Because they are easy to use","Because they are cheap"],"answer":"Because they make her happy","explanation":"A Maria le gusta usar colores brillantes porque la hacen sentir feliz, como se menciona en el texto."},{"question":"True or False: Maria and Lucas both love playing football.","answer":"False","explanation":"Falso. Aunque Lucas ama jugar al fútbol, Maria prefiere actividades más tranquilas y normalmente dice que no cuando Lucas la invita a jugar."},{"question":"What does Lucas do after playing football?","options":["He goes home","He goes to a cafe","He plays another game","He watches a movie"],"answer":"He goes to a cafe","explanation":"Después de jugar al fútbol, Lucas y sus amigos a menudo van a un café a hablar sobre su día."},{"question":"What can be inferred about Maria's weekends?","options":["They are very busy","They are relaxing","They are stressful","They are boring"],"answer":"They are relaxing","explanation":"Se puede inferir que los fines de semana de Maria son relajantes porque disfruta de actividades tranquilas como pintar en el parque."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex32',
  'a2-m2-l10',
  32,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Acento de palabras en inglés$_$,
  $_${"title":"Práctica de pronunciación: Acento de palabras en inglés","instructions":"Practica la pronunciación de las siguientes oraciones en inglés, enfocándote en el acento de las palabras. Escucha el modelo de audio y repite cada oración. Analiza los consejos de pronunciación para mejorar.","targetSentences":[{"english":"I love playing tennis on weekends.","spanish":"Me encanta jugar al tenis los fines de semana.","phonetic":"/aɪ lʌv ˈpleɪɪŋ ˈtɛnɪs ɒn ˈwiːkˌɛndz/","audioModelUrl":"https://example.com/audio1","pronunciationTips":"Pon énfasis en 'love' y 'tennis'. Asegúrate de prolongar el sonido /iː/ en 'weekends'.","focusPoints":"Acento en 'love' y 'tennis'.","commonMistakesToAvoid":"No pronuncies 'tennis' como /ˈtniːs/."},{"english":"She hardly ever goes swimming.","spanish":"Ella casi nunca va a nadar.","phonetic":"/ʃi ˈhɑːdli ˈɛvə ˈɡəʊz ˈswɪmɪŋ/","audioModelUrl":"https://example.com/audio2","pronunciationTips":"Enfatiza 'hardly ever'. Asegúrate de pronunciar claramente el sonido /ʃ/ en 'she'.","focusPoints":"Acento en 'hardly ever'.","commonMistakesToAvoid":"No omitas el sonido /h/ en 'hardly'."},{"english":"My brother likes hiking in the mountains.","spanish":"A mi hermano le gusta hacer senderismo en las montañas.","phonetic":"/maɪ ˈbrʌðə laɪks ˈhaɪkɪŋ ɪn ðə ˈmaʊntɪnz/","audioModelUrl":"https://example.com/audio3","pronunciationTips":"Pon énfasis en 'likes' y 'hiking'. Asegúrate de pronunciar el sonido /aɪ/ en 'hiking' correctamente.","focusPoints":"Acento en 'likes' y 'hiking'.","commonMistakesToAvoid":"No pronuncies 'hiking' como /ˈhaɪkɪnɡɪ/."},{"english":"They enjoy watching movies at home.","spanish":"Disfrutan viendo películas en casa.","phonetic":"/ðeɪ ɪnˈdʒɔɪ ˈwɒtʃɪŋ ˈmuːviz æt həʊm/","audioModelUrl":"https://example.com/audio4","pronunciationTips":"Enfatiza 'enjoy' y 'movies'. Pronuncia claramente el sonido /w/ en 'watching'.","focusPoints":"Acento en 'enjoy' y 'movies'.","commonMistakesToAvoid":"No pronuncies 'movies' como /ˈmuviz/."},{"english":"My friend loves to paint landscapes.","spanish":"A mi amigo le encanta pintar paisajes.","phonetic":"/maɪ frɛnd lʌvz tə peɪnt ˈlændskeɪps/","audioModelUrl":"https://example.com/audio5","pronunciationTips":"Pon énfasis en 'loves' y 'paint'. Asegúrate de pronunciar correctamente el sonido /eɪ/ en 'paint'.","focusPoints":"Acento en 'loves' y 'paint'.","commonMistakesToAvoid":"No pronuncies 'landscapes' como /ˈlændskəps/."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex33',
  'a2-m2-l10',
  33,
  'multiple-choice',
  $_$Leisure Activities and Preferences$_$,
  $_${"title":"Leisure Activities and Preferences","instructions":"Selecciona la opción correcta para completar cada oración sobre actividades de ocio y preferencias.","questions":[{"id":"q1","question":"I _____ swimming in the summer because it's refreshing.","options":["A. love","B. loving","C. loves","D. loved"],"correctAnswer":"A","explanation":"La opción correcta es 'love' porque después de 'I' usamos la forma base del verbo para expresar una preferencia general."},{"id":"q2","question":"He _____ plays football, only once a month.","options":["A. always","B. never","C. hardly ever","D. really"],"correctAnswer":"C","explanation":"La opción correcta es 'hardly ever' que significa 'casi nunca', lo cual concuerda con la frecuencia baja mencionada."},{"id":"q3","question":"They like _____ in the park on weekends.","options":["A. running","B. run","C. runs","D. ran"],"correctAnswer":"A","explanation":"La opción correcta es 'running' porque después de 'like' se utiliza el gerundio para indicar una actividad que se disfruta."},{"id":"q4","question":"Maria _____ playing tennis, she prefers swimming.","options":["A. love","B. hate","C. hates","D. loving"],"correctAnswer":"C","explanation":"La opción correcta es 'hates' porque después de 'Maria' (tercera persona singular) el verbo debe terminar en 's' para indicar una acción que no le gusta."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex34',
  'a2-m2-l10',
  34,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"1. I _____ (like) to play soccer with my friends on Saturdays.","gap":"like","acceptableAnswers":["like"],"explanation":"Usamos 'like' para hablar de actividades que disfrutamos hacer.","points":1},{"sentence":"2. She _____ (hardly ever) goes to the cinema because she prefers to read books.","gap":"hardly ever","acceptableAnswers":["hardly ever"],"explanation":"'Hardly ever' se utiliza para indicar que algo ocurre con muy poca frecuencia.","points":1},{"sentence":"3. We _____ (love) going to the beach during the summer.","gap":"love","acceptableAnswers":["love"],"explanation":"Usamos 'love' seguido de un verbo en '-ing' para hablar de actividades que nos encantan.","points":1},{"sentence":"4. My brother _____ (hate) swimming in the pool because he finds it boring.","gap":"hates","acceptableAnswers":["hates"],"explanation":"Usamos 'hate' para actividades que no nos gustan nada.","points":1},{"sentence":"5. Do you _____ (like) playing tennis with your sister?","gap":"like","acceptableAnswers":["like"],"explanation":"Preguntamos con 'Do you like' para saber si a alguien le gusta una actividad.","points":1},{"sentence":"6. They _____ (hardly ever) watch TV because they prefer playing board games.","gap":"hardly ever","acceptableAnswers":["hardly ever"],"explanation":"'Hardly ever' indica que algo ocurre casi nunca.","points":1},{"sentence":"7. I _____ (enjoy) going for long walks in the park.","gap":"enjoy","acceptableAnswers":["enjoy"],"explanation":"Usamos 'enjoy' seguido de un verbo en '-ing' para indicar que una actividad es placentera.","points":1},{"sentence":"8. She _____ (doesn't like) playing video games because she finds them too difficult.","gap":"doesn't like","acceptableAnswers":["doesn't like"],"explanation":"Usamos 'doesn't like' para hablar de actividades que no nos gustan.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex35',
  'a2-m2-l10',
  35,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["many","How","players","are","there","?"],"correctOrder":["How","many","players","are","there","?"],"translation":"¿Cuántos jugadores hay?","hint":"Usa 'How many' para preguntar por cantidad con sustantivos contables.","points":2},{"id":2,"words":["going","to","They","visit","the","museum","are","."],"correctOrder":["They","are","going","to","visit","the","museum","."],"translation":"Ellos van a visitar el museo.","hint":"Emplea 'going to' para expresar planes futuros.","points":2},{"id":3,"words":["would","What","you","like","to","drink","?"],"correctOrder":["What","would","you","like","to","drink","?"],"translation":"¿Qué te gustaría beber?","hint":"Empieza la pregunta con 'What' y usa 'would like to' para expresar deseos.","points":2},{"id":4,"words":["is","This","the","most","interesting","book","."],"correctOrder":["This","is","the","most","interesting","book","."],"translation":"Este es el libro más interesante.","hint":"Usa 'the most' para formar el superlativo de adjetivos largos.","points":2},{"id":5,"words":["must","You","wear","a","helmet","."],"correctOrder":["You","must","wear","a","helmet","."],"translation":"Debes llevar un casco.","hint":"Usa 'must' para indicar obligación.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex36',
  'a2-m2-l10',
  36,
  'speaking-analysis',
  $_$Speaking Analysis: Leisure Activities$_$,
  $_${"title":"Speaking Analysis: Leisure Activities","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"Your friend asks you about your hobbies.","question":"What is the most natural response?","options":["A: I like play soccer on weekends.","B: I like playing soccer on weekends.","C: I like to playing soccer on weekends.","D: I like soccer play on weekends."],"correctAnswer":"B","explanation":"La forma correcta es 'I like playing' porque después de 'like' se utiliza el verbo con '-ing'.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are telling someone about how often you go running.","question":"Which sentence is correct?","options":["A: I run always in the morning.","B: I run hardly in the morning.","C: I hardly ever run in the morning.","D: I hardly run ever in the morning."],"correctAnswer":"C","explanation":"'Hardly ever' significa 'casi nunca' y es la opción correcta para describir la frecuencia de una actividad.","points":2},{"id":"q3","type":"multiple-choice","scenario":"You're discussing your dislike for a particular sport.","question":"Which sentence is most appropriate?","options":["A: I hate to play basketball.","B: I hate playing basketball.","C: I hate play basketball.","D: I hate to playing basketball."],"correctAnswer":"B","explanation":"Después de 'hate', se utiliza el verbo con '-ing' para hablar de actividades que no te gustan.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You are asked about your weekend plans.","question":"Which is the most informal response?","options":["A: I might partake in some leisure activities.","B: I may engage in some recreational activities.","C: I think I'll do some fun stuff.","D: I will participate in several leisure pursuits."],"correctAnswer":"C","explanation":"'Fun stuff' es una expresión informal y adecuada para una conversación casual.","points":2},{"id":"q5","type":"multiple-choice","scenario":"Someone asks you if you enjoy swimming.","question":"Which reply is most appropriate in a friendly conversation?","options":["A: Swimming is a pastime I find enjoyable.","B: I derive pleasure from swimming.","C: I like swimming a lot.","D: Swimming is quite satisfactory."],"correctAnswer":"C","explanation":"La respuesta 'I like swimming a lot' es directa y común en conversaciones informales.","points":2},{"id":"q6","type":"multiple-choice","scenario":"Discussing a new hobby with a friend.","question":"Which sentence uses the correct idiomatic expression?","options":["A: I have taken up gardening recently.","B: I have taken down gardening recently.","C: I have put up gardening recently.","D: I have put down gardening recently."],"correctAnswer":"A","explanation":"'Take up' es la expresión idiomática correcta para empezar un nuevo hobby.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex37',
  'a2-m2-l10',
  37,
  'word-search',
  $_$Word Search: Leisure Activities$_$,
  $_${"title":"Word Search: Leisure Activities","instructions":"Find the hidden words in the grid","words":["SWIMMING","CYCLING","HIKING","READING","PAINTING","DANCING","JOGGING","FISHING"],"gridSize":10,"clues":[{"word":"SWIMMING","clue":"An activity you do in the water"},{"word":"CYCLING","clue":"Riding a bicycle"},{"word":"HIKING","clue":"Walking in nature"},{"word":"READING","clue":"Enjoying a book"},{"word":"PAINTING","clue":"Creating art with colors"},{"word":"DANCING","clue":"Moving to music"},{"word":"JOGGING","clue":"Running at a slow pace"},{"word":"FISHING","clue":"Catching fish for fun"}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex38',
  'a2-m2-l10',
  38,
  'crossword',
  $_$Crossword: Leisure Activities$_$,
  $_${"title":"Crossword: Leisure Activities","instructions":"Complete the crossword puzzle","items":[{"word":"SWIMMING","clue":"An activity you do in a pool. You might like or love this.","direction":"across","row":0,"col":0},{"word":"READING","clue":"You do this with books. Some people hardly ever do this.","direction":"down","row":0,"col":3},{"word":"CYCLING","clue":"Riding a bike. Some people love doing this on weekends.","direction":"across","row":2,"col":1},{"word":"DANCING","clue":"Moving to music. Many people enjoy this at parties.","direction":"down","row":1,"col":5},{"word":"RUNNING","clue":"A sport you can do in the park. You might hate this if you get tired quickly.","direction":"across","row":4,"col":2},{"word":"PAINTING","clue":"Creating art with colors. Some people find this relaxing.","direction":"down","row":3,"col":5},{"word":"GARDENING","clue":"Working with plants. Many people love doing this in their free time.","direction":"across","row":6,"col":0}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex39',
  'a2-m2-l10',
  39,
  'reading-comprehension',
  $_$Leisure Activities and Preferences$_$,
  $_${"title":"Leisure Activities and Preferences","textPassage":"Many people enjoy different leisure activities in their free time. Some people love playing sports like football or tennis because it helps them stay active and healthy. Others prefer quieter activities such as reading books or painting. Maria is one of those who love reading. She reads almost every day and likes discovering new stories. On the other hand, her brother Tom hardly ever reads. He finds it boring and prefers playing basketball with his friends. He says it's exciting and fun. In their family, they also enjoy going to the park together on weekends. They like having picnics and playing games. Maria sometimes paints pictures of the trees and flowers in the park. Tom loves riding his bike around the park. Even though they have different interests, they both agree that leisure activities are important for relaxing and having fun.","wordCount":138,"estimatedReadingTime":2,"questions":[{"question":"What is the main idea of the passage?","options":["Different people have different leisure activities.","Everyone loves reading books.","Maria and Tom have the same hobbies.","Playing sports is better than reading."],"answer":"Different people have different leisure activities.","explanation":"La idea principal del texto es que las personas disfrutan de diferentes actividades de ocio según sus preferencias."},{"question":"What does Maria love doing?","options":["Playing basketball","Reading books","Painting","Riding a bike"],"answer":"Reading books","explanation":"El texto menciona que a Maria le encanta leer libros casi todos los días."},{"question":"How often does Tom read?","options":["Every day","Once a week","Hardly ever","Every weekend"],"answer":"Hardly ever","explanation":"Tom hardly ever reads porque encuentra la lectura aburrida y prefiere jugar al baloncesto."},{"question":"Why does Tom prefer playing basketball?","options":["Because it is boring","Because it is exciting and fun","Because it is quiet","Because it helps him discover new stories"],"answer":"Because it is exciting and fun","explanation":"Tom prefiere jugar al baloncesto porque lo encuentra emocionante y divertido, según el texto."},{"question":"TRUE or FALSE: Maria and Tom have the same interest in reading.","options":["TRUE","FALSE"],"answer":"FALSE","explanation":"Es falso porque Maria ama leer, mientras que Tom casi nunca lee y lo encuentra aburrido."},{"question":"What does the family like doing together on weekends?","options":["Going to the cinema","Visiting the zoo","Going to the park","Shopping"],"answer":"Going to the park","explanation":"La familia disfruta yendo al parque los fines de semana para hacer picnics y jugar juntos."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex40',
  'a2-m2-l10',
  40,
  'pronunciation-practice',
  $_$Práctica de pronunciación: Estrés de palabras$_$,
  $_${"title":"Práctica de pronunciación: Estrés de palabras","instructions":"Practica la pronunciación enfocándote en el estrés de las palabras. Escucha cada oración y repite prestando atención al estrés en las palabras clave.","targetSentences":[{"englishSentence":"I hardly ever play tennis.","spanishTranslation":"Casi nunca juego al tenis.","phoneticTranscription":"/ˈhɑːrdli ˈɛvər pleɪ ˈtɛnɪs/","audioModelURL":"https://audiomodel.com/hardlyevertennis","pronunciationTips":"Enfócate en el énfasis inicial en 'hardly' y 'ever', asegurándote de que 'play' y 'tennis' también tengan un sonido claro.","focusPoints":"El estrés en 'hardly' y 'ever'.","commonMistakes":"No subestimes el estrés en 'hardly'; puede cambiar el significado si no se enfatiza correctamente."},{"englishSentence":"She loves swimming in the sea.","spanishTranslation":"Le encanta nadar en el mar.","phoneticTranscription":"/ʃi lʌvz ˈswɪmɪŋ ɪn ðə siː/","audioModelURL":"https://audiomodel.com/lovesswimmingsea","pronunciationTips":"Asegúrate de enfatizar 'loves' y 'swimming' para que se escuchen claramente.","focusPoints":"El estrés en 'loves' y 'swimming'.","commonMistakes":"No olvides el sonido al final de 'loves'."},{"englishSentence":"We must visit the new restaurant.","spanishTranslation":"Debemos visitar el nuevo restaurante.","phoneticTranscription":"/wi mʌst ˈvɪzɪt ðə njuː ˈrɛstrɒnt/","audioModelURL":"https://audiomodel.com/mustvisitrestaurant","pronunciationTips":"Enfatiza 'must' y 'restaurant' para asegurar claridad en la intención de la frase.","focusPoints":"El estrés en 'must' y 'restaurant'.","commonMistakes":"No omitas la 't' en 'must'."},{"englishSentence":"He enjoys reading books.","spanishTranslation":"Disfruta leyendo libros.","phoneticTranscription":"/hi ɪnˈdʒɔɪz ˈriːdɪŋ bʊks/","audioModelURL":"https://audiomodel.com/enjoysreadingbooks","pronunciationTips":"Concentra el estrés en 'enjoys' y 'reading' para una pronunciación efectiva.","focusPoints":"El estrés en 'enjoys' y 'reading'.","commonMistakes":"No olvides el sonido 's' al final de 'enjoys'."},{"englishSentence":"She should try new sports.","spanishTranslation":"Debería probar nuevos deportes.","phoneticTranscription":"/ʃi ʃʊd traɪ njuː spɔːrts/","audioModelURL":"https://audiomodel.com/shouldtrysports","pronunciationTips":"Enfatiza 'should' y 'try' para que se perciba claramente la sugerencia.","focusPoints":"El estrés en 'should' y 'try'.","commonMistakes":"Asegúrate de no suavizar demasiado 'should'."},{"englishSentence":"We like going to the park.","spanishTranslation":"Nos gusta ir al parque.","phoneticTranscription":"/wi laɪk ˈɡoʊɪŋ tuː ðə pɑːrk/","audioModelURL":"https://audiomodel.com/likegoingpark","pronunciationTips":"El estrés debe caer en 'like' y 'going' para que suene natural.","focusPoints":"El estrés en 'like' y 'going'.","commonMistakes":"No olvides el sonido 'g' en 'going'."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex41',
  'a2-m2-l10',
  41,
  'multiple-choice',
  $_$Leisure Activities: Like/Love/Hate + -ing$_$,
  $_${"title":"Leisure Activities: Like/Love/Hate + -ing","instructions":"Choose the correct option for each question.","questions":[{"id":"q1","question":"Anna loves ______ in the park every morning.","options":["A. running","B. to run","C. run","D. ran"],"correctAnswer":"A","explanation":"La forma correcta es usar 'like/love/hate + verbo en -ing' para hablar de actividades que disfrutamos. En este caso, 'running' es la forma correcta."},{"id":"q2","question":"Do you like ______ books in your free time?","options":["A. to read","B. reading","C. read","D. reads"],"correctAnswer":"B","explanation":"Después de 'like', usamos el verbo con '-ing' para actividades que nos gustan. Por lo tanto, 'reading' es correcto."},{"id":"q3","question":"I hardly ever ______ swimming because I don't enjoy it.","options":["A. go","B. goes","C. going","D. went"],"correctAnswer":"A","explanation":"Usamos 'hardly ever' para decir que algo no sucede a menudo. La forma correcta del verbo es 'go' para indicar acción general."},{"id":"q4","question":"Peter hates ______ basketball with his friends.","options":["A. play","B. playing","C. plays","D. played"],"correctAnswer":"B","explanation":"Con 'hate', el verbo que sigue debe estar en '-ing' para indicar una actividad que no nos gusta. Así, 'playing' es la opción correcta."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex42',
  'a2-m2-l10',
  42,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"Maria ______ (like) going to the park on weekends.","gap":"likes","explanation":"Usamos 'likes' porque 'Maria' es tercera persona singular en presente simple. En inglés, añadimos 's' al verbo en estas situaciones.","points":1},{"sentence":"I ______ (not like) playing basketball, but I really want to learn.","gap":"don't like","explanation":"Para decir que no nos gusta algo en primera persona singular, usamos 'don't like'.","points":1},{"sentence":"They ______ (love) dancing at the club every Friday.","gap":"love","explanation":"Usamos 'love' porque 'they' es plural y en presente simple no se modifica el verbo.","points":1},{"sentence":"We ______ (hardly ever, go) to the cinema because we prefer watching movies at home.","gap":"hardly ever go","explanation":"'Hardly ever' es un adverbio de frecuencia que se usa con el verbo en presente simple.","points":1},{"sentence":"Tom ______ (hate) waking up early for soccer practice.","gap":"hates","explanation":"'Tom' es tercera persona singular, así que añadimos 's' al verbo 'hate'.","points":1},{"sentence":"Do you ______ (often) read books in your free time?","gap":"often","explanation":"En este caso usamos un adverbio de frecuencia para preguntar con qué frecuencia se lee.","points":1},{"sentence":"Laura ______ (hardly ever, watch) TV in the evenings.","gap":"hardly ever watches","explanation":"Añadimos 'es' al verbo 'watch' porque 'Laura' es tercera persona singular.","points":1},{"sentence":"My friends ______ (sometimes) play video games on weekends.","gap":"sometimes","explanation":"'My friends' es plural, y usamos un adverbio de frecuencia para indicar que ocurre algunas veces.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex43',
  'a2-m2-l10',
  43,
  'sentence-building',
  $_$Exercise$_$,
  $_${"sentences":[{"id":1,"words":["I","like","playing","football","on","Sundays","."],"correctOrder":["I","like","playing","football","on","Sundays","."],"translation":"Me gusta jugar al fútbol los domingos.","hint":"Use 'like + -ing' to express an activity you enjoy.","points":2},{"id":2,"words":["My","brother","hardly","ever","goes","swimming","."],"correctOrder":["My","brother","hardly","ever","goes","swimming","."],"translation":"Mi hermano casi nunca va a nadar.","hint":"Use 'hardly ever' to express something that rarely happens.","points":2},{"id":3,"words":["She","loves","reading","books","at","the","beach","."],"correctOrder":["She","loves","reading","books","at","the","beach","."],"translation":"Ella ama leer libros en la playa.","hint":"Use 'love + -ing' to express an activity you adore.","points":2},{"id":4,"words":["They","hate","watching","horror","movies","."],"correctOrder":["They","hate","watching","horror","movies","."],"translation":"Ellos odian ver películas de terror.","hint":"Use 'hate + -ing' to express an activity you dislike.","points":2},{"id":5,"words":["We","are","going","to","visit","the","museum","next","Saturday","."],"correctOrder":["We","are","going","to","visit","the","museum","next","Saturday","."],"translation":"Vamos a visitar el museo el próximo sábado.","hint":"Use 'going to' to express a planned future activity.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex44',
  'a2-m2-l10',
  44,
  'speaking-analysis',
  $_$Speaking Analysis: Leisure Activities$_$,
  $_${"title":"Speaking Analysis: Leisure Activities","instructions":"Choose the most appropriate option for each speaking situation","questions":[{"id":"q1","type":"multiple-choice","scenario":"You are talking to a friend about your weekend plans.","question":"Your friend asks, 'Do you like going to the gym on weekends?' How do you reply?","options":["A: I love going to the gym.","B: I hardly go to the gym.","C: I should go to the gym.","D: I must go to the gym."],"correctAnswer":"A","explanation":"La respuesta más natural es expresar un gusto o preferencia usando 'like/love'.","points":2},{"id":"q2","type":"multiple-choice","scenario":"You are invited to a formal dinner party.","question":"How might you reply to an invitation to join a game of tennis?","options":["A: Yeah, sure!","B: I would be delighted to join.","C: Why not?","D: Sounds cool!"],"correctAnswer":"B","explanation":"En una situación formal, es mejor usar un lenguaje más diplomático y cortés.","points":2},{"id":"q3","type":"multiple-choice","scenario":"A friend tells you they 'hit the books' during the weekend.","question":"What does your friend mean by this?","options":["A: They went to a concert.","B: They studied a lot.","C: They went hiking.","D: They played soccer."],"correctAnswer":"B","explanation":"'Hit the books' es una expresión idiomática que significa estudiar mucho.","points":2},{"id":"q4","type":"multiple-choice","scenario":"You're chatting with a friend about a new hobby.","question":"Which discourse marker fits best in this sentence: 'I started painting last week, ____, it's been really fun!'","options":["A: so","B: however","C: although","D: actually"],"correctAnswer":"D","explanation":"'Actually' se usa para dar información adicional o nueva que puede ser sorprendente.","points":2},{"id":"q5","type":"multiple-choice","scenario":"A colleague asks if you like surfing.","question":"What is the most polite way to express that you don't enjoy it?","options":["A: I hate surfing.","B: I'm not really into surfing, but thanks for asking.","C: Surfing is the worst.","D: I never want to surf."],"correctAnswer":"B","explanation":"Esta opción es la más educada, ya que suaviza la negativa y agradece al interlocutor.","points":2},{"id":"q6","type":"multiple-choice","scenario":"You're watching a match with friends.","question":"Which expression shows excitement in American English?","options":["A: That's brilliant!","B: Awesome!","C: That's wicked!","D: Smashing!"],"correctAnswer":"B","explanation":"'Awesome' es una expresión informal de entusiasmo común en inglés americano.","points":2},{"id":"q7","type":"multiple-choice","scenario":"A friend asks you about your weekend plans.","question":"Which phrase helps you take your turn in the conversation?","options":["A: Anyway,","B: In addition,","C: By the way,","D: As a result,"],"correctAnswer":"C","explanation":"'By the way' es una frase que se usa para cambiar de tema o volver a tomar el turno en una conversación.","points":2}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
