INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex46',
  'a2-m2-l10',
  46,
  'crossword',
  $_$Crossword: Leisure Activities$_$,
  $_${"title":"Crossword: Leisure Activities","instructions":"Complete the crossword puzzle","items":[{"word":"SWIMMING","clue":"An activity you do in the pool and many people love doing it in summer.","direction":"across","row":0,"col":0},{"word":"HIKING","clue":"A leisure activity in the mountains that some people hardly ever do.","direction":"down","row":0,"col":6},{"word":"CYCLING","clue":"This activity involves riding a bike, and many people enjoy it on weekends.","direction":"across","row":2,"col":0},{"word":"FISHING","clue":"A relaxing activity that involves catching fish, often done by a lake.","direction":"down","row":0,"col":3},{"word":"READING","clue":"Many people love doing this with books in their free time.","direction":"across","row":4,"col":2},{"word":"PAINTING","clue":"An artistic activity loved by people who enjoy creating art.","direction":"down","row":1,"col":8},{"word":"GARDENING","clue":"This involves growing plants and flowers, often done in a yard.","direction":"across","row":6,"col":0},{"word":"JOGGING","clue":"A form of running that some people hardly ever do in the morning.","direction":"down","row":0,"col":9}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex47',
  'a2-m2-l10',
  47,
  'reading-comprehension',
  $_$Leisure Activities - A Day at the Park$_$,
  $_${"title":"Leisure Activities - A Day at the Park","text":"Every weekend, Lisa loves going to the park with her family. She likes playing frisbee with her brother and enjoys cycling around the park paths. Her parents like relaxing on a picnic blanket and reading books. Sometimes, they bring a kite to fly if the weather is nice. Lisa hardly ever misses a weekend at the park because it's her favorite place to have fun. When the weather is bad, she feels sad because she hates staying indoors all day. In the park, she also loves watching people playing different sports like football and basketball. Even though she doesn't play these sports, she enjoys watching them. Lisa thinks that spending time outdoors is important and fun. She believes everyone should spend more time in parks and enjoy nature.","wordCount":134,"estimatedReadingTime":2,"questions":[{"question":"What does Lisa like doing at the park?","options":["Playing football","Playing frisbee","Playing basketball","Playing video games"],"answer":"Playing frisbee","explanation":"Lisa le gusta jugar al frisbee con su hermano en el parque."},{"question":"What do Lisa's parents like to do at the park?","options":["Play sports","Read books","Fly a kite","Cycle around"],"answer":"Read books","explanation":"Los padres de Lisa disfrutan relajándose en una manta de picnic y leyendo libros."},{"question":"How often does Lisa go to the park?","options":["Every day","Hardly ever","Every weekend","Once a month"],"answer":"Every weekend","explanation":"Lisa va al parque todos los fines de semana porque es su lugar favorito para divertirse."},{"question":"Lisa enjoys watching people play which sports?","options":["Tennis and golf","Cricket and rugby","Football and basketball","Swimming and running"],"answer":"Football and basketball","explanation":"Aunque Lisa no juega al fútbol ni al baloncesto, le gusta ver a otros practicarlos."},{"question":"True or False: Lisa hates spending time outdoors.","options":["True","False"],"answer":"False","explanation":"Lisa piensa que pasar tiempo al aire libre es importante y divertido, así que le gusta estar afuera."},{"question":"Why does Lisa feel sad sometimes?","options":["She can't fly a kite","She misses the park","She hates staying indoors","She doesn't like cycling"],"answer":"She hates staying indoors","explanation":"Lisa se siente triste cuando el clima es malo porque odia quedarse en casa todo el día."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex48',
  'a2-m2-l10',
  48,
  'pronunciation-practice',
  $_$Práctica de Pronunciación: Estrés de frases$_$,
  $_${"title":"Práctica de Pronunciación: Estrés de frases","instructions":"Practica el estrés de frases en inglés con las siguientes oraciones. Escucha las palabras resaltadas y presta atención a la forma en que se enfatizan. Usa los consejos de pronunciación para mejorar tu entonación.","targetSentences":[{"english":"I hardly ever play tennis, but I love watching it.","spanish":"Casi nunca juego al tenis, pero me encanta verlo.","ipa":"/ˈhɑːrdli/ /ˈevər/ /pleɪ/ /ˈtɛnɪs/","audioModelUrl":"https://example.com/audio1","pronunciationTips":"Enfatiza 'hardly ever' para destacar la frecuencia baja y 'love' para la emoción.","focusPoints":["Estrés en 'hardly'","Estrés en 'love'"],"commonMistakes":["No enfatizar 'hardly ever'","No marcar 'love'"]},{"english":"She loves swimming in the ocean every weekend.","spanish":"A ella le encanta nadar en el océano cada fin de semana.","ipa":"/ˈʃiː/ /lʌvz/ /ˈswɪmɪŋ/ /ˈoʊʃən/","audioModelUrl":"https://example.com/audio2","pronunciationTips":"Resalta 'loves' para mostrar pasión y 'swimming' como actividad preferida.","focusPoints":["Estrés en 'loves'","Estrés en 'swimming'"],"commonMistakes":["No enfatizar 'loves'","No resaltar 'swimming'"]},{"english":"We have to decide if we will join the dance class.","spanish":"Tenemos que decidir si nos uniremos a la clase de baile.","ipa":"/wiː/ /hæv/ /tʊ/ /dɪˈsaɪd/ /dæns/ /klæs/","audioModelUrl":"https://example.com/audio3","pronunciationTips":"Enfatiza 'decide' para mostrar la importancia de la decisión.","focusPoints":["Estrés en 'decide'","Estrés en 'dance'"],"commonMistakes":["No enfatizar 'decide'","No marcar 'dance'"]},{"english":"I like reading books about different sports.","spanish":"Me gusta leer libros sobre diferentes deportes.","ipa":"/ˈlaɪk/ /ˈriːdɪŋ/ /ˈbʊks/ /ˈspɔrts/","audioModelUrl":"https://example.com/audio4","pronunciationTips":"Enfatiza 'like' para mostrar preferencia y 'books' para el objeto.","focusPoints":["Estrés en 'like'","Estrés en 'books'"],"commonMistakes":["No enfatizar 'like'","No resaltar 'books'"]},{"english":"He must finish his painting before dinner.","spanish":"Debe terminar su pintura antes de la cena.","ipa":"/hiː/ /mʌst/ /ˈfɪnɪʃ/ /ˈpeɪntɪŋ/","audioModelUrl":"https://example.com/audio5","pronunciationTips":"Enfatiza 'must' para la obligación y 'finish' para la acción.","focusPoints":["Estrés en 'must'","Estrés en 'finish'"],"commonMistakes":["No enfatizar 'must'","No destacar 'finish'"]}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex49',
  'a2-m2-l10',
  49,
  'multiple-choice',
  $_$Leisure Activities: Hobbies and Sports$_$,
  $_${"title":"Leisure Activities: Hobbies and Sports","instructions":"Choose the correct option for each question.","questions":[{"id":"q1","question":"I _______ playing tennis on weekends. (love)","options":["love","loves","loving","loved"],"correctAnswer":"love","explanation":"Usamos 'love' + -ing para hablar de actividades que nos gustan mucho. En este caso, 'I love playing tennis' es correcto."},{"id":"q2","question":"They _______ go to the park on weekdays. (casi nunca)","options":["hardly ever","always","often","usually"],"correctAnswer":"hardly ever","explanation":"'Hardly ever' se usa para decir que algo casi nunca ocurre. Por eso, 'They hardly ever go to the park on weekdays' es correcto."},{"id":"q3","question":"She doesn't like _______ basketball.","options":["play","playing","plays","played"],"correctAnswer":"playing","explanation":"Después de 'like', usamos el verbo en -ing para hablar de actividades. Por lo tanto, 'She doesn't like playing basketball' es correcto."},{"id":"q4","question":"Do you _______ going to the gym?","options":["enjoy","enjoys","enjoyed","enjoying"],"correctAnswer":"enjoy","explanation":"Con 'do you' usamos la forma base del verbo, así que 'Do you enjoy going to the gym?' es correcto."},{"id":"q5","question":"We _______ watch movies on Friday nights. (siempre)","options":["hardly ever","sometimes","never","always"],"correctAnswer":"always","explanation":"'Always' se usa para indicar que algo ocurre todo el tiempo o regularmente. Por eso, 'We always watch movies on Friday nights' es correcto."}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES (
  'a2-m2-l10-ex50',
  'a2-m2-l10',
  50,
  'fill-blank',
  $_$Exercise$_$,
  $_${"questions":[{"sentence":"I like __________ (play) soccer with my friends on weekends.","acceptableAnswers":["playing"],"explanation":"La estructura 'like + -ing' se utiliza para hablar de actividades que nos gustan. En este caso, se refiere a 'playing soccer'.","points":1},{"sentence":"She loves __________ (read) books in her free time.","acceptableAnswers":["reading"],"explanation":"La estructura 'love + -ing' se usa para hablar de actividades que amamos. Aquí, se refiere a 'reading books'.","points":1},{"sentence":"They hate __________ (wake up) early on Saturdays.","acceptableAnswers":["waking up"],"explanation":"La estructura 'hate + -ing' se emplea para actividades que no nos gustan. En este caso, 'waking up early'.","points":1},{"sentence":"We hardly ever __________ (go) skiing in the winter.","acceptableAnswers":["go"],"explanation":"'Hardly ever' se utiliza para indicar que algo ocurre muy pocas veces. Después de 'hardly ever' se usa el verbo en su forma base.","points":1},{"sentence":"Do you like __________ (watch) movies at the cinema?","acceptableAnswers":["watching"],"explanation":"La estructura 'like + -ing' se usa para hablar de actividades que nos gustan, como 'watching movies'.","points":1},{"sentence":"He hardly ever __________ (play) tennis because he prefers soccer.","acceptableAnswers":["plays"],"explanation":"'Hardly ever' indica que algo sucede raramente. El verbo 'play' está conjugado para tercera persona singular en presente simple.","points":1},{"sentence":"I don't like __________ (swim) in cold water.","acceptableAnswers":["swimming"],"explanation":"La estructura 'not like + -ing' se usa para hablar de actividades que no nos gustan. Aquí, 'swimming'.","points":1},{"sentence":"She hardly ever __________ (watch) sports on TV.","acceptableAnswers":["watches"],"explanation":"'Hardly ever' se usa con la forma de presente simple del verbo. Aquí 'watch' se conjuga para tercera persona singular.","points":1}]}$_$
) ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content;

