INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U4_B3_I3', 'ingles-b1-U4', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''by the students'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''by the students'':","mastery_tag":"grammar:b1-passive","explanation":"La estructura ''will be'' es fundamental en la voz pasiva para indicar acciones futuras.","stimulus_en":"The students will complete the project.\n\nThe project will be completed... ...","correct_answer":"by the students.","example":"by the students","complexity":2,"concept_tags":["grammar","passive","unit"]}')
